    mesh mesh_create_circle(int points,float scale) {
    mesh msh_out;
    vec3[mesh_vertlimit] vecs;

        for(int a=0; a<points; a++) {
        float fac=float(a)/float(points);
        float x=sin(fac*pi*2.0)*scale;
        float y=cos(fac*pi*2.0)*scale;
        vecs[a]=vec3(x,y,0.0);
        }

    msh_out.vertices=vecs;
    msh_out.vcount=points;
    return msh_out;
    }


