    mat3 rot_y = mat3(cosi,0.0, sina,
                      0.0, 1.0, 0.0,
                      -sina,1.0,cosi);

        for(int a=0; a<points; a++) {
        //vec3 it=msh.vertices[a];

        msh.vertices[a]*=rot_y;
