    return msh;
    }

    // rotate on X axis
    mesh mesh_rotate_z(mesh msh,float angle) { 
    float sina=sin(angle);
    float cosi=cos(angle);
    int points=msh.vcount;

    mat3 rot_z = mat3(cosi,-sina, 0.0,
                      sina, cosi, 0.0,
                      0.0,0.0,1.0);

        for(int a=0; a<points; a++) {
        //vec3 it=msh.vertices[a];

        msh.vertices[a]*=rot_z;
        }    


    return msh;
    }

    // wave animation 
    mesh mesh_wave_animation(mesh msh,float time, float intensity) { 
    int points=msh.vcount;

        for(int a=0; a<points; a++) {
        //vec3 it=msh.vertices[a];
        float sina=sin((msh.vertices[a].x*pi*2.0)+time);
        float cosi=cos((msh.vertices[a].y*pi*2.0)+time);    

        msh.vertices[a].x+=(sina-cosi)*intensity;
        }    


    return msh;
    }


