float shape(int k, vec3 center, float arg) {
  if (k == 0)
    return sdSphere(center, arg);
  else if (k == 1)
    return sdOctahedron(center, arg);
  else if (k == 2)
    return sdBox(center, vec3(0.3, 0.2, 0.3));
  else if (k == 3)
    return sdPyramid(center, 0.5);
  else if (k == 4)
    return sdCappedCone(center, vec3(0.3, 0.1, 0), vec3(0.0, 0.3, 0.5),
                        0.5, 0.6);
  else if (k == 5)
    return sdCone(center, vec2(0.5, 0.4), 0.8);
  else return 1e10;
}

