#define rot(a)        mat2(cos(a),sin(a),-sin(a),cos(a))  // rotation                  
//#define fwidth(v)   sqrt( dFdx(v)*dFdx(v) + dFdy(v)*dFdy(v) )                 

void mainImage(out vec4 O, vec2 u)
{
    vec2  R =  iResolution.xy, 
          U = ( 2.*u - R ) / R.y,
          M = iMouse.xy!=vec2(0) ? 2.*iMouse.xy/R - 1. : vec2(sin(iTime),0);
    vec3  V,
          D = normalize(vec3(U, 1)),             // ray direction
          p = vec3(0,1,-1),                      // ray start 
          L = vec3(0);                           // lookat

 // p.x += M.x*U.x; p.xz /=length(p.xz);         // Xcam changes with U.x
 // p.x += M.x*U.y;                              // Xcam changes with U.y
    p.y += M.x*U.y;                              // Ycam changes with U.y
 // p.xz*= rot(M.x*(.5+.5*U.y));                 // XZcam rotate with U.y
 // p.y += M.x*U.x;                              // Ycam changes with U.x
    p *= 1.+M.y;                                 // Zoom

    V = L - p;                                   // D from Lookat
    float phi = atan( V.y, length(V.xz) ),
          theta = atan(V.x,V.z);   
    D.yz *= rot(phi); D.xz *= rot(theta); 


    float d = -p.y/D.y;                          // intersect plane
    if (d<0.) { O-=O; return; }
    p += d* D;

    O = 6.28*vec4(1,2,4,0);                      // color scheme (checker texture with AA )
    O = sin(O*p.x) * sin(O*p.z);
    O = .5 + .5* O/fwidth(O)  * smoothstep(1.,0.,length(fwidth(p.xz)));
                              // * smoothstep(0.,-5.,log2(fwidth(p.z))); // AA at horizon
    p = abs(p); if (min(p.x,p.z)>.5) O = O.rrrr; // B&W out of [-1,1]²
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float col = 1.0;
    float minLineThickness = 0.1;
    float speed = 3.0;
    float pulseWidth = 200.;

    float b = cos(iTime*speed+fragCoord.x/pulseWidth)*0.05+minLineThickness;

    col *= smoothstep(b, 0.00, abs(uv.y));
    fragColor = vec4(.0, col, 0.0,1.0);
}

#define FAR 200.0
#define EPSILON 1e-3
#define ID(func, nid) dist = func(pos); if (dist < closest) { closest = dist; id = nid; }

vec2 rand2d(vec2 uv) {
    return fract(sin(vec2(dot(uv, vec2(12.34, 45.67)),
        dot(uv, vec2(78.9, 3.14)))) * 12345.67) * 2.0 - 1.0;
}

float perlin(vec2 uv) {
    vec2 u = floor(uv);
    vec2 f = fract(uv);
    vec2 s = smoothstep(0.0, 1.0, f);

    vec2 a = rand2d(u);
    vec2 b = rand2d(u + vec2(1.0, 0.0));
    vec2 c = rand2d(u + vec2(0.0, 1.0));
    vec2 d = rand2d(u + vec2(1.0, 1.0));

    return mix(mix(dot(a, f), dot(b, f - vec2(1.0, 0.0)), s.x),
        mix(dot(c, f - vec2(0.0, 1.0)), dot(d, f - vec2(1.0, 1.0)), s.x), s.y);
}

float sol(vec3 pos) {
    vec2 uv = pos.xz;
    return pos.y - perlin(uv);
}

float ball(vec3 pos) {
    return length(pos) - 0.5;
}

vec2 map(vec3 pos) {
    float closest = 1000.0;
    float id = -1.0;
    float dist = 0.0;

    ID(sol, 0.5);

    return vec2(closest, id);
}

vec3 objectColor(vec3 pos, float id) {
    if (id > 0.0 && id < 1.0) {
        vec2 u = mod(floor(pos.xz), 2.0);
        vec3 color = max(vec3(abs(u.x - u.y)), 0.8);

        return color;
    }
    if (id > 1.0 && id < 2.0) {
        return vec3(0.5, 1.0, 0.0);
    }
    return vec3(1.0, 0.0, 0.0);
}

vec2 trace(vec3 ro, vec3 rd) {
    float depth = 0.0;
    float id = -1.0;
    for (int i = 0; i < 800; i++) {
        vec2 info = map(ro + depth * rd);
        if (info.x < EPSILON) {
            id = info.y;
            break;
        }
        depth += info.x;
        if (depth > FAR) {
            break;
        }
    }
    return vec2(depth, id);
}

vec3 getNormal(vec3 pos) {
    float mapped = map(pos).x;

    return normalize(vec3(
        mapped - map(pos - vec3(EPSILON, 0.0, 0.0)).x,
        mapped - map(pos - vec3(0.0, EPSILON, 0.0)).x,
        mapped - map(pos - vec3(0.0, 0.0, EPSILON)).x));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    vec3 ro = vec3(0.0, perlin(vec2(0.0, iTime)) + 0.2, iTime);
    vec3 center = ro + vec3(0.0, perlin(vec2(0.0, ro.z + 1.0)), 1.0);
    vec3 worldUp = getNormal(vec3(ro.x, ro.y - 0.2, ro.z));
//    vec3 center = vec3(0.0, 0.1, 0.0);
    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, worldUp));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0));
    vec3 rd = vec3(lookAt * normalize(vec4(uv, 1.0, 1.0)));

    vec2 info = trace(ro, rd);

    vec3 sky = mix(vec3(0.78, 0.9, 1.0), vec3(1.0), 1.0 - uv.y);

    if (info.y > 0.0) {
        vec3 pos = ro + info.x * rd;
        vec3 color = objectColor(pos, info.y);

        // Lighting calculations
        vec3 norm = getNormal(pos);

        // World lights
        vec3 ambient = 0.1 * vec3(1.0);
        vec3 skyColor = clamp(dot(norm, vec3(0.0, 1.0, 0.0)), 0.0, 1.0) * vec3(1.0);
        // Light 1

        fragColor = vec4(
            (ambient + skyColor) * color, 
            1.0);

        // Falloff begins at depth > 5 and goes all the way to 10.0
        float falloff = min(max(info.x - 5.0, 0.0) / 5.0, 1.0);
        fragColor = mix(fragColor, vec4(sky, 1.0), falloff);
    } else {
        fragColor = vec4(sky, 1.0);
    }
}

// reuse "swirls on sphere"  https://shadertoy.com/view/ssGSDh

#define H(p)       fract(sin((p)*mat3(127.1,311.7, 74.7,  269.5,183.3,246.1,  113.5,271.9,124.6))*43758.5453123)
#define R(p,a,r) ( mix( a*dot(p,a), p, cos(r) ) + sin(r)*cross(p,a) )
#define hue(v)     vec4( .6 + .6 * cos( 6.3*(v)  + vec3(0,23,21)  ) , 1 )
#define S(v)       smoothstep( 0., -6./R.y, v )
#define shad(P)    vec4(vec3( .2 + .8* max(0., dot(P,vec3(1,.5,1)))), 1)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, S,
         U = 2.* ( 2.*u - R ) / R.y;

    O-=O;
    float r = length(U), y, l=9., t = -iTime; int i,k;
    vec3 P;
#define moon(d,v,w,h)                                         \
        S = d* vec2(2.*cos(v*t), .2*sin(v*t) );               \
        if ( r > 1. || S.y < 0.)                              \
            if ( length( S = (U-S)/w ) < 1.)                  \
                P = vec3( S, sqrt(1.-dot(S,S)) ),             \
                O +=    S( length(S) - 1. )                   \
                      * shad(P) * (.7+.3*hue(h)) 
    moon(1., 1.,.1,.0);
    moon(2.,.4,.07,.5);
    moon(3.,.2,.05,.3);
    moon(4.,.15,.03,.0);
    moon(5.,.1,.03,.5);

    if ( r > 1. ) return;

    vec3 P0 = vec3( U, sqrt(1.-r*r) ),         // raytracing point in sphere
         I,F, H,D,A;
         P = 4.*P0;
         P.xz *= mat2(cos( .2*iTime + vec4(0,11,33,0) )); // planet rotation
         I = floor(P), F = fract(P);           // coords in 3D grid

#define dist2seed  \
        D = vec3( k%3, (k/3)%3, k/9 ) -1.;     /* cell offset         */  \
        D += H(I+D)-.5;                        /* random seed point   */  \
        A = normalize(I+D);                                               \
        D = 4.*A - I;                          /* project it on sphere ( for even radius ) */ \
        r = length(F-D);                       /* distance to seed    */

    for ( k = 0; k < 27; k++) {                // visit neighbor cells to find closest seed point
        dist2seed;
     // P = R( F-D, A, 10.*smoothstep(.5,0.,r) ) + D+I; // shouldn't we blend effects ?
        r < l ? l = r, i = k : i;              // keep closest dot 
     }
    k = i;
    dist2seed;
    y = atan(P.y,length(P.xz));                                   // latitude ( to tune swirl direction & amplitude )
    y = 4.*cos(4.*y);
    P = R( F-D, A, y * smoothstep(.5,0.,r) ) + D+I;               // rotate coords around seed point

 // O = vec4(smoothstep(12./R.y,0., l-.2) ); return;              // test: plot seed points

    S = vec2( atan(P.z,P.x) - .1*iTime, atan(P.y,length(P.xz)) ); // surface coordinates
//  S = vec2(acos(U.x/sqrt(1.-U.y*U.y)) , asin(U.y) );            // debug
    O += (1.-O.a) * mix( 
          smoothstep( .8, .2, texture(iChannel0, S*vec2(1,3) ) )  // swirled texture
        * (.5+.5*hue(abs(U.y*2./3.14)))                           // color
        , vec4(.7,.8,.9,0), 1.-abs(P0).z )                        // atmosphere
        * S( length(U)-1.)                                        // antialiasing
        * 1.5*shad(P0);                                           // shading

}

// POINT_COUNT must be greater than 3 and be an even number!
#define POINT_COUNT 12

#define SEGMENT_COUNT 25
#define ANTI_ALIAS .002
#define SHOW_CONSTRUCTS true
#define SHOW_LINES true
#define SHOW_POINTS true
#define HORIZONTAL true
#define SHOW_CURVE_SEGMENTS false
#define HARMONIC_MODE false

// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float circleSmoothStepFill(vec2 center, float radius, vec2 p, float antiAlias) {
	return 1.f - smoothstep(radius - antiAlias, radius + antiAlias, distance(center, p));
}

float circleSmoothStep(vec2 center, float radius, float thickness, vec2 p, float antiAlias) {
    float distFromCenter = distance(center, p);
    float halfThickness = thickness / 2.f;
    if (distFromCenter < radius) {
        return smoothstep(radius - halfThickness - antiAlias, radius - halfThickness + antiAlias, distFromCenter);
    } else {
        return 1.f - smoothstep(radius + halfThickness - antiAlias, radius + halfThickness + antiAlias, distFromCenter);
    }
}

/*
	returns smoothstep value for line segment
	example usage:
		outColor = mix(outColor, lineColor, lineSegmentSmoothStep(start, end, uv, thickness, AA, true));
*/
float lineSegmentSmoothStep(vec2 start, vec2 end, vec2 p, float lineThickness, float antiAlias, bool roundEnds) {
    float halfThickness = lineThickness / 2.f;
    if (dot(p-end, start - end) < 0.f || dot(p - start, end - start) < 0.f) {
        return !roundEnds ? 0.f : max(
			circleSmoothStepFill(start, halfThickness, p, antiAlias),
			circleSmoothStepFill(end, halfThickness, p, antiAlias)
        );  
    }

    vec2 lineVec = normalize(end - start);
    vec2 pVec = normalize(p - start);

    float angle = acos(dot(lineVec, pVec) / length(lineVec) * length(pVec));
    float distFromLine = sin(angle) * distance(start, p);

    return 1.f - smoothstep(lineThickness / 2.f - antiAlias, halfThickness + antiAlias, distFromLine);
}

float getQuadraticBezierSmoothStep(vec2 p0, vec2 p1, vec2 p2, vec2 p3, vec2 uv, float lineThickness, float aa) {
    vec2 previousPoint = p0;

    float val = 0.f;

    for (int i=0; i<SEGMENT_COUNT; i++) {
        if (i==0) {
            continue; // ignore the first segment
        }

        float t = float(i) / float(SEGMENT_COUNT);

        vec2 a = mix(p0, p1, t);
        vec2 b = mix(p1, p2, t);
        vec2 c = mix(p2, p3, t);

        vec2 d = mix(a, b, t);
        vec2 e = mix(b, c, t);

        vec2 p = mix(d, e, t);
        val = mix(val, 1.0, lineSegmentSmoothStep(previousPoint, p, uv, lineThickness, aa, true));

        previousPoint = p;
    }
    val = mix(val, 1.0, lineSegmentSmoothStep(previousPoint, p3, uv, lineThickness, aa, true));

    return val;
}

float getPointSmoothStep(vec2 p, vec2 uv, float radius, float antiAlias) {
    float pDist = distance(uv, p);
    return smoothstep(radius + antiAlias, radius - antiAlias, pDist);
}

float normalSin(float sinVal) {
    return (sinVal + 1.f) / 2.f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (POINT_COUNT % 2 == 1 || POINT_COUNT < 4) {
        fragColor = vec4(1.f, 0, 0, 1.f);
        return;
    }

    vec4 curveColor = vec4(0.f, 1.f, 0.5f, 1.f);
    float lineColorVal = .5f;
    vec4 lineColor = vec4(lineColorVal, lineColorVal, lineColorVal, 1.f);
    vec4 pointColor = vec4(1);

    float lineThickness = .005;
    float pointRadius = .01;

    int lineSegments = 4;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.x / iResolution.y;
    uv.x *= aspectRatio;

    vec2[POINT_COUNT] points;

    float canvasBuffer = .05f;

    for (int i=0; i<POINT_COUNT; i++) {
        if (HARMONIC_MODE) {
            points[i].y = normalSin(sin(iTime * float(i + 1) * .5f));
        } else {
            points[i].y = normalSin(sin(iTime * hash(vec2(i + 1, i + 1))));
        }

        points[i].y  = mix(canvasBuffer, 1.f - canvasBuffer, points[i].y);

        if (HORIZONTAL) {
            points[i].x = float(i) * 1.f/float(POINT_COUNT-1);
        } else {
            points[i].x = normalSin(sin(iTime * hash(vec2(i - 1, i - 1))));
        }

        points[i].x = mix(canvasBuffer, aspectRatio - canvasBuffer, points[i].x);
    }

     // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    curveColor.x = col.x;
    curveColor.y = col.y;
    curveColor.z = col.z;

    // Output to screen
    fragColor = vec4(0.f, 0.f, 0.f,1.0);

    for (int i=1; i<POINT_COUNT; i++) {
        vec2 p0 = points[i-1];
        vec2 p1 = points[i];

        if (SHOW_CONSTRUCTS) {
            if (SHOW_LINES) {
                fragColor = mix(fragColor, lineColor, lineSegmentSmoothStep(p0, p1, uv, .002, ANTI_ALIAS, true));
            }

            if (SHOW_POINTS) {
                fragColor = mix(fragColor, pointColor, getPointSmoothStep(p0, uv, pointRadius, ANTI_ALIAS));    
                if (i == POINT_COUNT - 1) {
                    fragColor = mix(fragColor, pointColor, getPointSmoothStep(p1, uv, pointRadius, ANTI_ALIAS));
                }
            } 
        }
    }

    int numCurves = (POINT_COUNT - 2) / 2;

    vec2 previousPoint = points[0];
    for (int n=0; n<numCurves; n++) {
        float lineValR = hash(vec2(n+1, n+1));
        float lineValG = hash(vec2(n, n));

        if (SHOW_CURVE_SEGMENTS) {
            curveColor = vec4(lineValR, lineValG, .5f, 1.f);
        }

        vec2 p0 = previousPoint;

        int curveIndex = n * 2;
        vec2 p1 = points[curveIndex + 1];
        vec2 p2 = points[curveIndex + 2];

        vec2 p3 = curveIndex + 3 == POINT_COUNT - 1 ? points[curveIndex + 3] : mix(p2, points[curveIndex + 3], .5f);
        previousPoint = p3;

        if (SHOW_POINTS && curveIndex + 3 != POINT_COUNT - 1) {
            fragColor = mix(fragColor, pointColor * .5, getPointSmoothStep(p3, uv, pointRadius * .75, ANTI_ALIAS));
        }

        fragColor = mix(fragColor, curveColor, getQuadraticBezierSmoothStep(p0, p1, p2, p3, uv, lineThickness, .002));
    }
}

// 'The Alien' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/sdGSzt (YouTube: https://youtu.be/2QoR8L16Lc0)
//
// Processed by 'GLSL Shader Shrinker'
// (https://github.com/deanthecoder/GLSLShaderShrinker)
//
// I nearly gave up several times on this shader. I knew modelling
// a human face would be hard, so thought attempting an alien would
// be easier. No two 'alien' references seem to be the same, so
// I can use artistic license. Also there's no eyes/node to get wrong.
// I'm quite happy with the result, but the body could do with some arms.
// But hey - The framerate is still acceptable (for me), so...
//
// I'm also trying to improve my animation skills. I've added some
// 'anticipations', where before a strike the alien pulls back a bit
// to build up some 'power'.
//
// The skull pattern was simpler than I was expecting - I just threw
// a simple gyroid at it and it looked good! Surprising! And fast
// to calculate in real-time.
//
// Tricks to get the performance:
//   - No 'max dist' needed in raymarching loop (all rays hit something).
//   - Only the skull is reflective, and that reflects just a fake sky.
//     (I know there's no sky in a corridor, but it looks good!)
//   - I precalculate two smooth noises variables in the lighting
//     code and reuse them as much as possible.
//   - Lots of domain repetition.
//     E.g. Each jaw only has one tooth!
//          There's only one SDF for the wall pipe.
//          The Weyland-Yutani logo is _nearly_ just one line.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane,
// totetmatt, Blackle, Dave Hoskins, byt3_m3chanic, and a bunch
// of others for sharing their time and knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define R	iResolution
#define U	normalize
#define L	length
#define Z0	min(iTime, 0.)
#define sat(x)	clamp(x, 0., 1.)
#define S(a, b, c)	smoothstep(a, b, c)
#define S01(a)	S(0., 1., a)
#define minH(a, b, c)	{ float h_ = a; if (h_ < h.d) h = Hit(h_, b, c); }

float t;
struct Hit {
	float d;
	int id;
	vec3 p;
};

// Thnx Dave_Hoskins - https://www.shadertoy.com/view/4djSRW
float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);

	// Thanks Shane - https://www.shadertoy.com/view/lstGRB
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, s.y + s.z) + dot(ip, s);
	h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float fbm(vec3 p) {
	float i,
	      a = 0.,
	      b = .5;
	for (i = Z0; i < 4.; i++) {
		a += b * n31(p);
		b *= .5;
		p *= 2.;
	}

	return a * .5;
}

float smin(float a, float b, float k) {
	float h = sat(.5 + .5 * (b - a) / k);
	return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) { return -smin(-a, -b, k); }

float min2(vec2 v) { return min(v.x, v.y); }

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return L(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float cyl(vec3 p, vec2 hr) {
	vec2 d = abs(vec2(L(p.xy), p.z)) - hr;
	return min(max(d.x, d.y), 0.) + L(max(d, 0.));
}

float cap(vec3 p, float h, float r) {
	p.x -= clamp(p.x, 0., h);
	return L(p) - r;
}

float arc(vec3 p) {
	p.x -= -3.83333;
	vec2 v = vec2(-.56464, .82534),
	     q = p.xy - 2. * v * max(0., dot(v, p.xy));
	v = vec2(-3.83333, 0);
	float u = -v.x - L(q);
	return sqrt((q.y < 0. ? dot(q + v, q + v) : u * u) + p.z * p.z) - max(.001, .8 - (p.y > 0. ? atan(-p.y, p.x) * v.x : -p.x < 0. ? p.y : 4.6 - p.y) * .06);
}

float backInOut(float x) {
	float f = x < .5 ? 2. * x : 1. - (2. * x - 1.),
	      g = pow(f, 3.) - f * sin(f * 3.141);
	return x < .5 ? .5 * g : .5 * (1. - g) + .5;
}

float bone(vec3 p, float h, float r, float s) {
	float f = S(.7, 1., abs(p.x / h - .5) * 2.) * .05 * s;
	p.y = abs(p.y) - f;
	return cap(p, h, r - f * .6);
}

float sky(vec3 rd) {
	float d = 10. / rd.y,
	      den = 1.;
	if (d < 0.) return 0.;
	vec3 p = rd * d + vec3(1, .2, 1) * t * .8;
	p.xz *= .2;
	for (int i = 0; i < 3; i++)
		den *= exp(-.12 * fbm(p));

	return S(.9, 1., den) * (1. - sat(d / 64.));
}

float th(inout vec3 p) {
	float f = atan(p.z, p.x) + .25087;
	p.xz = L(p.xz) * cos(mod(f, .26174) - .13087 + vec2(0, 1.57));
	p = vec3(.04 - p.y, p.x - .36, p.z);
	f = .45 + abs(floor(f * 2.) * .01);
	return cap(p, f, .05 * S(0., f * 1.5, cos(abs(p.x / f * 1.86 - 1. + p.x))));
}

Hit map(vec3 p) {
	Hit h;
	h.d = 1e7;
	float d, lip, at, f, nd, s, O, x, o,
	      ph = S(0., 5., t);
	p.x -= 12. * (1. - ph);
	p.xz *= rot(sin(t * .5) * .02);
	p.yz *= rot(sin(t) * .01);
	vec3 r, np, pp,
	     op = p;

	// Corridor.
	p.xz *= mat2(.96891, -.2474, .2474, .96891);
	p.y = abs(p.y);
	p.z -= 30.;
	d = -p.z - 2.;
	p.yz *= mat2(.87758, -.47943, .47943, .87758);
	p.y -= 2.5;
	minH(min(d, -p.z), 6, p);
	p.x = abs(p.x + 3.) - 15.;
	minH(box(p - vec3(0, 8.5, 0), vec3(5, 2.3, .1)), 8, p - vec3(0, 8.5, 0));

	// Pipework.
	d = L(p.yz - vec2(14, 0)) - 1.5;
	p.y = abs(p.y - 2.5) - 1.1;
	minH(min(d, L(p.yz) - .8), 7, p);

	// Anim.
	lip = 0.;
	r = vec3(0);

	// Walk on.
	op.x += 16. * (1. - ph);
	r.xy = sin(ph * 12.566 + vec2(0, 8)) * vec2(.05, .1) * S(1., .5, ph * ph);
	r.z = -r.x * .5;
	op.y += abs(r.x);

	// Slight turn.
	ph = S(4., 8., t);
	r.xy = mix(r.xy, sin(backInOut(ph) * -.25132 + vec2(0, 8)) * vec2(1, .12), S(0., .2, ph));

	// Sniff.
	ph = S(8.2, 12., t);
	lip = abs(sin(ph * 6.283));
	r.x += sin(ph * -3.142) * .015;

	// Face forward.
	r.xy *= S(14., 11.5, t);

	// Whip-round anticipation.
	ph = S(14.5, 15., t);
	r += vec3(.1, .05, .1) * ph;
	op.y += ph * .1;

	// Whip.
	ph = S(15., 15.3, t);
	r = mix(r, vec3(-.5, .12, 0), ph);

	// Mouth open.
	at = S(15.3, 20., t) * .2;
	f = S01(at * 30.);
	lip += f;
	r.yz = mix(r.yz, vec2(.34, -.1 + sin(t) * .07), at * 5.);

	// Inner mouth open.
	at += S(20., 23., t) * .25;

	// Strike anticipation.
	op.z -= S(24., 24.2, t) * .5;

	// Strike.
	ph = S(24.2, 24.5, t);
	op.z += ph * 1.5;
	at += ph * .4;

	// Withdraw.
	ph = S(29., 32., t);
	at += ph;
	op.z -= ph;
	r.y *= S(34., 31., t);

	// Body position.
	op += vec3(.9, 1.3, -3.6);
	op.yz *= rot(r.y + .2);
	op.xz *= rot(r.x - .2);
	r *= 3.;

	// Spine.
	p = op;
	p.xy *= mat2(-.73739, -.67546, .67546, -.73739);
	p.x++;
	d = cap(p + vec3(0, .68, 0), 4., .1 + abs(sin(p.x * 26.) * .04));
	minH(d, 2, p);

	// Neck/body.
	p.z *= 1. - mix(.4, 1.2, S01(p.x)) * .42;
	f = abs(sin(p.x * 15.) * .07);
	f *= S(.01, .05, abs(p.z));
	f += S(0., .3, p.x) * .6 * S01(p.y);
	nd = cap(p, 4., mix(.3, .5, S01(p.x)) * (1. + f));
	np = p;

	// Rotate head.
	p = op - vec3(1, .4, 0);
	r *= .5;
	p.xz *= rot(r.x);
	p.yz *= rot(r.z);
	p += vec3(1, .4, 0);
	op = p;

	// Skull.
	p = op;
	p.x -= 1.5;
	p.xy *= mat2(.85252, -.52269, .52269, .85252);
	p.xy = vec2(p.y - 1., -p.x);
	d = arc(p);
	p = op - vec3(.9, 0, 0);
	p.xy *= mat2(.995, -.09983, .09983, .995);
	f = .15 - box(p, vec3(2.6, cos((p.x + .3) * .4) * 1.71 - .5, 1));
	d = smax(d, f, .06);
	minH(d, 1, p);

	// Head bulk.
	p = op;
	p.y -= .86;
	d = min(d, L(p.yz) - .3 - .04 * abs(sin(p.x * 7.7)) - .02 * abs(sin(p.x * 23.1)));
	minH(max(d, abs(p.x) - 2.4), 2, p);

	// Frills.
	p = op;
	p.xy += vec2(1.8, -.88);
	p.z = abs(p.z) - .22;
	p.xy *= mat2(.99281, .11971, -.11971, .99281);
	p.xz *= mat2(.9968, .07991, -.07991, .9968);
	d = max(cap(p, 3.5, .2 + .05 * abs(sin(p.x * 22.)) + sin(p.x) * .1), -p.y - .14);

	// Lower organs.
	pp = p + vec3(-1.8, .3, 0);
	pp.yz /= .8 + .5 * cos(pp.x);
	pp.yz *= rot(pp.x * -.3 + .8);
	f = sin(pp.x * 35.);
	f = max(smin(box(pp, vec3(2, vec2(.1 + .01 * abs(f * f)))), L(abs(pp.yz) - .116) - .02, .08), abs(pp.x) - 2.);
	minH(min(d, f), 2, pp);
	if (nd < d) minH(smin(d, nd, .5), 9, np);

	// Skull bone.
	p.yz += vec2(.21, -.15) - sin(p.x - .2) * .14;
	minH(bone(p, 3.6, .08, 1.), 3, p);

	// Cheek bone.
	p.x -= 3.63;
	p.xy *= mat2(.88699, -.46178, .46178, .88699);
	p.yz *= mat2(.5403, .84147, -.84147, .5403);
	d = min(d, bone(p, .65, .06 - .01 * sin(p.x * 10. - 1.), .8));
	minH(d, 3, p);

	// Top gums.
	p = op;
	p.xy -= vec2(2.3, .76);
	d = max(cyl(p.xzy, vec2(.4 + S(-.15, .1, p.y) * .17 - S(.2, -.2, p.y + p.x) * .09, .2 - sin(p.y * 77.) * .02 * lip)) - .1, 2.75 * p.y + p.x - .4);
	minH(d, 3, p);

	// Smooth connect organs to top gums.
	minH(max(max(smin(f, d, .25) + .01, -f), -d), 2, p);

	// Top teeth.
	p.x -= .08;
	minH(max(smax(th(p), p.y - .03, .02), 2.29 - op.x), 4, p);

	// Strike animation.
	f = S(1., .9, at);
	s = S(.5, .51, at);
	O = (S(0., .15, at) * .6 + s * .4) * f + sin(t) * .03;
	x = S(0., .2, at) * .22 + s * .5;
	o = S(.15, .4, at) * .5 + s * (sin(t * 14.) - .8) * .2;
	x *= f * f;
	o *= f * f;

	// Inner mouth.
	p = op;
	p.xy *= mat2(.87758, -.47943, .47943, .87758);
	p.xz *= rot(sin(t * 2.) * .014 * s);
	p.y -= 1.44;
	p.x -= x - .9;
	pp = p;
	d = L(p.yz) - .1 - sin(p.x * 58.) * .01;
	p.yz = abs(p.yz) - .08;
	d = max(smin(max(d, -d - .012), L(p.yz) - .03, .05), abs(p.x - 2.2) - .5);
	minH(d, 5, p);
	p = pp;
	p.y = abs(p.y) - .08;
	p.x -= 2.68;
	p.xy *= rot(o + .1);
	p.x -= .05;
	minH(smin(d, box(p, vec3(.035 + abs(sin(abs(p.z) * 81.) * .006), .008, .1)) - .014, .03), 5, p);

	// Inner teeth.
	p.x -= .025;
	p.z = abs(abs(p.z) - .034) - .017;
	p.y *= -1.;
	p.xy = p.yx;
	d = cap(p, .06, .016 * cos(p.x * 22.));
	p.z = abs(pp.z) - .09;
	minH(min(d, cap(p, .1, .02 * cos(p.x * 17.))), 4, p);

	// Jaws.
	o = O * -.4 - .9;
	p = op - vec3(1.6, .95, 0);
	p.xy *= rot(o);
	f = p.x * -.25;
	p.z -= clamp(p.z, -.6 - f, .6 + f);
	d = bone(p, .9, .1 + f * .2, .8);
	p.x -= .9;
	p.xy *= mat2(.69671, .71736, -.71736, .69671);
	d = smin(d, cap(p, .3, p.x * -.03 + .08), .1);

	// Bottom gums.
	p = op - vec3(1.6, .95, 0);
	p.xy *= rot(.8 + o);
	f = cyl(p.xzy * vec3(1, 1.3, 1) - vec3(.77, 0, -.73), vec2(.38 - .005 * sin(p.y * 126.), .04)) - .05;
	minH(smin(d, max(f, .7 - p.x), .1), 3, p);

	// Cheek skin.
	pp = p - vec3(.6, -.2, 0);
	f = S(-.32, .32, pp.y);
	pp.z = abs(pp.z) - .4 - f * .14;
	f = mix(.8, .5, O) * (.04 - .03 * sin(3.14 * f));
	d = smin(d, min(box(pp + vec3(.2, -.2, -.05), vec3(.1 * pp.y + f, .32, 0)), box(pp, vec3(f, .35, 0))) - .02, .03);
	minH(d, 3, pp);

	// Bottom teeth.
	p.x -= .73;
	p.y += .9;
	p.xy *= mat2(-.98999, .14112, -.14112, -.98999);
	minH(max(max(p.x + .1, p.y + .3), th(p)), 4, p);
	return h;
}

vec3 N(vec3 p, float t) {
	float h = t * .4;
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .005773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * h).d;
	}

	return U(n);
}

float shadow(vec3 p, vec3 lp) {
	float d,
	      s = 1.,
	      t = .1,
	      mxt = L(p - lp);
	vec3 ld = U(lp - p);
	for (float i = Z0; i < 25.; i++) {
		d = map(t * ld + p).d;
		s = min(s, 15. * d / t);
		t += max(.1, d);
		if (mxt - t < .5 || s < .001) break;
	}

	return S01(s);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) { return sat(map(h * n + p).d / h); }

vec3 strobe(vec3 p, vec3 c) {
	p.x -= 1.5;
	vec2 q = p.xz * rot(t * 2.);
	return mix(c, pow(c, vec3(.7, 1, 1)), S(.8, .6, abs(q.x / q.y)));
}

vec3 lights(vec3 p, vec3 rd, vec3 n, Hit h) {
	vec3 ld = U(vec3(6, 3, -10) - p), c;
	vec2 spe = vec2(10, 1);
	float ao = mix(ao(p, n, .2), ao(p, n, 2.), .7),
	      nh = n31(h.p * 55.),
	      nl = n31(h.p * 23.);
	if (h.id == 1) {
        // Gyroid.
		c = pow(abs((vec3(.005, .01, .01) * dot(sin(h.p * 26.), cos(h.p.zxy * 26.))
		 + nh * .0046) * .5), vec3(1.12, map(ld + p).d * -.07 + 1.46, 1.43));
		c *= 1. - (.9 * S01(.9 + dot(rd, n)) + .5);
		spe = vec2(5, 28);
	}
	else if (h.id == 2 || h.id == 5 || h.id == 9) {
		c = vec3(ao * .001);
		c += S(.8, 1., ao) * vec3(1.4, 1.4, 1) * .009;
		c *= nh;
		c += 1e-4;
		spe = vec2(30. * ao, 3);
		if (h.id == 5) {
			c *= 4.5;
			spe = vec2(3, 13);
		}
	}
	else if (h.id == 3) {
		c = vec3(.56, .5, .6) * .001;
		c += S(.09, .88, abs(n.y) * nh) * .002 * vec3(1, .62, .65);
		n += (nl - .5) * .08;
		spe = vec2(18, 20);
	}
	else if (h.id == 6) {
		c = vec3(.01 + nl * .01);
		spe = vec2(19, 1);
	}
	else if (h.id == 7) {
		c = vec3(.03 + nl * .02);
		spe = vec2(42, 50);
	}
	else if (h.id == 8) {
		h.p *= .3;
        if (p.y < 0.0) h.p.y *= -1.;
		float w1 = abs(abs(abs(h.p.x) - .5) - h.p.y - .25);
		c = 5e-4 + min2(step(abs(h.p.xy), vec2(1.2, .4))) * (vec3(1, 1, 0) * S(.2, .15, w1) + vec3(.5) * S(.2, .15, min(abs(abs(h.p.x) - h.p.y - .15), max(h.p.y, abs(h.p.x) - .05))) * S(.25, .2, S(.4, .3, w1))) * .05 + nh * .001;
	}
	else {
		c = vec3(.01 + pow(sat(dot(rd, reflect(ld * vec3(-1, -1, 1), n))), 24.) * .6);
		spe = vec2(8, 5);
	}

	if (h.id == 9) c *= .4;

	// Combine into final color.
	return (sat(.1 + .9 * dot(ld, n)) * (.1 + .9 * shadow(p, vec3(6, 3, -10))) * (.3 + .7 * ao) * ao + pow(sat(dot(rd, reflect(ld, n))), spe.x) * spe.y) * strobe(p, abs(c)) * 5.;
}

vec3 scene(vec3 rd) {
    vec3 p = vec3(0);
	float i,
	      d = 0.;
	Hit h;
	for (i = Z0; i < 120.; i++) {
		h = map(p);
		if (abs(h.d) < .0015) break;
		d += h.d;
		p += h.d * rd;
	}

	vec3 n = N(p, d),
	     col = lights(p, rd, n, h) * exp(d * d * -.002);
	if (h.id == 1) {
		// We hit a reflective surface - Cheat and just reflect sky.
		n = U(n + (n31(h.p * 44.) - .5) * .05);
		col += .015 * sky(reflect(rd, n));
	}

	return col;
}

void mainImage(out vec4 fragColor, vec2 fc) {
	t = mod(iTime, 36.);
	vec2 uv = (fc - .5 * R.xy) / R.y,
	     v = fc.xy / R.xy;
	vec3 col = scene(U(vec3(uv, 1)));
	col *= .5 + .5 * pow(16. * v.x * v.y * (1. - v.x) * (1. - v.y), .4);
	fragColor = vec4(pow(max(vec3(0), col), vec3(.45)) * sat(t) * S(34., 33., t), 0);
}

// --- utils from https://www.shadertoy.com/view/llySRh

#define H(p)     fract( sin( (p) * 78.233 ) * 43758.5453 )
#define hue(v) ( .6 + .6 * cos( v  + vec4(0,23,21,0)  ) )

float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    return length( p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );
}
// -----------------------------------------------------------

vec2 f(float t) {                               // curve P(t)
    vec2 P = vec2(0);
    for (float f, i = 0.; i < 6.; i++ )         // Fourier series 
        f = exp2(i)*t + 6.283*H(i) + i*iTime,
        P += vec2(cos(f),sin(f)) / pow(1.4, i); // spectrum 1.4^-f, random phase
    return P;
}
#define D(t)  l = dot( P = f(t) - U, P )        // distance² to curve(t)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, P,
         U = 2.5*( u+u - R ) / R.y;
    float l, d0 = 9., d1=d0, T0,T1,t0,t1,e, t=0., dt = .015, dt2 = dt/4.;
    O-=O;
    for ( ; t < 6.283; t+=dt )                  // Sample the curve and get the 2 closests
        D(t),                                   //   that are not on the same segment.
          l < d0 ?  t<=T0+dt ? d0=l,T0=t        // Prev best was on same segment: don't update 2nd best
                             : ( d1=d0, T1=T0, d0=l, T0=t ) // independant best: save old as 2nd best
        : l < d1 && t> T0+dt ? d1=l,T1=t : t;   // update 2nd best only if not on best segment

    if (iMouse.z > 0.) O += smoothstep(8./R.y, 0., sqrt(min(d0,d1))-.01 ); // test: draw dots

    t0=T0; t1=T1;    
    for ( t = -dt; t < dt; t += dt2 )           // Look more accurately around 1st and second
        D(T0+t), l < d0 ? d0=l,  t0=T0+t : t,   //   closest curve segments.
        D(T1+t), l < d1 ? d1=l,  t1=T1+t : t;

                           // draw upstream and dowstream segment for 1st and 2nd best
#define  L(t) smoothstep(  8./R.y, 0.,                      \
                           min ( line(U, f(t-dt2), f(t) ),  \
                                 line(U, f(t+dt2), f(t) )   \
                        ) -.01 )                            \
              * hue(t) 
    O += max( L(t0), L(t1) );
}

#define TIME_SCALE .5
#define SEGMENT_COUNT 25

// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float circleSmoothStepFill(vec2 center, float radius, vec2 p, float antiAlias) {
	return 1.f - smoothstep(radius - antiAlias, radius + antiAlias, distance(center, p));
}

float circleSmoothStep(vec2 center, float radius, float thickness, vec2 p, float antiAlias) {
    float distFromCenter = distance(center, p);
    float halfThickness = thickness / 2.f;
    if (distFromCenter < radius) {
        return smoothstep(radius - halfThickness - antiAlias, radius - halfThickness + antiAlias, distFromCenter);
    } else {
        return 1.f - smoothstep(radius + halfThickness - antiAlias, radius + halfThickness + antiAlias, distFromCenter);
    }
}

/*
	returns smoothstep value for line segment
	example usage:
		outColor = mix(outColor, lineColor, lineSegmentSmoothStep(start, end, uv, thickness, AA, true));
*/
float lineSegmentSmoothStep(vec2 start, vec2 end, vec2 p, float lineThickness, float antiAlias, bool roundEnds) {
    float halfThickness = lineThickness / 2.f;
    if (dot(p-end, start - end) < 0.f || dot(p - start, end - start) < 0.f) {
        return !roundEnds ? 0.f : max(
			circleSmoothStepFill(start, halfThickness, p, antiAlias),
			circleSmoothStepFill(end, halfThickness, p, antiAlias)
        );  
    }

    vec2 lineVec = normalize(end - start);
    vec2 pVec = normalize(p - start);

    float angle = acos(dot(lineVec, pVec) / length(lineVec) * length(pVec));
    float distFromLine = sin(angle) * distance(start, p);

    return 1.f - smoothstep(lineThickness / 2.f - antiAlias, halfThickness + antiAlias, distFromLine);
}

float getQuadraticBezierSmoothStep(vec2 p0, vec2 p1, vec2 p2, vec2 p3, vec2 uv, int segmentCount, float lineThickness, float aa, bool deriv) {
    vec2 previousPoint = p0;

    float val = 0.f;

    for (int i=0; i<segmentCount; i++) {
        if (i==0) {
            continue; // ignore the first segment
        }

        float t = float(i) / float(segmentCount);

        float tSquared = pow(t, 2.);
        float tCubed = pow(t, 3.);
        float p0Val = -tCubed + 3.*tSquared - 3.*t + 1.;
        float p1Val = 3.*tCubed - 6.*tSquared + 3.*t;
        float p2Val = -3.*tCubed + 3.*tSquared;
        float p3Val = tCubed;

        float p0Deriv = -3.*tSquared + 6.*t - 3.;
        float p1Deriv = 9.*tSquared - 12.*t + 3.;
        float p2Deriv = -9.*tSquared + 6.*t;
        float p3Deriv = 3.*tSquared;

        vec2 pDeriv = p0 * p0Deriv + p1 * p1Deriv + p2 * p2Deriv + p3 * p3Deriv;

        vec2 p = p0 * p0Val + p1 * p1Val + p2 * p2Val + p3 * p3Val;

        val = mix(val, 1.0, lineSegmentSmoothStep(previousPoint, p, uv, lineThickness, aa, true));

        previousPoint = deriv ? pDeriv : p;
    }
    val = mix(val, 1.0, lineSegmentSmoothStep(previousPoint, p3, uv, lineThickness, aa, true));

    return val;
}

float getPointSmoothStep(vec2 p, vec2 uv, float radius, float antiAlias) {
    float pDist = distance(uv, p);
    return smoothstep(radius + antiAlias, radius - antiAlias, pDist);
}

float normalSin(float sinVal) {
    return (sinVal + 1.f) / 2.f;
}

float getXVal(float x, float time) {
    return (time * TIME_SCALE + x) * 2.f;
}

vec2 getPointFromXVal(float xVal, float time) {
    float adjustedXVal = getXVal(xVal, time);
    float hashVal = hash(vec2(floor(adjustedXVal))); 
    return vec2((xVal / 2.) - time, hashVal);
}

vec2 getPointFromXIndex(float xIndex, float time) {
    float hashVal = hash(vec2(xIndex)); 
    return vec2((xIndex / 2.) - time, hashVal);
}

float getScreenCoordsFromXVal(float xVal, float time) {
    return (xVal / 2.) - time * TIME_SCALE;
}

float getYFromXAndTwoPoints(float x, vec2 p0, vec2 p1) {
    vec2 rightmost = p0.x > p1.x ?  p0 : p1;
    vec2 leftmost =  p0.x > p1.x ?  p1 : p0;

    float slope = (rightmost.y - leftmost.y) / (rightmost.x - leftmost.x);

    float deltaX = x - leftmost.x;
    return slope * deltaX + leftmost.y;
}

float getYFromBezierCurve(float x, vec2 p0, vec2 p1, vec2 p2, vec2 p3, int segmentCount) {
    vec2 previousPoint = p0;

    float val = 0.f;

    for (int i=0; i<segmentCount; i++) {
        if (i==0) {
            continue; // ignore the first segment
        }

        float t = float(i) / float(segmentCount);

        float tSquared = pow(t, 2.);
        float tCubed = pow(t, 3.);
        float p0Val = -tCubed + 3.*tSquared - 3.*t + 1.;
        float p1Val = 3.*tCubed - 6.*tSquared + 3.*t;
        float p2Val = -3.*tCubed + 3.*tSquared;
        float p3Val = tCubed;

        vec2 p = p0 * p0Val + p1 * p1Val + p2 * p2Val + p3 * p3Val;

        vec2 rightmost = p.x > previousPoint.x ?  p : previousPoint;
        vec2 leftmost =  p.x > previousPoint.x ?  previousPoint : p;

        if (x > leftmost.x && x < rightmost.x) {
            return getYFromXAndTwoPoints(x, p, previousPoint);
        }

        previousPoint = p;
    }

    vec2 rightmost = p3.x > previousPoint.x ?  p3 : previousPoint;
    vec2 leftmost =  p3.x > previousPoint.x ?  previousPoint : p3;

    if (x > leftmost.x && x < rightmost.x) {
        return getYFromXAndTwoPoints(x, p3, previousPoint);
    }

    // this is prone to bugs but can't pass by ref in shaders so meh
    return -1.;
}

float getUndulatingVal(float time, float seed) {  
    float randVal1 = (sin(time * TIME_SCALE + hash(vec2(seed))) + 1.) / 2.;
    float randVal2 = (sin(time * TIME_SCALE + hash(vec2(seed * -1.))) + 1.) / 2.;
    float xVal = (time * TIME_SCALE * 2.f) + hash(vec2(seed, 1.));
    float hashVal = hash(vec2(floor(xVal)));   

    float nextXValFloor = floor(xVal) + 1.;
    float nextHashVal = hash(vec2(nextXValFloor));
    vec2 nextPoint = vec2((nextXValFloor / 2.) - time, nextHashVal);

    float interpVal = xVal - floor(xVal);

    float xIndex = floor(xVal);
    vec2 thisPoint = vec2((xIndex / 2.) - time, hashVal);

    float indexVal = mod(xIndex, 2.f);

    float zeroVal = xIndex - floor(indexVal);

    vec2 p0 = getPointFromXVal(thisPoint.x, time);
    vec2 p1 = getPointFromXVal(floor(indexVal) + 2., time);
    vec2 p2 = getPointFromXVal(floor(indexVal) + 2., time);
    vec2 p3 = getPointFromXVal(floor(indexVal) + 3., time);

    vec2 pn2, pn1;

    float xIndexAdjustment = indexVal < .99 ? -2. : -3.;

    pn2 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment, time), hash(vec2(xIndex + xIndexAdjustment)));
    pn1 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 1., time), hash(vec2(xIndex + xIndexAdjustment + 1.)));
    p0 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 2., time), hash(vec2(xIndex + xIndexAdjustment + 2.)));
    p1 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 3., time), hash(vec2(xIndex + xIndexAdjustment + 3.)));
    p2 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 4., time), hash(vec2(xIndex + xIndexAdjustment + 4.)));
    p3 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 5., time), hash(vec2(xIndex + xIndexAdjustment + 5.)));

    vec2 p05 = mix(p0, p1, .5f);
    vec2 p25 = mix(p2, p3, .5f);

    vec2 pn15 = mix(pn2, pn1, .5f);

    p1.x = mix(p05.x, p25.x, .333f);
    p2.x = mix(p05.x, p25.x, .666f);

    pn1.x = mix(pn15.x, p05.x, .333f);
    p0.x = mix(pn15.x, p05.x, .666f);
    float x = hash(vec2(seed)) * .2;
    float midY = getYFromBezierCurve(x, p05, p1, p2, p25, SEGMENT_COUNT);

    if (midY < 0.) {
        midY = getYFromBezierCurve(x, pn15, pn1, p0, p05, SEGMENT_COUNT);
    }

    return midY;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;  
    float aspectRatio = iResolution.x / iResolution.y;
    uv.x *= aspectRatio;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(vec3(0.),1.0);

    float xVal = getXVal(uv.x, iTime);
    float hashVal = hash(vec2(floor(xVal)));   

    float nextXValFloor = floor(xVal) + 1.;
    float nextHashVal = hash(vec2(nextXValFloor));
    vec2 nextPoint = vec2((nextXValFloor / 2.) - iTime, nextHashVal);

    float interpVal = xVal - floor(xVal);

    //fragColor = vec4(vec3(interpVal), 1.f);

    float xIndex = floor(xVal);
    vec2 thisPoint = vec2((xIndex / 2.) - iTime, hashVal);

    float indexVal = mod(xIndex, 2.f);

    //fragColor = mix(fragColor, vec4(1.f, 0.f, 0.f, 1.f), lineSegmentSmoothStep(thisPoint, nextPoint, uv, .01, .002, true));
    //fragColor = vec4(vec3(hashVal), 1.f);

    //fragColor = mix(fragColor, vec4(1.f, 0.f, 0.f, 1.f), getPointSmoothStep(thisPoint, uv, .1, .002));
    //fragColor = mix(fragColor, vec4(1.f, 0.f, 0.f, 1.f), getPointSmoothStep(nextPoint, uv, .1, .002));

    //fragColor = mix(fragColor, vec4(1.f, 1.f, 0.f, 1.f), lineSegmentSmoothStep(thisPoint, nextPoint, uv, .01, .002, true));
    //fragColor = vec4(vec3(hashVal), 1.f);

    // indexVal is val between 0 and 4
    // what the indexVal is determines which p point below is p0

    float zeroVal = xIndex - floor(indexVal);

    vec2 p0 = getPointFromXVal(thisPoint.x, iTime);
    vec2 p1 = getPointFromXVal(floor(indexVal) + 2., iTime);
    vec2 p2 = getPointFromXVal(floor(indexVal) + 2., iTime);
    vec2 p3 = getPointFromXVal(floor(indexVal) + 3., iTime);

    vec2 pn2, pn1;

    float xIndexAdjustment = indexVal < .99 ? -2. : -3.;

    //fragColor = vec4(vec3(indexVal / 2.), 1.f);

    pn2 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment, iTime), hash(vec2(xIndex + xIndexAdjustment)));
    pn1 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 1., iTime), hash(vec2(xIndex + xIndexAdjustment + 1.)));
    p0 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 2., iTime), hash(vec2(xIndex + xIndexAdjustment + 2.)));
    p1 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 3., iTime), hash(vec2(xIndex + xIndexAdjustment + 3.)));
    p2 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 4., iTime), hash(vec2(xIndex + xIndexAdjustment + 4.)));
    p3 = vec2(getScreenCoordsFromXVal(xIndex + xIndexAdjustment + 5., iTime), hash(vec2(xIndex + xIndexAdjustment + 5.)));

    vec2 p05 = mix(p0, p1, .5f);
    vec2 p25 = mix(p2, p3, .5f);

    vec2 pn15 = mix(pn2, pn1, .5f);

    p1.x = mix(p05.x, p25.x, .333f);
    p2.x = mix(p05.x, p25.x, .666f);

    pn1.x = mix(pn15.x, p05.x, .333f);
    p0.x = mix(pn15.x, p05.x, .666f);

    fragColor = mix(fragColor, vec4(vec3(p05.y), 1.f), lineSegmentSmoothStep(vec2(p05.x, 0.), vec2(p05.x, 1.), uv, .005, .002, true));
    fragColor = mix(fragColor, vec4(vec3(p1.y), 1.f), lineSegmentSmoothStep(vec2(p1.x, 0.), vec2(p1.x, 1.), uv, .005, .002, true));
    fragColor = mix(fragColor, vec4(vec3(p2.y), 1.f), lineSegmentSmoothStep(vec2(p2.x, 0.), vec2(p2.x, 1.), uv, .005, .002, true));

    fragColor = mix(fragColor, vec4(1.f, 0.f, 1.f, 1.f), getQuadraticBezierSmoothStep(p05, p1, p2, p25, uv, SEGMENT_COUNT, .02, .002, false));
    fragColor = mix(fragColor, vec4(1.f, 0.f, 1.f, 1.f), getQuadraticBezierSmoothStep(pn15, pn1, p0, p05, uv, SEGMENT_COUNT, .02, .002, false));
    //fragColor = mix(fragColor, vec4(1.f, 0.f, 1.f, 1.f), getQuadraticBezierSmoothStep(p05, p1, p2, p25, uv, 15, .002, .002, true));
    //fragColor = mix(fragColor, vec4(1.f, 0.f, 1.f, 1.f), getQuadraticBezierSmoothStep(pn15, pn1, p0, p05, uv, 15, .002, .002, true));
    //fragColor = mix(fragColor, vec4(1.f, 0.f, 0.f, 1.f), getPointSmoothStep(p1, uv, .03, .002));
    //fragColor = mix(fragColor, vec4(0.f, 1.f, 0.f, 1.f), getPointSmoothStep(p2, uv, .03, .002));
    //fragColor = mix(fragColor, vec4(0.f, 0.f, 1.f, 1.f), getPointSmoothStep(p05, uv, .03, .002));

    float screenmid = .5 * aspectRatio;
    float midY = getYFromBezierCurve(screenmid, p05, p1, p2, p25, SEGMENT_COUNT);

    if (midY < 0.) {
        midY = getYFromBezierCurve(screenmid, pn15, pn1, p0, p05, SEGMENT_COUNT);
    }

    vec2 pBez = vec2(screenmid, midY);
    fragColor = mix(fragColor, vec4(vec3(midY), 1.f), getPointSmoothStep(pBez, uv, .03, .002));

    float undulatingX = getUndulatingVal(iTime, .5);
    float undulatingY = getUndulatingVal(iTime, 200.25);

    vec2 randPoint = vec2(undulatingX * aspectRatio, undulatingY);

    fragColor = mix(fragColor, vec4(1.f, 0.2f, .8f, 1.f), getPointSmoothStep(randPoint, uv, .03, .002));

    //fragColor = vec4(vec3(getUndulatingVal(iTime)), 1.);
}

vec3 heat(float v) {
    float value = 1.0 - (mod(iTime,2.0) + 0.5) * v;
    return (0.5+0.5*smoothstep(0.0, 0.1, value))*vec3(
      	smoothstep(0.5, 0.3, value),
      	value < 0.3 ? smoothstep(0.0, 0.3, value) : smoothstep(1.0, 0.6, value),
    	smoothstep(0.4, 0.6, value)
	);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float sum = iTimeDelta;
    vec3 color = texture(iChannel0, uv).rgb;
    sum += smoothstep(vec4(0.0).z, 0.0, distance(vec4(0.0).xy, uv));
    float sum1 = iTimeDelta;
    sum1 += smoothstep(color.z, 0.0, distance(color.xy, uv));
    fragColor = vec4(mix(heat(sum),heat(sum1),0.5), 1.0);    
}

// even more primitive idea than 
// AngularSeg from https://shadertoy.com/view/ssyXzy
// I discovered this little trick first,
// and only after much banging of my head
// against the problem did I manage to 
// come up with AngularSeg

// iirc I started with a simple angularly-computed disc
// then noticed "hey, I can get perspective correction
// by removing a sqrt" and wound up with this!

// view vector d is assumed unit length

//float angularDistance(vec3 d, vec3 a)
//{
//	return length(cross(d, a));
//}

float angularDistanceSquared(vec3 d, vec3 a)
{
    vec3 v = cross(d, a); return dot(v, v);
}

