#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,q,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
    for(;++i<99.;)
    {
        p=g*d;
        p.z-=5.;
        p=R(p,vec3(.577),t*.2);      
        q=p;
        p=vec3(sin(atan(p.x,p.y)*3.1415),length(p.xy)-2.,asin(cos(p.z)));
        s=2.;
        for(int i;i++<8;)
        {
            p=abs(p)-vec3(1,.3,1.5);
            s*=e=9./clamp(dot(p,p),.5+sin(t)*.5,8.);
            p=p*e;
        }
        g+=e=max(length(q)-3.5,length(p.xy-clamp(p.xy,-.3,.3))/s);        
        c+=mix(vec3(1),H(log(s)*.5),.3)*.012*exp(-i*i*e);
    }
    c*=c*c*c*c;
    O=vec4(c,1);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float sqLength(vec2 a) {
    return max(abs(a.x), abs(a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float sc = 192. / (1. + tanh(-length(uv) + iTime)) / (1. + 8. * length(uv) * (1. + 0.5 * cos(8. * sqLength(uv) + 0.5 * iTime)));
    vec2 ipos = ceil(sc * uv) - 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;
    float id = length(ipos);
    float fd = length(fpos);

    float a = atan(ipos.y, ipos.x);

    float box = sqLength(fpos);
    box = mix(0.5 - box * box, 0.5 - fd * fd, .5 + .5 * cos(id + iTime));
    //box = 0.55 - box * box;

    float k = (1. + sin(id - a - iTime)) * (1. + cos(0.5 * id + iTime)) / (1. + cos(id - a - iTime));
    float c = 0.8 * box + 0.08 * id + .5 * cos(id - iTime);
    c *= 0.5 + 0.5 * cos(c);   
    c *= step(0.2 + k,box) - step(0.3 + k, box);

    vec3 col = pal(0.05 * length(ipos) / c, vec3(0.5), vec3(0.5), vec3(1.5), vec3(0.,0.333,0.666));

    fragColor = vec4(col,1.0);
}

#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

#define STEPS 200.0
#define MDIST 100.0

//some neat animation
//#define FUN_MODE

//Reverse time
//#define iTime -iTime

//Change to 2.0 for AA
#define AA 1.0

vec3 rdg = vec3(0);
float ext(vec3 p, float s, float h){
  vec2 b = vec2(s,abs(p.y)-h);
  return min(max(b.x,b.y),0.)+length(max(b,0.));
}
float h11(float a) {
    a+=0.65343;
    return fract(fract(a*a*12.9898)*43758.5453123);
}
float diplane(vec3 p,vec3 b,vec3 rd){
    p/=b;
    vec3 dir = sign(rd)*.5;   
    vec3 rc = (dir-p)/rd;
    rc*=b;
    float dc = rc.z+0.01;
    return dc;
}
float lim(float p, float s, float lima, float limb){
    return p-s*clamp(round(p/s),lima,limb);
}
float idlim(float p, float s, float lima, float limb){
    return clamp(round(p/s),lima,limb);
}
float lim2(float p, float s,  float limb){
    return p-s*min(round(p/s),limb);
}
float idlim2(float p, float s, float limb){
    return min(round(p/s),limb);
}
float spiral(vec2 p, float t, float m, float scale, float size, float expand){
    size-=expand-0.01;
    //Offset Spiral To the left
    t = max(t,0.);

    p.x+=pi*-t*(m+m*(-t-1.));
    t-=0.25;

    vec2 po = p;
    //Move Spiral Up
    p.y+=-t*m-m*0.5;

    //Counter the rotation
    p*=rot(t*pi*2.+pi/2.);

    //Polar Map
    float theta = atan(p.y,p.x);
    theta = clamp(theta,-pi,pi);
    p = vec2(theta,length(p));

    //Create Spiral
    p.y+=theta*scale*0.5;

    //Duplicate Line outwards to fill spiral
    float py = p.y;
    float id = floor((p.y+m*0.5)/m);
    p.y = lim(p.y,m,0.,floor(t));

    //float hel = -(theta+pi)/(2.*pi)+id;

    //Line SDF of the spiral
    float a = abs(p.y)-size;

    //Calcuate moving outer spiral segment
    p.y = py;
    p.x -= pi;
    p.y -= (floor(t)+1.5)*m-m*0.5;
    float b = max(abs(p.y),abs(p.x)-(pi*2.)*fract(t)+size );

    //The unrolled Line SDF
    a = min(a,b-size);
    b = abs(po.y)-size;
    b = max(po.x,b);
    //if(b<a) hel = po.x-(pi*-t*(m+m*(-t-1.))-3.);
    //else hel*=id;

    //Combine Them
    a = min(a,b);

    return a;
}
vec3 map(vec3 p){
    vec2 a = vec2(1);
    vec2 b = vec2(1);
    float c = 0.;
    float t = 10. * -sin(0.5*iTime)*sin(-iTime)*sin(iTime);

    float size = 0.062; //Thickness of spiral curls
    float scale = size-0.01 ; //Space between spiral curls

    float expand = 0.04; //Corner Rounding Amount 

    float m2 = size*6.0; //Repetition Sizes
    float m = pi*scale; //Repetition Sizes

    float ltime = 10.; //How often the spirals rolls repeat

    //Move everything upwards so it stays in frame
    p.y-=(t/ltime)*size*6.;

    //small offset for framing
    p.x-=3.; 

    float width = 0.5; //Lane Width
    float count = 6.; //Number of spirals (x2)

    float modwidth = width*2.0+0.04+0.06;

    float id3 = idlim(p.z,modwidth,-count,count);
    t+=h11(id3*0.76)*8.0;
    p.z = lim(p.z,modwidth,-count,count);

    #ifdef FUN_MODE
        scale+=(sin(t)*0.5+0.5)*0.05;
        m = pi*scale;
    #endif

    float to = t;
    vec3 po = p;

    //float id = 0.;

    //Spiral 1
    float stack = -floor(t/ltime);
    float id2 = idlim2(p.y,m2,stack);
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);
    a.x = spiral(p.xy,t,m,scale,size,expand);
    //a.y = id2*3.-2.;
    c = a.x;
    a.x = min(a.x,max(p.y+size*5.,p.x));//Artifact Removal

    //Spiral 2
    p = po;
    t = to;
    p.y+=size*2.0;
    t-=ltime/3.0;
    stack = -floor(t/ltime);
    id2 = idlim2(p.y,m2,stack);
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);

    b.x = spiral(p.xy,t,m,scale,size,expand);
    //b.y = id2*3.-1.;
    c = min(c,b.x);
    a=(a.x<b.x)?a:b;
    a.x = min(a.x,max(p.y+size*5.,p.x));//Artifact Removal

    //Spiral 3
    p = po;
    t = to;
    p.y+=size*4.0;
    t-=2.*ltime/3.0;
    stack = -floor(t/ltime);
    id2 = idlim2(p.y,m2,stack);    
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);
    b.x = spiral(p.xy,t,m,scale,size,expand);
    //b.y = id2*3.;
    c = min(c,b.x);
    a=(a.x<b.x)?a:b;
    a.x = min(a.x,max(p.y+size*5.,p.x)); //Artifact Removal


    a.x = ext(po.yzx,a.x,width-expand*0.5+0.02)-expand;
    c = ext(po.yzx,c,width-expand*0.5+0.02)-expand;

    //Intersection distance to plane between each lane
    b.x = diplane(po ,vec3(modwidth), rdg); //Artifact Removal
    b.y = 0.;

    //a.y-=10.0;
    //a.y+=h11(id3);

    a=(a.x<b.x)?a:b; //Artifact Removal

    return vec3(a,c);
}
vec3 norm(vec3 p){
    vec2 e = vec2(0.01,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}
void render( out vec4 fragColor,in vec2 fragCoord){

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float t = iTime;
    float px = 8./iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(5,1.8,-12)*1.2;
    ro.zx*=rot(0.09);

    //Mouse control
    if(iMouse.z>0.5){
    ro.yz*=rot(0.5*(iMouse.y/iResolution.y-0.5));
    ro.zx*=rot(-0.5*(iMouse.x/iResolution.x-0.5));
    }
    //Camera Setup
    vec3 lk = vec3(-2.5,0.,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*2.0+uv.x*r+uv.y*cross(f,r));  
    rdg = rd;

    vec3 p = ro;
    vec3 d;
    float dO = 0.;
    bool hit = false;

    //Raymarcher
    for(float i = 0.; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+=d.x;
        if(d.x<0.001||i==STEPS-1.0){
            hit = true;
            break;
        }
        if(dO>MDIST){
            dO = MDIST;
            break;
        }
    }
    //Color Surface
    if(hit&&d.y!=0.){
        vec3 ld = normalize(vec3(0.5,0.4,0.9));
        vec3 n = norm(p);
        vec3 r = reflect(rd,n);
        rdg = ld;
        float shadow = 1.;
        for(float h = 0.09; h<7.0;){
            vec3 dd = map(p+ld*h+n*0.005);
            if(dd.x<0.001&&dd.y==0.0){break;}
            if(dd.x<0.001){shadow = 0.0; break;}
            shadow = min(shadow,dd.z*30.0);
            h+=dd.x;
        }
        shadow = max(shadow,0.8);

        #define AO(a,n,p) smoothstep(-a,a,map(p+n*a).z)
        float ao = AO(0.05,n,p)*AO(.1,n,p);
        ao = max(ao,0.1);
        n.xz*=rot(4.*pi/3.);
        col = n*0.5+0.5;
        col = col*shadow;
        col*=ao;

    }
    //Color Background
    else{
        col = mix(vec3(0.355,0.129,0.894),vec3(0.278,0.953,1.000),clamp((rd.y+0.05)*2.0,-0.15,1.5));
    }
    //Gamma Approximation
    col = sqrt(col);
    fragColor = vec4(col,1.0);  
}

