// Original idea from this video with a great explenation of how this works: https://www.youtube.com/watch?v=cQXAbndD5CQ


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;

    uv *= mat2(.707, -.707, .707, .707);
    uv *= 8. * (sin(iTime * .1) * .4 + 1.4);

    vec2 gv = fract(uv)-.5; 
	vec2 id = floor(uv);

	float m = 0.;
    float t;
    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {
            vec2 offs = vec2(x, y);

            t = -iTime*.32+length(id-offs)*.42;
            float r = mix(.4, 1.5, sin(t)*.5+.5);
    		float c = smoothstep(r, r*.99, length(gv+offs));
    		m = m*(1.-c) + c*(1.-m);
        }
    }

    float mult = sin(iTime) * 0.25 + 1.4;
    float modBase = uv.x * uv.y;
    fragColor = vec4(m * mod(modBase, mult * 1.3), m * mod(modBase * 2.3, (mult + 1.) / 1.3), m * mod(modBase, mult), 1.);
}

// BB8 Camera Filter, October 2021
// by Tito Morab - ChutneyPot
// https://www.shadertoy.com/view/sdtSRM
//--------------------------------------------------------------------------
#define BG1 vec4(0.96, 0.84, 0.55, 1.0)
#define BG2 vec4(0.55, 0.35, 0.24, 1.0)
#define BODY1 vec4(0.81, 0.79, 0.71, 1.0)
#define BODY2 vec4(0.45, 0.42, 0.41, 1.0)
#define BODY3 vec4(0.88, 0.49, 0.27, 1.0)
#define BODY4 vec4(0.05, 0.05, 0.2, 1)
#define BODY5 vec4(0.35, 0.32, 0.31, 1.0)
#define BODY6 vec4(1.0, 0.92, 0.43, 1.0)
#define OUTLINE vec4(0.18, 0.02, 0.07, 1.0)
#define SCREEN vec4(0.0, 0.0, 0.1, 1.0)
#define GREYSCALE vec3(0.3, 0.59, 0.11)

#define ss(a, b, x) smoothstep(a, b, x)
#define BLUR 4.0 / iResolution.y

//https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float circle(in vec2 uv, float r)
{
    return length(uv) - r;
}

float box(in vec2 uv, in vec2 s)
{
    vec2 d = abs(uv) - s;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

float line(in vec2 uv, in vec2 a, in vec2 b)
{
    vec2 pa = uv - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float parallelogram(in vec2 uv, float wd, float ht, float sk)
{
    vec2 e = vec2(sk, ht);
    uv = (uv.y < 0.0) ? -uv : uv;
    vec2 w = uv - e; 
    w.x -= clamp(w.x, -wd, wd);
    vec2 d = vec2(dot(w, w), -w.y);
    float s = uv.x * e.y - uv.y * e.x;
    uv = (s < 0.0) ? -uv : uv;
    vec2 v = uv - vec2(wd, 0); 
    v -= e * clamp(dot(v, e) / dot(e, e), -1.0, 1.0);
    d = min(d, vec2(dot(v, v), wd * ht - abs(s)));
    return sqrt(d.x) * sign(-d.y);
}

vec2 rotate(in vec2 uv, float d)
{
    float rad = radians(d);
    return uv * mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

vec4 camera(in vec2 uv)
{
    float feed = dot(texture(iChannel0, uv * 1.6 - vec2(0.25, 0.37)).xyz, GREYSCALE);

    uv *= 4.0;
    float l1 = step(fract(uv.y + iTime * 0.2), 0.005);

    uv *= 50.0;
    float l2 = step(fract(uv.y + iTime * 0.7), 0.5);

    uv /= 200.0;
    uv -=0.5;
    uv.x *= iResolution.x / iResolution.y;
    float c = smoothstep(0.35, 0.2, length(uv));

    return vec4(mix(0.0, feed, (l1 + l2) * c)) + SCREEN; 
}

vec4 bb8(in vec2 uv, in vec2 cuv)
{
    uv = rotate(uv, sin(iTime * 0.2) + sin(iTime * 0.4));

    //Head
    float uo1 = ss(0.0, 0.0 - BLUR, circle((uv - vec2(0, -0.6)) * vec2(0.55, 0.66), 0.96));
    float uo2 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, -12.0), 11.06));
    float uo = uo1 - min(uo1, uo2);
    float ui1 = ss(0.0, 0.0 - BLUR, circle((uv - vec2(0.01, -0.61)) * vec2(0.55, 0.66), 0.95));
    float ui2 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.05, -11.997), 11.07));
    float ui = ui1 - min(ui1, ui2);

    //Bottom bar
    float bbo1 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, -12.0), 11.26));
    float bbo = min(uo, bbo1);
    float bbi1 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(-0.05, -12.0), 11.248));
    float bbi = min(ui, bbi1);

    //Top bar
    float tbo1 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.2, -10.56), 11.26));
    float tbo = uo - min(uo, tbo1);
    float tbi1 = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.2, -10.56), 11.27));
    float tbi = ui - min(ui, tbi1);
    float tl1 = ss(0.01, 0.01 - BLUR, line(uv, vec2(0.4, 0.71), vec2(0.4, 0.79)));
    float tl2 = ss(0.008, 0.008 - BLUR, line(uv, vec2(0.15, 0.71), vec2(0.15, 0.82)));
    float tl3 = ss(0.01, 0.01 - BLUR, line(uv, vec2(-0.15, 0.7), vec2(-0.15, 0.82)));
    float tl4 = ss(0.008, 0.008 - BLUR, line(uv, vec2(-0.4, 0.69), vec2(-0.4, 0.79)));
    float tl = max(max(max(tl1, tl2), tl3), tl4);

    //Side bars
    float sbro = ss(0.0, 0.0 - BLUR, parallelogram(uv - vec2(0.905, 0.45), 0.28, 0.1, -0.12));
    float sbri = ss(0.0, 0.0 - BLUR, parallelogram(uv - vec2(0.905, 0.45), 0.26, 0.09, -0.12));
    float sblo = ss(0.0, 0.0 - BLUR, parallelogram(rotate(uv - vec2(-0.95, 0.4), 5.5), 0.32, 0.1, 0.12));
    float sbli = ss(0.0, 0.0 - BLUR, parallelogram(rotate(uv - vec2(-0.95, 0.4), 5.5), 0.3, 0.09, 0.11));

    //Centre
    float cco = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, 0.08), 0.57));
    float cbo = ss(0.0, 0.0 - BLUR, box(uv - vec2(-0.28, -0.2), vec2(0.29, 0.29)));
    float co = max(cco, cbo);
    float cci = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, 0.08), 0.555));
    float cbi = ss(0.0, 0.0 - BLUR, box(rotate(uv, 0.5) - vec2(-0.28, -0.2), vec2(0.275, 0.275)));
    float ci = max(cci, cbi);
    float cc = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, 0.08), 0.54));
    float c = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, 0.08), 0.51));
    float clb = ss(0.0, 0.0 - BLUR, abs(box(uv - vec2(-0.82, -0.352), vec2(0.25, 0.12)) - 0.01) - 0.008);
    float cll = ss(0.011, 0.011 - BLUR, line(uv, vec2(-0.557, -0.8), vec2(-0.557, -0.4)));
    float cl = max(clb, cll);
    float crtbo = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.58, -0.4), vec2(0.3, 0.4)) - 0.1);
    float crbbo = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.1, -0.6), vec2(0.2, 0.3)));
    float crco = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.9, -0.3), 0.4));
    float cro = max(max(crbbo, crtbo - min(cco, crtbo)), crco);
    float crtbi = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.58, -0.4), vec2(0.29, 0.39)) - 0.1);
    float crbbi = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.1, -0.6), vec2(0.19, 0.3)));
    float crci = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.9, -0.3), 0.39));
    float cri = max(max(crbbi, crtbi - min(cco, crtbi)), crci);
    float csr = ss(0.0, 0.0 - BLUR, abs(circle(uv - vec2(0.9, -0.3), 0.3)) - 0.03);
    float csc = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.9, -0.3), 0.25));
    float csci = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0.9, -0.3), 0.2));
    float cscr = ss(0.02, 0.017 - BLUR, parallelogram(uv - vec2(0.82, -0.22), 0.01, 0.01, 0.01));
    float ccr = ss(0.02, 0.017 - BLUR, parallelogram(uv - vec2(-0.25, 0.35), 0.01, 0.01, 0.01));

    //Bottom panels
    float brpo1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.1, -0.76), vec2(0.06, 0.07)));
    float brpi1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.1, -0.76), vec2(0.05, 0.06)));
    float brpo2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.435, -0.76), vec2(0.15, 0.15)));
    float brpi2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(1.435, -0.76), vec2(0.14, 0.14)));
    float brp = max(brpo1, brpo2);
    float brpi = max(brpo1 - brpi1, brpo2 - brpi2);
    float bco = ss(0.0, 0.0 - BLUR, abs(circle(uv - vec2(0, -0.615), 0.1)) - 0.01);
    float bci = ss(0.0, 0.0 - BLUR, circle(uv - vec2(0, -0.615), 0.08));
    float blpo1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-0.6, -0.63), vec2(0.05, 0.15)));
    float blpi1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-0.6, -0.63), vec2(0.04, 0.14)));
    float blpo2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.35, -0.72), vec2(0.27, 0.15)));
    float blpi2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.35, -0.72), vec2(0.26, 0.14)));
    float blp = max(blpo1, blpo2);
    float blpi = max(blpo1 - blpi1, blpo2 - blpi2);
    float blplo1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.2, -0.65), vec2(0.08, 0.03)));
    float blpli1 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.2, -0.65), vec2(0.07, 0.02)));
    float blplo2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.2, -0.75), vec2(0.08, 0.03)));
    float blpli2 = ss(0.0, 0.0 - BLUR, box(uv - vec2(-1.2, -0.75), vec2(0.07, 0.02)));
    float blpl = max(blplo1, blplo2);
    float blpli = max(blplo1 - blpli1, blplo2 - blpli2);
    float blb = ss(0.007, 0.007 - BLUR, line(uv, vec2(-0.19, 0.94), vec2(-0.21, 0.93)));

    //Antennae
    float abro = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.15, 0.87), vec2(0.04, 0.04)));
    float abri = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.15, 0.87), vec2(0.03, 0.03)));
    float atro = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.15, 1.0), vec2(0.02, 0.1)));
    float atri = ss(0.0, 0.0 - BLUR, box(uv - vec2(0.15, 1.0), vec2(0.01, 0.09)));
    float alo = ss(0.0, 0.0 - BLUR, box(uv - vec2(-0.2, 0.92), vec2(0.025, 0.1)));
    float ali = ss(0.0, 0.0 - BLUR, box(rotate(uv - vec2(-0.201, 0.92), 1.5), vec2(0.015, 0.09)));
    float ald1 = ss(0.007, 0.007 - BLUR, line(uv, vec2(-0.19, 0.94), vec2(-0.21, 0.93)));
    float ald2 = ss(0.007, 0.007 - BLUR, line(uv, vec2(-0.19, 0.9), vec2(-0.21, 0.89)));
    float ald = max(ald1, ald2);

    vec4 col = vec4(0);
    col = mix(col, OUTLINE, uo1);
    col = mix(col, BODY1, uo);  
    col = mix(col, BODY3, sbro);
    col = mix(col, BODY3, sblo);
    col = mix(col, BODY2, abro);
    col = mix(col, BODY1, atro);
    col = mix(col, BODY1, alo);
    col = mix(col, BODY4, cc);    
    col = mix(col, OUTLINE, sbro - sbri);
    col = mix(col, OUTLINE, sblo - sbli);
    col = mix(col, OUTLINE, abro - abri);
    col = mix(col, OUTLINE, atro - atri);
    col = mix(col, OUTLINE, alo - ali);
    col = mix(col, OUTLINE, ald);
    col = mix(col, OUTLINE, co - ci);
    col = mix(col, OUTLINE, cl);
    col = mix(col, OUTLINE, cro - cri);
    col = mix(col, OUTLINE, csc);
    col = mix(col, BODY4, csci);
    col = mix(col, BODY5, csr);
    col = mix(col, BODY3, brp);
    col = mix(col, BODY3, blp);
    col = mix(col, mix(BODY6, BODY2, round(sin(iTime) * 0.5 + 0.5)), blplo1);
    col = mix(col, mix(BODY2, BODY6, round(sin(iTime) * 0.5 + 0.5)), blplo2);
    col = mix(col, BODY5, bci);
    col = mix(col, OUTLINE, brpi);
    col = mix(col, OUTLINE, blpi);
    col = mix(col, OUTLINE, blpli);
    col = mix(col, OUTLINE, bco);
    col = mix(col, BODY2, bbo);
    col = mix(col, BODY2, tbo);
    col = mix(col, OUTLINE, tl);
    col = mix(col, OUTLINE, uo1 - ui);
    col = mix(col, OUTLINE, bbo - bbi);
    col = mix(col, OUTLINE, tbo - tbi);    
    col = mix(col, camera(cuv), c);
    col = mix(col, vec4(1), max(ccr, cscr));

    return col;
}

