const float PI = 3.14159;
const vec3 sunglow = vec3(255., 202., 58.)/255.;
const vec3 skyBlue = vec3(76., 201., 240.)/255.;
const vec3 royalBlue = vec3(73., 109., 219.)/255.;
const vec3 rubineRed = vec3(202., 46., 85.)/255.;

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float stroke(float x, float w){
    float d = smoothstep(0.,.01,x+w*.5) - smoothstep(0.,.01,x-w*.5);
    return clamp(d,0.,1.);
}

float fill(float sdf){
    return 1.-smoothstep(0.,.025,sdf);
}

float mixedSDF(vec2 st, vec2 pos){
    float circleSDF = sdCircle(st-pos,.2);
    float triSDF = sdTriangleIsosceles((st-vec2(0.,.2))-pos,vec2(.2,-.4));
    float squareSDF = sdBox(st-pos,vec2(.2));

    float circleTimer = smoothstep(.1,.9,sin(iTime));
    float triTimer = smoothstep(.1,.9,sin(iTime-2.1));
    float squareTimer = smoothstep(.1,.9,sin(iTime-4.2));

    float mixSDF = 0.;
    mixSDF += mix(0.,circleSDF,circleTimer);
    mixSDF += mix(0.,triSDF,triTimer);
    mixSDF += mix(0.,squareSDF,squareTimer);

    return mixSDF;
}

vec2 offset(float x){
    return vec2(cos(iTime - x),sin(iTime - x))*.8;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    vec2 st = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float bgCircle = sdCircle(st,.8);
    float bgSquare = sdBox(st,vec2(.8));
    float sinLine = st.y + .25*sin((st.x*2.*PI)/1.6);

    col += fill(bgSquare)*skyBlue*step(0.,sinLine);
    col += fill(bgSquare)*royalBlue*step(0.,-sinLine);
    if (bgCircle < 0. && sinLine > 0.) {
        col = royalBlue;
    } else if (bgCircle < 0. && sinLine < 0.) {
        col = skyBlue;
    }

    float bgShape = stroke(bgCircle,.025);
    bgShape += stroke(bgSquare,.025);
    bgShape += stroke(sinLine, .025) * fill(sdBox(st,vec2(.802)));
    col *= 1.-bgShape;
    col += clamp(bgShape,0.,1.) * sunglow;
    if (bgShape > .7) {
        col = sunglow;
    }

    float movingShapes = stroke(mixedSDF(st,offset(0.)),.05);
    movingShapes += stroke(mixedSDF(st,offset(2.*PI / 3.)),.05);
    movingShapes += stroke(mixedSDF(st,offset(4.*PI / 3.)),.05);
    col = mix(col, rubineRed, movingShapes);

    fragColor = vec4(col,1.0);
}

const int MAX_STEPS = 100;
const float MIN_DISTANCE = 0.0001;
const float MAX_DISTANCE = 50.0;
const float EPSILON_NORMAL = 0.01;

vec3 getRay(vec2 fragCoord, vec3 camera, vec3 lookAt) {
    const float zoom = 1.5;
    vec2 uv = (fragCoord - (iResolution.xy / 2.0)) / iResolution.y;
    vec3 c = normalize(lookAt - camera);
    vec3 u = vec3(c.y, -c.x, 0.0);
    vec3 v = normalize(vec3(c.z*c.x, c.z*c.y, c.x*c.x+c.y*c.y));
    return normalize(c + (uv.x * u + uv.y * v) / zoom);
}

bool isInSphere(vec3 camera, vec3 ray, vec3 center, float radius) {
    float d = distance(camera, center);
    float dp = dot(ray, center-camera);
    return dp*dp > d*d-radius*radius;
}

float getDS(vec3 marcher) {
    const int len = 7;
    const float a = 3.0;

    float mins[len];

    mins[0] = marcher.z;
    mins[1] = distance(marcher, vec3(0.0, 0.0, 0.7)) - 1.0;
    int nb = len - 2;
    for (int i = 0; i < len-2; i++) {
        float angle = radians(270.0 * float(i) / float(nb));
        vec3 center = vec3(cos(angle), sin(angle), 0.5+1.0*sin(3.0*iTime+float(i)));
        mins[2+i] = distance(marcher, center) - 0.7;
    }

    float sa = 0.0;
    float sb = 0.0;
    for (int i = 0; i < len; i++) {
        sa += mins[i] * exp(-a * mins[i]);
        sb += exp(-a * mins[i]);
    }

    return sa / sb;
}

vec3 march(vec3 camera, vec3 ray) {
    vec3 marcher = camera;
    for (int i = 0; i < MAX_STEPS; i++) {
        float ds = getDS(marcher);
        if (ds < MIN_DISTANCE || ds > MAX_DISTANCE) { break; }
        marcher += ds * ray;
    }
    return marcher;
}

vec3 getNormal(vec3 camera, vec3 ray) {
    vec3 surface = march(camera, ray);
    float dx = getDS(surface+vec3(EPSILON_NORMAL, 0.0, 0.0));
    float dy = getDS(surface+vec3(0.0, EPSILON_NORMAL, 0.0));
    float dz = getDS(surface+vec3(0.0, 0.0, EPSILON_NORMAL));
    return normalize(vec3(dx, dy, dz));
}

float getLight(vec3 camera, vec3 ray) {
    vec3 source = vec3(4.0, 0.0, 5.0);
    vec3 surface = march(camera, ray);
    vec3 normal = getNormal(camera, ray);
    return -dot(normal, normalize(surface-source));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float speed = 0.7;
    vec3 camera = vec3(5.0 * vec2(cos(speed*iTime), sin(speed*iTime)), 1.0);
    vec3 lookAt = vec3(0.0, 0.0, 0.5);
    vec3 ray = getRay(fragCoord, camera, lookAt);

    fragColor = vec4(vec3(0.0), 1.0);
    fragColor.rgb += 0.5+0.5*getLight(camera, ray);
}



