#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)cos((h)*6.3+vec3(0,23,21))*.5+.5
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,g=0.,e,s;
    for(;i++<99.;)
    {
        p=g*d-vec3(.05*sin(iTime*.5),.1,1.8);
        p=R(p,normalize(vec3(1,-2,2)),iTime*.5);
        s=2.;
        vec4 q=vec4(p,sin(iTime*.4)*.5);
		for(int j=0;j++<8;)
        {
            q=abs(q);
            q=q.x<q.y?q.zwxy:q.zwyx;
            s*=e=2.3/clamp(dot(q,q),.2,1.5);
            q=q*e-vec4(1,4,2,1);
        }
        g+=e=min(
                length(q.w)/s,
                length(cross(q.xyw,vec3(.577)))/s-.3e-4
            )+.001;
        O.rgb+=mix(r/r,H(log(s)*2.),.3)*.015*exp(-.03*i*i*e);
    }
    O*=O*O*O;
 }

mat2 R(float a){
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}


void mainImage( out vec4 C, vec2 FC )
{
    float o,i,e,f,s, g=4., t=iTime, k=.1;
    vec3 p,q, r=iResolution, l=vec3(2.);

    for(;i++<1e2;g+=min(f,max(e,.4))*k){
        s=2.;
        p=vec3((FC.xy-r.xy/s)/r.y*g,g-5.);
        k*=1.015;
        p.yz*=R(-.7);
        p.z+=t;
        for(e=f=p.y;s<4e2;s/=.6)
            p.xz*=R(s),
            q=p,
            q.x+=t*.2*log(s),
            e+=abs(dot(sin(q.xz*s*.1)/s,l.xz*2.)),
            f+=abs(dot(sin(p*s*.15)/s,l));
         o=1.+(f>1e-3?(e>.01?f*e:-exp(-e*e)):-1.);

         C *= .96;
         C.rgb += .1*max(o,.5)*(exp(-f)*vec3(.5,.3,.2)/4.+exp(-e)*vec3(.2,.1,.0));
         }

}

#define PI 3.14159265359

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;
float time = 0.;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float tri(float delta) {
    if (delta > 1.) {
        return 0.;
    }
    if (delta < 0.) {
        return 0.;
    }
    return abs(2.*fract(delta)-1.);
}

float endlessTri(float delta) {
    return abs(2.*fract(delta)-1.);
}

float saw(float delta) {
    return delta-floor(delta);
}

vec3 checker(vec2 st) {
    st += vec2(time/PI);
    st *= 8.496;

    vec3 color = vec3(0.);
    color = vec3(floor(endlessTri((st.x+0.5)*1.)+(1.0-endlessTri(st.y))));

    return color;
}

vec3 c;
bool inside = false;

vec3 diamond(vec2 st) {
    float offset = -0.5;
    float dd = st.y-offset;
    float d = (st.x-0.25-offset)*2.;
    if (d > 1.0 || d < 0.0) {
        return vec3(0.);
    }
    if (dd > 1.0 || dd < 0.0) {
        return vec3(0.);
    }
    return (vec3(1.0)-vec3(floor(tri((d)*1.)+tri(dd))));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    time = saw(iTime*0.2)*PI*2.;

    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 backCol = vec3(0.343,0.131,0.490)-vec3(0.358,0.349,0.360)*(st.y);
    st += vec2(-0.70,0.190);
    st = rotate2d(0.568)*st;
    st -= 0.5;
    st *= 2.244;


    st.x -= st.y*-0.864*(sin(time)+1.);

    st = rotate2d(time)*st;

    vec3 color = vec3(0.);
    color = diamond(st);

    st = rotate2d(PI*0.5)*st;
    c = vec3(st.x+0.564, 0., st.y+1.152);
    c += checker(st)*0.192;

    if (color == vec3(0.)) {
    	color = diamond(st);
	    if (color == vec3(0.)) {
        	color = backCol;
        }
    }

    if (color == vec3(1.)) {
        color *= c;
    }

    fragColor = vec4(color,1.0);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g,e=1.;++i<99.;){
        p=g*d;
        p.z-=5.;
        p=R(p, normalize(vec3(1,2,3)), iTime*2.);
        p+=cross(sin(p*.4+iTime*3.),cos(p.zxy*.3+iTime*2.));
        g+=e=(length(p-clamp(p,-1.,1.))-.1)*.7;
        O.xyz+=mix(vec3(1),H(dot(p,p)),.8)*.01*exp(-.05*i*i*e);
    }
}

//Daniel Piker 2021
/* 
Changed to get the Seifert surface using (1/4πi) log (Q/Q∗)
(added complex log line 85 and new field expression line 163)

Still trying to make it work for keeping the knot fixed while varying the Seifert fiber like in 
https://twitter.com/KangarooPhysics/status/1381380025568616459
an attempt here
https://www.shadertoy.com/view/7sVXWK


// Inigo Quilez 2021
/*

An (appoximated) SDF that has a Figure-8 knot shape. It's not very
symmetric or pretty, but it counts (topologically).

The idea is described here https://arxiv.org/pdf/1610.02463.pdf and
goes like this:

From R3, our regular 3D space, we do an inverse stereographic
projection to land in a 4D hyper sphere S3, a subset of R4.

In R4 we define a function F that returns a R2 field (or
two scalar fields, if you want). We find the zero isosurface
(the nodal set, or roots) of F by zeroing its two components.

Because we are only evaluating the subset of R4 that lays on S3,
we are really computing the intersection of the isosurfce of F
with the surface of the sphere S3. This creates a curve on the surface
of the sphere, which thanks to the stereographic projection we started
with becomes a curve in R3, our 3D space. That's the curve we are
raymarching.

The R4 parameter to F can be grouped as two complex numbers u and v.
And in the case of this shader I'm using

F(u,v) = 64v³ − 12v(3+2u²−2u*²) − (14u²+14u*²+u⁴-u*⁴)

where u* is the conjugate of u. This curve is a figure 8 shape, as
described in the paper. You can change the SHAPE define below to
see another shape (a torus knot).

Lastly, there's more than one way to convert F into a R1 scalar field
than taking it's length |F|. For example, you can pass F through any
2D SDF formula you want, such a square. Change the PROFILE define
bellow to explore that.


See: https://www.shadertoy.com/view/tsBGzt
See: https://www.shadertoy.com/view/7sGSRd

*/

#define AA 1

// 0: circular
// 1: square
#define PROFILE 0

// 0: figure 8
// 1: torus 5-2
// 2: something i made
#define SHAPE 0

//------------------------------------------------------------
// oldschool rand() from Visual Studio
//------------------------------------------------------------
int   seed = 1;
void  srand(int s ) { seed = s; }
int   rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float frand(void) { return float(rand())/32767.0; }
//------------------------------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
//------------------------------------------------------------
int hash( int n )
{
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}

//------------------------------------------------------------
// complex number operations
//------------------------------------------------------------
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 cdiv( vec2 a, vec2 b )  { float d = dot(b,b); return vec2( dot(a,b), a.y*b.x - a.x*b.y ) / d; }
vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }
vec2 csqrt( vec2 z ) { float m = length(z); return sqrt( 0.5*vec2(m+z.x, m-z.x) ) * vec2( 1.0, sign(z.y) ); }
vec2 conj( vec2 z ) { return vec2(z.x,-z.y); }
vec2 cpow( vec2 z, float n ) { float r = length( z ); float a = atan( z.y, z.x ); return pow( r, n )*vec2( cos(a*n), sin(a*n) ); }
vec2 clog( vec2 z) {return vec2 (log(sqrt(z.x*z.x+z.y*z.y)),atan(z.y,z.x));}
//------------------------------------------------------------
// https://en.wikipedia.org/wiki/Stereographic_projection
vec4 inversestereographic(vec3 p)
{
    float r2 = 1.0+dot(p,p);
    return vec4(2.0*p,r2-2.0)/r2;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/biplanar/biplanar.htm
vec3 tex( sampler2D sam, in vec4 p, in vec4 n )
{
    n = n*n;
    n = n*n;
    vec3 col = vec3(0.0);
    col += n.x*texture(iChannel0,p.yz).xyz;
    col += n.y*texture(iChannel0,p.zw).xyz;
    col += n.z*texture(iChannel0,p.wx).xyz;
    col += n.w*texture(iChannel0,p.xy).xyz;
    return col / (n.x+n.y+n.z+n.w);
}
//===============================================================================================

vec4 r3_to_s3( in vec3 p )
{
    vec4 q = inversestereographic(p);

    // rotate in yw
    float a = iTime*6.283185/6.0;
    q.yw *= mat2(cos(a),sin(a),-sin(a),cos(a));

    return q;
}

vec2 F( in vec2 u, vec2 v )
{
    #if SHAPE==0
    // figure-8
    vec2 a = vec2(3.0,0.0) - 2.0*csqr(v) + 2.0*csqr(conj(v));
    vec2 q = 64.0*cmul(u,csqr(u)) 
           - 12.0*cmul(u,a) 
           + 14.0*cmul(v,v) 
           + 14.0*csqr(conj(v)) 
           -      csqr(csqr(v)) 
           +      csqr(csqr(conj(v)));
    return q;
    #endif

    #if SHAPE==1
    // torus
    return 20.0*(cmul(u,csqr(csqr(u))) + csqr(v));
    #endif    

    #if SHAPE==2
    // something I made
    return    64.0*cmul(v,csqr(csqr(u)))
           + 32.0*cmul(u,csqr(v))
           + 32.0*csqr(v) 
           + 8.0;
    #endif    

}

float field( in vec4 q )
{
    vec2 phi = F(q.xy,q.zw);

    #if PROFILE==0
    //return length(phi) - 10.0;  

    //(1/4πi) log (Q/Q∗)  
    return (length(cmul(vec2(0,0.785398163),clog(cdiv(phi,conj(phi))))));    

    #else
    return sdRoundSquare( phi, 8.0, 0.5 );
    #endif
}

// only used for texturing, not for distance-estimation
vec4 field_grad( in vec4 q )
{
    float f = field(q);
    const vec2 e = vec2(0.001,0.0);
    return normalize(vec4(
        field(q+e.xyyy),
        field(q+e.yxyy),
        field(q+e.yyxy),
        field(q+e.yyyx))-f );
}

float map( in vec3 p )
{
    float d = field(r3_to_s3(p));

    // could do some automatic differentiation here...
    float eps = 0.0001;
    vec3 g = (vec3(field(r3_to_s3(p+vec3(eps,0.0,0.0))),
                   field(r3_to_s3(p+vec3(0.0,eps,0.0))),
                   field(r3_to_s3(p+vec3(0.0,0.0,eps))))-d)/eps;

    // distance estimator
    return d/length(g);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float eps )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float k )
{
    const float tmax = 5.0;
    float t = 0.001;
    float sh = 1.0;
    for( int i=0; i<256; i++ )
    {
        vec3 pos = ro + rd*t;
        float h = map(pos);
        sh = min( sh, clamp(k*h/t,0.0,1.0) );
        if( sh<0.001 ) break;
        t += clamp(h,0.001,0.1);
        if( t>tmax ) break;
    }
    return sh;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
	for( int i=0; i<32; i++ )
	{
		float h = frand();
        vec3 kv = normalize( vec3(frand(), frand(), frand()) );
        kv *= sign(dot(kv,nor));
		ao += clamp( map(pos+nor*0.001+kv*h*2.0)*1.0, 0.0, 1.0 );
	}
	ao /= 32.0;

	return clamp( ao*1.5-0.1, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // init random seed
    ivec2 q = ivec2(fragCoord);
    srand( hash(q.x+hash(q.y+hash(iFrame))));

    // camera movement	
    float an = -0.0*iTime/20.0;
    vec3 ro = vec3( 5.0*sin(6.283185*an), 4.0, 5.0*cos(6.283185*an) );
    #if SHAPE>0
    ro.xz *= 1.5;
    ro.y = 0.0;
    #endif
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 3.0*ww );

        // raymarch
        const float tmax = 15.0;
        float t = 0.0;
        for( int i=0; i<8192; i++ )
        {
            vec3 pos = ro + rd*t;
            float d = map(pos);
            if( d<0.001 ) break;
            d = min(d*0.8,0.1);
            t += d;
            if( t>tmax ) break;
        }

        vec3 col = vec3(0.0);
        col = vec3(0.05 + 0.05*rd.y);
        if( t<tmax )
        {
            vec3  pos = ro + rd*t;
            vec3  nor = calcNormal( pos, 0.0001 );
            float occ = calcAO( pos+nor*0.001, nor );

            // we do texturing in R4
            vec4 pos4 = r3_to_s3(  pos );
            vec4 nor4 = field_grad( pos4 );
            vec3 mate = tex(iChannel0,pos4,nor4);
            mate = mate*mate;
            mate *= 0.3;

            // sun
            {
            vec3 lig = normalize(vec3(1.0,1.0,0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            if( dif>0.001 ) dif *= shadow(pos+nor*0.001, lig, 48.0);
            vec3 hal = normalize(lig-rd);
            float spe = pow(clamp(dot(nor,hal),00.,1.0),32.0);
            spe *= 0.04 + 0.96*pow( clamp(1.0-dot(lig,hal), 0.0, 1.0), 5.0 );
            spe *= 10.0;

            col  = 0.6*mate*vec3(9.0,8.0,7.0)*dif;
            col += spe*dif*mate.x*10.0;

            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
            col += mate*fre*1.0*(0.25+0.75*dif)*vec3(1.0,0.7,0.4);
            }

            // sky
            {
            vec3 ref = reflect(rd,nor);
            float dif = 0.5+0.5*nor.y;
            float spe = smoothstep(0.3,0.5,ref.y);
            spe *= dif;
            spe *= 0.04 + 0.96*pow( clamp(1.0+dot(rd,nor), 0.0, 1.0), 5.0 );
            if( spe>0.001 ) spe *= shadow(pos+nor*0.001, ref, 8.0);
            col += 3.0*occ*mate*dif*vec3(0.5,0.6,1.0)*1.5;
            col += 1.0*spe;
            }

            // sun bounce
            col += mate*(1.0-occ)*vec3(1.2,0.5,0.2)*0.2;
        }

        // gain
        col = col*1.5/(1.0+col);

        // to gamma space
        col = pow( col, vec3(0.4545) );
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // remove color banding through dithering
    tot += (1.0/255.0)*frand();

    fragColor = vec4( tot, 1.0 );
}

#define rot(a) mat2(cos(a+vec4(0,11,33,0))) // --- utils from https://www.shadertoy.com/view/llySRh
#define H(p)   fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

vec4   lum = vec4(0,1,0,0),           // balls color on lit side
      shad = vec4(0,0,1,0),           // balls color on shadow side
        bg = vec4(1,0,0,0);           // background color
float r = .3;                         // spheres radius

float map(vec3 q) {                   // --- scene  SDF ----------------
    q *= 2.;
    vec2 I = floor(q.yx), F = fract(q.yx), D;
    float d, l=9.;
    for( int k=0; k < 9; k++ )        // find closest seed within neihbors
        D = vec2(k%3,k/3) -1.,
        d = length( D + H(I+D) - F ), // offset to seed
        l = min(l,d);                 // keep closest dist
    return length(vec2(l,q.z)) - r;   // sphere SDF
}

void mainImage(out vec4 O, vec2 U)    // --- renderer ----------------
{
    vec3 R = iResolution;
    U = (U+U-R.xy)/R.y;
    float t=9., i; 
    vec3  N, e = vec3(1,-1,0)/1e2, 
          D = normalize(vec3(U, -2.)),                 // ray direction
          p = vec3(0,-2,22),                           // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(20,12,0)/1e2*cos(.3*iTime+vec3(0,11,0))+vec3(0,.2,0);
    p.yz *= rot(.5-6.3*M.y),                           // rotations
    p.xz *= rot(-6.3*M.x);
    D.yz *= rot(.5-6.3*M.y),
    D.xz *= rot(-6.3*M.x);
    if (D.z*p.z>0.) { O=vec4(1); return; }             // sky

    if ( abs(U.x) < .5 ) {                        // --- analytical average
         t = -p.z/D.z;
         if ( t < 0. ) { O = bg; return; }
         p += t*D;                                     // ray-trace to plane
         N = vec3(0,0,1);                              // plane normale
         float d = 3.14*r*r,                           // sphere density
               a = 1.- pow( 1.-d, 1./ abs(dot(N,D)) ), // apparent density
               s = ( 1.+ D.x ) / 2. ; // D.x: dot(D,L) // sphere apparent shadowing proportion
         O = mix( bg, mix( lum, shad, s ), a );        // compose contributions
      }
    else {     
        for ( i=0.; i<200. && t>.01; i++ )        // --- explicit rendering
            t = min(9., map(p)),                       // SDF
            p += .25*t*D;                              // step forward = dist to obj

        if (i<200.)                                    // hit
             N = normalize(   map(p+e.xyy)/e.xyy +  map(p+e.yxy)/e.yxy // https://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
                            + map(p+e.yyx)/e.yyx +  map(p+e.xxx)/e.xxx ),
             O = N.x > 0. ? lum : shad;                // toon shading
          // O = vec4( .3 + .7*max(0.,N.x) );          // ambient + diffuse
        else O = bg;
    }
}

#define PI 3.1415926538

vec2 mult(vec2 a, vec2 b)
{
    return vec2(a.x*b.x - a.y*b.y, a.x * b.y + a.y * b.x);
}
//https://stackoverflow.com/questions/26070410/robust-atany-x-on-glsl-for-converting-xy-coordinate-to-angle
float atan2(in float y, in float x)
{
    float s = (abs(x) > abs(y))?1.:0.;
    return mix(PI/2.0 - atan(x,y), atan(y,x), s);
}

vec2 powc(vec2 x, float n) {
     float ang = atan2(x.y, x.x);
     float modulus = length(x);
     float newAng = n * ang;
     float newMod = pow(modulus, n);
     return newMod*vec2(cos(newAng), sin(newAng));

}


vec2 conj(vec2 a)
{
    return vec2(a.x, -a.y);
}

float mod2(vec2 a)
{
    return a.x*a.x + a.y*a.y;
}

vec2 inv(vec2 x) {
    return conj(x) / mod2(x);
}
vec2 f(vec2 x) {
    return vec2(-1, 0) + powc(x, 3.);
}

vec2 fp(vec2 x) {

     return 3.0*powc(x, 2.); //+ 2.0*x + vec2(1.0, 0);
}

vec2 newt(vec2 x) {
     return x - mult(f(x), inv(fp(x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.x;
    vec2 xy = uv*2.0 -vec2(1.0, iResolution.y/iResolution.x);
    vec2 x = mult(xy*16.0 / pow(iTime, 2.), vec2(cos(iTime), sin(iTime)));

    vec3 col = vec3(152.,125.,124.)/255.;
    for (int i =0; i < 100; i++){
       float qTime = mod(iTime, 10.0);
       if (qTime > 5.0) {
       qTime = 10.0-qTime;
       }
       if (float(i) >= qTime*24.0) {
           break;
        }
        x =newt(x);

    }
    vec2 unity = vec2(cos(2.*PI/3.), sin(2.*PI/3.));
    vec2 u2 = powc(unity, 2.);
    vec2 o = vec2(1., 0.);

    float di = mod2(x - unity);
    float dni = mod2(x - u2);
    float dn1 = mod2(x - o);
    if (dni < di && dni < dn1) {
       col = vec3(160., 156., 176.)/255.;
    }
    else if (di < dn1) {
       col = vec3(255.,193.,69.)/255.;
    }

    // Time varying pixel color


    // Output to screen
    fragColor = vec4(col,1.0);
}

float Mandelbrot(vec2 z){
    float radius = 180.;
    float speed=.1;
    vec2 screenPos = vec2(iResolution.x*.48,iResolution.y*.5) + vec2(cos(iTime*speed),sin(iTime*speed))*radius;
    vec2 c;
    c = (screenPos/iResolution.xy - vec2(.5,.5))*3.;
    int iterations = 100;

    for(int i = 0; i < iterations; i++){
        float a = z.x;
        float b = z.y;
        z = vec2(a*a-b*b, 2.*a*b) + c;
        if(dot(z,z)>4.){
            return float(i)/float(iterations);
        }
    }
    return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float v = Mandelbrot((fragCoord - iResolution.xy/2.)*3./iResolution.y);
    fragColor = vec4(0.5 + 0.5*cos(v*500.+vec3(0,2,4)+iTime/2.),0.);
}

#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)

void mainImage( out vec4 O, vec2 u )
