#define PI 3.14159

float hash21(vec2 p) {return fract(fract(dot(p,vec2(5.15123,2.1235)))*41.3151);}
float circle (vec2 p, vec2 c, float r, float w) {return 1.-smoothstep(0.,.01,abs(length(p-c)-r)- w);}
mat2 rot2(float a) {return mat2(cos(a), -sin(a), sin(a), cos(a));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = ( fragCoord.xy - iResolution.xy * .5 ) / iResolution.y;

	uv *= 3.;
	uv += vec2(iTime * .1, -iTime*.15);

	float steps = 7.; // play with this value :)
	float backclfun = clamp(sin(uv.x*2.)*sin(uv.y*2.)*.5+.5,1./abs(steps)-.01,1.)-.001;
	float bclffl = floor((backclfun)*abs(steps));
	float bclffr = backclfun*steps-bclffl;

	vec3 col = mix(vec3(.55, .55, .96), vec3(.96, .55, .52), clamp(bclffl / steps,0.,1.)) ;

	vec2 grid = floor(uv);
	float id = hash21(grid);
	vec2 cent = uv-grid-.5;
	cent = rot2(PI/2. * floor(id * 7.)) * cent;

    vec3 cl = col;
	col = mix(col, (1.-col)*1.2, circle(cent, vec2( .5, .5), .65, .001));
	col = mix(col, (1.-col)*1.2, circle(cent, vec2( .5, .5), .35, .001));
	col = mix(col, (1.-col)*1.2, circle(cent, vec2(-.5,-.5), .65, .001));
	col = mix(col, (1.-col)*1.2, circle(cent, vec2(-.5,-.5), .35, .001));

	col = mix(col, (1.-cl)*1.2, circle(cent, vec2(-.5,.5), .5, .063));
	col = mix(col, (1.-cl)*1.2, circle(cent, vec2(.5,-.5), .5, .063));
	col = mix(col, vec3(.9), circle(cent, vec2(-.5,.5), .5, .05));
	col = mix(col, vec3(.9), circle(cent, vec2(.5,-.5), .5, .05));

    col = mix(col, vec3(0.), float(texture(iChannel0, cent).xyz) * .2);

	col = mix(col, vec3(PI*.1), pow(1.-bclffr, PI));

    fragColor = vec4(col,1.0);
}

float PI = 3.14159265;

float atan2(in float y, in float x){
    return x == 0.0 ? sign(y)*PI/2.0 : atan(y, x);
}

float peek(in float x, in float spr, in float target){
    return min(
        spr/((x-target)*(x-target)+0.0000001),
        1.0
    );
}

float rand(float x){
  return fract(sin(dot(vec2(x, x*1.34) ,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 ring(float time, float ang, float d, float spr, float p1, float p2){
    vec3 rgb;
    rgb.x = sin(time*p2+ang*1.0);
    rgb.y = cos(time*p2+ang*1.0);
    rgb.z = sqrt(abs(1.5 - abs(rgb.x) - abs(rgb.y)));

    float b = 0.05*cos(cos(time));
    float r = 0.15 + b * cos(cos(ang+time*p2) + 0.6*sin(ang*p1));

    float ringeff = peek(d, spr, r);
    return ringeff*rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float d = distance(fragCoord, iResolution.xy/2.0)/distance(vec2(0.0, 0.0),iResolution.xy/2.0);
    vec2 p = fragCoord - iResolution.xy/2.0;
    float ang = atan2(p.y,p.x);
    vec3 col;

    float time=iTime*5.0;
    float offset=17.0;

    float spr = 0.0000004;
    spr += spr*cos(time*1.0) + spr*0.8;

    col = ring(time, ang, d, spr, 4.0, 1.0);
    col += ring(time, ang, d, spr, 3.0, 1.4);
    col += ring(time, ang, d, spr, 5.0, 1.6);
    col += vec3(1.0, 1.0, 1.0) * (1.0 - distance(vec3(0.,0.,0.),col));

    // Output to screen
    fragColor = vec4(col,1);
}

