struct LegPose
{
	vec3 legAngle;
	float kneeAngle;
	vec3 footAngle;
};

struct Pose
{
	vec3 offset;
	vec3 rotation;
	float cameraOffset;
	vec3 headRotation1;
	float headRotation2;
	float earsAngle;
	float eyesOpenFactor;
	vec3 eyesDirection;
	float eyebrowsHeight;
	float cheeksFactor;
	float mouthFactor;
	vec3 bodyRotation;
	vec3 bodyCurve;
	vec3 tailRotation;

	ArmPose leftArm;
	ArmPose rightArm;
	LegPose leftLeg;
	LegPose rightLeg;
};

// Cubic Smin
// https://iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    k*=1.35; // This line is an adjustment to match the quadratic smin. It is safe to remove.
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float smax( float a, float b, float k )
{
    k*=1.35; // This line is an adjustment to match the quadratic smin. It is safe to remove.
    float h = max( k-abs(a-b), 0.0 )/k;
    return max( a, b ) + h*h*h*k*(1.0/6.0);
}

vec2 smin(in vec2 a, vec2 b, float k) // material smooth min
{
	float x = smin(a.x, b.x, k);
	return vec2(x, a.x < b.x ? a.y : b.y);
}

vec2 smax(in vec2 a, vec2 b, float k) // material smooth max
{
	float x = smax(a.x, b.x, k);
	return vec2(x, a.x > b.x ? a.y : b.y);
}

vec2 min2(in vec2 a, vec2 b) // material min
{
	return a.x < b.x ? a : b;
}

vec2 max2(in vec2 a, vec2 b) // material max
{
	return a.x > b.x ? a : b;
}

float hash(float p)
{
	p = fract(p * 0.1031);
	p *= p + 33.33;
	p *= p + p;
	return fract(p);
}

float hash(vec3 p)
{
	p  = fract(p * 0.1031);
	p += dot(p, p.yzx + 33.33);
	return fract((p.x + p.y) * p.z);
}

float hash(vec2 p)
{
	return hash(p.xyx);
}

float noise(in vec3 pos)
{
	vec3 p = floor(pos);
	vec3 f = fract(pos);

	f = f * f * (3.0 - 2.0 * f);

	float a = 7.2;
	float b = 9.3;

	float n = p.x + a * p.y + b * p.z;

	float res = mix(mix(mix(hash(n), hash(n + 1.0), f.x),
						mix(hash(n + a), hash(n + a + 1.0), f.x), f.y),
					mix(mix(hash(n + b), hash(n + b + 1.0), f.x),
						mix(hash(n + a + b), hash(n + a + b + 1.0), f.x), f.y), f.z);
	return res;
}

float fbm(in vec3 p)
{
	float f = 0.5 * noise(p);
	p *= 2.1; f += 0.25 * noise(p);
	return f;
}

float linearstep(float edge0, float edge1, float x)
{
	return clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
}

float getTimeSegment(float time, float start, float end)
{
	return clamp((time - start) / (end - start), 0.0, 1.0);
}

float easeIn(float time, float factor)
{
	return pow(time, factor);
}

float easeOut(float time, float factor)
{
	return 1.0 - pow(1.0 - time, factor);
}

float easeInOut(float time, float factor)
{
	return easeIn(time * 2.0, factor) * step(0.5, 0.5 - time) +
		easeOut(time * 2.0 - 1.0, factor) * step(0.5, time);
}

vec3 vec3x(in float x)
{
	return vec3(x, 0.0, 0.0);
}

vec3 vec3y(in float y)
{
	return vec3(0.0, y, 0.0);
}

vec3 vec3z(in float z)
{
	return vec3(0.0, 0.0, z);
}

float smoothNoise(float x)
{
	float floorx = floor(x);
	float fractx = fract(x);

	return mix(hash(floorx), hash(floorx + 1.0), smoothstep(0.0, 1.0, fractx));
}

float smoothNoise(float x, float f)
{
	float floorx = floor(x);
	float fractx = fract(x);

	return smoothstep(f, 0.0, fractx) * hash(floorx - 1.0) + smoothstep(0.0, f, fractx) * hash(floorx);
}

float sdSurface(in vec3 pos)
{
	return pos.y;
}

float sdSphere(in vec3 pos, float rad)
{
	return length(pos) - rad;
}

float sdEllipsoid(in vec3 pos, vec3 rad)
{
	float k0 = length(pos / rad);
	float k1 = length(pos / rad / rad);
	return k0 * (k0 - 1.0) / k1;
}

float sdArc(in vec3 pos, float len, float angle, float width, float taper)
{
	// parameters
	vec2 sc = vec2(sin(angle), cos(angle));
	float ra = 0.5 * len / angle;

	// recenter
	pos.x -= ra;

	// reflect
	vec2 q = pos.xy - 2.0 * sc * max(0.0, dot(sc, pos.xy));

	float u = abs(ra) - length(q);
	float d2 = (q.y < 0.0) ? dot(q + vec2(ra, 0.0), q + vec2(ra, 0.0)) : u * u;
	float s = sign(angle);

	float t = (pos.y > 0.0) ? atan(s * pos.y, -s * pos.x) * ra : (s * pos.x < 0.0) ? pos.y : len - pos.y;
	width = max(0.001, width - t * taper);

	return sqrt(d2 + pos.z * pos.z) - width;
}

float sdCone(vec3 p, vec2 c, float h)
{
	float q = length(p.xz);
	return max(dot(c.xy, vec2(q, p.y)), -h - p.y);
}

vec2 rotate(in vec2 pos, float angle)
{
	float s = sin(angle);
	float c = cos(angle);
	mat2 m = mat2(c, s, -s, c);
	return pos * m;
}

vec3 rotate_yzx(in vec3 pos, vec3 angle)
{
	pos.xz = rotate(pos.xz, angle.y);
	pos.xy = rotate(pos.xy, angle.z);
	pos.yz = rotate(pos.yz, angle.x);

	return pos;
}

vec3 rotate_xzy(in vec3 pos, vec3 angle)
{
	pos.yz = rotate(pos.yz, angle.x);
	pos.xy = rotate(pos.xy, angle.z);
	pos.xz = rotate(pos.xz, angle.y);

	return pos;
}

float getGroundHeight(in vec3 pos)
{
	pos *= vec3(0.1, 0.0, 0.1);
	pos.xz *= mat2(0.93, 0.34, -0.34, 0.93);

	return -2.0 +
		0.9 * (sin(pos.x) + sin(pos.z)) +
		0.6 * (sin(2.0 * pos.x) + sin(2.0 * pos.z));
}

void mixPose(inout ArmPose pose1, in ArmPose pose2, float f)
{
	pose1.armAngle = mix(pose1.armAngle, pose2.armAngle, f);
	pose1.elbowAngle = mix(pose1.elbowAngle, pose2.elbowAngle, f);
	pose1.wristAngle = mix(pose1.wristAngle, pose2.wristAngle, f);
	pose1.fingersAngle = mix(pose1.fingersAngle, pose2.fingersAngle, f);
}

ArmPose getArmPose3(float time, vec3 noise)
{
	ArmPose p;

	float time1 = sin(PI2 * time);

	p.fingersAngle = 1.2;
	p.armAngle = vec3(-0.3, -0.9, 0.3) + time1 * vec3(0.0, -0.2, 0.0);
	p.elbowAngle = vec3(0.3, -1.2, 0.1) + time1 * vec3(0.5, -0.6, 0.0);
	p.wristAngle = vec3(0.0, 0.0, 0.0);

	noise = 2.0 * noise - 1.0;

	p.armAngle += noise.x * vec3(0.0, 0.1, 0.2);
	p.elbowAngle += noise.y * vec3(0.0, 0.3, 0.0);

	return p;
}

ArmPose getArmPose1(float time, vec3 noise)
{
	ArmPose p;

	float time1 = sin(PI2 * time);

	p.armAngle = vec3(-2.0, -0.5, 0.3) + time1 * vec3(0.0, 0.2, 0.4);
	p.elbowAngle = vec3(0.0, 0.0, 0.0) + time1 * vec3(0.0, 0.3, 0.0);
	p.wristAngle = vec3(0.0, -0.2, 0.0) + time1 * vec3(0.0, -0.1, 0.0);

	p.armAngle += noise.x * vec3(0.0, 0.3, 0.2) - vec3(0.0, 0.0, 0.2);
	p.elbowAngle += noise.y * vec3(0.0, 1.0, 0.0);
	p.wristAngle += noise.z * vec3(0.0, -0.1, 0.0);

	return p;
}


ArmPose getArmPose2(float time, vec3 noise)
{
	ArmPose p;

	float time1 = sin(PI2 * time);

	p.fingersAngle = 1.2;
	p.armAngle = vec3(-2.0, 0.0, 0.5) + time1 * vec3(0.0, 0.2, 0.2);
	p.elbowAngle = vec3(0.5, 0.0, 1.0) + time1 * vec3(0.0, 0.1, 0.0);
	p.wristAngle = vec3(0.0, 0.0, 0.0) + time1 * vec3(0.0, 0.3, 0.0);

	noise = 2.0 * noise - 1.0;

	p.armAngle += noise.x * vec3(0.0, 0.2, 0.2);
	p.elbowAngle += noise.y * vec3(0.0, 0.2, 0.0);
	p.wristAngle += noise.z * vec3(0.0, 0.0, 0.0);

	return p;
}

ArmPose getArmPose(float time, vec3 noise, float pose1Factor, float pose2Factor)
{
	ArmPose p = getArmPose1(time, noise);
	mixPose(p, getArmPose2(time, noise), pose1Factor);
	mixPose(p, getArmPose3(time, noise), pose2Factor);

	return p;
}

LegPose getLegPose(float time, vec3 noise, float speed, float pose1Factor)
{
	LegPose p;
	float time1, t1, t2;

	time1 = fract(time);

	float hop = pose1Factor;
	float range = 1.3 * speed;

	t1 = getTimeSegment(time1, 0.0, 0.6 - 0.4 * hop);
	t2 = getTimeSegment(time1, 0.6 - 0.4 * hop, 1.0);
	p.legAngle = vec3x(0.5 + (1.0 - hop * 0.4) * range * (1.0 - 2.3 * (easeOut(t1, 1.3) - easeOut(t2, 1.3))));

	time1 = fract(time + 0.2);

	t1 = getTimeSegment(time1, 0.0, 0.3);
	t2 = getTimeSegment(time1, 0.4, 0.7);
	p.kneeAngle = -0.7 + range * (-1.0 + 1.5 * (easeOut(t1, 1.2) - easeIn(t2, 1.5)));

	t1 = getTimeSegment(time1, 0.0, 0.5);
	t2 = getTimeSegment(time1, 0.5, 1.0);
	p.footAngle = vec3x(0.5 - 0.6 * (easeOut(t1, 2.0) - easeOut(t2, 2.0)));

	return p;
}

Pose getPose(float time)
{
	Pose pose;
	float time1, time2;
	vec3 n1, n2, n3;

	float poseDuration = 4.0;
	float mode0 = floor(hash(floor(time / poseDuration) - 1.0) * 4.0);
	float mode1 = floor(hash(floor(time / poseDuration)) * 4.0);

	float mixFactor = smoothstep(0.0, 0.2, fract(time / poseDuration));
	float armsPose1Factor = mix(step(abs(mode0 - 2.0), 0.0), step(abs(mode1 - 2.0), 0.0), mixFactor);
	float armsPose2Factor = mix(step(abs(mode0 - 3.0), 0.0), step(abs(mode1 - 3.0), 0.0), mixFactor);

	poseDuration = 5.5;
	mixFactor = smoothstep(0.0, 0.2, fract(time / poseDuration));
	mode0 = floor(hash(0.1 + floor(time / poseDuration) - 1.0) * 4.0);
	mode1 = floor(hash(0.1 + floor(time / poseDuration)) * 4.0);
	float legsPoseFactor = mix(step(abs(mode0 - 2.0), 0.0), step(abs(mode1 - 2.0), 0.0), mixFactor);
	legsPoseFactor *= max(0.0, 1.0 - armsPose1Factor - armsPose2Factor);

	time1 = time * 0.4; // noise change interval
	n1 = vec3(smoothNoise(time1, 0.3), smoothNoise(time1 + 3.0, 0.3), smoothNoise(time1 + 5.0, 0.3));
	n2 = vec3(smoothNoise(time1 + 2.0, 0.3), smoothNoise(time1 + 4.0, 0.3), smoothNoise(time1 + 1.0, 0.3));
	n3 = 0.5 * (n1 + n2);

	pose.leftArm = getArmPose(time, n2, armsPose1Factor, armsPose2Factor);
	pose.rightArm = getArmPose(time + 0.5, n1, armsPose1Factor, armsPose2Factor);

	time1 = time * 0.4;
	float runSpeed = 0.15 * (RUN_SPEED + 1.3 * (cos(time1) + cos(time1 / 2.0) / 2.0 + cos(time1 / 3.0) / 3.0 + cos(time1 / 5.0) / 5.0));

	pose.leftLeg = getLegPose(time, n1, runSpeed, legsPoseFactor);
	pose.rightLeg = getLegPose(time + 0.5, n2, runSpeed, legsPoseFactor);

	pose.headRotation1 = vec3(0.3, 0.0, 0.0) + (n3 * 2.0 - 1.0) * vec3(0.7, 0.2, 0.4);
	pose.bodyRotation = vec3(-0.2 - 0.3 * n1.y, 0.0, 0.0);
	pose.tailRotation = (n3 * 2.0 - 1.0) * vec3(0.0, 0.5, 0.0) +
		vec3(0.0, 0.0, 0.1 + 1.6 * (pose.bodyRotation.x + 0.5)) +
		(0.4 + 0.6 * legsPoseFactor) * vec3(0.0, 0.3 * sin(time * PI2), -0.6 * pow(abs(sin(time * PI2 - 0.1)), 1.4));
	pose.bodyCurve = vec3(0.1 - 0.2 * runSpeed, 0.1 * sin(PI05 + time * PI2), 0.0);

	time1 = time / 2.5; // ears bounces interval
	pose.earsAngle = 0.7 * abs(sin(time1 * 30.0)) * // speed and amplitude
		max(0.2 - fract(time1), 0.0) * // duration
		step(hash(floor(time1)), 0.5); // probability

	pose.eyesDirection = 0.2 * vec3(n3.x, n3.y, -3.0 + pose.headRotation1.x * 4.0);

	time1 = time / 1.5; // eyes blink interval
	time2 = fract(time1);

	float open0 = step(hash(floor(time1) - 1.0), 0.8); // open probability
	float open1 = step(hash(floor(time1)), 0.8); // open probability
	float isBlinking = step(hash(floor(time1)), 0.7); // blink probability
	float isOpened = step(2.0, open0 + open1);

	float openPosition = open0 * linearstep(0.3, 0.0, time2) + open1 * linearstep(0.0, 0.2, fract(time1));
	float blinkPosition = 1.0 - isBlinking * (linearstep(0.0, 0.05, time2) - linearstep(0.1, 0.2, time2));
	pose.eyesOpenFactor = (1.0 - isOpened) * openPosition + isOpened * blinkPosition;
	pose.eyebrowsHeight = (1.0 - isOpened) * (1.0 - openPosition) + isOpened * blinkPosition * 0.1;
	pose.cheeksFactor = (1.0 - isOpened) * (1.0 - openPosition);

	pose.mouthFactor = clamp(5.0 * (pose.headRotation1.x - 0.2), 0.0, 1.0);

	pose.offset = (1.0 - legsPoseFactor) * vec3(0.0, -0.4 * pow(abs(sin(time * PI2 - 0.1)), 1.4), 0.0);
	pose.offset += legsPoseFactor * vec3(0.0, -0.9 * abs(sin(time * PI2 - 0.2)), 0.2 * abs(sin(time * PI2 - 0.2)));

	time1 = time * 0.4;
	float runDistance = -(RUN_SPEED * time + 1.5 * (sin(time1) + sin(time1 / 2.0) + sin(time1 / 3.0) + sin(time1 / 5.0)));
	float h = getGroundHeight(vec3(0.0, 0.0, runDistance));
	float hx = getGroundHeight(vec3(-SURF_DIST, 0.0, runDistance));

	pose.offset -= vec3(0.0, h - runSpeed * 0.1, runDistance);
	pose.rotation = vec3(0.0, 0.0, -0.6 * atan(hx - h, SURF_DIST));

	return pose;
}  

vec2 sdEars(in vec3 pos, float angle)
{
	float bounds = sdSphere(pos - vec3(0.0, 1.0, 0.0), 0.7);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	pos.x = abs(pos.x);
	pos.xy = rotate(pos.xy, -0.32 + angle);
	pos -= vec3(0.0, 0.9, 0.0);

	vec2 d1, d2;
	float y = -0.05;
	d1 = vec2(sdEllipsoid(pos - vec3(0.14, y, 0.0), vec3(0.38, 0.5, 0.2)), MATERIAL_SKIN);

	d2 = vec2(sdEllipsoid(pos - vec3(-0.14, y, 0.0), vec3(0.38, 0.5, 0.2)), MATERIAL_SKIN);
	d1 = smax(d1, d2, 0.03);

	d2 = vec2(sdEllipsoid(pos - vec3(0.0, y, 0.5), vec3(0.6,0.7,0.6)), MATERIAL_SKIN);
	d1 = smax(d1, d2, 0.1);

	d2 = vec2(sdEllipsoid(pos - vec3(0.0, y, -0.1), vec3(0.12, 0.33, 0.12)), MATERIAL_EAR);
	d1 = smax(d1, vec2(-d2.x, d2.y), 0.05);

	return d1;
}

vec2 sdArm(in vec3 pos, vec3 armAngle, vec3 elbowAngle, vec3 wristAngle, float fingersAngle)
{
	float d1, d2, d3;
	vec3 pos1, pos2;

	pos1 = pos;
	pos1 = rotate_xzy(pos1, armAngle);

	float bounds = sdSphere(pos1 - vec3(0.9, 0.0, 0.0), 1.0);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	// upper arm
	pos1.x -= 0.38;
	d1 = sdEllipsoid(pos1 - vec3(0.52, 0.0, 0.0), vec3(0.54, 0.38, 0.38));

	// forearm
	pos1.x -= 0.77;
	pos1 = rotate_xzy(pos1, elbowAngle);
	d2 = sdEllipsoid(pos1 - vec3(0.26, 0.0, 0.0), vec3(0.32, 0.26, 0.26));
	d1 = smin(d1, d2, 0.1);

	// palm
	pos1.x -= 0.15;
	pos1 = rotate_xzy(pos1, wristAngle);
	d2 = sdEllipsoid(pos1 - vec3(0.3, 0.0, 0.0), vec3(0.2, 0.22, 0.15));
	d1 = smin(d1, d2, 0.1);

	// palm bend
	d2 = sdEllipsoid(pos1 - vec3(0.4, 0.0, -0.2), vec3(0.05, 0.25, 0.07));
	d1 = smax(d1, -d2, 0.1);

	// fingers
	pos2 = rotate_yzx(pos1, vec3(0.0, -0.4, 0.6));
	pos2 -= vec3(0.38, 0.0, 0.1);
	pos2 = rotate_yzx(pos2, fingersAngle * vec3(0.0, -1.5, -0.5));
	d2 = sdEllipsoid(pos2 - vec3(0.05, 0.0, 0.0), vec3(0.1, 0.06, 0.06)); // thumb

	float spreadAngle = 0.2;
	for (int i = 0; i < 4; i++)
	{
		pos2 = rotate_yzx(pos1, vec3(-0.3, -0.4, float(i) * spreadAngle - 0.5));
		pos2 -= vec3(0.4, 0.0, 0.15);
		pos2 = rotate_yzx(pos2, fingersAngle * vec3(0.0, -1.3, 0.0));
		d3 = sdEllipsoid(pos2 - vec3(0.05, 0.0, 0.0), vec3(0.1, 0.06, 0.06)); // finger
		d2 = min(d2, d3);
	}
	d1 = smin(d1, d2, 0.06);

	return vec2(d1, MATERIAL_SKIN);
}

vec2 sdLeg(in vec3 pos, vec3 legAngle, float kneeAngle, vec3 footAngle)
{
	float d1, d2, d3;
	vec3 pos1, pos2;
	float s = 1.15; // scale

	pos1 = pos;

	pos1.xz = rotate(pos1.xz, legAngle.y);
	pos1.yz = rotate(pos1.yz, legAngle.x);
	pos1.xy = rotate(pos1.xy, legAngle.z);

	float bounds = sdSphere(pos1 - vec3(0.0, -1.0, 0.0), 1.0);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	// thigh
	pos1.y += 0.3 * s;
	d1 = sdEllipsoid(pos1 + vec3(0.0, 0.3 * s, 0.0), vec3(0.4, 0.6, 0.4) * s);

	// shin
	pos1.y += 0.6 * s;
	pos1.yz = rotate(pos1.yz, kneeAngle);
	d2 = sdEllipsoid(pos1 + vec3(0.0, 0.2 * s, 0.0), vec3(0.3, 0.34, 0.3) * s);
	d1 = smin(d1, d2, 0.1);

	// foot
	pos1 += vec3(0.0, 0.38, -0.13) * s;
	pos1 = rotate_yzx(pos1, footAngle);
	d2 = sdEllipsoid(pos1 + vec3(0.0, 0.0, 0.3) * s, vec3(0.22, 0.15, 0.25) * s * 1.2);
	d1 = smin(d1, d2, 0.1);

	d2 = sdEllipsoid(pos1 + vec3(0.0, 0.3, 0.3) * s, vec3(0.4, 0.2, 0.5) * s);
	d1 = smax(d1, -d2, 0.1);

	return vec2(d1, MATERIAL_SKIN);
}

vec2 sdTail(in vec3 pos, vec3 angle)
{
	float bounds = sdSphere(pos - vec3(0.0, -0.4, 0.7), 1.0);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	vec3 pos1 = pos;

	pos1 = pos;
	pos1 = rotate_yzx(pos1, angle);
	pos1.y = -pos1.y;

	float d1 = sdArc(pos1, 1.6, 0.7, 0.1, 0.04);

	pos1 += vec3(-0.93, -1.13, 0.0);
	pos1.xy = rotate(pos1.xy, -1.2);
	float d2 = sdArc(pos1, 0.2, 0.4, 0.15, 0.5);
	d1 = smin(d1, d2, 0.1);

	return vec2(d1, MATERIAL_SKIN);
}

vec2 sdEyelids(in vec3 pos, float openFactor, vec3 direction)
{
	pos.x = abs(pos.x);
	pos.x -= 0.28;

	float bounds = sdSphere(pos, 0.3);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	float s = 1.1;

	float eyelidsBounds = sdSphere(pos, 0.18 * s);

	// eyelids
	vec3 pos1 = pos;
	float directionBias = direction.z * openFactor;
	pos1.xy = rotate(pos1.xy, -0.3 - 0.2 * directionBias);
	pos1.yz = rotate(pos1.yz, -0.4 - 0.3 * openFactor + 0.5 * directionBias); // eyelids meeting angle
	pos1.z = abs(pos1.z);
	pos1.y += 0.5;
	pos1.yz = rotate(pos1.yz, 0.1 + 0.4 * openFactor);
	pos1.y -= 0.5;
	float d = smax(sdEllipsoid(pos1, vec3(0.17 * s)), eyelidsBounds, 0.01);

	return vec2(d, MATERIAL_SKIN);
}

vec2 sdEyes(in vec3 pos, vec3 direction)
{
	direction.x *= sign(pos.x);
	pos.x = abs(pos.x);
	pos.x -= 0.28;

	float bounds = sdSphere(pos, 0.3);
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	float s = 1.1;

	// pupil distance
	float dist = length(pos - normalize(vec3(0.4, 1.0, -1.0) + direction));

	// eye
	float pupilSize = 0.87;
	float d = sdSphere(pos, 0.15 * s);
	float m = MATERIAL_EYE + step(dist, pupilSize) * (1.0 + 2.0 * (pupilSize - dist));

	return vec2(d, m);
}

vec2 sdEyebrows(in vec3 pos, float height)
{
	pos.x = abs(pos.x);

	pos.yz = rotate(pos.yz, 0.3);
	pos -= vec3(0.25, 0.46, -0.01);
	pos -= vec3(0.02, 0.2, 0.07) * height;
	pos.yz = rotate(pos.yz, 0.95);
	pos.xy = rotate(pos.xy, 0.2 - 0.1 * height);
	float d = sdEllipsoid(pos, vec3(0.25, 0.08, 0.25)) ;

	return vec2(d, MATERIAL_SKIN);
}

vec2 sdMouth(in vec3 pos)
{
	float d1, d2;
	float s = 0.2;
	d1 = sdEllipsoid(pos, vec3(1.0 * s));
	d2 = sdEllipsoid(pos + vec3(0.0, 0.0, -2.0) * s, vec3(2.0 * s));
	d1 = smax(d1, d2, 0.1);
	return vec2(d1, MATERIAL_MOUTH);
}

vec2 sdMoomin(in vec3 pos, inout Pose pose)
{
	vec2 d1, d2, d3;
	vec3 pos1, pos2;

	pos += pose.offset;
	pos = rotate_xzy(pos, pose.rotation);
	pos.y -= 1.2;
	pos.z += 1.5;

	float bounds = MAX_DIST;
	bounds = min(bounds, sdSphere(pos - vec3(0.0, 2.0, 1.0), 1.6));
	bounds = min(bounds, sdSphere(pos - vec3(0.0, 0.2, 1.6), 2.2));
	bounds = min(bounds, sdSphere(pos - vec3(0.0, -0.0, 4.2), 1.4));
	if (bounds > 0.2) return vec2(bounds, 0.0); // optimization

	// head
	pos1 = pos - vec3(0.0, 1.6, 1.0);
	pos1 = rotate_yzx(pos1, pose.headRotation1 + vec3(0.4, 0.0, 0.0)); // head rotation
	pos1.y -= 0.3;

	// jaw
	d1.x = sdEllipsoid(pos1 - vec3(0.0, -0.35, -0.7), vec3(0.65, 0.65, 0.7));
	d2.x = sdEllipsoid(pos1 - vec3(0.0, -1.1, -0.5), vec3(1.2));
	d1 = smax(d1, d2, 0.1);

	d2.x = sdEllipsoid(pos1 - vec3(0.0, -0.2, -0.5), vec3(0.6, 0.3, 0.4) * (1.0 + 0.1 * pose.cheeksFactor));
	d1 = smin(d1, d2, 0.25);

	// skull
	pos2 = pos1;
	pos2.yz = rotate(pos2.yz, pose.headRotation2); // head rotation
	d2.x = sdEllipsoid(pos2 - vec3y(0.04), vec3(0.7, 0.91, 0.7));
	d1 = smin(d1, d2, 0.2);

	pos2.yz = rotate(pos2.yz, 0.5);
	pos2 += vec3(0.0, 0.4, 0.1);
	d2.x = sdEllipsoid(pos2, vec3(0.6, 0.6, 0.4));

	d1 = smin(d1, d2, 0.2);
	d1.y = MATERIAL_SKIN;

#ifdef RENDER_EYES
	pos2 = pos1 - vec3(0.0, 0.05, -0.6);

	// eyes sockets
	d2.x = sdSphere(vec3(abs(pos2.x) - 0.28, pos2.yz), 0.10);
	d1.x = smax(d1.x, -d2.x, 0.2);

	// eyelids
	d2 = sdEyelids(pos2 + vec3(0.0, 0.02, 0.0), pose.eyesOpenFactor, pose.eyesDirection);
	d1 = smin(d1, d2, 0.09);

	// eyes
	d2 = sdEyes(pos2 + vec3(0.0, 0.02, 0.0), pose.eyesDirection);
	d1 = smin(d1, d2, 0.02);

	d2 = sdEyebrows(pos2 + vec3(0.0, 0.3, 0.0), pose.eyebrowsHeight);
	d1 = smin(d1, d2, 0.03);
#endif

#ifdef RENDER_EARS
	// ears
	d2 = sdEars(pos1, pose.earsAngle);
	d1 = smin(d1, d2, 0.05);
#endif

#ifdef RENDER_MOUTH
	// mouth
	d2 = sdMouth(pos1 + vec3(0.0, 1.1 - 0.1 * pose.mouthFactor, 0.3));
	d1 = smax(d1, vec2(-d2.x, d2.y), 0.04);
#endif

	float t = 0.9; // taper

	// body
	pos1 = pos - vec3(0.0, 1.7, 1.1);
	pos1 = rotate_yzx(pos1,  pose.bodyRotation); // body rotation

	float y;

	// neck
	y = 0.5;
	pos2 = rotate_yzx(pos1, pose.bodyCurve * y);
	pos2.y += y;
	d2 = vec2(sdSphere(pos2, 0.41 * (t + y)), MATERIAL_SKIN);
	d1 = smin(d1, d2, 0.2);

	d2 = vec2(sdEllipsoid(pos2 - vec3(0.0, -0.1, 0.1), vec3(0.6, 1.2, 0.7)), MATERIAL_SKIN);
	d1 = smin(d1, d2, 0.2);

	// shoulders
	y = 1.0;
	pos2 = rotate_yzx(pos1, pose.bodyCurve * y);
	pos2.y += y;
	d2 = vec2(sdSphere(pos2, 0.41 * (t + y)), MATERIAL_SKIN);
	d1 = smin(d1, d2, 0.2);

	// ribcage
	y = 1.6;
	pos2 = rotate_yzx(pos1, pose.bodyCurve * y);
	pos2.y += y;
	d2 = vec2(sdSphere(pos2, 0.41 * (t + y)), MATERIAL_SKIN);
	d1 = smin(d1, d2, 0.2);

	// belly
	y = 1.9;
	pos2 = rotate_yzx(pos1, pose.bodyCurve * y);
	pos2.y += y;

	float bellyStretch = 0.09 * abs(pose.leftLeg.legAngle.x - pose.rightLeg.legAngle.x);
	d2 = vec2(sdEllipsoid(pos2 - vec3y(bellyStretch), 0.41 * (t + y) * vec3(1.0, 1.0 - bellyStretch, 1.0)), MATERIAL_SKIN);
	d1 = smin(d1, d2, 0.1);

#ifdef RENDER_ARMS
	// arms
	y = 0.9;
	vec3 armsPos = rotate_yzx(pos1, pose.bodyCurve * y);
	armsPos.y += y;
	vec3 armOffset = vec3(0.0, 0.0, 0.2);

	// right arm
	pos2 = armsPos - armOffset;
	d2 = sdArm(pos2, pose.rightArm.armAngle, pose.rightArm.elbowAngle, pose.rightArm.wristAngle, pose.rightArm.fingersAngle);

	// left arm
	pos2 = vec3(-armsPos.x, armsPos.yz) - armOffset; // mirror
	d3 = sdArm(pos2, pose.leftArm.armAngle, pose.leftArm.elbowAngle, pose.leftArm.wristAngle, pose.leftArm.fingersAngle);
	d2 = min2(d2, d3);

	d1 = smin(d1, d2, 0.08);
#endif

#ifdef RENDER_LEGS
	// legs
	y = 1.8;
	vec3 legsPos = rotate_yzx(pos1, pose.bodyCurve * y);
	legsPos.y += y;
	vec3 legOffset = vec3(0.4, 0.0, 0.0);

	// right leg
	pos2 = legsPos - legOffset;
	d2 = sdLeg(pos2, pose.rightLeg.legAngle, pose.rightLeg.kneeAngle, pose.rightLeg.footAngle);

	// left leg
	pos2 = vec3(-legsPos.x, legsPos.yz) - legOffset; // mirror
	d3 = sdLeg(pos2, pose.leftLeg.legAngle, pose.leftLeg.kneeAngle, pose.leftLeg.footAngle);
	d2 = min2(d2, d3);
#endif

#ifdef RENDER_TAIL
	// tail 
	y = 2.6;
	vec3 tailPos = rotate_yzx(pos1, pose.bodyCurve * y);
	tailPos.y += y - 0.5 * bellyStretch;
	tailPos.z -= 0.6 - 0.5 * bellyStretch;

	d3 = sdTail(tailPos, pose.tailRotation + vec3(0.0, 1.57, 0.2));

	d2 = min2(d2, d3);
#endif

	d1 = smin(d1, d2, 0.1);

	return d1;
}

vec2 sdGrass(in vec3 pos)
{
	float height = getGroundHeight(pos);
	vec2 d1 = vec2(sdSurface(pos - vec3(0.0, height, 0.0)), MATERIAL_GRASS);
	return d1;
}

float getSkyTextureCloud(in vec2 uv)
{
	return fbm(vec3(uv + 10.0, 0.0)) * 1.8;
}

vec4 getCloudPosIndex(in vec2 uv, float time, float offset)
{
	time *= CLOUDS_SPEED + 0.2 * offset;
	time += 1000.0 * offset;

	float grid = 32.0;

	float index = floor((uv.y + time) / grid);

	float origin = index * grid;
	float p = 0.05 * (origin - 1.2 * time);
	vec2 pos = uv;

	pos.y -= origin - time;
	pos.y -= 0.5 * grid;
	pos.x *= 1.2; // stretch
	pos.x += 8.0 * sin(p) + 5.0 * sin(2.0 * p + 2.0) + sin(3.0 * p + 2.0);

	return vec4(pos.x, 0.0, pos.y, index);
}

vec3 getSkyTexture(in vec3 rd)
{
	vec3 color = vec3(0.05, 0.4, 0.95);

	vec2 uv = rd.xz / rd.y; // uv map
	float cloud = getSkyTextureCloud(uv);
	vec3 cloudColor = mix(vec3(1.0, 0.75, 1.0), vec3(0.6, 0.3, 0.6), (cloud - 0.5) * 0.3);

	color = mix(color, cloudColor, clamp((cloud - 1.0) * 10.0, 0.0, 1.0));

	// fog
	color = mix(color, vec3(0.5, 0.85, 0.95), clamp(1.0 - 2.0 * rd.y, 0.0, 1.0));

	return color;
}

vec2 sdFlowers(in vec3 pos)
{
	float grid = 2.0;
	vec2 cellIndex = floor(pos.xz / grid);
	float h1 = hash(cellIndex);
	if (h1 < 0.9)
	{
		return vec2(MAX_DIST, 0.0);
	}

	float h2 = hash(cellIndex + 0.1);

	vec3 cellOrigin = vec3(grid * (cellIndex.x + 0.5), 0.0, grid * (cellIndex.y + 0.5));
	cellOrigin.xz += 0.2 * grid * (h2 * 2.0 - 1.0);
	cellOrigin.y = getGroundHeight(cellOrigin);

	vec3 pos1 = pos - cellOrigin;
	float bounds = length(pos1) - 0.3;
	if (bounds > 0.3) return vec2(bounds, 0.0); // optimization

	float h3 = hash(cellIndex + 0.2);
	float h4 = hash(cellIndex + 0.3);

	float hx = getGroundHeight(cellOrigin - vec3(SURF_DIST, 0.0, 0.0));
	float hz = getGroundHeight(cellOrigin - vec3(0.0, 0.0, SURF_DIST));
	pos1.xy = rotate(pos1.xy, -atan(hx - cellOrigin.y, SURF_DIST));
	pos1.yz = rotate(pos1.yz, atan(hz - cellOrigin.y, SURF_DIST));
	pos1.xz = rotate(pos1.xz, PI * h4);

	float stemLength = 0.1 + 0.3 * h3;
	float s = 0.3 + 0.5 * h4;

	vec2 d1 = vec2(length(pos1.xz) - 0.05 * s, MATERIAL_STEM);
	vec2 d2 = vec2(pos1.y - stemLength * s, MATERIAL_STEM);

	pos1.y -= stemLength * s;
	d1 = max2(d1, d2);
	d2 = vec2(sdSphere(pos1, 0.1 * s), MATERIAL_FLOWER2);
	d1 = min2(d1, d2);

	vec3 pos2 = pos1;
	pos2.y += 0.07 * s;

	float a = atan(pos2.x, pos2.z);
	float l = length(pos2.xz);

	a = mod(a, PI2 / 5.0) - PI2 / 10.0;
	pos2.x = l * cos(a);
	pos2.z = l * sin(a);
	pos2.xy = rotate(pos2.xy, 0.4);

	d2 = vec2(sdEllipsoid(pos2 - vec3(0.07 + s * 0.1, 0.0, 0.0), s * 0.2 * vec3(1.5 , 0.3, 1.0)), MATERIAL_FLOWER1);
	d1 = min2(d1, d2);

	return d1;
}

vec2 sdMountains(in vec3 pos)
{
	float bounds = 80.0 - length(pos);
	if (bounds > 20.0) return vec2(bounds, 0.0); // optimization

	vec3 pos1 = pos;

	float a = atan(pos1.x, pos1.z);
	float l = length(pos1.xz);

	float c = 8.0;
	float index = floor(c * a / PI2);

	float h1 = hash(index + 0.1) * 2.0 - 1.0;
	float h2 = hash(index + 0.2) * 2.0 - 1.0;
	float h3 = hash(index + 0.3);
	float h4 = hash(index + 0.4);

	a = mod(a, PI2 / c) - PI / c;
	a += h2 * 0.1;

	pos1.x = l * cos(a);
	pos1.z = l * sin(a);

	pos1.x -= 102.0;
	pos1.y += 5.0;
	pos1.yz = rotate(pos1.yz, 0.5 * h2);

	float d1 = sdEllipsoid(pos1, 12.0 * (vec3(1.0) + vec3(1.0, h3 * 0.5, h4 * 2.0)));

	if (d1 < 5.0)
	{
		pos1 = pos * 0.23;
		d1 += 2.0 * (sin(pos1.x) + sin(pos1.z));
		d1 *= 0.8;
	}

	return vec2(d1, MATERIAL_MOUNTAIN);
}

vec2 sdTrees(in vec3 pos)
{
	float bounds = 55.0 - abs(pos.x);
	if (bounds > 1.0) return vec2(bounds, 0.0); // optimization

	vec2 grid = vec2(130.0, 20.0);
	vec2 cellIndex = floor(pos.xz / grid);
	if (abs(cellIndex.x + 0.5) > 1.0)
	{
		return vec2(MAX_DIST, 0.0);
	}

	float h1 = hash(cellIndex);
	if (h1 > 0.8)
	{
		return vec2(MAX_DIST, 0.0);
	}

	float h2 = hash(cellIndex + 0.1) * 2.0 - 1.0;
	float h3 = hash(cellIndex + 0.2) * 2.0 - 1.0;

	vec3 cellOrigin = vec3(grid.x * (cellIndex.x + 0.5), 0.0, grid.y * (cellIndex.y + 0.5));
	cellOrigin.x += 4.0 * h2;
	cellOrigin.z += 5.0 * h3;

	bounds = length(pos.xz - cellOrigin.xz) - 4.0;
	if (bounds > 1.0) return vec2(bounds, 0.0); // optimization

	cellOrigin.y = getGroundHeight(cellOrigin);

	vec3 pos1 = pos - cellOrigin;

	float h4 = hash(cellIndex + 0.3) * 2.0 - 1.0;
	float h5 = hash(cellIndex + 0.4) * 2.0 - 1.0;
	pos1.yz = rotate(pos1.yz, 0.2 * h4);
	pos1.xy = rotate(pos1.xy, 0.2 * h5);

	float s = 0.8 + 0.2 * h4;
	vec3 pos2 = pos1;

	vec2 d1 = vec2(sdCone(pos2 - vec3y(11.0 * s), vec2(0.866, 0.5), 5.0 * s), MATERIAL_TREE1);
	vec2 d2 = vec2(sdCone(pos2 - vec3y(8.5 * s), vec2(0.866, 0.5), 6.5 * s), MATERIAL_TREE1);
	d1 = min2(d1, d2);

	d2 = vec2(length(pos1.xz) - 0.5 * s, MATERIAL_TREE2);
	float d3 = pos1.y - 2.0 * s;
	d2.x = max(d2.x, d3);

	d1 = min2(d1, d2);

	return d1;
}

float mapClouds(in vec3 pos, float time)
{
	float d1, d2;

	vec4 pi = getCloudPosIndex(pos.xz + vec2(0.0, 0.0), time, 0.0);
	vec3 pos1 = vec3(pi.x, pos.y - (CLOUDS_HEIGHT - 3.0), pi.z);
	d1 = sdEllipsoid(pos1, vec3(4.0, 2.0, 4.0));

	pi = getCloudPosIndex(pos.xz - vec2(0.0, 0.0), time, 1.0);
	pos1 = vec3(pi.x, pos.y - (CLOUDS_HEIGHT + 3.0), pi.z);
	d2 = sdEllipsoid(pos1, vec3(4.0, 2.0, 4.0));
	d1 = min(d1, d2);

	return d1;
}

vec2 map(in vec3 pos, float time, inout Pose pose, bool includeClouds)
{
	vec2 d1, d2;

	d1 = vec2(MAX_DIST, 0.0);

#ifdef RENDER_MOOMIN
	d1 = sdMoomin(pos, pose);
#endif

#ifdef RENDER_FLOWERS
	d2 = sdFlowers(pos);
	d1 = min2(d1, d2);
#endif

#ifdef RENDER_MOUNTAINS
	d2 = sdMountains(pos + vec3z(pose.offset.z));
	d1 = min2(d1, d2);
#endif

#ifdef RENDER_TREES
	d2 = sdTrees(pos);
	d1 = min2(d1, d2);
#endif

	d2 = sdGrass(pos); 
	d1 = min2(d1, d2);

#ifdef RENDER_CLOUDS
	if (includeClouds)
	{
		d2 = vec2(mapClouds(pos, time), MATERIAL_CLOUD);
		d1 = min2(d1, d2);
	}
#endif

	return d1;
}

// inspired by iq, tdhooper, and klems - a way to prevent the compiler from inlining map() 4 times
vec3 getNormal(in vec3 pos, float time, inout Pose pose)
{
	vec3 n = vec3(0.0);

	for (int i = 0; i < 4; i++)
	{
		vec3 e = 0.5773 * (2.0 * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.0);
		n += e * map(pos + 0.0005 * e, time, pose, false).x;
	}

	return normalize(n);
}

vec2 castRay(in vec3 ro, vec3 rd, float time, inout Pose pose, bool includeClouds) // ray origin, ray direction
{
	float d = 0.0; // distance from ray origin
	float m = 0.0; // material

	for (int i = 0; i < MAX_STEPS; i++)
	{
		vec3 pos = ro + rd * d; // current position
		vec2 dm = map(pos, time, pose, includeClouds); // distance from current position to the scene
		m = dm.y;

		if (abs(dm.x) < SURF_DIST)
		{
			break;
		}

		d += dm.x;

		if (d > MAX_DIST)
		{
			break;
		}
	}

	return vec2(d, m);
}

float castCloudsRay(in vec3 ro, vec3 rd, float time) // ray origin, ray direction
{
	float d = 0.0; // distance from ray origin

	for (int i = 0; i < MAX_STEPS; i++)
	{
		vec3 pos = ro + rd * d; // current position
		float d1 = mapClouds(pos, time); // distance from current position to the scene

		if (abs(d1) < SURF_DIST)
		{
			break;
		}

		d += d1;

		if (d > MAX_DIST)
		{
			break;
		}
	}

	return d;
}

float calcOcclusion(in vec3 pos, float time, vec3 nor, inout Pose pose)
{
	float occlusion = 0.0; // occlusion
	float scale = 1.0; // scale

	for (int i = 0; i < 5; i++)
	{
		float h = 0.01 + 0.11 * float(i) / 4.0;
		vec3 occlusionPos = pos + h * nor; // occlusion position
		float d = map(occlusionPos, time, pose, false).x;
		occlusion += (h - d) * scale;
		scale *= 0.95;
	}

	return clamp(1.0 - 2.0 * occlusion, 0.0, 1.0);
}

float castShadow(in vec3 ro, vec3 rd, float time, inout Pose pose)
{
	float result = 1.0;
	float t = 0.01; // position

	float maxT = MAX_DIST;

	for (int i = 0; i < MAX_STEPS; i++)
	{
		vec3 pos = ro + t * rd;
		vec2 d = map(pos, time, pose, true);
		if (d.y > 0.0) // skip optimizations
		{
			result = min(result, max(0.0, 16.0 * d.x / t)); // soft shadow
		}

		t += d.x;

		if (abs(d.x) < (t * 0.001) || t > maxT)
			break;
	}

	return result;
}

vec3 getMaterialColor(in vec2 dm, vec3 pos, vec3 nor, vec3 rd, float time, inout Pose pose)
{
	vec3 color = vec3(0.0);

	vec3 posOffset = pos + nor * SURF_DIST; // point with a small offset
	vec3 material;

	if (dm.y < 0.5)
	{
		material = vec3(1.0, 1.0, 0.0); // missing
	}
	else if (dm.y < MATERIAL_SKIN + 0.5)
	{
		material = vec3(0.3, 0.3, 0.35);
	}
	else if (dm.y < MATERIAL_EAR + 0.5)
	{
		material = vec3(0.5, 0.3, 0.3);
	}
	else if (dm.y < MATERIAL_EYE + 0.5)
	{
		material = vec3(0.4);
	}
	else if (dm.y < MATERIAL_PUPIL + 0.5)
	{
		material = vec3(0.02, 0.15, 0.6) * (0.2 + 0.8 * step(dm.y, MATERIAL_PUPIL + 0.06));
	}
	else if (dm.y < MATERIAL_MOUTH + 0.5)
	{
		material = vec3(0.3, 0.15, 0.15);
	}
	else if (dm.y < MATERIAL_GRASS + 0.5)
	{
		material = vec3(0.25, 0.25, 0.0);
	}
	else if (dm.y < MATERIAL_FLOWER1 + 0.5)
	{
		material = vec3(0.5);
	}
	else if (dm.y < MATERIAL_FLOWER2 + 0.5)
	{
		material = vec3(0.5, 0.4, 0.0);
	}
	else if (dm.y < MATERIAL_STEM + 0.5)
	{
		material = vec3(0.2, 0.4, 0.0);
	}
	else if (dm.y < MATERIAL_MOUNTAIN + 0.5)
	{
		material = vec3(0.2, 0.3, 0.5);
	}
	else if (dm.y < MATERIAL_TREE1 + 0.5)
	{
		material = vec3(0.04, 0.15, 0.1);
	}
	else if (dm.y < MATERIAL_TREE2 + 0.5)
	{
		material = vec3(0.3, 0.05, 0.0);
	}
	else if (dm.y < MATERIAL_CLOUD + 0.5)
	{
		material = vec3(0.6, 0.4, 0.5);
	}

	float occlusion = calcOcclusion(pos, time, nor, pose);
	float fresnel = clamp(1.0 + dot(rd, nor), 0.0, 1.0); // fresnel

	// sun
	float sunDiffuse = clamp(dot(nor, SUN_DIRECTION), 0.0, 1.0);
	float sunShadow = castShadow(posOffset, SUN_DIRECTION, time, pose);

	float bounceDiff = clamp(0.5 + 0.5 * dot(nor, vec3(0.0, -1.0, 0.0)), 0.0, 1.0); // bounce diffuse, from the ground

	vec3 sunLight = sunDiffuse * 4.0 * vec3(0.7, 0.4, 0.3); // sun color
	sunLight *= vec3(sunShadow, pow(sunShadow, 2.0), pow(sunShadow, 4.0)); // sun shadow transition color
	sunLight += vec3(0.25, 0.25, 0.0) * bounceDiff; // bounce light
	sunLight += fresnel * 3.0 * vec3(0.6, 0.8, 1.2) * (0.5 + 0.5 * sunDiffuse) * (0.9 + 0.1 * clamp(sunShadow, 0.0, 1.0)); // sun fresnel

	// sky light
	vec3 skyDirection = normalize(vec3(0.0, 1.0, 0.0));
	float skyDiffuse = clamp(0.5 + 0.5 * dot(nor, skyDirection), 0.0, 1.0); // sky diffuse
	float skyReflection = smoothstep(0.3, 0.5, reflect(rd, nor).y); // sky reflection
	vec3 skyColor = vec3(0.1, 0.8, 1.0);
	vec3 skyLight = 0.3 * skyDiffuse * skyColor + 0.02 * skyReflection * skyColor;

	float fog = pow(smoothstep(5.0, 80.0, dm.x), 2.0);
	fog *= clamp(1.0 - rd.y * 6.0, 0.0, 1.0);

	color = material;
	color *= sunLight;
	color += skyLight;
	color *= occlusion;
	color = mix(color, vec3(0.5, 0.85, 0.95), fog);

	return color;
}

float getCloudDensity(vec3 pos, float time)
{
	vec4 pi = getCloudPosIndex(pos.xz, time, step(CLOUDS_HEIGHT, pos.y));
	vec3 pos1 = vec3(pi.x, pos.y, pi.z * 0.7);
	float index = pi.w;

	pos1.z += pos.z * 0.5;
	float density = fbm(0.55 * (pos1 + vec3(index * 10.0))) - 0.1;
	density *= 0.2;
	density *= smoothstep(0.0, -1.5, mapClouds(pos, time)); // shape mask

	return clamp(density, 0.0, 1.0);
}

vec3 composeClouds(vec3 ro, vec3 rd, float time, float d, vec3 color)
{
	float cd = castCloudsRay(ro, rd, time); // cloud distance

	float f = 1.0; // composition factor
	f -= 0.008 * max(cd - 40.0, 0.0); // clouds distance fog

	for (int i = 0; i < 2; i++) // number of overlapping clouds
	{
		if (cd >= d || cd >= MAX_DIST)
		{
			break;
		}

		float stepSize = 0.1;
		for (int j = 0; j < 100; j++) // steps
		{
			if (cd >= d || f < 0.0)
			{
				break;
			}

			vec3 pos = ro + cd * rd;

			float density = getCloudDensity(pos, time);
			float density2 = 0.5 * (
				getCloudDensity(pos + 0.1 * SUN_DIRECTION, time) +
				getCloudDensity(pos + 0.2 * SUN_DIRECTION, time));

			vec3 stepColor = vec3(1.0, 0.5, 0.6);
			stepColor = mix(vec3(1.2), stepColor, clamp(density2 * 15.0, 0.0, 1.0)); // light
			stepColor = mix(vec3(0.2, 0.0, 0.1), stepColor, clamp(1.0 - density2 * 6.0, 0.0, 1.0)); // shadow

			color = mix(color , stepColor, density * f * 3.0);
			f -= density;

			cd += stepSize;
		}

		cd += castCloudsRay(ro + cd * rd, rd, time);
	}

	return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float time = iTime;
	time += 0.2;
	time *= ANIMATION_SPEED;

	vec2 uv = -1.0 + 2.0 * fragCoord/iResolution.xy;
	uv.x *= iResolution.x / iResolution.y;

	vec3 target = vec3(0.0, 1.0 + CAMERA_HEIGHT, -RUN_SPEED * time); // camera target
	float dist = 8.0 + CAMERA_DISTANCE; // camera distance

	float fov = 1.5; // field of view

	vec3 ro = vec3(0.0, 1.5 + CAMERA_HEIGHT, dist); // camera origin

	if (iMouse.z < 0.5)
	{
		vec2 an = vec2(-3.0 * sin(3.5 + time * 0.1), -0.7 + 0.5 * sin(1.0 + time * 0.2));
		dist = 10.0 + CAMERA_DISTANCE + 2.0 * sin(time * 0.3);
		fov = 1.2;

		vec3 dir = normalize(vec3(0.1, 0.3, 0.4)); // camera direction
		dir.yz = rotate(dir.yz, an.y);
		dir.xz = rotate(dir.xz, an.x);
		ro = target + dir * dist;
	}
	else
	{
		// mouse camera angle
		vec2 an = vec2(3.0 + 12.0 * iMouse.x / iResolution.x, -3.0 * clamp(iMouse.y / iResolution.y, 0.0, 1.0));

		dist = 5.5 + CAMERA_DISTANCE + 2.0 * sin(time * 0.3);
		fov = 1.2;

		vec3 dir = normalize(vec3(0.1, 0.3, 0.4)); // camera direction
		dir.yz = rotate(dir.yz, an.y);
		dir.xz = rotate(dir.xz, an.x);
		ro = target + dir * dist;
	}

	ro.y = max(getGroundHeight(vec3(ro.x, 0.0, ro.z)) + 0.1, ro.y);

	// camera direction
	vec3 rdz = normalize(target - ro);
	vec3 rdx = normalize(cross(rdz, vec3(0.0, 1.0, 0.0)));
	vec3 rdy = cross(rdx, rdz);
	vec3 rd = normalize(uv.x * rdx + uv.y * rdy + fov * rdz);

	Pose pose = getPose(time);

	vec2 dm = castRay(ro, rd, time, pose, false);

	vec3 color = vec3(0.0);

	if (dm.x < MAX_DIST)
	{
		vec3 pos = ro + dm.x * rd; // point in scene
		vec3 nor = getNormal(pos, time, pose); // point normal
		color = getMaterialColor(dm, pos, nor, rd, time, pose);
	}
	else
	{
		// sky texture
		color = getSkyTexture(rd);
	}

#ifdef RENDER_CLOUDS
	color = composeClouds(ro, rd, time, dm.x, color);
#endif

	// gamma correction
	color = pow(color, vec3(0.5));

	fragColor = vec4(color, 1.0);
}

