#define MAX_STEPS 50
#define MAX_DIST 5.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float GetDist(vec3 p) {
    float a = atan(p.z, p.x);
   // float b = .5 + .5 * cos(0.3 * iTime);
    p = vec3(p.x,p.y +0.1 *  length(p.xz) * cos(6. * a + 4. * iTime),p.z);
    float bd = sdBox(p, vec3(0.8)) - .2;
    float sd = length(p) * length(p) - 1.2;// + 0.4 * cos(0.7 * iTime);
    //return  0.2 * (0.8 + sin(16.* (0.2 * min(bd,sd) + (1. - abs(min(sd/(bd),bd/(sd)))))));
    return  -0. * min(bd,sd) + 0.05 * (2. + sin(0.5 * iTime + 1. * (0.6 - abs(min(sd/(bd),bd/(sd))))));


   //return bd;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 Bg(vec3 rd) {
    float k = rd.y*.5 + .5;
    //  vec3 col = mix(vec3(.5,0.,0.),vec3(0.),16. * k * (1.-k) * k * (1.-k));
   // vec3 col = vec3(10.,0.,0.);
    vec3 col = mix(vec3(.5,0.,0.),vec3(0.),16. * k * (1.-k) * k * (1.-k));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    float t = 0.35 * iTime;
    vec3 ro = 2.5 * vec3(cos(t), cos(t + (2./3.) * 3.1415), cos(t + (4./3.) * 3.1415));
   // vec3 ro = vec3(3.5 * cos(0.2 * iTime),2. * sin(0.5 * iTime), 3.5 * sin(0.2 * iTime));
    // ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0);
    col += Bg(rd);

    float d = RayMarch(ro, rd);
    float depth = 2.; //1.5 + cos(iTime);

    // comment / uncomment me
    //d = RayMarch(ro + rd * (1. + depth) * d, -0.5 * depth * rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(1.5*rd, n);

        float spec = pow(max(0., -r.y),32.);
        spec = .5 + .5 * cos(0.00001*spec); // <-- absolute fudge but works alright

       float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
       float a = atan(p.x,p.z);
       // dif = 16. * dif * dif * (1.-dif) * (1.-dif);

       col = 0.4 * vec3(dif) + 1.5 * cos(2. * a) * Bg(r);        
       col *= (1. + spec);

       // comment / uncomment me
       //col = vec3(dif);
    }
    col = pow(col, vec3(.4545));	// gamma correction
    //col *= 100000.;
    fragColor = vec4(col,1.0);
}

const float scale = 3.0f;
const float intensivity = 0.05f;
const int iteration = 3;
const float speed = 85.0; // range 1 to 100

const mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );

vec2 hash( vec2 p ) {
	p = vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p ) {
    const float K1 = 0.366025404; 
    const float K2 = 0.211324865; ;
	vec2 i = floor(p + (p.x+p.y)*K1);	
    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0); 
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;
    vec3 h = max(0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot(n, vec3(70.0));	
}

float fbm(vec2 n) {
	float total = 0.0, amplitude = 0.1;
	for (int i = 0; i < 7; i++) {
		total += noise(n) * amplitude;
		n = m * n;
		amplitude *= 0.4;
	}
	return total;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);    
    float time = iTime*0.03f;
    float q = fbm(uv  * 0.5);

	uv *= scale;

    float f = 0.0;
    float weight = 0.7;
    for (int i=0; i<iteration; i++){
		f += weight*noise( uv );
        uv = m*uv + time;
		weight *= 0.6;
    }

    f = fract(f+iTime/(101.-speed));

    float coeff = intensivity/abs(.5 - f);
    vec3 result = vec3(.9,.2,.1)*coeff;

    fragColor = vec4(result,1.0);
}

