float sabs(float x, float a, float b){
    // Asymmetric smooth-abs

    // For -b < x < a the graph is a tilted quarter of an ellipse
    // Coincides with abs() for x < -b or x > a

    float d = x/2.;
    float det = a*b + 2.*(a-b)*d - 2.*d*d;

    float mu = (x > -b && x<a) ?
     2.*(a*a*(b+d) + b*b*(a-d) - sqrt(2.)*a*b*sqrt(det))/(a*a+b*b)
     : abs(x);

    return mu;
}

float smin(float d1, float d2, float r1, float r2)
{
    // Asymmetric smooth-min
    // The zero-set of perpendicular planes is an elliptic surface with radii (r1, r2)

    return 0.5*(d1+d2 - sabs(d1-d2, r1, r2));
}

float smax(float d1, float d2, float r1, float r2)
{
    // Asymmetric smooth-max
    // The zero-set of perpendicular planes is an elliptic surface with radii (r1, r2)

    return 0.5*(d1+d2 + sabs(d1-d2, r1, r2));
}

