#define MAX_STEPS 100
#define MAX_DIST 2.
#define SURF_DIST .0001

#define ITERATIONS 8
#define BAILOUT 50.1
#define POWER 8.

mat2 Rotate(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, -s, s, c);
}

// http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/
vec2 DE(vec3 pos) {
	vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
    int i = 0;
	for (i = 0; i < ITERATIONS ; i++) {
		r = length(z);
		if (r>BAILOUT) break;

		// convert to polar coordinates
		float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
		dr =  pow( r, POWER-1.0)*POWER*dr + 1.0;

		// scale and rotate the point
		float zr = pow( r,POWER);
		theta = theta*POWER;
		phi = phi*POWER;

		// convert back to cartesian coordinates
		z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z+=pos;
	}
	return vec2(0.5*log(r)*r/dr,i);
}

vec2 GetDistance(vec3 point) {
    vec3 p = point;
    p.yz *= Rotate(iTime/10.);
    p.xy *= Rotate(iTime/10.);
    return DE(p);
}

vec2 RayMarch(vec3 rayOrgin, vec3 rayDirection) {
	float distance=0.;
    vec2 d = vec2(0.);

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 point = rayOrgin + rayDirection * distance;
        d = GetDistance(point);
        float surfaceDistance = d.x;
        distance += surfaceDistance;
        // Stop marching if we go too far or we are close enough of surface
        if(distance>MAX_DIST || surfaceDistance<SURF_DIST) break;
    }

    return vec2(distance, d.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // put 0,0 in the center
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // camera   
    float diff = iTime/10.;
    vec3 rayOrgin = vec3(0.63-(sin(diff)*.3), 0.2-(sin(diff)*.05), -1.-(sin(diff)*.5));
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    vec2 data = RayMarch(rayOrgin, rayDirection);
    float d = clamp(data.x,0.,1.) + .2;
    float r = (data.y)/2. - d;
    float g = data.y/5. -d;
    float b = data.y/15. -d;

    vec3 col = vec3(r,g,b);

    fragColor = vec4(col,1.0);
}

#define quat vec4

const quat qidentity = quat(0,0,0,1);

// quaternion from 2 unit vectors
quat qfromto(vec3 vsrc, vec3 vdst) // construct rotation that maps vsrc to vdst
{
    vec3 vhalf = vsrc + vdst;
    vhalf *= length(vdst) / length(vhalf);
    return quat(cross(vsrc, vhalf), dot(vsrc, vhalf));
}

quat qaxisangle(vec3 axis, float rad)	// axis should be unit length!!
{
    rad *= .5;
    return quat(sin(rad) * axis, cos(rad));
}

// conjugate
quat qrev(quat q)
{
    q.w = -q.w;
    return q;
}

// quaternion composition
quat qmul(quat a, quat b)
{
	vec3 va = a.xyz, vb = b.xyz;
	return quat(b * a.w) - quat(cross(vb, va) - va * b.w, dot(va, vb));
}

quat qrenormalize(quat q)
{
    float l2 = dot(q, q);
	return l2 == 1. ? q : .5 * (3. - l2) * q;
}

vec3 qrot(quat q, vec3 r)
{
	vec3 axis = q.xyz;
	float w = q.w;
	return 2.*((cross(axis,r) + r * w) * w + axis * dot(axis,r)) - r;
}

#if 0
// unused here, enjoy
quat qlog(quat q)
{
	vec3 v = q.xyz;
	float l = length(v), s = 2. * atan(l / q.w) / l;
	return quat(v * s, l);
}

quat qexp(quat v)
{
	vec3 v3 = v.xyz;
	float a = dot(v3, v3);
	if (a < 1e-7) return qidentity;
	a = .5 * sqrt(a);
	return quat(v3 * (sin(a) * .5 / a), cos(a));
}

// untested, just tossed together to demo qlog,qexp instead of porting original lol - probably backward
