// not super efficient,
// just showing how to use this
// mini quaternion library.
vec3 QrbitCamera(vec2 a, vec3 d)
{
    a.y = clamp(a.y, -.5, .5);
    const float pi = acos(-1.);  //3.141592
    a *= pi;
    quat q = qidentity;
    q = qmul(q, qaxisangle(vec3(0,1,0), 2.*a.x));
    q = qmul(q, qaxisangle(vec3(-1,0,0), a.y));
    q = qrenormalize(q);
    return qrot(q, d);
}

float dbox(vec3 p, vec3 e)
{
    p = abs(p); p -= e; 
    return all(lessThan(p, vec3(0)))
     ? max(max(p.x, p.y), p.z)
     : length(max(p, vec3(0)));
}

vec2 scene(vec3 e)
{
    e.xz = abs(e.xz); // duplicate scene into quadrants
    //if (e.x > e.z) e.xz = e.zx;
    e.zx -= 6.;
    float dp = e.y + .1*(cos(9.*e.x) + cos(9.*e.z));
    vec2 r = vec2(dp, 1);
    vec3 p0 = vec3(0), p1;
    float a = .2 * sin(iTime * 1.7);
    quat q = qidentity, R = quat(normalize(vec3(.5,1,-.2)) * sin(a), cos(a));
    for (int i = 8; i-- > 0; ) {
        p1 = p0;
        p0 += qrot(q, vec3(0,1,0));
        float d = dbox(qrot(qrev(q), e - .5*(p0 + p1)), vec3(.1,.4,.1)) - .1;
        if (d < r.x) r = vec2(d, i+2);
        q = qmul(q, R);
        q = qrenormalize(q);
    }
    return r;
}

void mainImage(out vec4 o, vec2 p)
{
    o = iMouse; // bonus: initializes o
    vec3 e = iResolution,
      d = normalize(vec3((p - .5 * e.xy) / e.y, 1));
	o.xy /= e.xy; // last use of resolution, now e -> eye pos
    if (abs(o.z) > 1.) // quality detail in lists and prior to click
        o -= .5;
    else
        o.x += .1*sin(.2*iTime);    
    d = QrbitCamera(o.xy, d); // quaternion look
    d = normalize(d);
    e -= e; e.y += 4.; // ray init    
    float r = 24., t = 0.; // sphere caster
    vec2 h;
    for (int i = 40; 
      h = scene(e),
      h.x *= .8,
      t += h.x, e += h.x * d, // march
      i-- > 0 && abs(h.x) >= .001 * t && t < r && e.y < 9.; );
    o -= o - exp(-.06 * min(r, t)); // black fog
    float g = (scene(e + vec3(.1)).x - scene(e).x) / length(vec3(.1)); // directed gradient
    o.rgb *= clamp(g * .25 + .5, 0., 1.); // simple lighting
    o.rgb *= cos(vec3(0,2,4)-1.+11.7*h.y) * .5 + .5; // albedo
    if (e.y >= 9. || t > r) o *= 0.;
    o = sqrt(o); // gamma
}

//The raycasting code is somewhat based around a 2D raycasting toutorial found here: 
//http://lodev.org/cgtutor/raycasting.html

const bool USE_BRANCHLESS_DDA = true;
const int MAX_RAY_STEPS = 64;

float sdSphere(vec3 p, float d) { return length(p) - d; } 

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

bool getVoxel(ivec3 c, float iTime) {
    vec3 p = vec3(c) + vec3(0.5);
    float d = min(max(-sdSphere(p, 7.5), sdBox(p, vec3(6.0))), -sdSphere(p, 42.0 + 16.0 * sin(iTime * 0.1)));
    return d < 0.0;
}

vec2 rotate2d(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA);	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	vec3 cameraDir = vec3(0.0, 0.0, 0.8);
	vec3 cameraPlaneU = vec3(1.0, 0.0, 0.0);
	vec3 cameraPlaneV = vec3(0.0, 1.0, 0.0) * iResolution.y / iResolution.x;
	vec3 rayDir = cameraDir + screenPos.x * cameraPlaneU + screenPos.y * cameraPlaneV;
	vec3 rayPos = vec3(0.0, 2.0 * sin(iTime * 0.5), -24.0);

	rayPos.xz = rotate2d(rayPos.xz, 0.1 * iTime);
	rayDir.xz = rotate2d(rayDir.xz, 0.1 * iTime);

	ivec3 mapPos = ivec3(floor(rayPos + 0.));

	vec3 deltaDist = abs(vec3(length(rayDir)) / rayDir);

	ivec3 rayStep = ivec3(sign(rayDir));

	vec3 sideDist = (sign(rayDir) * (vec3(mapPos) - rayPos) + (sign(rayDir) * 0.5) + 0.5) * deltaDist; 

	bvec3 mask;

	for (int i = 0; i < MAX_RAY_STEPS; i++) {
		if (getVoxel(mapPos, iTime)) continue;
		if (USE_BRANCHLESS_DDA) {
            //Thanks kzy for the suggestion!
            mask = lessThanEqual(sideDist.xyz, min(sideDist.yzx, sideDist.zxy));
			/*bvec3 b1 = lessThan(sideDist.xyz, sideDist.yzx);
			bvec3 b2 = lessThanEqual(sideDist.xyz, sideDist.zxy);
			mask.x = b1.x && b2.x;
			mask.y = b1.y && b2.y;
			mask.z = b1.z && b2.z;*/
			//Would've done mask = b1 && b2 but the compiler is making me do it component wise.

			//All components of mask are false except for the corresponding largest component
			//of sideDist, which is the axis along which the ray should be incremented.			

			sideDist += vec3(mask) * deltaDist;
			mapPos += ivec3(vec3(mask)) * rayStep;
		}
		else {
			if (sideDist.x < sideDist.y) {
				if (sideDist.x < sideDist.z) {
					sideDist.x += deltaDist.x;
					mapPos.x += rayStep.x;
					mask = bvec3(true, false, false);
				}
				else {
					sideDist.z += deltaDist.z;
					mapPos.z += rayStep.z;
					mask = bvec3(false, false, true);
				}
			}
			else {
				if (sideDist.y < sideDist.z) {
					sideDist.y += deltaDist.y;
					mapPos.y += rayStep.y;
					mask = bvec3(false, true, false);
				}
				else {
					sideDist.z += deltaDist.z;
					mapPos.z += rayStep.z;
					mask = bvec3(false, false, true);
				}
			}
		}
	}

	vec3 color;
	if (mask.x) {
		color = vec3(0.5);
	}
	if (mask.y) {
		color = vec3(1.0);
	}
	if (mask.z) {
		color = vec3(0.75);
	}
	fragColor.rgb = color;
	//fragColor.rgb = vec3(0.1 * noiseDeriv);
}

// modification based on BigWIngs shader
// https://www.shadertoy.com/view/wdlGRM

#define PI 3.1415926535

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;

    uv *= 10.;

    float angle = atan(uv.y, uv.x);
    float radius = length(uv) * 2.;

    // convert to polar coordinate
    vec2 gv = mod(vec2(angle, radius), PI/3.); 
	vec2 id = floor(vec2(angle, radius)/PI/1.) ;

	float m = 0.;
    float t;
    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {
            vec2 offs = vec2(x, y);
            t = -iTime+length(id.y)*.2+4.; 
            float r = mix(.45, 2., sin(t)*.5+.5);
    		float c = smoothstep(r, r*.9, length(gv+offs));
    		m = m*(1.-c) + c*(1.-m);
        }
    }


    fragColor = vec4(m );
}

#define S(a,b,t) smoothstep(a,b,t)
#define clamp01(x) clamp(x,0.,1.)
#define white vec3(1.)
#define red vec3(1.,0.,0.)
#define black vec3(0.)
#define v01 abs(sin(iTime))

float remap01(float a, float b, float t)
{
    return clamp01((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t)
{
    return remap01(a,b,t) * (d-c) + c;
}

vec2 within(vec2 uv, vec4 rect)
{
    return (uv - rect.xy)/(rect.zw-rect.xy);
}

vec4 Brow(vec2 uv, float smile)
{    

    float offset = mix(0.2,0.0,smile);
    uv.y -= offset;

    float y = uv.y;

    uv.y += uv.x*mix(0.8,0.5,smile) - mix(.3,.1,smile);
    uv.x -= mix(.1,0.,smile);

    uv -= 0.5;

    float d1 = length(uv);
    float d2 = length(uv - vec2(0.05,-0.1));

    float blur = 0.1;

    float s1 = S(0.45,0.45-blur,d1);
    float s2 = S(0.5,0.5-blur,d2);

    float browMask = clamp01(s1 - s2);
    float glowMask = remap01(.7,.8,y);
    glowMask *= S(0.3,0.7,browMask);
    glowMask *= (1.-smile);
    vec4 browColor = mix(vec4(.4,.2,.2,1.),vec4(1., .75, .5, 1.), glowMask);

    blur += 0.1 * smile;

    uv.y +=  offset;

    d1 = length(uv);
    d2 = length(uv - vec2(0.05,-0.1));

    s1 = S(0.45,0.45-blur,d1);
    s2 = S(0.5,0.5-blur,d2);

    float shadowMask = clamp01(S(0.45,0.45-blur,d1) - S(0.5,0.5-blur,d2));

    vec4 shadowColor = mix(vec4(0.,0.,0.,0.),vec4(0.,0.,0.,1.),S(0.,1.,shadowMask)*0.8);

    vec4 color = mix(shadowColor, browColor,  S(.1,.2,browMask));

    return color;
}

vec4 Eye(vec2 uv, vec2 mouse, float smile, float side)
{
    uv -= 1.;
    uv.x *= side;

    float d = length(uv);

    vec4 irisColor = vec4 (.3,.5,1.,1.);
    vec4 color = mix(vec4(white,1.), irisColor,S(.1,1.8,d));
    color.a = S(1.,0.96, d);

    color.rgb *= 1. - S(.9, 1., d) * 0.5 * clamp01(-uv.y - uv.x * side);

    d = length(uv - mouse * 0.5);
    float irisMask =  S(.5,.48, d);

    color.rgb = mix(color.rgb, black, irisMask);

    irisColor.rgb *= 1. + S(.4,.1,d);
    color.rgb = mix(color.rgb, irisColor.rgb, S(.45,.43, d));

    d = length(uv - mouse * 0.6);

    float pupilSize = mix(.25,.5,smile);
    float pupilMask = S(pupilSize, pupilSize * .95, d) * irisMask;
    color.rgb = mix(color.rgb, black, pupilMask);

    float t = iTime*3.;
    vec2 offset = vec2(sin(t+uv.y*25.), sin(t+uv.x*25.));
    offset *= .01*smile;

    uv += offset;

    float highlight = S(.2,.18, length(uv - vec2(-0.3,0.3)));
    highlight += S(.1,.09, length(uv - vec2(0.12,-0.12)));
    color.rgb = mix (color.rgb, white, highlight);
    return color;
}

vec4 Mouth(vec2 uv, float smile)
{
    uv -=0.5;

    vec4 color = vec4(.5, .18, .05, 1.);

    uv.y *= 1.5;
    uv.y -= uv.x * uv.x * (1. - smile);
    uv.x *= mix(1., 2.5, smile);

    float d = length(uv);
    color.a = S(1.,.98,d);

    vec2 tUv = uv;
    tUv.y += (abs(uv.x)*.5+.1)*smile;
    float td = length (tUv - vec2(0.,1.2));

    vec3 toothCol = white * S(1.4, .7, d);

    color.rgb = mix(color.rgb, toothCol, S(.9,.84,td));

    td = length (uv - vec2(0., -1.0));
    color.rgb = mix(color.rgb, vec3(1., .5, .5), S(1.,.4, td));

    return color;
}

vec4 Head(vec2 uv)
{

    vec4 color = vec4(1, .65, .2,1.);

    float d = length(uv);
    color.a = S(1.,.98,d);

    float edgeShade = remap01(0.7,1.,d);
    edgeShade *= edgeShade;
    color.rgb *= 1. - edgeShade*.5;

    color.rgb = mix(color.rgb, vec3(.7,.3,.1), S(0.94,0.95,d));

    float highlight = S(0.82,0.8, d);
    highlight *= S(0.35,0.37, length(uv - vec2(0.43,0.13)));
    highlight *= remap(1.,0., 0.95, 0.0,uv.y);
    color.rgb = mix (color.rgb,white,highlight);

    //Left cheek
    d = length(uv-vec2(.5,-.4));
    float cheek = S(.4,.1,d) * 0.4;
    cheek *= step(d,.36);
    color.rgb = mix(color.rgb, vec3(1.,.1,.1), cheek);

    return color;
}

vec4 Smiley(vec2 uv, vec2 mouse, float smile)
{
    vec4 color = vec4(0.);

    float side = sign(uv.x);
    uv.x = abs(uv.x);

    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv,vec4(.09,-.2,.415, .135)), mouse, smile, side);
    vec4 mouth = Mouth(within(uv,vec4(-.3,-.7,.3, -.4)), smile);
    vec4 brow = Brow(within(uv, vec4(0.03,0.3,0.8,0.85)), smile);

    color = mix(color, head, head.a);
    color = mix(color, eye, eye.a);
    color = mix(color, mouth, mouth.a);
    color = mix(color, brow, brow.a);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.0 - iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy - 0.5;
    float smile = (cos(iTime) + 1.)/2.;

    float ratio = (iResolution.y/iResolution.x);
    vec2 scaledUV = uv/2.;
    uv -= mouse * (0.25-dot(scaledUV,scaledUV));

    fragColor = Smiley(uv, mouse, smile);
}

