vec2 lissajous(float t, float a, float b) {
    return vec2(sin(a*t), sin(b*t));
}

const float R = 0.02;
void paint_plane(vec2 p4, vec2 p5, vec2 p6, vec2 p7) {
    circle(p4, R);
    circle(p5, R);
    circle(p6, R);
    circle(p7, R);
	fill();

    move_to(p4);
	line_to(p5);
    line_to(p6);
    line_to(p7);
    close_path();
    stroke();
}

void paint() {
    float t = iTime * 0.1;

    // clear screen with a subtle gradient

    set_source_rgb(vec3(0.0));
    clear();

    scale(0.7);
    set_source_rgb(vec3(1.0));
	set_line_width_px(1.0);

    vec2 p0 = lissajous(t, 5.0, 4.0);
    vec2 p1 = lissajous(t + 1.0, 5.0, 4.0);
    vec2 p2 = lissajous(t + 2.0, 5.0, 4.0);
    vec2 p3 = lissajous(t + 3.0, 5.0, 4.0);

    vec2 p01 = (p0 + p1)*0.5;
    vec2 p02 = (p0 + p2)*0.5;
    vec2 p03 = (p0 + p3)*0.5;
    vec2 p12 = (p1 + p2)*0.5;
    vec2 p13 = (p1 + p3)*0.5;
    vec2 p23 = (p2 + p3)*0.5;

    // center
    vec2 p0123 = (p0 + p1 + p2 + p3)*0.25;
    // orthogonal vectors
    float S = 1.0 / 3.0;
    vec2 gx = (p23 - p0123) * S;
    vec2 gy = (p13 - p0123) * S;
    vec2 gz = (p12 - p0123) * S;
    // cube vertices
    vec2 c000 = p0123 - gx - gy - gz;
    vec2 c001 = p0123 - gx - gy + gz;
    vec2 c010 = p0123 - gx + gy - gz;
    vec2 c011 = p0123 - gx + gy + gz;
    vec2 c100 = p0123 + gx - gy - gz;
    vec2 c101 = p0123 + gx - gy + gz;
    vec2 c110 = p0123 + gx + gy - gz;
    vec2 c111 = p0123 + gx + gy + gz;

    circle(p0, R);
    circle(p1, R);
    circle(p2, R);
    circle(p3, R);
	fill();

    set_source_rgba(1.0, 0.5, 0.5, 1.0);
    move_to(p0);
    line_to(p2);
    stroke();
	move_to(p1);
    line_to(p3);
    stroke();
    paint_plane(p01, p12, p23, p03);
    set_source_rgba(0.5, 1.0, 0.5, 1.0);
    move_to(p0);
	line_to(p1);
    stroke();
    move_to(p2);
	line_to(p3);
    stroke();
    paint_plane(p02, p12, p13, p03);
    set_source_rgba(0.5, 0.5, 1.0, 1.0);
    move_to(p0);
	line_to(p3);
    stroke();
    move_to(p1);
	line_to(p2);
    stroke();
    paint_plane(p01, p13, p23, p02);


    set_source_rgb(vec3(1.0));
    move_to(c000);
    line_to(c010);
    line_to(c110);
    line_to(c100);
    close_path();
    move_to(c001);
    line_to(c011);
    line_to(c111);
    line_to(c101);
    close_path();
    move_to(c000);
    line_to(c001);
    move_to(c010);
    line_to(c011);
    move_to(c110);
    line_to(c111);
    move_to(c100);
    line_to(c101);
    stroke();

}

