    Postcard of a Mirage 
    10/27/21 @byt3_m3chanic

    A shader I've kicked around for a while. 
    Parts of the noise from various @iq's examples.
    Add in some anisotropic glitter and floating 
    shapes, and you have a abstract isometric shader! 
*/


#define S	smoothstep
#define R   iResolution
#define M   iMouse
#define T   iTime
#define PI  3.14159265
#define PI2 6.28318530
#define ZRO 0

#define MAX_DIST    50.
#define MIN_DIST    .001

vec2 hash2( vec2 p ){ return fract(sin(vec2(dot(p,vec2(127.1,211.7)),dot(p,vec2(219.5,183.3))))*43758.5453); }
float hash21(vec2 p) { return fract(sin(dot(p,vec2(23.86,48.32)))*4374.432); }
mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }

// @gaz
float zag(vec3 p, float s) {
    p = abs(p)-s;
    if (p.x < p.z) p.xz = p.zx;
    if (p.y < p.z) p.yz = p.zy;
    if (p.x < p.y) p.xy = p.yx;
    return dot(p,normalize(vec3(s*.42,s,0)));
}

float box(vec2 p,vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// @iq
vec3 noised( in vec2 x ){
    vec2 f = fract(x);
    vec2 u = f*f*(3.0-2.0*f);
    // texel fetch version
    vec2 p = vec2(floor(x));
    float a = hash21( p+vec2(0,0) );
	float b = hash21( p+vec2(1,0) );
	float c = hash21( p+vec2(0,1) );
	float d = hash21( p+vec2(1,1) );
	return vec3(a+(b-a)*u.x+(c-a)*u.y+(a-b-c+d)*u.x*u.y,
		   6.0*f*(1.0-f)*(vec2(b-a,c-a)+(a-b-c+d)*u.yx));
}
// @iq
float fbm ( in vec2 _st, float oct) {
    oct = min(oct,5.);
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(.5), sin(.5),-sin(.5), cos(.5));
    for (float i = 0.; i < 8.25-oct; ++i) {
        v += a * noised(_st).x;
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}
//generate terrain using above noise algorithm
float terrain( vec2 p, float freq ) {	
	float h = -1.;
	float w = 2.6;
	float m = 0.35;
	for (float i = 0.; i < freq; i++) {
		h += w * noised((p * m)).x;
		w *= 0.5;
		m *= 2.0;
	}
	return h;
}

//globals
vec3 hitPoint,hit;
vec2 vuv;
mat2 r45,turn,tilt;
float mvt = 0.,ghs,shs;
float midpt;

//constants
const float sz = .5;
const float hf = sz*.5;
const float db = sz *2.;

vec2 map(vec3 p) {
	vec2 res = vec2(1e5,0.);
    p.y+=4.;
    vec3 pp = p;

    if(vuv.x<midpt) p.z-=T*.3;

    float ter = terrain(p.xz*sz,2.)*.85;
    float d2 = p.y - ter;

    if(d2<res.x && vuv.x<midpt) {
       res = vec2(d2,2.);
       hit=p;
    }

    float d3 = pp.y-1.-(ter*.75);
    if(d3<res.x && vuv.x>midpt) {
        res=vec2(d3,1.);
        hit=p;
    }

    vec2 id = floor(p.xz*sz) + .5; 
    vec2 r = p.xz - id/sz;
    vec3 qx = vec3(r.x,p.y,r.y);
    float hs = hash21(id);
    float xtr = terrain(id,2.)*.85;
    vec3 qqx=qx-vec3(0,xtr+.1,0);

    float bf = length(qqx)-(.3*hs);
    if(bf<res.x && vuv.x<midpt && hs>.4 && xtr>1.15) {
        res=vec2(bf,4.);
        hit=qqx;
        shs=hs;
    }

    float cells = 8.;
    vec3 q = pp-vec3(0,4.5,0);
    q.xz*=turn;

    // @Shane Polar Repetion 
    float a = atan(q.z, q.x);
    // Partitioning the angle into "aNum" cells.
    float ia = floor(a/6.2831853*cells);
    ia = (ia + .5)/cells*6.2831853;

    // find object via mod and cells and wave
    float ws = -mod(ia,.0);
    float cy = sin( ws*4. + (T * .25) * PI) * 1.5;
    q.y +=cy;

    // Converting the radial centers to their positions.
    q.xz *= rot(ia);
    q.x -= 5.75;

    q.zy*=rot(ws+T);
    q.xz*=rot(ws);

    float df = zag(q,.295);
    if(df<res.x) {
        res=vec2(df,3.);
        hit=q;
    }

    return res;
}

// normal
vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h = vec2(1.,-1.)*.5773;
    return normalize( 
        h.xyy*map( p + h.xyy*e ).x + 
        h.yyx*map( p + h.yyx*e ).x + 
        h.yxy*map( p + h.yxy*e ).x + 
        h.xxx*map( p + h.xxx*e ).x );
}

// water ripples - using bmp map to add extra
// depth to water effects
vec3 fxBumpMap( vec3 p, vec3 n, float bf, float per) {
    vec2 e = vec2(per*MIN_DIST, 0);   
    mat3 m = mat3( 
        vec3(fbm(p.xz - e.xy, 3.)), 
        vec3(fbm(p.xz - e.yx, 2.)), 
        vec3(fbm(p.xz - e.yy, 3.))
    );
    vec3 g = vec3(.3, .59, .12) * m; 
    g = (g - dot(vec3(fbm(p.xz , 3.)), vec3(.3, .59, .12)) )/e.x; g -= n*dot(n, g);  
    return normalize( n + g*bf );
}

// reduced voronoi based off @iq
// https://www.shadertoy.com/view/ldl3W8
// used for the glitter pattern
vec3 voronoi( in vec2 x) {
    vec2 n = floor(x);
    vec2 f = fract(x);
	vec2 mg, mr;
    float md = 8.,ox = 0.;
    for( float j=-1.; j<=1.; j++ )
    for( float i=-1.; i<=1.; i++ )
    {
        vec2 g = vec2(i,j);
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;
        float d = dot(r,r);
        if( d<md ){
            md = d;
            mr = r;
            mg = g;
        }
    }
    md = 8.;
    //reduced to 1
    for( float j=-1.; j<=1.; j++ )
    for( float i=-1.; i<=1.; i++ )
    {
        vec2 g = mg + vec2(i,j);
		vec2 o = hash2( n + g );
        ox = o.x;
    }
    return vec3(md,ox,mr.x);
}

// lame based off the tex3D and used
// to add texture for glintz
vec3 vor3D(in vec3 p, in vec3 n ){
    n = max(abs(n), MIN_DIST);
    n /= dot(n, vec3(1));
	vec3 tx = voronoi(p.yz).xyz;
    vec3 ty = voronoi(p.zx).xyz;
    vec3 tz = voronoi(p.xy).xyz;
    //return mat3(tx*tx, ty*ty, tz*tz)*n;
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}

vec3 glintz( vec3 lcol, vec3 hitPoint, vec3 n, vec3 rd, vec3 lpos) {
    vec3 mate = vec3(0);
    vec3 pos = hitPoint;

    vec3 h = normalize(lpos-rd);
    float nh = abs(dot(n,h)), nl = dot(n,lpos);
    vec3 light = lcol*max(.0,nl)*1.5;
    vec3 coord = pos*1.5, coord2 = coord;

    vec3 ww = fwidth(pos);
    vec3 glints=vec3(0);
    float pw,q,anisotropy;
    vec3 tcoord;

    //build layers
    for(int i = 0; i < 2;i++) {

        if( i==0 ) {
            anisotropy=.55;
            pw=R.x*.20;
            tcoord=coord;
        } else {
            anisotropy=.62;
            pw=R.x*.10;
            tcoord=coord2;
        }

        vec3 aniso = vec3(vor3D(tcoord.zyx*pw,n).yy, vor3D(tcoord.xyz*vec3(pw,-pw,-pw),n).y)*1.0-.5;
        if(i==0) {
            aniso -= n*dot(aniso,n);
            aniso /= min(1.,length(aniso));
        }

        float ah = abs(dot(h,aniso));

        if( i==0 ) {
            q = exp2((1.15-anisotropy)*2.5);
            nh = pow( nh, q*4.);
            nh *= pow( 1.-ah*anisotropy, 10.);
        } else {
            q = exp2((.1-anisotropy)*3.5);
            nh = pow( nh, q*.4);
            nh *= pow( 1.-ah*anisotropy, 150.);
        }     

        glints += 
        (lcol*nh*exp2(((i==0?1.25:1.)-anisotropy)*1.3))*smoothstep(.0,.5,nl);
    }

    return 
        mix(light*vec3(0.3), vec3(.05), .1) +
        glints +
        lcol * .3;
}

vec3 sky = vec3(0);
// modified slim sky based on 
// @Shane https://www.shadertoy.com/view/WdtBzn
vec3 ACESFilm(in vec3 x) { return clamp((x*(.6275*x+.015))/(x*(.6075*x+.295)+.14),0.,1.); }
vec3 getSky(vec3 ro, vec3 rd, vec3 ld, float ison) { 
    rd.z *= .95 - length(rd.xy)*.5;
    rd = normalize(rd);
    // rayleigh / mie
    vec3 betaR = vec3(5.8e-2, 1.35e-1, 3.31e-1), betaM = vec3(4e-2); 
    float zAng = max(2e-6, rd.y);
    // scatter - Klassen's model.
    vec3 extinction = exp(-(betaR*1. + betaM*1.)/zAng);	
    vec3 col = 2.*(1. - extinction);
    float t = (1e5 - ro.y - .15)/(rd.y + .45);
    vec2 vuv = (ro + t*rd).xz;
	if(t>0.&&ison>0.) {
        col = mix(col, vec3(3), smoothstep(.45, 1., fbm(3.*vuv/1e5,1.))*
                                smoothstep(.25, .65, rd.y*.5 + .5)*.4);  
    }
    return clamp(ACESFilm(col), 0., 1.);
} 

vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, bool last, inout float d) {

    vec3 sky = getSky(ro,rd,vec3(.0,.02,1.01),1.);
    vec3 C = vec3(0);

    float  m = 0.;
    vec3 p = ro;
    for (int i = 0; i<128;i++) {
     	p = ro + rd * d;
        vec2 ray = map(p);
        if(abs(ray.x)<d*MIN_DIST || d>MAX_DIST)break;
        d += i<32? ray.x*.45 : ray.x*.85; 
        m = ray.y;
    }

    hitPoint=hit;
    ghs=shs;
    float alpha = 0.;

    if(d<MAX_DIST){
        alpha = d;
      	vec3 n = normal(p, d);

        vec3 lpos = vec3(-14.,9,-4.);
        vec3 l = normalize(lpos-p);   
        vec3 h = vec3(.5);

        float fresnel = pow(clamp(1.+dot(rd, n), 0., 1.), 9.);
        fresnel = mix(.0, 1., fresnel);
        ref=vec3(.01); 
        // materials and bump
        if(m==1.) {    
            n = fxBumpMap(.05*T+hitPoint*.025, n, .15 ,d);
            h = glintz(vec3(0.114,0.361,0.447), .01*T+hitPoint*.015, n, normalize(p-lpos), l);
            ref = (h+.2)-fresnel;
        }
        if(m==2.) {   
            h = glintz(vec3(0.945,0.753,0.533), hitPoint.zyx*.055, n, rd, l);
            ref = (h*.5)-fresnel;
        }
        if(m==3.) {
            ref = vec3(.7)-fresnel;
        }
        if(m==4.) {
            h = mix(vec3(0.624,0.169,0.016),vec3(0.737,0.588,0.043),clamp(ghs*.85,0.,1.));
            ref = (h*.5)-fresnel;
        }
        // diff and shadows
        float diff = clamp(dot(n,l),.03,1.);
        float shdw = 1., t = .3;
        for(int i=0; i<18; i++){
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) {shdw = .03; break;}
            shdw = min(shdw, 24.*h/t);
            t += h;
            if( shdw<MIN_DIST || t>32. ) break;
        }
        diff = mix(diff,diff*shdw,.65);

        vec3 view = normalize(p - ro);
        vec3 ret = reflect(normalize(lpos), n);
        float spec = .5 * pow(max(dot(view, ret), 0.), 24.);

        C += h * diff+spec;
        C = mix(sky,C,  exp(-.000025*d*d*d));

        ro = p+n*.1;
        rd = reflect(rd,n);

    } else {
        C = sky;
    }
    return vec4(C,alpha);
}

void topLayer(inout vec3 C, vec2 uv) {
    float px = fwidth(uv.x);
    //uv*=tilt;
    float bx = box(uv-vec2(.0,.02),vec2(.9,.475))-.0025;
    float dx = abs(bx)-.001;
    dx=smoothstep(px,-px,dx);
    bx=smoothstep(-px,px,bx);

    float bd = box(uv-vec2(.001,-.015),vec2(.86,.435))-.0025;
    float sd =smoothstep(.085-px,px,bd);

    vec3 ln = mix(vec3(0.647,0.545,0.435),vec3(0.208,0.588,0.753),.5+uv.y*.75);
    C=mix(C,ln,bx);
    if(hash21(uv)>.7)C=mix(C,C*.8,bx);
    C=mix(C,C*.15,min(sd,bx));

    float en = noised(vec2(uv.y*20.,.1)).x*.05;
    float tp = box(uv-vec2(en*.5,.5),vec2(.2,.025));
    float ts = smoothstep(px,-px,tp);
    tp = abs(tp)-.001;
    tp=smoothstep(px,-px,tp);
    C=mix(C,C*.2,ts);
    C=mix(C,C*.6,tp);

}

void mainImage( out vec4 O, in vec2 F ) {
    // precal
    mvt+= T*.5;
    turn=rot(T*.2);

    // uv + ro + rd
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vuv=uv;
    float vv = uv.y;
    midpt = .45*sin(vv*5.+T*1.15);

    float zoom = 10.;
    vec3 ro = vec3(uv*zoom,-zoom);
    vec3 rd = vec3(0.,0.,1.);

    float x = M.xy==vec2(0)?0.:(M.x/R.x * 1. - .5) * PI;
    mat2 rx = rot(-.38531); mat2 ry = rot(-.38531+x);
    ro.yz *= rx; ro.xz *= ry;
    rd.yz *= rx; rd.xz *= ry;

    vec3 C = vec3(0);
    vec3 ref=vec3(0);
    vec3 fil=vec3(1.);

    // ref loop @BigWIngs
    float d =0.;
    // if slow reduce to 2
    for(float i=0.; i<3.; i++) {
        vec4 pass = render(ro, rd, ref, i==3.-1., d);
        C += pass.rgb*fil;
        fil*=ref;
    }



    C=clamp(C,vec3(0),vec3(1));
    topLayer(C,uv);
    C = pow(C, vec3(.4545));
    O = vec4(C,1.0);
}
