float rand(vec2 co){
    return fract(sin(dot(co*iTime, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col = rand(fragCoord);
    fragColor = vec4(vec3(.7)*col,1.0);
}

#define SC (250.0)

vec2 noise(in vec2 uv)
{
    //return sin(uv.x/1.6)+cos(-uv.y*1.6);
    return vec2(sin(uv.x),cos(uv.y))+vec2(1.);
}

#define OCTAVES 8
float fbm1(in vec2 uv,int octaves,float i1,float value1)
{
    //this function generates the terrain height
    float value = value1;
    float amplitude = .65/i1;
    //float amplitude = .6/(i1+value1/4.);

    float freq = 2.1/i1;
    float n1 = 0.;
    vec2 noise2;
    for (int i = 0; i < octaves; i++)
    {


        noise2 = noise(noise2.yx+uv*(freq));
        vec2 noise1 = noise2-value;
        freq *= 2.1;
        n1 = noise1.x+noise1.y;

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = (value+abs(n1) * amplitude)-n1/freq;
        //value += n1/freq;

        amplitude *= amplitude;
        uv -= (n1)/freq;


        //uv += (noise1+vec2(1.))/freq;
        //uv1 = uv*freq;

        //uv = uv.yx;
    }

    return value;
}

float fbm(vec2 uv, int oct){
    //uv *= 2.;
    float result = 0.;
    for(float i = 1.; i < 3.; i++){
        float i1 = i*i;
        result -= fbm1(uv/i1,oct,i1,result/i1);
        oct -= 2;
    }
    return result/6.;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,OCTAVES+4);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,OCTAVES);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

