vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
}

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(0., 1., -1.));

    vec3 camStep = vec3(-1., 0., 0.) * (iTime+12.)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;


    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,OCTAVES)-1.8;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iTime/8.-iMouse.x/iResolution.x*3.14*2.0);
    }


    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

//  conversion fns from Iñigo Quiles @ https://www.shadertoy.com/view/MsS3Wc
vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 calculateIDW(vec2 p) {
    vec2 v1 = vec2(sin(5.+iTime*.1)*.5+.5, sin(iTime*1.2)*.5+.5);
    vec2 v2 = vec2(sin(4.+iTime*.2)*.5+.5, sin(iTime*.9)*.5+.5);
    vec2 v3 = vec2(sin(3.+iTime*.3)*.5+.5, sin(iTime*.6)*.5+.5);
    vec2 v4 = vec2(sin(2.+iTime*.4)*.5+.5, sin(iTime*.3)*.5+.5);
    vec2 v5 = vec2(sin(1.+iTime*.5)*.5+.5, sin(iTime*.1)*.5+.5);

    // convert to actual pixels to deal with stretching with non-square aspect ratios
    float pWidth = 5.;
    if (distance(p*iResolution.xy, v1*iResolution.xy) < pWidth ||
        distance(p*iResolution.xy, v2*iResolution.xy) < pWidth ||
        distance(p*iResolution.xy, v3*iResolution.xy) < pWidth ||
        distance(p*iResolution.xy, v4*iResolution.xy) < pWidth ||
        distance(p*iResolution.xy, v5*iResolution.xy) < pWidth) {
        return vec3(0., 0., 1.);
    }

    // Shepard's method
    // https://en.wikipedia.org/wiki/Inverse_distance_weighting
    float powerParam = 10. + 5.*sin(iTime);
    float w1 = 1./pow(distance(p, v1), powerParam);
    float w2 = 1./pow(distance(p, v2), powerParam);
    float w3 = 1./pow(distance(p, v3), powerParam);
    float w4 = 1./pow(distance(p, v4), powerParam);
    float w5 = 1./pow(distance(p, v5), powerParam);

    vec3 u1 = vec3(.4,  1., .4);
    vec3 u2 = vec3(.1,  1., 1.);
    vec3 u3 = vec3(.5,  1., 1.);
    vec3 u4 = vec3(.6, 1., 1.);
    vec3 u5 = vec3(.7,  1., .4);

    /*

    vec3 u1 = vec3(.0, 1., 1.);
    vec3 u2 = vec3(.2, 1., 1.);
    vec3 u3 = vec3(.4, 1., 1.);
    vec3 u4 = vec3(.6, 1., 1.);
    vec3 u5 = vec3(.8, 1., 1.);

    vec3 u1 = vec3(217., 156., 94.)/255.;
    vec3 u2 = vec3(231., 201., 125.)/255.;
    vec3 u3 = vec3(11., 207, 139.)/255.;
    vec3 u4 = vec3(34, 214., 153.)/255.;
    vec3 u5 = vec3(122., 132., 94.)/255.;
    */
    vec3 u = (w1*u1 + w2*u2 + w3*u3 + w4*u4 + w5*u5)/(w1+w2+w3+w4+w5);
    return u;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = calculateIDW(uv);
    fragColor = vec4(hsb2rgb(col), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
