    float h = b - (b * floor(b/t));
    float h2 = exp(h)/2.718;


    //vec3 midcol = vec3(1.,1.,1.);
    //vec3 cola = mix(vec3(1.,.0,1.), vec3(1.,0.,0.), h2);
    //vec3 colb = mix(vec3(0.,1.,1.), vec3(0.,.5,1.), h2);

