// 四棱锥金字塔
float sdPyramid( in vec3 p, in float h )
{
    float m2 = h*h + 0.25;

    // symmetry
    p.xz = abs(p.xz);
    p.xz = (p.z>p.x) ? p.zx : p.xz;
    p.xz -= 0.5;

    // project into face plane (2D)
    vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

    float s = max(-q.x,0.0);
    float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

    float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
	float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

    float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

    // recover 3D and scale, and add sign
    return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));;
}

// 
// la,lb=semi axis, h=height, ra=corner
float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
    p = abs(p);
    vec2 b = vec2(la,lb);
    float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
	vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
    return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}

// 距离长遍历
float GetDist(vec3 p)
{
    vec4 s=vec4(0,1,6,1);// A sphere's pos.xyzr

    float planeDist=sdPlane(p);// ground

    // 遍历形式所有物体 物体摆放 从远至近-行
    float bd=dBox(p-vec3(-6,.75,6),vec3(.6));
    float td=sdTours(p-vec3(-3,.75,6),vec2(.75,0.3));
    float cd=sdCapsule(p,vec3(0,.75,6),vec3(1,2,6),.4);
    float sd=dSphere(p,vec3(3,.75,6),.75);
    float bbd=sdBoundingBox(p-vec3(6,.75,6),vec3(0.6,0.6,0.6),0.05);  

    float cyld=sdCylinder(p-vec3(-1,.75,0),vec3(0,.3,4),vec3(2,.3,5),.2);
    float ed=sdEllipsoid(p-vec3(-5.5,.75,4),vec3(0.2,0.15,0.25));
    float ctd=sdCappedTorus(p-vec3(-3,.75,4),vec2(0.866025,-0.5),0.5, 0.1);
    float hpd=sdHexPrism(p-vec3(3,0.75,4.4),vec2(0.6,0.1));
    float opd=sdOctogonPrism(p-vec3(6,0.75,4.3),0.6,0.1);

    //float rcd0=sdRoundCone(p-vec3(-6,0.75,3.5),0.2,0.1,0.5);
    float rcd=sdRoundCone(p-vec3(-6,0.75,3.5), vec3(0.1,0.0,0.0), vec3(-0.3,0.4,0.1), 0.15, 0.05);
    float tpd=sdTriPrism(p-vec3(-3.3,0.75,3.4),vec2(0.5,0.05));
    float coned=sdCone(p-vec3(5.3,1.1,3.4),vec2(0.3,0.8),0.85);
    float ccd=sdCappedCone(p-vec3(0.0,0.75,3.5),vec3(0.1,0.0,0.0),vec3(-0.3,0.40,0.1),0.3,0.15);
    float sad=sdSolidAngle(p-vec3(3,0.05,3.5), vec2(1.5,2)/2.5, 1.);

    float odd=sdOctahedron(p-vec3(-3.0,0.5,2),0.45);// 四棱锥因为正交投影的问题很难看
    float pd=sdPyramid(p-vec3(2,0.5,2.0), 0.9);
    float rbd= sdRhombus(p-vec3(-0.2,0.34,2.0),0.15,0.25,0.04,0.08);

    float d=min(cd,planeDist);
    d=min(d,sd);
    d=min(d,td);
    d=min(d,bd);
    d=min(d,cyld);
    d=min(d,bbd);
    d=min(d,ed);
    d=min(d,ctd);
    d=min(d,hpd);
    d=min(d,opd);
    d=min(d,rcd);
    d=min(d,tpd);
    d=min(d,coned);
    d=min(d,ccd);
    d=min(d,sad);
    d=min(d,odd);
    d=min(d,pd);
    d=min(d,rbd);

    return d;
}

float RayMarch(vec3 ro,vec3 rd)
{
    float dO=0.;

    for(int i=0;i<MAX_STEPS;i++){
        vec3 p=ro+rd*dO;
        float dS=GetDist(p);
        dO+=dS;
        if(dO>MAX_DIST||dS<SURF_DIST)break;
    }
    return dO;
}

vec3 GetNormal(vec3 p)
{
    float d=GetDist(p);
    vec2 e=vec2(.01,0);

    vec3 n=d-vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));
    return normalize(n);
}

float GetLight(vec3 p)
{
    vec3 lightPos=vec3(0,5,6);// a light's pos.xyz
    lightPos.xz+=vec2(sin(iTime),cos(iTime))*2.;
    vec3 l =normalize(lightPos-p);
    vec3 n=GetNormal(p);

    float dif=clamp(dot(n,l),0.,1.);
    float d=RayMarch(p+n*SURF_DIST*2.,l);
    if(d<length(lightPos-p)) dif*=.1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    vec3 ro=vec3(0,3,0);// cameraPos
    vec3 rd=normalize(vec3(uv.x,uv.y-.3,.35));// cameraDir

    float d=RayMarch(ro,rd);

    vec3 p=ro+rd*d;
    float dif=GetLight(p);
    col=vec3(dif);

    // Output to screen
    fragColor = vec4(col,1.0);
}

const int AMOUNT = 10;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 20.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y))*1.0;

        coord.x = coord.x - cos(coord.y + sin(len)) + cos(iTime / 12.0);
        coord.y = coord.y + sin(coord.x + cos(len)) + sin(iTime / 9.0);
    }

    fragColor = vec4(cos(len * 1.1), cos(len * 1.05), cos(len * 1.01), 1.0);
}

