 float heart( in vec2 p, in float r ) 
{
    vec2 s = vec2(1.0, 0.0);

    p = p-s.xy;
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(.25,.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}



