float box(vec3 p, vec3 a) {
  vec3 q=abs(p)-abs(a);
  return min(length(max(q,0.)),max(q.x,max(q.y,q.z)));
}

vec3 erot(vec3 p,vec3 ax,vec3 ro) {
  return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

#define MISS(D) (D>1000.)


vec3 erot(vec3 p,vec3 ax,float o) {
  return mix(dot(ax,p)*ax,p,cos(o))+sin(o)*cross(ax,p);
}

#define bump(X) (sqrt(abs(sin(X))))


mat2 rot(float t) { float s=sin(t),c=cos(t);return mat2(c,s,-s,c); }
#define nsin(X) (.5+.5*sin(X))          
float warp(vec2 p) {
  //p *= rot(bump(T));
  //p *= rot(pow(2.,length(p)));
  //p.x-=3.*sin(T);
  p*=(2.+10.*nsin(T));
  return tan(p.x)*tan(p.x)-tan(p.y)*tan(p.y);
}

float sph(vec3 p, float r) { return length(p)-r; }

float boxle(vec3 p, float r) {
  return max(box(p,vec3(r)), -sph(p,r*(4./3.)));
}

float S(vec3 p) {
  p.x+=sin(T);
  p = erot(p, normalize(vec3(1)), T*4.);
  float D= boxle(p,1.5);
  p = erot(p, normalize(vec3(11,-1,0)),-T*16.);
  D=min(D,boxle(p,.5));
  return D;
}


float ray(vec3 p, vec3 dir) {
  float dist=0.;
  for (int i=0; i<100;i++) {
    float m=S(p+dist*dir);
    dist+=m;
    if (m<0.01) return dist;
    if (MISS(dist)) return dist;
  }
  return 1001.;
}

vec3 norm(vec3 p) {
  mat3 K=mat3(p,p,p)-mat3(.001);
  return normalize(S(p)-vec3(S(K[0]),S(K[1]),S(K[2])));
}

float RES;

vec3 stext(vec2 uv) {
    vec2 xy = floor(uv*RES)/RES;
    vec2 idx = (clamp((uv-xy)*RES*4.,0.,4.));
    // Time varying pixel color
    float w = nsin(T)-warp(xy);
    int i=4*int(idx.y)+int(idx.x);
    vec3 c1 = 1.-mix(vec3(1),vec3(0),T1[i]);
    vec3 c2 = 1.-mix(vec3(1),vec3(0),T2[i]);
    vec3 c3 = 1.-mix(vec3(1),vec3(0),T3[i]);//erot(mix(vec3(uv,.6),vec3(.3,.1,.9),T3[i]),vec3(1.,.5,.4),T)/2.+.1;
    return vec3(mix(c1,mix(c2,c3,step(2.,w)),step(0.,w)));

}
vec3 text(vec2 uv) {
    uv = uv+0.;
    uv /= max(1.-1.*length(uv),.5);
    uv *= rot(length(uv));

    uv.y = -uv.y;
    return stext(uv);

}
vec3 light(vec3 cam, vec3 dir, float dist,vec3 bg) {
  vec3 p = cam+dir*dist;
  vec3 n = norm(p);
  vec3 R = cross(dir,n);
  float inc = dot(dir,-n);
  float ring = n.y;
  float angle = atan(n.x, n.z)/TAU;

  //vec3 R = mix(dir,n,.05*inc);
  return mix(bg,stext(p.xx),step(.1,inc));//inc*text(n.xy);
}



