            dot(_st,vec2(127.326,321.324)),
            dot(_st,vec2(15.31,45.332))
                    );
    return 2.0*fract(sin(_st)*432.23) +1.0; //1.0 ~ 3.0
}
float snoise(vec2 st){ //Gradient Noise
    vec2 i = fract(st);//取小数
    vec2 k = floor(st);//向负无穷取整
    vec2 u = i*i*(3.0-2.0*i);//插值函数
    return  mix(
                    mix(dot(random(k + vec2(0.0,0.0)),i - vec2(0.0,0.0)),
                        dot(random(k + vec2(0.0,1.0)),i - vec2(0.0,1.0)),
                         u.y),
                    mix(dot(random(k + vec2(1.0,0.0)),i - vec2(1.0,0.0)),
                        dot(random(k + vec2(1.0,1.0)),i - vec2(1.0,1.0)),
                         u.y),
                       u.x
                   );//二维noise
}
float Model(vec2 st,float N){
    st = st*2.-1.;
    float a = atan(st.x,st.y)+3.14;
    float r = 6.28/N;
    return abs(cos(floor(.5+a/r)*r-a)*length(st));
}
float getModel(vec2 st,vec2 size){
    return Model(st*size,4.);
}
float createModel(in vec2 st,in float num[4]){
    float exy =4.;
    float exx =2.;
    st*=vec2(exx,exy);
    vec2 fi = fract(st);
    vec2 df = floor(st);
    fi.x = mix(fi.x,1.-fi.x,step(1.0,df.x));
    return mix(getModel(fi,vec2(0.83,1.)),getModel(fi - vec2(0.03,0.),vec2(1.0)),num[int(df.y)]);
}
float getWork(vec2 st,float t){
    float exy =4.;
    float exx =2.;
    float num[4];
    float j = floor(mod(t,pow(2.,exy)));
    for(int i=0;i<4;i++){
        num[i] =0.0;
        num[i] = step(1.0,mod(j,2.));
        j = ceil(j/2.);
    }
    return createModel(st,num);
}
