        float c1 = length(uv + sin(iTime - i * (uv.y + uv.x)) * .03);
        float cc1 = smoothstep(r+.0001, r, c1);
        color = mix(
            color,
            vec3(
                length(uv) * i * .1,
                uv.y + i * .22,
                i * .23
            ),
            cc1 * i * .006
        );
    }

    fragColor = vec4(color,1.0);
}

