//from iq https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 p )
{
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*CLOSE_ENOUGH ) + 
                      k.yyx*sdf( p + k.yyx*CLOSE_ENOUGH ) + 
                      k.yxy*sdf( p + k.yxy*CLOSE_ENOUGH ) + 
                      k.xxx*sdf( p + k.xxx*CLOSE_ENOUGH ) );
}


// Inverse Distance Weighting - for color interpolation
vec3 calculateIDW(vec2 p) {
    vec3 v1 = vec3(sin(iTime*v1params.x), sin(iTime*v1params.y), 1.);
    vec3 v2 = vec3(sin(iTime*v2params.x), sin(iTime*v2params.y), 1.);
    vec3 v3 = vec3(sin(iTime*v3params.x), sin(iTime*v3params.y), 1.);
    vec3 v4 = vec3(sin(iTime*v4params.x), sin(iTime*v4params.y), 1.);
    vec3 v5 = vec3(sin(iTime*v5params.x), sin(iTime*v5params.y), 1.);

    // Shepard's method
    // https://en.wikipedia.org/wiki/Inverse_distance_weighting
    float powerParam = 5.;
    float w1 = 1./pow(distance(vec3(p.x, p.y, 1.), v1), powerParam);
    float w2 = 1./pow(distance(vec3(p.x, p.y, 1.), v2), powerParam);
    float w3 = 1./pow(distance(vec3(p.x, p.y, 1.), v3), powerParam);
    float w4 = 1./pow(distance(vec3(p.x, p.y, 1.), v4), powerParam);
    float w5 = 1./pow(distance(vec3(p.x, p.y, 1.), v5), powerParam);
    vec3 u1 = vec3(.4,  1., 1.);
    vec3 u2 = vec3(.7,  1., .7);
    vec3 u3 = vec3(.8,  1., .5);
    //vec3 u4 = vec3(.89,  1., 1.);
    //vec3 u5 = vec3(.5,  1., 1.);

    //vec3 u = (w1*u1 + w2*u2 + w3*u3 + w4*u4 + w5*u5)/(w1+w2+w3+w4+w5);
    vec3 u = (w1*u1 + w2*u2 + w3*u3)/(w1+w2+w3);
    return u;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
         uv.x *= iResolution.x / iResolution.y;
	vec3 cameraPos = vec3(.0, .0, .0);//vec3(.0,sin(iTime)*.5,.0);// + iTime);

    // Time varying pixel color
    vec3 ro = vec3(cameraPos.x + uv.x, cameraPos.y + uv.y, cameraPos.z);
	vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));
    vec3 col = hsb2rgb(BG_COLOR);
    //col = calculateIDW(uv.xy); 
    float dist = .0;

    float pWidth = .005;
    if (false) {
        /*distance(uv.xy*2., vec2(sin(iTime*v1params.x), sin(iTime*v1params.y))) < pWidth ||
        distance(uv.xy*2., vec2(sin(iTime*v2params.x), sin(iTime*v2params.y))) < pWidth ||
        distance(uv.xy*2., vec2(sin(iTime*v3params.x), sin(iTime*v3params.y))) < pWidth ||
        distance(uv.xy*2., vec2(sin(iTime*v4params.x), sin(iTime*v4params.y))) < pWidth ||
        distance(uv.xy*2., vec2(sin(iTime*v5params.x), sin(iTime*v5params.y))) < pWidth) {
        fragColor = vec4(1.,1.,1., 1.0);*/
    }
    else{    
        vec3 lightDir = normalize(vec3(sin(iTime*0.125), sin(iTime*0.25), -1.0));

        while(dist < RENDER_DISTANCE)
        {
            float d = sdf(ro + rd * dist);
            if(d < CLOSE_ENOUGH)
            {


                vec3 hit = ro + rd * dist;
                col = calculateIDW(hit.xy);//BLUE; 
                col *= mix(hsb2rgb(SHADOW_COLOR), col, max(dot(calcNormal(hit), lightDir), .0));
                col = hsb2rgb(col);
                break;
            }
            dist += d;
        }

        // Output to screen
        fragColor = vec4(col, 1.0);
        }
}

#define NUM_EXPLOSIONS 5.
#define NUM_PARTICLES 75.

vec2 Hash12(float t){

float x = fract(sin(t*674.3)*453.2);
float y = fract(sin((t+x)*714.3)*263.2);

return vec2(x, y);
}

vec2 Hash12_Polar(float t){

float p_Angle = fract(sin(t*674.3)*453.2)*6.2832;
float p_Dist = fract(sin((t+p_Angle)*714.3)*263.2);

return vec2(sin(p_Angle), cos(p_Angle))*p_Dist;
}

float Explosion(vec2 uv, float t){

 float sparks = 0.;

    for(float i = 0.; i<NUM_PARTICLES; i++){

        vec2 dir = Hash12_Polar(i+1.)*.5;
        float dist = length(uv-dir*t);
        float brightness = mix(.0005, .0005, smoothstep(.05, 0., t));

        brightness *= sin(t*20.+i)*.5+.5; 
        brightness*= smoothstep(1., .6, t);
        sparks += brightness/dist;
    }
    return sparks;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    for(float i = 0.; i<NUM_EXPLOSIONS; i++){
    float t =iTime+i/NUM_EXPLOSIONS;
    float ft = floor(t);
        vec3 color = sin(4.*vec3(.34,.54,.43)*ft)*.25+.75;


        vec2 offset = Hash12(i+1.+ft)-.5;
        offset*=vec2(1.77, 1.);
        //col+=.0004/length(uv-offset);

         col += Explosion(uv-offset, fract(t))*color;
       }

   col*=2.;
    fragColor = vec4(col,1.0);
}

float map(float value, float min1, float max1, float min2, float max2)
{
    return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

bool in_rect(vec2 pos, vec2 size, vec2 fragCoord)
{
    return (fragCoord.x >= pos.x && fragCoord.x <= pos.x+size.x
        && fragCoord.y >= pos.y && fragCoord.y <= pos.y+size.y);
}

bool in_circle(vec2 center, float r, vec2 fragCoord)
{
    return (length(fragCoord - center) <= r);
}

float aa_circle(vec2 center, float r, float glow, vec2 fragCoord)
{
    return min(1.0, (-length(fragCoord - center) + r)*glow);
}

bool in_line(vec2 a, vec2 b, float t, vec2 fragCoord)
{
    if (b.x - a.x == 0.0)
    {
        float highest = max(a.y, b.y);
        float lowest = min(a.y, b.y);
        return (fragCoord.x <= a.x + t && fragCoord.x >= a.x - t && fragCoord.y <= highest && fragCoord.y >= lowest);
    }
    else
    {
        float slope = (b.y- a.y)/(b.x - a.x);
        float c = a.y - slope*a.x;
        float f_x = slope*fragCoord.x + c;

        float highest = max(a.x, b.x);
        float lowest = min(a.x, b.x);
        return (fragCoord.y <= f_x+t && fragCoord.y >= f_x-t && fragCoord.x <= highest && fragCoord.x >= lowest);
    }
}

float aa_line(vec2 a, vec2 b, float t, vec2 fragCoord)
{
    if (b.x - a.x == 0.0)
    {
        float highest = max(a.y, b.y);
        float lowest = min(a.y, b.y);
        if (fragCoord.y <= highest && fragCoord.y >= lowest)
        {
            return map(abs(fragCoord.x - a.x), 0.0, t, 1.0, 0.0);
        }
        else
        {
            return 0.0;
        }
    }
    else
    {
        float slope = (b.y- a.y)/(b.x - a.x);
        t += abs(slope*0.7);
        float c = a.y - slope*a.x;
        float f_x = slope*fragCoord.x + c;
        vec2 highest = vec2(max(a.x, b.x), max(a.y, b.y));
        vec2 lowest = vec2(min(a.x, b.x), min(a.y, b.y));
        if (fragCoord.x <= highest.x && fragCoord.y <= highest.y && fragCoord.x >= lowest.x && fragCoord.y >= lowest.y)
        {
            return map(abs(fragCoord.y - f_x), 0.0, t, 1.0, 0.0);
        }
        else
        {
            return 0.0;
        }
        //( && fragCoord.x <= highest && fragCoord.x >= lowest);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    vec2 p1 = vec2(100.0, 100.0);
    float r = 0.0; //aa_line(p1, iMouse.xy, 2.0, fragCoord);
    float g = in_circle(vec2(250.0, 250.0 + abs(sin(2.0*iTime))*27.0), 200.0, fragCoord) ? 1.0 : 0.0;
    float b = aa_circle(vec2(655.0, 250.0 + abs(sin(2.0*iTime + 1.6))*27.0), 200.0, 1.0, fragCoord);
    fragColor += vec4(r, g, b, 1.0);
}

const float TWO_PI = 6.28318530718,
        distThresh = 0.4,
        baseRadius = 0.2,
      brightAdjust = 5.,
  numControlPoints = 16.,
             speed = 1.;

#define SC(a) vec2(sin(a),cos(a))

void mainImage( out vec4 O,  vec2 U )
{
    U = (2. * U - iResolution.xy) / iResolution.y;

    float time = iTime * speed,
        radius = baseRadius * ( 1. + .7 * sin(time) ),
          dist = 0., d;

    for (float i=0.; i < 1.; i+=1./numControlPoints) {
        d = distance (U, 2.*radius * SC(i*TWO_PI) );
        if (d < distThresh) dist += d;
    }
    //vec3 r =0.5 + 0.5*cos(iTime+vec3(0,2,4))

    O = vec4(cos(iTime+vec3(0,2,4))*dist/numControlPoints * brightAdjust,0);


   // O = vec4(dist/numControlPoints * brightAdjust);
}

/*

    Extruded Circle Animation
    -------------------------

	As previously mentioned, I on occasion like to take a simple flat 2D 
    example, then extrapolate it to a quasi 3D setting to see if it looks
    interesting. In this case, I've extruded a 2D animation I've seen in 
    various forms. I think this particular one was originally written by 
    FLRN (Florian).

    I got the idea to do it after looking at a version on Shadertoy written
    by Vamoss -- The links to it and the original it was based on are below,
    for anyone interested.

    The extruded version is interesting, but it's really hard to beat the 
    simplicity of the original. Anyway, there's nothing technically
    challenging here, but I've provided a heap of options below, for anyone
    who wishes to play around with various rendering styles.



	Based on the following:

	Square Circle Dance - vamoss
    https://www.shadertoy.com/view/ldK3Dc

    Which in turn was based  on:
    FLORIAN (FLRN)
    https://flrn-gif.tumblr.com/post/116319247625



*/

// Textured, metallic and environmental lighting options. Turning them
// all off looks a little cleaner, which is sometimes preferred, but I've
// defaulted to a weathered appearance.
#define TEXTURE
#define METALLIC
#define ENVIRONMENT

// Palette - Spectrum: 0, Golden brown: 1, Green and blue: 2, Blinking: 3, 
// Mondrian: 4, White: 5.
#define PALETTE 0

// Paint the sides a lighter color.
#define SIDES

// Bump map the background cells.
#define BUMP_BG

// Giving the camera a subltle tilt.
#define CAM_TILT
// Lens warp.
#define LENS_WARP




// Grayscale (and slightly sepia), for that pretentious artistic look. :)
//#define GRAYSCALE

// Max ray distance.
#define FAR 20.

// Scale.
const vec2 gScale = vec2(.5);



// Scene object ID to separate the mesh object from the terrain.
float objID;


// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453); }

// Tri-Planar blending function. Based on an old Nvidia tutorial by Ryan Geiss.
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n){ 

    n = max(abs(n) - .2, .001); // max(abs(n), 0.001), etc.
    n /= dot(n, vec3(1)); 
    //n /= length(n);

	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;

    // Textures are stored in sRGB (I think), so you have to convert them to linear space 
    // (squaring is a rough approximation) prior to working with them... or something like that. :)
    // Once the final color value is gamma corrected, you should see correct looking colors.
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}



// Getting the video texture. I've deliberately stretched it out to fit across the screen,
// which means messing with the natural aspect ratio.
//
// By the way, it'd be nice to have a couple of naturally wider ratio videos to choose from. :)
//
vec3 getTex(vec2 p){

    // Strething things out so that the image fills up the window. You don't need to,
    // but this looks better. I think the original video is in the oldschool 4 to 3
    // format, whereas the canvas is along the order of 16 to 9, which we're used to.
    // If using repeat textures, you'd comment the first line out.
    //p *= vec2(iResolution.y/iResolution.x, 1);
    vec3 tx = texture(iChannel0, fract(p)).xyz;
    return tx*tx; // Rough sRGB to linear conversion.
}

// Height map value, which is just the pixel's greyscale value.
float hm(in vec2 p){ return dot(getTex(p), vec3(.299, .587, .114)); }


// Two different angles. One for each alternate checkered set.
vec2 gAng;

// Moving the extruded tiles: There are two sets split into
// alternate checkers. There are 8 key frames.
void move(){


    float n = mod(floor(iTime), 8.); // 8 keyframe.
    float t = mod(iTime, 8.); // Fractional time.
    float st = smoothstep(.3, .7, fract(t)); // Smoothing.

    // Each checkered set moves differently: Rotating back and forth,
    // or rotating by a quarter whilst stopping and resting on alternate
    // frames. It's mostly common sense.

    // Angle one.
    float ndiv2 = floor(n/2.);
    if(mod(n, 2.) == 0.) gAng.x = mix(ndiv2*3.14159/2., (ndiv2 + 1.)*3.14159/2., st);
    else gAng.x = (ndiv2 + 1.)*3.14159/2.;


    // Angle two.
    if(mod(floor(t), 2.)<.5){
        gAng.y = mix(3.14159/2., 0., st);
    }
    else {
        gAng.y = mix(0., 3.14159/2., st);
    }


}

// IQ's extrusion formula.
float opExtrusion(in float sdf, in float pz, in float h){

    vec2 w = vec2( sdf, abs(pz) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));

    /*
    // Slight rounding. A little nicer, but slower.
    const float sf = .025;
    vec2 w = vec2( sdf, abs(pz) - h - sf/2.);
  	return min(max(w.x, w.y), 0.) + length(max(w + sf, 0.)) - sf;
    */
}


// IQ's unsigned box formula.
float sBoxS(in vec2 p, in vec2 b, in float sf){

  return length(max(abs(p) - b + sf, 0.)) - sf;
}

/*
// IQ's signed box formula.
float sBoxS(in vec2 p, in vec2 b, in float sf){


  vec2 d = abs(p) - b + sf;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - sf;
}
*/

#define SHAPE 2

// Shape distance metrics. You could put whatever you feel like here.
float dist(vec2 p, vec2 b){


      #if SHAPE == 0
      // Square.
      p = abs(p) - b;
      return max(p.x, p.y);
      #elif SHAPE == 1
      // Diamond.
      p = abs(p);
      return (p.x + p.y)*.7071 - mix(b.x, b.y, .5);
      #elif SHAPE == 2
      // Octagon.
      //p *= rot2(3.14159/8.);
      p = abs(p);

      return max((p.x + p.y)*.7071, max(p.x, p.y)) - mix(b.x, b.y, .5);

      // Dodecahedron.
      // vec2 p2 = p*.8660254 + p.yx*.5;
      // p = vec2(max(p2.x, p2.y), max(p.y, p.x));
      // p = abs(p) - mix(b.x, b.y, .5);
      return max(p.x, p.y);


      #else
      // Circle.
      return length(p) - (b.x + b.y)/2.;
      #endif

      //p = abs(p) - b;
      //p = p*.8660254 + p.yx*.5;
      //return max(p.x, p.y);

      //p = abs(p)-b;
      //p = (p + p.yx)*.7071;
      //return max(p.x, p.y);
}


float faceDist;

// A regular extruded block grid.
//
// The idea is very simple: Produce a normal grid full of packed square pylons.
// That is, use the grid cell's center pixel to obtain a height value (read in
// from a height map), then render a pylon at that height.

vec4 blocks(vec3 q3){



    // Brick dimension: Length to height ratio with additional scaling.
	const vec2 l = gScale;
    // A helper vector, but basically, it's the size of the repeat cell.
	const vec2 s = l*2.;

    // Distance.
    float d = 1e5;
    // Cell center, local coordinates and overall cell ID.
    vec2 p, ip;

    // Individual brick ID.
    vec2 id = vec2(0);
    vec2 cntr = vec2(0);

    // For block corner postions.
    //vec2[4] ps4 = vec2[4](vec2(-l.x, l.y), l, -l, vec2(l.x, -l.y));
    vec2[4] ps4 = vec2[4](vec2(-.5, .5), vec2(.5), vec2(.5, -.5), vec2(-.5)); 

    float boxID = 0.; // Box ID. Not used in this example, but helpful.

    for(int i = 0; i<4; i++){

        // Block center.
        cntr = ps4[i]/2.;


        // Local coordinates.
        p = q3.xy;
        ip = floor(p/s - cntr) + .5; // Local tile ID.

        // Correct positional individual tile ID.
        vec2 idi = (ip + cntr)*s;

        p -= idi; // New local position.


        // The extruded block height. See the height map function, above.
        float h = 1.;//hm(idi);

        //h = floor(h*15.999)/15.*.1; // Or just, "h *= .15," for nondiscreet heights.
        h *= .025;



        // One larger extruded block.
        //float di2D = sBoxS(p, l/2. - .0*scale, .02);


        vec2 q = p;

        float t = mod(iTime, 4.);
;

        if((i&1)==0){
            q *= rot2(-gAng.x);
        }
        else {
            q *= rot2(-gAng.y);
        }

        //float pin = dist(q, vec2(.09*gScale.x));
        float pin = length(q) - .08*gScale.x;

        float di2D = length(q) - (l.x/2. - .01);
        //float di2D = dist(q, l/2. - .01);
        di2D = max(di2D, -(max(abs(q.x - .5), abs(q.y - .5)) - .5-.01));
        di2D = max(di2D, -(max(abs(q.x + .5), abs(q.y + .5)) - .5-.01));

        di2D = max(di2D, -pin);

        //pin = abs(pin + .02*gScale.x) - .02*gScale.x;






        // Boring out some of the lower boxes. I like it, but thought it
        // confused matters.
        //if(h<1./15.*.15 + .001) di2D = max(di2D, -(di2D + .3*scale));

        // The extruded distance function value.
        float di = opExtrusion(di2D, (q3.z + h), h);

        //di -= min(-di2D*.7, .02);
        di += di2D*.35;

        //float pat = (abs(fract(di2D*32.) - .5)*2. - .125)/32.;
        //di -= (pat)*.1;


        pin = opExtrusion(pin, (q3.z + h - .5), h + .5 + .01);

 //       di = min(di,  opExtrusion(abs(di2D + .01+.002) -.01, (q3.z + h + .005), h + .005));

        // Cell object ID -- Hacked in at the last minute.
        float cOID = di<pin? 0. : 1.;
        di = min(di, pin);

        // If applicable, update the overall minimum distance value,
        // ID, and box ID. 
        if(di<d){
            d = di;
            id = idi;
            // Not used in this example, so we're saving the calulation.
            boxID = cOID;

            faceDist = di2D;  
        }

    }

    // Return the distance, position-base ID and box ID.
    return vec4(d, id, boxID);
}


// Block ID -- It's a bit lazy putting it here, but it works. :)
vec4 gID;

// The extruded image.
float map(vec3 p){

    // Floor.
    float fl = -p.z + .05;
/*    
    vec2 sc = gScale/2.*vec2(1, 1);
    vec2 q = p.xy;// + vec2(sc.x/2.*1., sc.y/2.);
            //if(mod(floor(q.y/sc.y), 2.)==0.) q.x += sc.x/2.;
    vec2 iq = floor(q/sc) + .5;
    q -= iq*(sc);
    q = abs(q);
    float block = max(max(q.x, q.y) - sc.x/2. + .025, abs(p.z - sc.x/8.) - sc.x/8.);
    fl = min(fl + .04, block + (max(q.x, q.y) - sc.x/2.)/8.);
*/
    // The extruded blocks.
    vec4 d4 = blocks(p);
    gID = d4; // Distance, block ID, and cell object ID.



    // Overall object ID.
    objID = fl<d4.x? 1. : 0.;

    // Combining the floor with the extruded image
    return  min(fl, d4.x);

}


// Basic raymarcher.
float trace(in vec3 ro, in vec3 rd){

    // Overall ray distance and scene distance.
    float t = 0., d;

    for(int i = min(iFrame, 0); i<80; i++){

        d = map(ro + rd*t);
        // Note the "t*b + a" addition. Basically, we're putting less emphasis on accuracy, as
        // "t" increases. It's a cheap trick that works in most situations... Not all, though.
        if(abs(d)<.001 || t>FAR) break; // Alternative: 0.001*max(t*.25, 1.), etc.

        //t += i<32? d*.75 : d; 
        t += d*.7; 
    }

    return min(t, FAR);
}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 getNormal(in vec3 p, float t) {
	const vec2 e = vec2(.001, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	
                          map(p + e.yyx) - map(p - e.yyx)));
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing particular scenes with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, vec3 n, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow 
    // test machine, anyway.
    const int maxIterationsShad = 24; 

    ro += n*.0015;
    vec3 rd = lp - ro; // Unnormalized direction ray.


    float shade = 1.;
    float t = 0.;//.0015; // Coincides with the hit condition in the "trace" function.  
    float end = max(length(rd), 0.0001);
    //float stepDist = end/float(maxIterationsShad);
    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i = min(iFrame, 0); i<maxIterationsShad; i++){

        float d = map(ro + rd*t);
        shade = min(shade, k*d/t);
        //shade = min(shade, smoothstep(0., 1., k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, stepDist), etc.
        t += clamp(d, .01, .25); 


        // Early exits from accumulative distance function calls tend to be a good thing.
        if (d<0. || t>end) break; 
    }

    // Sometimes, I'll add a constant to the final shade value, which lightens the shadow a bit --
    // It's a preference thing. Really dark shadows look too brutal to me. Sometimes, I'll add 
    // AO also just for kicks. :)
    return max(shade, 0.); 
}


// I keep a collection of occlusion routines... OK, that sounded really nerdy. :)
// Anyway, I like this one. I'm assuming it's based on IQ's original.
float calcAO(in vec3 p, in vec3 n)
{
	float sca = 3., occ = 0.;
    for( int i = 0; i<5; i++ ){

        float hr = float(i + 1)*.15/5.;        
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);  


}



// Compact, self-contained version of IQ's 3D value noise function. I have a transparent noise
// example that explains it, if you require it.
float n3D(in vec3 p){

	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); //p *= p*p*(p*(p * 6. - 15.) + 10.);
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); // Range: [0, 1].
}

// Very basic pseudo environment mapping... and by that, I mean it's fake. :) However, it 
// does give the impression that the surface is reflecting the surrounds in some way.
//
// More sophisticated environment mapping:
// UI easy to integrate - XT95    
// https://www.shadertoy.com/view/ldKSDm
vec3 envMap(vec3 p){

    p *= 4.;
    p.y += iTime/4.;

    float n3D2 = n3D(p*2.);

    // A bit of fBm.
    float c = n3D(p)*.57 + n3D2*.28 + n3D(p*4.)*.15;
    c = smoothstep(.45, 1., c); // Putting in some dark space.

    p = vec3(c*c*c, c*c, c); // Blueish tinge.

    return mix(p.zxy, p, n3D2); // Mixing in a bit of red.

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

	// Camera Setup.
    #ifdef CAM_TILT
    vec3 ro = vec3(-.125, -.125, -2.5); // Camera position, doubling as the ray origin.
	vec3 lk = ro + vec3(.005, .01, .25);//vec3(0, -.25, iTime);  // "Look At" position.
    #else
    vec3 ro = vec3(0, 0, -2.5); // Camera position, doubling as the ray origin.
	vec3 lk = ro + vec3(0, 0, .25);//vec3(0, -.25, iTime);  // "Look At" position.
    #endif

    // Light positioning. One is just in front of the camera, and the other is in front of that.
 	vec3 lp = ro + vec3(.25, .5, 1);// Put it a bit in front of the camera.


    // Using the above to produce the unit ray-direction vector.
    float FOV = 1.; // FOV - Field of view.
    vec3 fwd = normalize(lk-ro);
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x )); 
    // "right" and "forward" are perpendicular, due to the dot product being zero. Therefore, I'm 
    // assuming no normalization is necessary? The only reason I ask is that lots of people do 
    // normalize, so perhaps I'm overlooking something?
    vec3 up = cross(fwd, rgt); 

    // rd - Ray direction.
    vec3 rd = normalize(fwd + FOV*uv.x*rgt + FOV*uv.y*up);
    //vec3 rd = mat3(rgt, up, fwd)*normalize(vec3(uv, 1./FOV));
    #ifdef LENS_WARP
    // Ray warping, lens warping, etc -- One of many variations.
    rd = normalize(vec3(rd.xy, sqrt(max(rd.z*rd.z + (.5 - dot(rd.xy, rd.xy))*.5, 0.))));
    #endif

    // Swiveling the camera about the XY-plane.
	//rd.yz *= rot2( sin(-3.14159/3.)/3. );
	//rd.xz *= rot2( sin(iTime/1.)/64. );
	//rd.xy *= rot2( cos(iTime/2.)/64. );


    // Retrieve the two angular postions of the alternating
    // grid cell objects.
    move();


    // Raymarch to the scene.
    float t = trace(ro, rd);

    // Save the block distance, ID and cell object ID.
    vec4 svGID = gID;

    float svfaceDist = faceDist;

    float svObjID = objID;


    // Initiate the scene color to black.
	vec3 col = vec3(0);

	// The ray has effectively hit the surface, so light it up.
	if(t < FAR){


    	// Surface position and surface normal.
	    vec3 sp = ro + rd*t;
	    //vec3 sn = getNormal(sp, edge, crv, ef, t);
        vec3 sn = getNormal(sp, t);


            	// Light direction vector.
	    vec3 ld = lp - sp;

        // Distance from respective light to the surface point.
	    float lDist = max(length(ld), .001);

    	// Normalize the light direction vector.
	    ld /= lDist;



        // Shadows and ambient self shadowing.
    	float sh = softShadow(sp, lp, sn, 16.);
    	float ao = calcAO(sp, sn); // Ambient occlusion.
        //sh = min(sh + ao*.25, 1.);

	    // Light attenuation, based on the distances above.
	    float atten = 1./(1. + lDist*.05);


    	// Diffuse lighting.
	    float diff = max( dot(sn, ld), 0.);
        //diff = pow(diff, 4.)*2.; // Ramping up the diffuse.

    	// Specular lighting.
	    float spec = pow(max(dot(reflect(ld, sn), rd ), 0.), 32.); 

	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow(clamp(1. - abs(dot(sn, rd))*.5, 0., 1.), 2.);

		// Schlick approximation. I use it to tone down the specular term. It's pretty subtle,
        // so could almost be aproximated by a constant, but I prefer it. Here, it's being
        // used to give a hard clay consistency... It "kind of" works.
		float Schlick = pow( 1. - max(dot(rd, normalize(rd + ld)), 0.), 5.);
		float freS = mix(.15, 1., Schlick);  //F0 = .2 - Glass... or close enough.        



        // Obtaining the texel color. 
	    vec3 texCol = vec3(0);   


        // Object: Forground or background.
        if(svObjID<.5){


            // The extruded propeller grid.

            // Coloring.

            vec2 sc = gScale;
            vec3 q = sp;
            vec2 iq = floor(q.xy/sc) + .5;
            q.xy -= iq*sc;
            int check = mod(iq.x + iq.y, 2.)<.5? 1 : 0;

            if(check==1) q.xy *= rot2(-gAng.x);
            else q.xy *= rot2(-gAng.y);


            // Random value based on cell ID.
            float rnd = (svGID.y + svGID.z)/4.;

            //rnd = floor(rnd*16.)/16.;

            #if PALETTE == 0
            // Spectrum.
            vec3 texCol1 = .5 + .45*cos(-rnd*6.2831 + vec3(0, 1, 2)*2. - 2.15);
            #elif PALETTE == 1
            // Golden brown.
            vec3 texCol1 = .5 + .45*cos(-rnd*6.2831/4.5 + vec3(0, 1, 2) + .35);
            #elif PALETTE == 2
            // Green and blue.
            vec3 texCol1 = .5 + .45*cos(-rnd*6.2831/4.5 + vec3(0, 1, 2) + .35);
            if(check == 1) texCol1 = texCol1.zyx;
            else texCol1 = texCol1.yxz;//vec3(dot(texCol1, vec3(.299, .587, .114)));
            //if(check == 1 && q.x>q.y) texCol1 = texCol1.yxz;
            //if(check == 0 && q.x>q.y) texCol1 = texCol1.yxz;
            #elif PALETTE == 3
            // Blinking earthtone quarter colors.
            vec2 iq2 = iq;
            if(q.x>0.) iq2 += 1./2.;
            if(q.y<0.) iq2 += 1./4.;
            rnd = hash21(iq2);
            float rnd2 = hash21(iq2 + .13);
            vec3 texCol1 = .5 + .45*cos(-rnd*6.2831/3. + vec3(0, 1, 2) + 1.65);
            texCol1 = mix(texCol1, mix(texCol1.xzy, texCol1, .25), 
                          smoothstep(.8, .9, sin(rnd2*6.2831 + iTime*2.)*.5 + .5));
            #elif PALETTE == 4
            // Mondrian.
            vec3 texCol1 = vec3(.7);
            if(check == 1 && q.x<q.y) texCol1 = vec3(1, .07, .06);
            if(check == 1 && q.x>q.y) texCol1 = vec3(0, .5, 1);
            if(check == 0 && q.x>q.y) texCol1 = vec3(1, .9, .1);
            /*
            // Alternative.
            vec3 texCol1 = vec3(.7);
            vec2 qID = mod(floor(svGID.yz*2.), 2.);
            if(qID.x == 1. && qID.y == 0.) texCol1 = vec3(1, .07, .06);
            if(qID.x == 0. && qID.y == 1.) texCol1 = vec3(0, .5, 1);
            if(qID.x == 1. && qID.y == 1.) texCol1 = vec3(1, .9, .1);
            */
            #else
            // White.
            vec3 texCol1 = vec3(.65);
            #endif

            // The outer propellor rim color.
            vec3 texCol2 = mix(vec3(1.5), texCol1, dot(texCol1, vec3(.299, .587, .114)));//tx/1.5;//
            #if PALETTE == 4
            texCol2 = vec3(.85);
            #endif

            // Applying the texture, if defined.
            #ifdef TEXTURE
            vec3 tx = tex3D(iChannel0, vec3(q.xy + iq/2. - .125, q.z)/2., sn);
            tx = smoothstep(-.1, .5, tx);
            texCol1 = mix(texCol1, texCol1*tx*1.75, .65);
            texCol2 = tx;
            #endif

            // Setting the outer and inner propellor colors.
            if(svGID.w == 1.) texCol = texCol2;
            else texCol = texCol1;





            // Constructing the dark propeller edge markings.          
            float ht = 1.;//hm(svGID.yz);
            vec2 lCoord = sp.xy - svGID.yz;
            sc = gScale;
            float lw = gScale.x*.06;//.05;
            float dS = abs(svfaceDist) - lw;

            // Tapering the sides, or not.
            #ifndef SIDES
            dS = max(dS, abs(sp.z + ht*.025*2.) - lw); // Just the rim.
            #endif



            if(svGID.w == 0.){
                // Rendering the propeller colors and dark edge markings. 
                texCol = mix(texCol, vec3(0), (1. - smoothstep(0., .005, dS))*.95);
                texCol = mix(texCol, texCol2*.7, (1. - smoothstep(0., .005, dS + .015)));
            }
            else {
                // Inner markings.
                float cir = length(lCoord) - lw/2.  -.005;
                texCol = mix(texCol, vec3(0), (1. - smoothstep(0., .007, cir)));
                texCol = mix(texCol, texCol2, (1. - smoothstep(0., .007, cir + .015)));
            }



        }
        else {

            // The dark floor in the background.
            //
            // Scaling, ID and local square cell coordinates.
            vec2 sc = gScale/2.*vec2(1, 1);
            vec2 q = sp.xy;// + vec2(sc.x/2.*1., sc.y/2.);
            //if(mod(floor(q.y/sc.y), 2.)==0.) q.x += sc.x/2.; // Brickwork.
            vec2 iq = floor(q/sc) + .5;
            q -= iq*(sc);

            // Two square samples for bump mapping purposes. In case it isn't obvious,
            // this is a very cheap and hacky procedure, but it's good enough.
            float sq = max(abs(q.x) - sc.x/2., abs(q.y) - sc.y/2.);
            vec2 ld2 = normalize(ld.xy);
            float sq2 = max(abs(q.x + ld2.x*.01) - sc.x/2., abs(q.y + ld2.y*.01) - sc.y/2.);
            float bord = abs(sq) - .005;
            bord = abs(bord - .015) - .005;

            float b = max((sq2 - sq)/.01, 0.); // Bump value.
            texCol = vec3(.05);
            #ifdef BUMP_BG
            texCol += b*b*.05; // Adding the bump value.
            #endif
            // Extra bordering.
            texCol = mix(texCol, texCol*2., (1. - smoothstep(0., .005*4., bord)));
            texCol = mix(texCol, texCol/8., (1. - smoothstep(0., .005, bord)));

            // If the texture is set, apply it.
            #ifdef TEXTURE
            vec3 tx = getTex(sp.xy/3.);
            tx = smoothstep(-.1, .55, tx);
            texCol *= tx*2.;
            #endif

        }

        // Greyscale values.
        #ifdef GRAYSCALE
        texCol = vec3(1.1, .95, .8)*dot(texCol, vec3(.299, .587, .114));
        #endif


        // Ramping up the diffuse values, depending on what part of the scene we've hit.
        vec2 sc = gScale;
        float lw = gScale.x*.06;
        float dS = abs(svfaceDist) - lw;

        #ifdef METALLIC

        if(svObjID == 0.){
           // Outer and inner propeller diffuse values.
           diff = mix(diff, pow(diff, 4.)*1.5, 1. - smoothstep(0., .005, dS));
           if(svGID.w == 1.) diff = pow(diff, 4.)*1.5;
           //diff = pow(diff, 4.)*1.5;
        }
        else {
           // Making the dark background more metallic.
           diff = pow(diff, 6.)*2.35;
        }
        #endif




        // Combining the above terms to procude the final color.
        col = texCol*(diff*sh + .3 + vec3(.25, .5, 1)*fre*0. + vec3(1, .97, .9)*spec*freS*2.*sh);


        #ifdef ENVIRONMENT
        // Fake environment mapping.
        vec3 cTex = envMap(reflect(rd, sn));

        if(svObjID == 0.){
            // Adding more environment lighting to the out propeller.
            col = mix(col + (col)*cTex*2., col + (col)*cTex*6., 1. - smoothstep(0., .005, dS));
        }
        else col = col + (col)*cTex*2.; // Just a bit of environment lighting to the background.
        #endif

        // Shading.
        col *= ao*atten;

        // Debug AO, shadows, etc.
        //col = vec3(ao);

	}


    // Rought gamma correction.
	fragColor = vec4(sqrt(max(col, 0.)), 1);

}

float PI=3.141592653589;

void addLight(float t,out vec3 col, vec2 mouse, float offset1, vec2 uv, vec3 barCol, float yOffset){
    vec3 lightCol=mix(vec3(1.,1.0,1.0),barCol,0.3);
    float lightY=0.1+(yOffset - t*0.1);//1.-mod(t*0.1+yOffset, 1.2)-yOffset+0.1;
    lightY=1.-mod(1.-lightY,1.1);

    float lightDst = distance(vec2(mouse.x-offset1,lightY),vec2(uv));
    float r = abs(sin(t*2.+yOffset*t*2.))*0.03+0.06;
    float lightF=smoothstep(r,0.,lightDst);
    col=mix(col,lightCol,lightF*1.5);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec3 col=vec3(0);
    float thk=0.01;
    vec3 col1=texture(iChannel0, uv).xyz;
    vec3 col2=texture(iChannel1, uv).xyz;
    //vec3 col1=vec3(0.);
    //vec3 col2=vec3(1.);
    vec3 barCol=vec3(0,1.0,1.0);
    float t = iTime;
    //t=0.;
    float offset1=sin(uv.y*PI*4.+t*7.)*0.005;
    offset1+=-cos(uv.y*PI*8.+t*3.)*0.007;
    offset1+=cos(uv.y*PI*8.+t*3.)*0.007 * cos(4.+uv.y*PI*3.+t*6.);//
    //offset1+=-sin(PI+uv.y*PI*13.+t*4.)*0.002;//
    //offset1+=-cos(7.+uv.y*PI*13.+t*14.)*0.003*sin(3.+uv.y*PI*3.+t*4.);//
    //float offset2=0.;//abs(sin(2.+uv.y*PI*14.))*0.015;
    float edgeCloseFactor = smoothstep(mouse.x-thk-offset1-0.03,mouse.x-offset1,uv.x);
    float barFactor = edgeCloseFactor* smoothstep(mouse.x+thk-offset1,mouse.x-offset1, uv.x);



    col=mix(col1,col2,step(mouse.x-offset1, uv.x));
    col=mix(col,barCol,barFactor);

    //addLight(t, col, mouse, offset1, uv, barCol,1.0);
    //addLight(t, col, mouse, offset1, uv, barCol,0.4);
    //addLight(t, col, mouse, offset1, uv, barCol,0.);



    // Output to screen
    fragColor = vec4(col,1.0);
}

