// oh, come on!  It can't be that easy.  Surely someone has done this before!

// some day I must go back and properly clean up these 1 letter local variable identifiers!

float angularSeg5(vec3 d, vec3 a, vec3 b)
{
    a = cross(d, a), b = cross(d, b);
    vec3 m = b - a;
    m = mix(b, a, clamp(dot(b, m) / dot(m, m), .0, 1.)); 
    return length(m);
}

