        L(3)
            D = min(D,length(.5-vec2(min(ceil(l=length(P.xy+.5)*s),s)-l,fract(P.z*s+.5*s)))/s),
            P.zxy=P,P.zx*= -1.;

        C += D*s*.025-4e-4;
