void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float x0 = (sin(iTime) + 1.0) / 2.0;
    float y0 = (cos(iTime) + 1.0) / 2.0;

    float x1 = (sin(-iTime) + 1.0) / 2.0;
    float y1 = (cos(-iTime) + 1.0) / 2.0;

    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 blob0 = vec2(x0, y0);
    vec2 blob1 = vec2(x1, y1);

    uv.y /= aspect;
    blob0.y /= aspect;
    blob1.y /= aspect;

    float sum = 0.0;

    sum += (1.0 / sqrt(pow(blob0.x - uv.x, 2.0) + pow(blob0.y - uv.y, 2.0))) * 0.05;
    sum += (1.0 / sqrt(pow(blob1.x - uv.x, 2.0) + pow(blob1.y - uv.y, 2.0))) * 0.05;

    // Output to screen
    vec3 color = vec3(sum - 1.5, sum - 3.0, sum);
    fragColor = vec4(color, 1.0);
}

//Hash2D based on: http://www.shadertoy.com/view/4djSRW

vec2 Hash2D(vec2 p)
{
 	p = vec2( dot(p, vec2(127.1, 311.7)),
              dot(p, vec2(269.5, 183.3)));
    return  -1.0 + 2.0 * fract(sin(p)*43758.5453123);
}

float PerlinNoise2D(vec2 seed)
{
    vec2 intFloorValue = floor(seed);
    vec2 intCeilValue = ceil(seed);
    vec2 fractValue = fract(seed);

    //vec2 interValue = smoothstep(0.0, 1.0, fractValue);
    vec2 interValue = fractValue*fractValue*fractValue*(fractValue*(fractValue*6.0-15.0)+10.0);

    return mix(mix(dot(Hash2D(intFloorValue), fractValue),
                   dot(Hash2D(intFloorValue + vec2(1.0, 0.0)), fractValue - vec2(1.0, 0.0)),
                   interValue.x),
               mix(dot(Hash2D(intFloorValue + vec2(0.0, 1.0)), fractValue - vec2(0.0, 1.0)),
                   dot(Hash2D(intCeilValue), fractValue - vec2(1.0, 1.0)),
                   interValue.x),
               interValue.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv+=0.5;

    float scaler = 10.;
	uv *= 2. * scaler;

    float amp = 50.;
    uv.x+=sin(iTime);
    uv.y+=cos(iTime);


    float value = PerlinNoise2D(uv+iTime);

    vec3 col = vec3(0.);
    if(uv.y < scaler*1.5)
    {
        col = vec3(1.0/value);
    }
    else if(uv.y < scaler* 2.0)
    {
        col = vec3(uv.x +uv.y+1.0/value);
    }   
    else if(uv.y < scaler* 2.5)
    {

        col = vec3(tan(1.0/value));
    }
    else
    {
        col = vec3(value * 0.5 + 0.5);
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265

#define REFLECTION_PLANES 6.0

// cosine based palette, by iq
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 smoothTransition(vec3 a, vec3 b, vec3 c, float t)
{
    float p = fract(t);
    int id = int(mod(t, 3.0));

    vec3 outCol = vec3(0,0,0);
    switch (id)
    {
        case 0:
            outCol = mix(a,b,smoothstep(0.0,1.0,p));
            break;
        case 1:
            outCol = mix(b,c,smoothstep(0.0,1.0,p));
            break;
        case 2:
            outCol = mix(c,a,smoothstep(0.0,1.0,p));
            break;
    }

    return outCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float line = 0.5;
    float yshift = 0.0;  //0.2
    float xshift = 0.3; //0.5 //0.05

    vec3 white = vec3(1.0,1.0,1.0);

    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(1.0,1.0,1.0);


    {
        uv -= vec2(0.5*iResolution.x/iResolution.y,0.5);


        float centerrepeat = 2.0*PI/REFLECTION_PLANES;

        float repeat = 2.0;

        vec2 ipol = vec2(atan(uv.y,uv.x),length(uv));

        ipol.x = abs(mod(ipol.x,centerrepeat)-centerrepeat*0.5);

        uv = vec2(ipol.y*cos(ipol.x),ipol.y*sin(ipol.x));

        vec4 bg1= vec4(palette((1.0-(ipol.y)+iTime*0.25),vec3(0.8, 0.5, 0.4),vec3(0.2, 0.4, 0.2),vec3(2.0, 1.0, 1.0),vec3(0.00, 0.25, 0.25)),1.0);

        uv*=7.0;    

        uv+=vec2(-iTime*xshift,iTime*yshift);

        uv.x= abs(mod(uv.x,repeat)-repeat*0.5);

        uv.y= abs(mod(uv.y,repeat)-repeat*0.5);

        vec2 coord = vec2(uv.y,uv.x);
        vec2 polar = vec2(atan(uv.y,uv.x),length(uv));

        coord = mix(coord, polar,sin(iTime)*0.5+0.5);

        vec3 pre1 = vec3(1.0,1.0,1.0)*polar.y*0.2;
        vec3 pre2 = palette((1.0-(polar.y)+iTime*0.05),vec3(0.2, 0.2, 0.2),vec3(0.5),vec3(2.0, 1.0, 1.0),vec3(0.00, 0.33, 0.67));

        float res = sin(6.0*coord.x+iTime*1.0) * (iMouse.x/iResolution.x) + (3.0 * iMouse.y/iResolution.y);

        vec3 col1 = mix(bg1.xyz,pre1,abs(abs(res-coord.y)-line));
        vec3 col2 = mix(bg1.xyz,pre2,abs(abs(res-coord.y)-line));
        vec3 col3  = texture( iChannel0, col1.x * 0.12 + coord*0.15 ).xyz;
        col = smoothTransition(col1,col2, col3,iTime * 0.2);
    }

    fragColor = vec4(col,1.0);
}

