#define SPHERE 1
#define TRIANGLE 2
#define INF 1e9
#define EPS 1e-3
#define N_SHAPES 3
#define N_LIGHTS 2
#define DEPTH 3
#define M_PI 3.14159265358979323846 
struct shape{
	vec3 q[3];
    int type;
    int material;
};


struct ray{
	vec3 o;
    vec3 d;
    float tMax;
    int depth;

};
shape make_sphere(vec3 center, float r){
	shape ret;
    ret.q[0] = center;
    ret.q[1].x = r;
    ret.type = SPHERE;
    ret.material = 0;
    return ret;
}

struct intersection{
    float t;
    vec3 normal;
    int material;
};

bool solve_quadratic(float a, float b, float c, out float tMin, out float tMax){
	float d = b*b-4.0f*a*c;
    if(d<0.0f)
        return false;
    tMin = (-b-sqrt(d))/(2.0f*a);
    tMax = (-b+sqrt(d))/(2.0f*a);
    return true;
}

bool intersect_sphere(inout ray r, in shape s, inout intersection i){
    vec3 delta = s.q[0] - r.o;
   	float c = dot(delta,delta) - s.q[1].x*s.q[1].x;
    float b = -2.0f*dot(r.d, delta);
    float a = dot(r.d, r.d);

    float tMin, tMax;

    if(solve_quadratic(a,b,c,tMin,tMax)){
        if(tMin>EPS && tMin<r.tMax){
            i.t = tMin;
            i.normal = normalize(i.t*r.d - delta);
            r.tMax = i.t;
            i.material = s.material;
            return true;
        } else if(tMax>EPS && tMax<r.tMax){
        	i.t = tMax;
            i.normal =  normalize(i.t*r.d - delta);
            r.tMax = i.t;
            i.material = s.material;
            return true;
        } else {
        	return false;
        }

    } else {
       return false;
    }    
	return false;
}



shape make_tri(vec3 v1, vec3 v2, vec3 v3){
	shape ret;
    ret.q[0] = v1;
    ret.q[1] = v2;
    ret.q[2] = v3;
    return ret;

}

ray getRay(vec2 fragCoord, mat3 rot){
	vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    ray r;
    r.o.x=0.0f;
    r.o.y=0.0f;
    r.o.z=3.0f;

	r.d.x = (uv.x-0.5f)*aspect;
    r.d.y = uv.y-0.5f;
    r.d.z = -1.0f;

    r.o = rot*r.o;
    r.d = rot*r.d;

    r.d = normalize(r.d);

    r.tMax = INF;
    return r;

}

ray makeRay(){
    ray r;
    r.o = vec3(0,0,0);
    r.d = vec3(0,0,0);
    r.tMax = INF;
    r.depth = 0;
    return r;
}
struct light{
	vec3 o;
    vec3 col;
};

struct material{
	vec3 diff_col;
    vec3 spec_col;
    vec3 trans_col;
    float d; //albedo
    float r; //reflectance
    float t; //transmittance

};
struct scene{
   	shape shapes[N_SHAPES];
	light lights[N_LIGHTS];
	material materials[2];
};



vec3 raycast(ray r, scene s){
    vec3 colFin = vec3(0,0,0);
    vec3 colMul = vec3(1,1,1);
    while(r.depth<=DEPTH){ 
        vec3 col = vec3(0,0,0);

   		bool hit = false;
   		intersection inter;
   		for(int i = 0; i<N_SHAPES; i++){
   		    bool prev_hit = hit;
   			hit = intersect_sphere(r, s.shapes[i], inter);
   		   	hit = hit || prev_hit;
   		}



   		if(hit){

            //for every light
            for(int i = 0 ; i<N_LIGHTS; i++){

           		hit = false;
                ray sr;
                sr.o = r.o + inter.t*r.d;
                sr.d = s.lights[i].o-sr.o;
                float l_sq = dot(sr.d,sr.d);
                sr.d = normalize(sr.d);
                sr.tMax = sqrt(l_sq);
                //for all the scene
           		for(int j = 0; j<N_SHAPES; j++){
                intersection dummy;
   		   		 	bool prev_hit = hit;
   					hit = intersect_sphere(sr, s.shapes[j], dummy);
   		   			hit = hit || prev_hit;
   				}

                //add light if not hit
                if(!hit){
                    vec3 l_dir = sr.d;

                    col += s.materials[inter.material].diff_col*s.lights[i].col
                        	*max(dot(l_dir, inter.normal),0.0f)*(1.0f/l_sq); 

                }


            }

            //global illumination
            if(s.materials[inter.material].r>EPS){
                ray rr;
                rr.o =  r.o + inter.t*r.d;
                rr.d = normalize(r.d - 2.0f*dot(inter.normal, r.d)*inter.normal);
                rr.depth = r.depth+1;
                rr.tMax = INF;
                r = rr;
                colFin+=col*colMul;

                colMul = colMul*s.materials[inter.material].spec_col*s.materials[inter.material].r;

            } else {
            	colFin+=col*colMul;
            }
        } else {
        	col = vec3(1,0,1);
            colFin+=col*colMul;
            break;
        }
    }
    return colFin;
}

material make_material(vec3 dif, vec3 spec, vec3 trans,float d, float r, float t){
	material mat;
    mat.diff_col = dif;
    mat.spec_col = spec;
    mat.trans_col = trans;
    mat.d = d;
    mat.r = r;
    mat.t = t;
    return mat;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    scene s;
    s.shapes[0] = make_sphere(vec3(0.0f,0.0f,-1000.5f), 1000.0f);
    s.shapes[1] = make_sphere(vec3(0.0f,0.0f,0.2f), 0.7f);
    s.shapes[2] = make_sphere(vec3(0.0f,0.9f,-0.1f), 0.4f);
    s.shapes[1].material = 1;
    s.materials[0] = make_material(vec3(.8f, .2f, .8f), vec3(1,1,1),vec3(0.9f,0.2f,0.0f), .8f, .2f, 0.0f);
    s.materials[1] = make_material(vec3(.5f, .5f, .5f), vec3(0.1f,0.1f,1.0f),vec3(0.0f,0.0f,0.9f), .8f, .2f, 0.0f);


    s.lights[0].o = vec3(2,2,2);
    s.lights[0].col = vec3(2,2,2);
    s.lights[1].col = vec3(4,1,1);
    s.lights[1].o = vec3(-4,-1,2);


	float theta = (iMouse.x/iResolution.x-0.5f)*2.0f*M_PI;
	float phi = iMouse.y/iResolution.y*M_PI;
    theta = iTime;
    phi= 1.0;

   	mat3 rot;

    float a = cos(-theta);
    float b = sin(-theta);
    float c = cos(phi);
    float d = sin(phi);

    rot[0][0]=a;
    rot[1][0]=-b*c;
    rot[2][0]= b*d;
    rot[0][1]= b;
    rot[1][1]= a*c;
    rot[2][1]= -a*d;
    rot[0][2]= 0.0f;
    rot[1][2]= d;
    rot[2][2]= c;

  	ray r = getRay(fragCoord, rot);


   	fragColor = vec4(raycast(r,s), 1.0f);

}

#define r(x) fract(sin(x*12.9898)* 43758.5453123)
#define rs(x) (r(x)*2.-1.)
#define t iTime
#define NOCLIP false
#define I 7

float lineWidth;

#define glow(x,f) (1. - pow(smoothstep(0.,(f)*lineWidth,abs(x)),0.2))

// Misc Functions

void pR(inout vec2 p,float a) {
	p=cos(a)*p+sin(a)*vec2(p.y,-p.x);
}

vec3 pal(float t) {
	return (0.5+0.5*cos(3.*(1.*t+vec3(0,0.33,0.67))));
}

// 2D Functions

// IQ's distance to line segment
float sdSegment2( vec2 p, vec2 a, vec2 b ) {
  vec2 pa = p-a, ba = b-a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h );
}

vec3 get2dColor( vec2 p ) {
  vec3 color = vec3(0), c; float d, md=1e2;
  vec4 l;
  for( int i=0; i < I; i++ ) {
    float a = r(float(i)+112.);
    l = vec4(rs(a+1.),rs(a+2.1),rs(a+3.8),rs(a+6.7))*2.;
    c = vec3(rs(a+1.),rs(a+2.1),r(a+3.8)*1.5+0.2);
    pR(l.xy, t*l.x);
    pR(l.zw, -t*l.z*0.1);
    d = sdSegment2(p, l.xy, l.zw);
    if(NOCLIP || d<md){ md = d; color += glow(d, 10.) * (0.5+0.5*cos(3.*(1.*length(l.xz)+vec3(0,0.33,0.67)))); }
    c.z *= sin(t*(c.z*c.x*2.1+0.1))*0.5+0.7;
    pR(c.xy,t*c.y);
    d = dot(p-c.xy,p-c.xy) - c.z;
    if(NOCLIP || d<md){ md = d; color += glow( d, 10. ) * (0.5+0.5*cos(3.*(1.*c.z*2.+vec3(0,0.33,0.67)))); }
  }
  return color;
}

void mainImage( out vec4 oc, in vec2 p ) {
  float minD = min(iResolution.x, iResolution.y);
  float maxD = max(iResolution.x, iResolution.y);
  lineWidth = minD*0.0003;
  vec2 st = 2.*(p-iResolution.xy*0.5) / maxD;

  vec3 col = get2dColor( 2.*(st) );

  oc = vec4(col*1.0,1);
}

#define PI 3.141693
#define CAVE_STRETCH 1.
#define RAYCASTING_STEPS 50
#define FOG_POWER 5.
#define FOG_STRENGTH .01
#define SHADOW_K .3
#define FIREFLY_SIZE 0.1
#define FIREFLY_REFLECTION_SIZE 0.3
#define FIREFLY_REFLECTION_STRENGTH 1.
#define WATER_LEVEL -3.2
#define REFRACTION_K 1.03
#define CAM_WIDTH 2500.
#define COLOR_SATURATION .5
#define TIME_SPEEDUP 2.;

float time;

// Sine function with frequency, amplitude and phase
float wave(float x, float freq, float amp, float phase) {
    return sin(x * freq + phase) * amp;
}

// Form of the tunnel
vec2 main_curve(float x) {
    x *= CAVE_STRETCH;
    // Some weird sine composition
    return vec2(
        wave(x, 0.0067, 2., 2.3) + wave(x, 0.1, 1., 0.), 
        wave(x, 0.01, 16., 2.) + wave(x, 0.23, 6., 0.2)
    );
}

// Checks if this coordinates is in solid block
bool checkVox(vec3 pos) {
    // Round coordinates
    pos = floor(pos);
    // Just another sine composition
    return distance(pos.yz, 
        vec2(
            wave(pos.x, 1., 1., 0.) + main_curve(pos.x).x, 
            wave(pos.x, 1., 1., 1.) + main_curve(pos.x).y
        )) > 6. + 2.*sin(pos.x * 0.1);
}

// Light source
vec3 light_pos;


// Rotate funcs
vec2 rotate(vec2 v, float a) {
	return vec2(v.x * cos(a) - v.y * sin(a), v.x * sin(a) + v.y * cos(a));
}

vec3 rotate(vec3 v, vec2 ang){
    v = v.zxy;
    vec2 p = rotate(vec2(v.x, v.z), ang.y);
	v = vec3(p.x, v.y, p.y);
    v = vec3(rotate(vec2(v.x, v.y), ang.x), v.z);
    return v.xzy;
}

// Actual ray direction, position
vec3 cam_pos;
vec3 ray_dir;
vec3 ray_pos;
vec3 normal;
bool contact = false;
struct ray {
    vec3 pos;
    vec3 dir;
    vec3 cam;
    vec3 normal;
};
int ray_stack_top = 0;
ray ray_stack[3];
vec3 ray_pos_prev;
vec3 ray_dir_prev;
vec3 cam_pos_prev;
vec3 normal_prev;

void ray_save() {
    ray_stack[ray_stack_top].pos = ray_pos;
    ray_stack[ray_stack_top].dir = ray_dir;
    ray_stack[ray_stack_top].cam = cam_pos;
    ray_stack[ray_stack_top].normal = normal;
    ray_stack_top++;
}

void ray_restore() {
    ray_stack_top--;
    ray_pos = ray_stack[ray_stack_top].pos;
    ray_dir = ray_stack[ray_stack_top].dir;
    cam_pos = ray_stack[ray_stack_top].cam;
    normal = ray_stack[ray_stack_top].normal;
    contact = false;
}

void ray_reflect(){
    ray_pos += normal * 0.01;
    cam_pos = ray_pos;
    ray_dir -= normal * dot(normal, ray_dir) * 2.;
}

void ray_refract() {
    float eta = REFRACTION_K;
    vec3 I = ray_dir;
    vec3 N = normal;
    float k = 1.0 - eta * eta * (1.0 - dot(N, I) * dot(N, I));
    k *= -1.;
    if (k >= 0.0)
        ray_dir = eta * I - (eta * dot(N, I) + sqrt(k)) * N;
}

// Step of raycasting
void raycastStep() {
    // Find corner coresponding to ray direction
    vec3 pos_cornered = floor(ray_pos);
    if (ray_dir.x > 0.) pos_cornered.x += 1.;
    if (ray_dir.y > 0.) pos_cornered.y += 1.;
    if (ray_dir.z > 0.) pos_cornered.z += 1.;

    // Get intersections
    vec4 intersections[3];
    intersections[0].xyz = ray_dir * (pos_cornered.xxx - ray_pos.xxx) / ray_dir.xxx + ray_pos;
    intersections[1].xyz = ray_dir * (pos_cornered.yyy - ray_pos.yyy) / ray_dir.yyy + ray_pos;
    intersections[2].xyz = ray_dir * (pos_cornered.zzz - ray_pos.zzz) / ray_dir.zzz + ray_pos;
    for (int i = 0; i < 3; i++) intersections[i].w = distance(ray_pos, intersections[i].xyz);

    // Find closest intersection
    float min_dist = intersections[0].w;
    int closest_intersection = 0;
    for (int i = 1; i < 3; i++) {
        if (intersections[i].w < min_dist) {
            closest_intersection = i;
            min_dist = intersections[i].w;
        }
    }

    ray_pos = intersections[closest_intersection].xyz;

    // Calculate normal vector
    normal = vec3(0.,0.,0.);
    switch (closest_intersection) {
        case 0: normal.x = -sign(ray_dir.x); break;
        case 1: normal.y = -sign(ray_dir.y); break;
        case 2: normal.z = -sign(ray_dir.z); break;
    }

    // If we should terminate, contact=true. If not, we apply micromovement to avoid ray_pos on very edge of voxel
    if (checkVox(ray_pos - normal*0.5))
        contact = true;
    else
        ray_pos -= normal*0.001;
}

// Returns color of a pixel
vec3 raycast() {
    vec3 col = vec3(0.4,0.4,0.4);

    // Find closest intersection
    for (int i = 0; i < RAYCASTING_STEPS && !contact; i++)
        raycastStep();

    // Texture & surface
    if (contact) {

        // Get right point on texture
        float k = 8.;
        vec3 pos = (ray_pos / k - floor(ray_pos / k));
        if (normal.x != 0.) pos.xy = pos.yz;
        if (normal.y != 0.) pos.xy = pos.xz;
        if (normal.z != 0.) pos.xy = pos.xy;

        // Texture
        col = texture(iChannel0, pos.xy).xyz * 0.4;

        // Surface
        normal += texture(iChannel1, pos.xy*4.).xyz * 0.4;
        normal = normalize(normal);
        //col = normal;

    }
    // Light
    float light = dot(light_pos - ray_pos, normal) * 5. / pow(distance(light_pos, ray_pos), 2.);
    col *= light;

    // Shadow
    bool shadow = false;
    // save ray parameters
    ray_save();
    // Find obastacles between light and current point
    cam_pos = ray_pos;
    ray_pos += normal * 0.01;
    ray_dir = normalize(light_pos - ray_pos);
    contact = false;
    float lim_dist = distance(cam_pos, light_pos);
    for (int i = 0; distance(cam_pos, ray_pos) < lim_dist && !contact; i++)
        raycastStep();
    shadow = contact;

    // restore ray parameters
    ray_restore();


    // Firefly
    {
        vec3 proj = cam_pos + dot(ray_dir, light_pos - cam_pos) * ray_dir;
        float dist = distance(proj, light_pos);
        if (distance(cam_pos, light_pos) < distance(cam_pos, ray_pos))
            col += vec3(FIREFLY_SIZE/pow(dist, 0.7),0.,0.).xxx;
    }



    // Firefly reflection
    ray_reflect();
    if (!shadow && dot(ray_dir, light_pos - cam_pos) > 0.) {
        vec3 proj = cam_pos + dot(ray_dir, light_pos - cam_pos) * ray_dir;
        float dist = distance(proj, light_pos);
        col += vec3(FIREFLY_REFLECTION_SIZE/pow(dist, .9),0.,0.).xxx * FIREFLY_REFLECTION_STRENGTH;
    }


    // Apply shadow
    if (shadow)
        col *= SHADOW_K;



    return col;
}

// Raycasting with water
vec3 raycast_water() {
    vec3 col = vec3(0,0,0);
    // Find intersection with water
    vec3 water_intersection = ray_dir * (WATER_LEVEL - ray_pos.yyy) / ray_dir.yyy + ray_pos;


    // Find closest intersection
    for (int i = 0; i < RAYCASTING_STEPS && !contact; i++)
        raycastStep();
    ray_save();

    // Water
    float opacity = 1.;
    if (dot(ray_dir, water_intersection-cam_pos) > 0. && distance(cam_pos, water_intersection) < distance(cam_pos, ray_pos)) {
        col += vec3(0.,0.05,0.1) * 0.5;
        normal = vec3(0,1.,0.);        
        contact = false;
        ray_pos = water_intersection;

        // Surface
        // Get right point on texture
        float k = 16.;
        vec3 pos = (ray_pos / k - floor(ray_pos / k));
        normal += 
            texture(iChannel2, (pos.xz + vec2(0., time * 0.1))/8.).xyz * 0.2 +
            texture(iChannel2, pos.xz + vec2(-time * 0.02, time * 0.02)/4.).xyz * 0.1 +
            texture(iChannel2, pos.xz + vec2(time * 0.02, -time * 0.02)).xyz * 0.05;
        normal = normalize(normal);

        ray_save();
        ray_reflect();
        col += raycast() * 0.8;

        opacity = 0.4;

        // Refraction
        ray_restore();
        ray_refract();
    } else {
        ray_restore();
        contact = true;
    }

    col += raycast() * opacity;
    // Calculate fog
    cam_pos = ray_stack[ray_stack_top - 1].cam;
    vec3 fog = vec3(.6, 0.8, 1.) * 0.5;
    float fogK = pow(distance(cam_pos, ray_pos) * 0.1, FOG_POWER) * FOG_STRENGTH;
    // Apply fog
    col = (col + fog * fogK) / (1. + fogK);

    return col;
}

// Coordinates based on x-pos (to process cam and light positions)
vec3 current_coordinates(float t) {
    return vec3(
        t,
        main_curve(t).xy
    );
}

// vec2-dir from direction vector
vec2 get_dir(vec3 dir) {
    return vec2(
        atan(dir.xz).y,
        atan(vec2(sqrt(dir.x * dir.x + dir.z * dir.z), dir.y)).y
    );
}

// Cubic saturation; x from 0 to 1
float saturate(float x) {
    float t = 2.*x-1.;
    return (3.*t-t*t*t)/4. + 0.5;
}

// Post-processing func
vec3 post_proc(vec3 col) {
    return col * (1. - COLOR_SATURATION) + 
        vec3(
            saturate(col.x),
            saturate(col.y),
            saturate(col.z)
        ) * COLOR_SATURATION;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    time = 235. + iTime * TIME_SPEEDUP;
    float x = time * 1.;
    // Camera position
    cam_pos = current_coordinates(x);
    // Light position
    light_pos = current_coordinates(x + 5. + 3.*sin(time));
    light_pos.y += 1. + 2.*sin(time * 0.33);
    // Some kind of camera velocity
    x += 5.;
    vec3 move_dir = normalize(current_coordinates(x+1.) - current_coordinates(x));
    // Camera direction
    vec2 cam_dir = vec2(2.*PI*(iMouse.x/iResolution.x - 0.5), PI* (1./2.0*(iMouse.y/iResolution.y - 1.0) +0.25)) * 2.; 

    float cam_width = CAM_WIDTH;
    cam_width /= iResolution.x;
    cam_dir = get_dir(move_dir);
    ray_pos = cam_pos ;

    // Normalized pixel coordinates
    vec2 uv = (fragCoord/iResolution.xy- vec2(0.5, 0.5)) * iResolution.xy * 0.001;
    // Rotation according to velocity
    uv = rotate(uv, -move_dir.z * 0.5);
    // Ray direction
    ray_dir = rotate(normalize(vec3(uv.xy, (1. ) / cam_width)), cam_dir);

    // Raycasting
    vec3 col = raycast_water();

    // Limiting color
    col.x = min(1., col.x);
    col.y = min(1., col.y);
    col.z = min(1., col.z);

    // Post-processing
    col = post_proc(col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float GetBallValue(vec3 ball, vec2 uv) {
    return ball.z/((ball.x-uv.x)*(ball.x-uv.x) + ((ball.y-uv.y)*(ball.y-uv.y)));//distance(ball.xy, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ball1 = vec3(0.9 + 0.5*cos(0.5*iTime),  0.5 + 0.3*sin(0.5*iTime), 0.02);
    vec3 ball2 = vec3(0.9 + 0.5*cos(0.37*iTime), 0.5 - 0.3*sin(0.2*iTime), 0.02);
    vec3 ball3 = vec3(0.9 + 0.5*cos(0.43*iTime), 0.5 + 0.3*sin(0.24*iTime), 0.01);

    float val
        = GetBallValue(ball1, uv)
        + GetBallValue(ball2, uv)
        + GetBallValue(ball3, uv);

    fragColor = vec4(0.1, 0.1, 0.1, 1);
    fragColor += step(1.0, val) * 1.4 * vec4(0.5 + 0.5*cos(0.5*iTime+uv.xyx+vec3(0.0,2.0,4.0)), 0);
}

