// Created by David Hoskins.
// See https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// Creative Commons Attribution-ShareAlike 4.0 International Public License
// Created by David Hoskins.
// See https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float lfnoise(vec2 t)
{
    vec2 i = floor(t);
    t = fract(t);
    t = smoothstep(c.yy, c.xx, t);
    vec2 v1 = vec2(hash12(i), hash12(i+c.xy)), 
        v2 = vec2(hash12(i+c.yx), hash12(i+c.xx));
    v1 = c.zz+2.*mix(v1, v2, t.y);
    return mix(v1.x, v1.y, t.x);
}

vec2 lf2(vec2 t)
{
    return .5+.5*vec2(lfnoise(t+.5*iTime), lfnoise(13.327+t+.5*iTime));
}

struct VoronoiCell2
{
    vec2 controlPoint;
    vec2 center;
    float incircleRadius;
    vec2 gridIndex;
    float dist;
};

vec2 scramble2(vec2 x)
{
    x = floor(x);
    return x + lf2(x);
}

vec3 dnvoronoi(vec2 x)
{
    float n,
        ret = 1.e3,
        df = 1.e3,
        d,
        rc = 1.e3;
    vec2 y = floor(x),
        pf = c.yy,
        ps = c.yy,
        p,
        nv;

    for(int i=-1; i<=1; i+=1)
        for(int j=-1; j<=1; j+=1)
        {
            p = scramble2(y+vec2(ivec2(i,j)));

            d = length(x-p);

            if(d < df)
            {
                df = d;
                pf = p;
            }
        }
    d = 1.e3;
    for(int i=-1; i<=1; i+=1)
        for(int j=-1; j<=1; j+=1)
        {
            p = scramble2(y+vec2(ivec2(i,j)));

            vec2 o = p-pf;
            float na = .5*length(o)-dot(x-pf,normalize(o));
            if(na < ret)
            {
                ret = na;
                nv = o;
            }
        }

    return vec3(ret, nv);
}

VoronoiCell2 voronoi2(vec2 x, float cellSize)
{
    // Scale to grid size 1
    x /= cellSize;

    VoronoiCell2 cell;

    float dmin = 1.e3, d;
    vec2 p;

    cell.gridIndex = floor(x);

    // Find nearest control point
    for(int i=-1; i<=1; ++i)
        for(int j=-1; j<=1; ++j)
        {
            p = scramble2(cell.gridIndex + vec2(ivec2(i,j)));
            d = length(x-p);

            if(d < dmin)
            {
                dmin = d;
                cell.controlPoint = p;
            }
        }

    // Approximate cell center
    // TODO: Is there a better way than this? 3 Sides determine an incircle, and the biggest one of these
    // is the polygon incircle. But that requires the polygon vertices! Those are annoying to determine.
    cell.center = cell.controlPoint;
    for(int i=0; i<55; ++i)
    {
        cell.center -= dnvoronoi(cell.center).yz/float(i+1)*.4;
    }

    // Find voronoi distance
    cell.dist = 1.e3;
    cell.incircleRadius = dnvoronoi(cell.center).x;
    for(int i=-1; i<=1; ++i)
        for(int j=-1; j<=1; ++j)
        {
            p = scramble2(cell.gridIndex + vec2(ivec2(i,j)));
            vec2 o = p-cell.controlPoint;
            cell.dist = min(cell.dist, .5*length(o)-dot(x-cell.controlPoint,normalize(o)));
        }

    // Rescale
    cell.controlPoint *= cellSize;
    cell.center *= cellSize;
    cell.dist *= cellSize;
    cell.incircleRadius *= cellSize;

    return cell;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;

    uv += 5.3;

    float size = 1./8.;
    VoronoiCell2 cell = voronoi2(uv, size);

    // Cell color, grid cell color and bounds
    vec3 col = mix(c.xxx-.4*c.yxx*(.5+.5*lfnoise(13.*cell.controlPoint))/*-.4*c.xxy*hash12(cell.gridIndex)*/, c.yyy, sm(cell.dist-.002));

    // Distance isolines
    col = mix(col, col - .4*c.yxx, sm(abs(mod(cell.dist+.005,.01)-.005)));

    // Cell centers
    float d = length(uv-cell.center)-.003;
    col = mix(col, c.xxx, sm(d));
    col = mix(col, c.yyy, sm(abs(d-.002)-.001));

    // Cell incircles
    col = mix(col, mix(col, c.yyy,.5), sm(length(uv-cell.center)-cell.incircleRadius));

    // Cell control points
    d = length(uv-cell.controlPoint)-.003;
    col = mix(col, c.yyy, sm(d));
    col = mix(col, c.xxx, sm(abs(d-.002)-.001));

    // Rectangular base grid
    col = mix(col, .1*c.xxx, sm(min(abs(uv.x/size-cell.gridIndex.x),abs(uv.y/size-cell.gridIndex.y))-.002/size));

    fragColor = vec4(col,1.0);
}

#define S(a,b,t) smoothstep(a,b,t)

mat2 Rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


// Created by inigo quilez - iq/2014
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
vec2 hash( vec2 p )
{
    p = vec2( dot(p,vec2(2127.1,81.17)), dot(p,vec2(1269.5,283.37)) );
	return fract(sin(p)*43758.5453);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    float n = mix( mix( dot( -1.0+2.0*hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                        dot( -1.0+2.0*hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                   mix( dot( -1.0+2.0*hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                        dot( -1.0+2.0*hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
	return 0.5 + 0.5*n;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    vec2 tuv = uv;
    tuv -= .5;

    // rotate with Noise
    float degree = noise(vec2(iTime*.1, tuv.x*tuv.y));

    tuv.y *= 1./ratio;
    tuv *= Rot(radians((degree-.5)*720.+180.));
	tuv.y *= ratio;


    // Wave warp with sin
    float frequency = 5.;
    float amplitude = 30.;
    float speed = iTime * 2.;
    tuv.x += sin(tuv.y*frequency+speed)/amplitude;
   	tuv.y += sin(tuv.x*frequency*1.5+speed)/(amplitude*.5);


    // draw the image
    vec3 colorWhite = vec3(.999, .999, .999);
    vec3 colorDeepBlue = vec3(.999, .999, .999);
    vec3 layer1 = mix(colorWhite, colorDeepBlue, S(-.3, .2, (tuv*Rot(radians(-5.))).x));

    vec3 colorRed = vec3(3.0, 0.4, 0.2);
    vec3 colorBlue = vec3(3.0, 0.4, 0.2);
    vec3 layer2 = mix(colorRed, colorBlue, S(-.3, .2, (tuv*Rot(radians(-5.))).x));

    vec3 finalComp = mix(layer1, layer2, S(.5, -.3, tuv.y));

    vec3 col = finalComp;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 规范化UV (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // 定义动画
    float delta = sin(iTime * 0.75) * 0.5;

    // 定义圆形场
    // 1
    vec2 circle1Center = vec2(0.78,0.5);
    float d1 = distance(uv,circle1Center);
    float f1 = 1. / (pow(d1, 2.) * 100.);
    // 2
    vec2 circle2Center = vec2(0.78 + delta,0.5);
    float d2 = distance(uv,circle2Center);
    float f2 = 1. / (pow(d2, 2.) * 100.);

    // 截取并平滑边缘
    float clampped = (clamp(f1 + f2, 0.7, 1.) - 0.7) / (1. - 0.7);
    clampped = clampped > 0.1 ? 1. : (clampped * 10.);

    // Output to screen
    fragColor = vec4(vec3(0.27, 0.6, 1.) * clampped, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) - 0.5;
    uv.x *= iResolution.x / iResolution.y;


    uv = fract(uv * 2.);

    float d = length(uv.xy - 0.5 );

    float c = smoothstep(0.1, 0.44 + abs(sin(iTime)) -0.2, d);
    c= fract(c * 45.);

    float b = smoothstep(0.01, 0.009, uv.x);
    float a = smoothstep(0.01, 0.009, uv.y);

    fragColor = vec4(c + b + a);
}

