const vec3 COLOR = vec3(0.25, 0.31, 0.49);
const vec3 BG = vec3(0.0, 0.0, 0.0);
const float ZOOM = 3.;
const int OCTAVES = 4;
const float INTENSITY = 2.;

float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9818,79.279)))*43758.5453123);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)), dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0 * fract(sin(st) * 7.);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // smootstep
    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


float fractal_brownian_motion(vec2 coord) {
	float value = 0.0;
	float scale = 0.2;
	for (int i = 0; i < 4; i++) {
		value += noise(coord) * scale;
		coord *= 2.0;
		scale *= 0.5;
	}
	return value + 0.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
	st *= iResolution.xy  / iResolution.y;    
    vec2 pos = vec2(st * ZOOM);
	vec2 motion = vec2(fractal_brownian_motion(pos + vec2(iTime * -0.5, iTime * -0.3)));
	float final = fractal_brownian_motion(pos + motion) * INTENSITY;
    fragColor = vec4(mix(BG, COLOR, final), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.0);
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 uv = (2.0 * fragCoord - iResolution.xy) /iResolution.y;
    vec3 mainColor = vec3(0.4, 0.5, 0.8);

    vec2 st = uv;
    uv.x += sin(st.y * iTime) * cos(st.y + iTime);
    uv.y += cos(st.x * iTime) * sin(st.x - iTime);

    float c = cos(iTime) * 2.0 - 1.0;
    float s = -sin(iTime) * 2.0 - 1.0;
    mat2 rot = mat2
    (
         c, -s, 
         s,  c
    );

    uv *= rot;
    uv.x += sin(iTime) * 2.0 - 1.0;
    uv.y -= cos(iTime) * 2.0 - 1.0;

    uv.x = mod(10.0, abs(uv.x));
    uv.y = mod(10.0, abs(uv.y));

    color = mix(vec3(1.0), mainColor, 1.0 - length(uv)) * (vec3(1.0) - vec3(abs(uv), 0.0));

    fragColor = vec4(color, 1.0);
}

float isInSector(vec2 tex, vec2 center, float dis, float t) // 是否在扇形中
{
	float res = 0.;
	float theta = radians(clamp(t, 0., 360.));

	float rotate_x = sin(theta/2.) + center.x;
	float rotate_y = cos(theta/2.) + center.y;
	vec2 half_theta_v = normalize(vec2(rotate_x, rotate_y) - center);
	vec2 c_top = vec2(0, 1);
	vec2 cur_v = normalize(tex - center);
	float cur_theta = acos(dot(half_theta_v, cur_v));
	return step(cur_theta, theta/2.) * step(0., -length(vec2(tex - center)) + dis);
}

float sdfCircle(vec2 tex, vec2 center, float dis)
{
    return dis - length(vec2(tex - center)) ;
}

float getPointLineDis(vec2 tex, vec2 p1, vec2 p2)
{
	if((tex.y - p2.y)*(p1.x - p2.x) == (p1.y - p2.y) * (tex.x - p2.x))
	{
		return 0.0;//共线
	}
	float len1 = length(tex -p1);
	float len2 = length(tex -p2);
	float len3 = length(p1 -p2);
	float half_p = 0.5 * (len1 + len2 + len3);
	float s = sqrt(half_p * (half_p - len1) * (half_p-len2) * (half_p-len3));
	float dis = s * 2. / len3;
	return dis;
}


float sdfPloygon(mat4x4 points, vec2 tex, float is_in ) // 计算多边形距离场
{
	float c=0.;
	float cur_dis = 100.;

	int j=0;
	int k=0;
	int temp_j = 0;
	int temp_k = 0;
	for (int i = 0; i < 3; i++) 
	{	
		j = (i*2) / 4;
		temp_j = i*2 - i*2 / 4 * 4;
		vec2 p = vec2(points[j][temp_j], points[j][temp_j + 1]);
		k = (i*2 + 2) / 4;
		temp_k = (i+1)*2 - (i+1)*2 / 4 * 4;
		vec2 p1 = vec2(points[k][temp_k], points[k][temp_k + 1]);

		float tmp = p1.x - p.x;
		vec2 cur_center = 0.5 *(p + p1);
		float dis_c = length(cur_center - tex);
		float length_line = length(p1 - p);
		float dis_l = getPointLineDis(tex, p, p1);
		float dis_2p = min(length(tex - p), length(tex - p1));
		float flag_in_range_line = dot(p-tex, p-p1) * dot(p1-tex, p1-p);
        cur_dis = min(cur_dis, max(dis_l * step(0., flag_in_range_line),  dis_2p * step(flag_in_range_line, 0.)));
    }

	return cur_dis * (1. - 2. *step(is_in, 0.5));
}

float IsInPloygon(mat4x4 points, vec2 tex ) // 判断是否在多边形内
{
	float c=0.;
	int j=0;
	int k=0;
	int temp_j = 0;
	int temp_k = 0;
	for (int i = 0; i < 3; i++) 
	{	
		float flag = 1.0f;
		j = (i*2) / 4;
		temp_j = i*2 - (i*2 / 4) * 4;
		vec2 p = vec2(points[j][temp_j], points[j][temp_j + 1]);
		k = (i*2 + 2) / 4;
		temp_k = (i+1)*2 - (i+1)*2 / 4 * 4;
		vec2 p1 = vec2(points[k][temp_k], points[k][temp_k + 1]);
		float tmp = p1.x - p.x;
		flag = step(0.00001, abs(tmp));
		float slope = (p1.y - p.y) / tmp;
		float cond1 = step(p.x, tex.x) * step(tex.x, p1.x);
		float cond2 = step(p1.x, tex.x) * step(tex.x, p.x);
		float above = step(tex.y, (slope * (tex.x - p.x) + p.y));
		float cur_val = above* flag *(cond1 + cond2);
		c = c + cur_val;
    }
	return mod(c, 2.);
}

float sdfTrigle(vec2 tex, vec2 p0, vec2 p1, vec2 p2)
{
	mat4x4 points;
	points[0].xy = p0;
	points[0].zw = p1;
	points[1].xy = p2;
	points[1].zw = p0;

	float is_in_tri = IsInPloygon(points, tex);
	float res = sdfPloygon(points, tex,  is_in_tri );
	return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;
    vec2 center = 0.5 * iResolution.xy;
    vec3 background_color = vec3(0.5, 0.5, 0.5);
    vec3 c1 = vec3(1.0, 0., 0.);
    vec3 c2 = vec3(0., 1., 0.);
    vec3 c3 = vec3(0., 0., 1.);
	float edge_width =iResolution.y * 0.025; 
	float radius = iResolution.y * 0.25;

    float cir_d = sdfCircle(uv, center, radius);
    float anti = fwidth(cir_d);
	vec3 col =  mix(background_color, c1, smoothstep(-anti, anti, cir_d ));
    float d1 = sdfCircle(uv, center, radius - edge_width);
	float anti1 = fwidth(d1);
	float edge_alpha = smoothstep(-anti1, anti1, d1);
	col = mix(col, background_color, edge_alpha);
    // Output to screen

	float is_in_0 = isInSector(uv, center, radius + anti, iTime*100.);

	if(is_in_0 <= 0.)
	{
		col = background_color.xyz;
	}

	float tri_d0 = 0.;
	float tri1_anti = 0.;
	float tri1_anti1 = 0.;

	float tri2_anti = 0.;
	float tri2_anti1 = 0.;

	vec3 col1 = col;
	vec3 col2 = col;
	float tri_radius = radius - edge_width - anti1;

	if((cir_d > (edge_width + anti1) || cir_d <0. - anti )&& iTime*100. > 360.)
	{
		vec2 p0 = vec2(center.x, center.y + tri_radius);
		vec2 p1 = vec2(center.x + tri_radius * cos(radians(30.)), center.y - tri_radius* sin(radians(30.)));
		vec2 p2 = vec2(center.x - tri_radius * cos(radians(30.)), center.y - tri_radius* sin(radians(30.)));

		tri_d0 = sdfTrigle(uv, p0, p1, p2);
		tri1_anti = fwidth(tri_d0);
		col1 = mix(background_color, c2, smoothstep(-tri1_anti, tri1_anti,tri_d0 ));

		tri1_anti1 = fwidth(tri_d0 - edge_width);
		col1 = mix(col1, background_color, smoothstep(-tri1_anti1, tri1_anti1,tri_d0 - edge_width ));

		float is_in_1 = isInSector(uv, center, radius + tri1_anti, iTime*100. - 360.);

		if(is_in_1 <= 0.)
		{
			col1 = background_color.xyz;
		}
		col = col1;
	}

	vec2 p0 = vec2(center.x - tri_radius * cos(radians(30.)),  center.y + tri_radius * sin(radians(30.)));
	vec2 p1 = vec2(center.x + tri_radius * cos(radians(30.)), center.y + tri_radius* sin(radians(30.)));
	vec2 p2 = vec2(center.x , center.y  - tri_radius);

	float tri_d1 = sdfTrigle(uv, p0, p1, p2);
	tri2_anti = fwidth(tri_d1);
	tri2_anti1 = fwidth(tri_d1 - edge_width);

	bool flag0 = tri_d0 > edge_width + tri1_anti || tri_d0 < 0. - tri1_anti1;
	bool flag1 = (tri_d0 <= edge_width + tri1_anti && tri_d0 >= 0. - tri1_anti1) &&  (tri_d1 <= edge_width + tri2_anti && tri_d1 >= 0. - tri2_anti1);

	if((cir_d > (edge_width + anti1) || cir_d < 0. - anti ) && (flag0 || flag1 ) && iTime*100. > 720.)
	{

        if(flag1)
        {
            col2 = mix(col, c3, smoothstep(-tri2_anti, tri2_anti,tri_d1 ));
            col2 = mix(col2, col, smoothstep(-tri2_anti1, tri2_anti1,tri_d1 - edge_width ));
        }
        else
        {
            col2 = mix(background_color, c3, smoothstep(-tri2_anti, tri2_anti,tri_d1 ));
            col2 = mix(col2, background_color, smoothstep(-tri2_anti1, tri2_anti1,tri_d1 - edge_width ));
        }

		float is_in_2 = isInSector(uv, center, radius + tri2_anti1, iTime*100. - 720.);

		if(is_in_2 <= 0.)
		{
			col2 = col;
		}
		col = col2;
	}

    fragColor = vec4(col,1.0);
   }

vec3 lerp(vec3 colorone, vec3 colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}
#define lineSpeed 0.2
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 50.0*(fragCoord-0.5)/iResolution.x; //Center the UV and make it bigger
    //P1 and P2 are point 1 and point 2 positions
    vec2 p1 = vec2(22.0+sin(iTime*1.123456789*lineSpeed)*12.5,15.0+sin(iTime*1.3210987654*lineSpeed)*10.0);
    vec2 p2 = vec2(33.0+sin(iTime*1.987654321*lineSpeed)*12.5,15.0+sin(iTime*1.6789012345*lineSpeed)*10.0);
    vec2 dir = normalize(p2-p1);

    float h = min(1.0,max(0.0,dot(dir,uv-p1)/length(p1-p2))); //from 0.0 to 1.0 wich point is closer p1 or p2
    float d = length((uv-p1)-h*(p2-p1));//<- This line is possibly why scientist believe in god.
    //The SDF is already on d
    float mask = smoothstep(0.95,1.05,d);

    //Add it some color
    vec3 color1=vec3(0.0,0.0,1.0);
    vec3 color2=vec3(0.0,1.0,1.0);

    vec3 color3=vec3(1.0,0.0,1.0);
    vec3 color4=vec3(1.0,1.0,0.0);

    vec3 bg1=vec3(0.0,0.5,0.75);
    vec3 bg2=vec3(0.0,0.75,1.0);

    fragColor = vec4(lerp(lerp(color1,color2,d),lerp(color3,color4,d),h)*(1.0-mask)+mask*lerp(bg1,bg2,(sin(-iTime*8.0-d*3.1415)*0.5+0.5)),1.0);

}



