void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 color = vec3(0.0);

  float angle = atan(-fragCoord.x + 0.25, fragCoord.x - 0.5) * 0.05;
  float len = length(fragCoord * .0125 -vec2(0.5, 0.25))*.7;

  color.r += (len * 40.0 + angle * 40.0 + iTime);
  color.g += cos(len * 3.0 + angle * 6.0 - iTime);
  color.b += cos(len * 50.0 + angle * 50.0 + 3.0);

  fragColor = vec4(color, 1.0);
