#define AA .002
#define PI 3.1415926
#define MIN_GEAR_RADIUS .1
#define MIN_GEAR_DIVISION 16.
#define TOOTH_LENGTH .01
#define GEAR_ROTATION_SPEED .5
#define SHADOW_MODIFIER .8

#define CYAN vec3(0., 1., 1.)
#define MAGENTA vec3(1., 0., 1.)
#define YELLOW vec3(1., 1., 0.)
#define RED vec3(1., 0., 0.)
#define GREEN vec3(0., 1., 0.)
#define BLUE vec3(0., 0., 1.)
#define ORANGE vec3(1., .75, .25)
#define PINK vec3(1., .25, .75)
#define WHITE vec3(1.)
#define BLACK vec3(0.)

