// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

mat2x2 createRotationMatrix(float rotation) {
    return mat2x2(
        cos(rotation), -sin(rotation),
        sin(rotation), cos(rotation)
    );
}

float getGearRadius(float gearIndex) {
    float randValue = hash(vec2(gearIndex, gearIndex));
    if (randValue < .333) {
        return MIN_GEAR_RADIUS;
    } else if (randValue < .666) {
        return MIN_GEAR_RADIUS * 2.;
    } else {
        return MIN_GEAR_RADIUS * 4.;
    }
}

vec4 getGearLocations(float gearLinkValue, float firstIndex, float secondIndex) {
    vec2 first = vec2(0.);

    float radialOffsetIncrement = PI / MIN_GEAR_DIVISION * 2.;
    float maxOffset = PI / 4.;
    float maxIncrements = 4.;
    float radialOffset = radialOffsetIncrement * floor(maxIncrements * hash(vec2(gearLinkValue * gearLinkValue, gearLinkValue * 2.)));
    float rotationVariance = PI / 2.;
    float rotation = mix(rotationVariance / -2., rotationVariance / 2., hash(vec2(gearLinkValue * gearLinkValue, gearLinkValue * 2.)));

    mat2x2 rotationMatrix = createRotationMatrix(radialOffset);

    float dist = getGearRadius(firstIndex) + getGearRadius(secondIndex);

    vec2 second = (vec2(dist, 0.) * rotationMatrix) + first;

    return vec4(first.x, first.y, second.x, second.y);
}

vec3 getRandomColor(float seed) {
    seed = clamp(seed, 0., 1.);
    float colorCount = 6.;
    float colorIncrement = 1.f / colorCount;
    float seedFloor = floor(seed / colorIncrement) * colorIncrement;
    float lerpVal = smoothstep(seedFloor, seedFloor + colorIncrement, seed);

    if (seed <= colorIncrement * 1.) {
        return mix(RED, YELLOW, lerpVal);
    } else if (seed <= colorIncrement * 2.) {
        return mix(YELLOW, GREEN, lerpVal);
    } else if (seed <= colorIncrement * 3.) {
        return mix(GREEN, CYAN, lerpVal);
    } else if (seed <= colorIncrement * 4.) {
        return mix(CYAN, BLUE, lerpVal);
    } else if (seed <= colorIncrement * 5.) {
        return mix(BLUE, MAGENTA, lerpVal);
    } else {
        return mix(MAGENTA, vec3(1.), lerpVal);
    }
}

float getPegSmoothStep(vec2 uv, vec2 gearLocation, float gearIndex) {
    float dist = distance(uv, gearLocation);
    float holeRadius = .03;

    if (dist < holeRadius) {
        return smoothstep(holeRadius - AA, holeRadius - AA * 2., dist);
    }
}

bool getHasSpokes(float gearIndex) {
    return hash(vec2(gearIndex * .23, gearIndex + 89.)) < .75;
}

vec2 rotateAroundPoint (vec2 anchor, vec2 p, float rads) {
    mat2x2 rotationMatrix = createRotationMatrix(rads);
    return (p - anchor) * rotationMatrix + anchor;
}

int getSpokeCount(float gearIndex) {
    return int(mix(3., 8., hash(vec2(gearIndex * -.118, gearIndex + 2.))));
}

float getGearSmoothStep(vec2 uv, vec2 gearLocation, float gearIndex, mat2x2 rotationMatrix) {
    float dist = distance(uv, gearLocation);

    float gearRadius = getGearRadius(gearIndex);

    float gearProportion = gearRadius / MIN_GEAR_RADIUS;
    float gearDivisions = gearProportion * MIN_GEAR_DIVISION * 2.;

    uv = ((uv - gearLocation) * rotationMatrix) + gearLocation;

    float rads = atan(uv.y - gearLocation.y, uv.x - gearLocation.x);

    float minVal = gearRadius - TOOTH_LENGTH;
    float maxVal = gearRadius + TOOTH_LENGTH;

    float val = 0.;
    int spokeCount = getSpokeCount(gearIndex);
    float innerRadiusDimension = minVal * .4 * hash(vec2(gearIndex, gearIndex));
    float outerRadiusDimension = minVal * mix(.7, .85, hash(vec2(-gearIndex, -gearIndex)));
    float spokeWidth = minVal * mix(.02, .1, hash(vec2(gearIndex, -gearIndex)));
    if (getHasSpokes(gearIndex)) {
        bool renderRadius = false;
        for (int i=0; i<spokeCount && !renderRadius; ++i) {
            vec2 rotatedUv = rotateAroundPoint(gearLocation, uv, float(i) * 2. * PI / float(spokeCount));
            if (rotatedUv.x < gearLocation.x && abs(rotatedUv.y - gearLocation.y) < spokeWidth) {
                renderRadius = true;
            }
        }

        if (!renderRadius) {
            if (dist < innerRadiusDimension) {
                return smoothstep(innerRadiusDimension, innerRadiusDimension - 2. * AA, dist);
            }

            if (dist > outerRadiusDimension && dist <= minVal) {
                return smoothstep(outerRadiusDimension, outerRadiusDimension + 2. * AA, dist);
            }

            if (dist < minVal) {
                float spokeVal = 0.;
                for (int i=0; i<spokeCount && !renderRadius; ++i) {
                    vec2 rotatedUv = rotateAroundPoint(gearLocation, uv, float(i) * 2. * PI / float(spokeCount));
                    if (rotatedUv.x < gearLocation.x) {
                        spokeVal = mix(spokeVal, 1., smoothstep(spokeWidth + AA, spokeWidth, abs(rotatedUv.y - gearLocation.y)));
                    }
                }

                return spokeVal;
            }
        }
    }

    float radIncrements = 2. * PI / gearDivisions;

    float radIndex = floor(rads / radIncrements);
    float toothPos = fract(rads / radIncrements);

    // max tooth width is .5
    float toothWidth = .4;

    if (mod(radIndex, 2.) == 0.) {
        float toothVal = smoothstep(toothWidth / 2., 1. - toothWidth / 2., toothPos);
        gearRadius = mix(minVal, maxVal, toothVal);
    } else {
        float toothVal = smoothstep(toothWidth / 2., 1. - toothWidth / 2., toothPos);
        gearRadius = mix(maxVal, minVal, toothVal);
    }

    return mix(val, 1., smoothstep(gearRadius + AA, gearRadius - AA, dist));
}

vec3 getNextGearLocationAndIndex(float previousIndex, vec2 previousGearLocation) {
    float gearIndex = previousIndex + 1.;
    float gearLinkValue = hash(vec2(previousIndex, gearIndex));
    vec4 gearLocations = getGearLocations(gearLinkValue, previousIndex, gearIndex);
    vec2 gearLocation = previousGearLocation + (gearLocations.zw - gearLocations.xy);

    return vec3(gearLocation.x, gearLocation.y, gearIndex);
}

float circleSmoothStepFill(vec2 center, float radius, vec2 p, float antiAlias) {
	return 1.f - smoothstep(radius - antiAlias, radius + antiAlias, distance(center, p));
}

float lineSegmentSmoothStep(vec2 start, vec2 end, vec2 p, float lineThickness, float antiAlias, bool roundEnds) {
    float halfThickness = lineThickness / 2.f;
    if (dot(p-end, start - end) < 0.f || dot(p - start, end - start) < 0.f) {
        return !roundEnds ? 0.f : max(
			circleSmoothStepFill(start, halfThickness, p, antiAlias),
			circleSmoothStepFill(end, halfThickness, p, antiAlias)
        );  
    }

    vec2 lineVec = normalize(end - start);
    vec2 pVec = normalize(p - start);

    float angle = acos(dot(lineVec, pVec) / length(lineVec) * length(pVec));
    float distFromLine = sin(angle) * distance(start, p);

    return 1.f - smoothstep(lineThickness / 2.f - antiAlias, halfThickness + antiAlias, distFromLine);
}

vec4 getPreceedingGearsColor(
    vec4 inColor,
    float referenceFirstIndex, 
    float referenceSecondIndex,
    vec4 referenceLocations,
    int gearsToRender,
    vec2 uv,
    float rotationSpeed,
    bool isShadow) {

    vec4 outColor = inColor;

    for (int i=0; i<gearsToRender; i++) {
        float targetIndex = referenceFirstIndex - 1.;
        float linkValue = hash(vec2(targetIndex, referenceFirstIndex));

        bool isEven = floor(mod(targetIndex, 2.)) <= 0.1;

        float gearRadius = getGearRadius(targetIndex);
        float gearProportion = gearRadius / MIN_GEAR_RADIUS;
        float gearDivisions = gearProportion * MIN_GEAR_DIVISION * 2.;

        float startingAngle = isEven ? PI / gearDivisions : PI / gearDivisions;

        float gearSizeSpeedAmplification = MIN_GEAR_RADIUS / gearRadius;

        float rotation = rotationSpeed * gearSizeSpeedAmplification;
        if (isEven) {
            rotation *= -1.;
        }

        mat2x2 rotationMatrix = createRotationMatrix(startingAngle + rotation);

        vec3 gearColor = isShadow ? (outColor * SHADOW_MODIFIER).rgb : getRandomColor(linkValue);

        vec4 targetReferenceFirstLocations = getGearLocations(linkValue, targetIndex, referenceFirstIndex);
        vec2 targetLocation = targetReferenceFirstLocations.xy - targetReferenceFirstLocations.zw + referenceLocations.xy;

        outColor = mix(outColor, vec4(gearColor, 1.), getGearSmoothStep(uv, targetLocation, targetIndex, rotationMatrix)); 

        referenceFirstIndex = targetIndex;
        referenceSecondIndex = targetIndex - 1.;
        referenceLocations = vec4(targetLocation.x, targetLocation.y, referenceLocations.x, referenceLocations.y);
    }

    return outColor;
}

vec4 getPreceedingPegsColor(
    vec4 inColor,
    float referenceFirstIndex, 
    float referenceSecondIndex,
    vec4 referenceLocations,
    int gearsToRender,
    vec2 uv,
    bool isShadow) {

    vec4 outColor = inColor;

    vec3 pegColor = isShadow ? (outColor * SHADOW_MODIFIER).rgb : vec3(.2);

    for (int i=0; i<gearsToRender; i++) {
        float targetIndex = referenceFirstIndex - 1.;
        float linkValue = hash(vec2(targetIndex, referenceFirstIndex));

        vec4 targetReferenceFirstLocations = getGearLocations(linkValue, targetIndex, referenceFirstIndex);
        vec2 targetLocation = targetReferenceFirstLocations.xy - targetReferenceFirstLocations.zw + referenceLocations.xy;

        outColor = mix(outColor, vec4(pegColor, 1.), getPegSmoothStep(uv, targetLocation, targetIndex)); 

        referenceFirstIndex = targetIndex;
        referenceSecondIndex = targetIndex - 1.;
        referenceLocations = vec4(targetLocation.x, targetLocation.y, referenceLocations.x, referenceLocations.y);
    }

    return outColor;
}

vec4 getSucceedingGearsColor(
    vec4 inColor,
    float referenceFirstIndex, 
    float referenceSecondIndex,
    vec4 referenceLocations,
    int gearsToRender,
    vec2 uv,
    float rotationSpeed,
    bool isShadow) {

    vec4 outColor = inColor;

    for (int i=0; i<gearsToRender; i++) {
        float linkValue = hash(vec2(referenceFirstIndex, referenceSecondIndex));

        bool isEven = floor(mod(referenceFirstIndex, 2.)) <= 0.1;

        float gearRadius = getGearRadius(referenceFirstIndex);
        float gearProportion = gearRadius / MIN_GEAR_RADIUS;
        float gearDivisions = gearProportion * MIN_GEAR_DIVISION * 2.;

        float startingAngle = isEven ? PI / gearDivisions : PI / gearDivisions;

        float gearSizeSpeedAmplification = MIN_GEAR_RADIUS / gearRadius;

        float rotation = rotationSpeed * gearSizeSpeedAmplification;
        if (isEven) {
            rotation *= -1.;
        }

        mat2x2 rotationMatrix = createRotationMatrix(startingAngle + rotation);

        vec3 gearColor = isShadow ? (outColor * SHADOW_MODIFIER).rgb : getRandomColor(linkValue);

        outColor = mix(outColor, vec4(gearColor, 1.), getGearSmoothStep(uv, referenceLocations.xy, referenceFirstIndex, rotationMatrix)); 

        vec3 nextLocationAndIndex = getNextGearLocationAndIndex(referenceSecondIndex, referenceLocations.zw);

        referenceFirstIndex = referenceSecondIndex;
        referenceSecondIndex = referenceSecondIndex + 1.;
        referenceLocations = vec4(referenceLocations.z, referenceLocations.w, nextLocationAndIndex.x, nextLocationAndIndex.y);
    }

    return outColor;
}

vec4 getSucceedingPegsColor(
    vec4 inColor,
    float referenceFirstIndex, 
    float referenceSecondIndex,
    vec4 referenceLocations,
    int gearsToRender,
    vec2 uv,
    bool isShadow) {

    vec4 outColor = inColor;

    vec3 pegColor = isShadow ? (outColor * SHADOW_MODIFIER).rgb : vec3(.2);

    for (int i=0; i<gearsToRender; i++) {  
        outColor = mix(outColor, vec4(pegColor, 1.), getPegSmoothStep(uv, referenceLocations.xy, referenceFirstIndex)); 

        vec3 nextLocationAndIndex = getNextGearLocationAndIndex(referenceSecondIndex, referenceLocations.zw);

        referenceFirstIndex = referenceSecondIndex;
        referenceSecondIndex = referenceSecondIndex + 1.;
        referenceLocations = vec4(referenceLocations.z, referenceLocations.w, nextLocationAndIndex.x, nextLocationAndIndex.y);
    }

    return outColor;
}

float sinestep(float edge1, float edge2, float val) {
    float lower = edge1 < edge2 ? edge1 : edge2;
    float upper = edge1 < edge2 ? edge2 : edge1;

    if (val < lower) {
        return 0.;
    } else if (val > upper) {
        return 1.;
    }

    return -1. * cos(1. / (upper - lower));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.x / iResolution.y;

    uv.x *= aspectRatio;

    float travelTime = 2.;

    float dotRadius = .1;

    float timelerp = fract(iTime / travelTime);

    int gearsToRender = 16;

    float originFirstIndex = floor(iTime / travelTime);
    float originSecondIndex = originFirstIndex + 1.;
    float linkValue = hash(vec2(originFirstIndex, originSecondIndex));
    vec4 originLocations = getGearLocations(linkValue, originFirstIndex, originSecondIndex);
    vec2 firstLocation = originLocations.xy;
    vec2 secondLocation = originLocations.zw;

    float previousIndex = originFirstIndex - 1.;
    float prevLinkValue = hash(vec2(previousIndex, originFirstIndex));
    vec4 prevFirstLocations = getGearLocations(prevLinkValue, previousIndex, originFirstIndex);
    vec2 previousLocation = prevFirstLocations.xy - prevFirstLocations.zw + firstLocation;

    vec2 prevFirstMid = mix(previousLocation, firstLocation, .5);
    vec2 firstSecondMid = mix(firstLocation, secondLocation, .5);

    vec2 lerpA = mix(prevFirstMid, firstLocation, timelerp);
    vec2 lerpB = mix(firstLocation, firstSecondMid, timelerp);
    vec2 lerpC = mix(lerpA, lerpB, timelerp);

    // modify uv's by bezier curves between midpoints for smooth transitions
    uv += lerpC - vec2(.5 * aspectRatio, .5);

    float rotation = 15. * sin(iTime / 5.);

    fragColor = vec4(.8);

    float shadowDepth = .02;
    vec2 shadowUv = uv + vec2(shadowDepth);

    if (DRAW_SHADOWS) {
        fragColor = getSucceedingGearsColor(
            fragColor,
            originFirstIndex, 
            originSecondIndex,
            originLocations,
            gearsToRender / 2,
            shadowUv, 
            rotation,
            true);

        fragColor = getPreceedingGearsColor(
            fragColor,
            originFirstIndex, 
            originSecondIndex,
            originLocations,
            gearsToRender / 2,
            shadowUv,
            rotation,
            true);
    }

    fragColor = getPreceedingGearsColor(
        fragColor,
        originFirstIndex, 
        originSecondIndex,
        originLocations,
        gearsToRender / 2,
        uv,
        rotation,
        false);

    fragColor = getSucceedingGearsColor(
        fragColor,
        originFirstIndex, 
        originSecondIndex,
        originLocations,
        gearsToRender / 2,
        uv, 
        rotation,
        false);

    if (DRAW_SHADOWS) {
        fragColor = getSucceedingPegsColor(
            fragColor,
            originFirstIndex, 
            originSecondIndex,
            originLocations,
            gearsToRender / 2,
            mix(uv, shadowUv, .35),
            true);

        fragColor = getPreceedingPegsColor(
            fragColor,
            originFirstIndex, 
            originSecondIndex,
            originLocations,
            gearsToRender / 2,
            mix(uv, shadowUv, .35),
            true);
    }

    fragColor = getSucceedingPegsColor(
        fragColor,
        originFirstIndex, 
        originSecondIndex,
        originLocations,
        gearsToRender / 2,
        uv,
        false);

    fragColor = getPreceedingPegsColor(
        fragColor,
        originFirstIndex, 
        originSecondIndex,
        originLocations,
        gearsToRender / 2,
        uv,
        false);

}

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}


float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord- 0.5 * iResolution.xy)/iResolution.y;
    float a = atan(uv.y, uv.x);

    float sc = 24. / (2. + thc(1.5, 8. * mlength(uv) + iTime));
    float sc2 = -24. / (2. + thc(1.5, 8. * mlength(uv) + iTime));

    float m1 = mix(sc, sc2, .5 + .5 * ths(0.5, 0.3 / length(uv) + 3. * a - iTime));
    float m2 = mix(sc, sc2, .5 + .5 * ths(0.5, 6. * length(uv) + 3. * a - iTime));

    sc = mix(m1, m2, .5 + .5 * thc(1., 4. * length(uv)+ iTime));

    uv *= 0.55 * sc;
    //vec2 ipos = floor(uv) + 0.5;
    vec2 fpos = fract(uv) - 0.5;

    float l = 0.4 * mlength(fpos);
    float d = 0.1 * min(abs(fpos.x) , abs(fpos.y));
    float s = smoothstep(-l, l,
    0.1 - d + 0.1 * thc(2., 2. * mlength(uv) - 32. * a));
    s *= s;
    vec3 col = vec3(s);


    // Output to screen
    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926535897932;

float circle(vec2 uv, vec2 pos, float r, float blur){
    vec2 a = uv-pos;
    return smoothstep(r+blur, r-blur, sqrt(a.x*a.x + a.y*a.y));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    //Parameters
    int amount = 100;    //amount of circles
    float speed = 50.; //speed of circles
    float radius = 10.; //radius of circles
    float spacing = .0; //spacing between each circle
    float blur = 1.;    //blur of each circle

    float t = float(iFrame)/5000000.*speed;
    vec2 R = iResolution.xy;
    vec3 col = vec3(0.);
    vec2 pos1 = vec2(R.x/2., R.y/2.);

    //circle loop
    for(float i=1.;i<2.+float(amount);i++){
        col += circle(fragCoord, pos1, radius, blur);
        pos1 = vec2(sin(t*i*i), cos(t*i*i))*2.*radius+vec2(pos1);
    }
    //invert color every other circle
    col = sin(col*pi/2.);

    //cyan/red center circle
    col = abs(col-vec3((circle(fragCoord, vec2(R.x/2., R.y/2.), radius, blur)/2.), 0., 0.));

    fragColor = vec4(col,1.0);

    //prevents lag after too much time passes
    t = float(int(t)%1);
}

const float PI = 3.14;

float steps = 12.0;
vec3 HUEtoRGB(in float hue)
{
    // Hue [0..1] to RGB [0..1]
    // See http://www.chilliant.com/rgb2hsv.html
    vec3 rgb = abs(hue * 6. - vec3(3, 2, 4)) * vec3(1, -1, -1) + vec3(-1, 2, 2);
    return clamp(rgb, 0., 1.);
}
vec3 HSVtoRGB(in vec3 hsv)
{
    // Hue-Saturation-Value [0..1] to RGB [0..1]
    vec3 rgb = HUEtoRGB(hsv.x);
    return ((rgb - 1.) * hsv.y + 1.) * hsv.z;
}


vec2 applyTransformation(vec2 pos,float iteration){

    float angle = atan(pos.y,pos.x);
    float initialDist = length(vec3(pos,1)) * (iteration+1.0);
    float currentStep = mod(floor(initialDist * steps),steps);
    vec2 outVec = vec2(1);
    float sinDist = (sin(angle*(3.0 * currentStep) + iTime * sin(currentStep))*0.05 + 1.0);

    outVec.xy = pos.xy * sinDist;

    return outVec;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5,0.5);

    vec2 offset = (uv - center);
    offset.y *= iResolution.y/iResolution.x;

    offset *= 4.5;
    for(float i = 0.0; i < 4.0; i++){
    offset = applyTransformation(offset,i/0.1);
    }


    float initialDist = length(offset);
    float currentStep = mod(floor(initialDist * steps),steps);


    float clampedDist = step(fract(length(offset)*steps),0.5);


    vec3 col = HSVtoRGB(vec3(currentStep/steps,1,1));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec3 glw; //glow

float bx(vec3 p, vec3 s) //box
{
  vec3 q=abs(p)-s;
  return min(max(q.x,max(q.y,q.z)),0.) + length(max(q,0.));
}

vec2 mp(vec3 p) //scene
{

  float scl=0.8;

  for(int i=0;i<3;i++)
  {
    p.yz*=rot(scl-0.3);
    p.y=abs(p.y)-scl;
    p.x+=p.y*scl;
    scl-=abs(p.y)*0.2;
    p.xz*=rot(iTime*0.4);
  }

  float s = length(p-vec3(0,0,2));

  float b = bx(p,vec3(scl)) - 0.1;
  b*=0.5;
  b=min(s,b);
  s*=8.;
  glw += 0.01/(0.01*s*s)*normalize(p*p);

  return vec2(b,1);
}

vec2 tr(vec3 ro, vec3 rd, float z) //raymarch
{
  vec2 d=vec2(0);
  for(int i=0;i<256;i++)
  {
    vec2 s=mp(ro+rd*d.x);
    s.x*=z;d.x+=s.x;d.y=s.y;
    if(s.x<0.0001||d.x>64.)break;
  }
  return d;
}

vec3 nm(vec3 p) //get normal
{
  vec2 e=vec2(0.001,0); return normalize(mp(p).x - vec3(mp(p-e.xyy).x,mp(p-e.yxy).x,mp(p-e.yyx).x));
}

vec4 px(vec2 h, vec3 p, vec3 n, vec3 r) //shade pixel
{
  vec4 bg = vec4(0.1,0.1,0.8,0) + length(r*r)*0.5;
  if(h.x>64.) return bg;

  vec4 fc = vec4(0.4,0.4,01,1);

  vec3 ld = normalize(vec3(0.6,0.4,0.8));

  float diff = length(n*ld);
  float fres = abs(1.-length(n*r))*0.2;
  float spec = pow(max(dot(reflect(ld,n)*ld,-r),0.),6.);
  float ao = clamp(1.-mp(p+n*0.1).x*10.,0.,1.)*0.1;
  float sss = smoothstep(0.,1.,mp(p*ld*3.).x)*0.6;

  fc.rgb+=fc.rgb*sss;
  fc*=diff;
  fc+=spec;
  fc+=fres;
  fc-=ao;

  return fc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 ro = vec3(0,0,-5),rd=normalize(vec3(uv,1));
  vec3 cp,cn,cr,h=vec3(1);
  vec4 cc,fc=vec4(1);

  //adjust this value for different amounts of refraction
  //higher = more refraction; lower = less refraction (1.0 is none)
  //below 1.0 makes them.. inverted i think? kinda interesting
  float io = 1.4; 

  for(int i=0;i<4*2;i++) //more efficient transparency loop
  {
     h.xy=tr(ro,rd,h.z);cp=ro+rd*h.x;
     cn=nm(cp);cr=rd;ro=cp-cn*(0.01*h.z);
     rd=refract(cr,cn*h.z,h.z>0.?1./io:io);
     if(dot(rd,rd)==0.)rd=reflect(cr,cn*h.z);
     cc=px(h.xy,cp,cn,cr);h.z*=-1.;
     if(h.z<0.)fc.rgb=mix(fc.rgb,cc.rgb,fc.a);
     fc.a*=cc.a;if(fc.a<=0.||h.x>64.)break;
  }

  //various interesting effects to try
  fragColor = vec4(fc+glw.rgbb);
  //fragColor = vec4(fc+sqrt(glw.rgbb*0.6));
  //fragColor = vec4(fc+sqrt(glw.rgbb-0.005));
  //fragColor = vec4(fc*sqrt(glw.rgbb-0.005));
}

