// Quarternions
vec4 conjQ(vec4 a) 
{
    return vec4(-a.x,-a.y,-a.z,a.w);
}

// library def from C source
vec4 mulQ_c(vec4 q1,vec4 q2) 
{
    vec4 v = vec4(0);
    v.x =  q1.x * q2.w + q1.y * q2.z - q1.z * q2.y + q1.w * q2.x;
    v.y = -q1.x * q2.z + q1.y * q2.w + q1.z * q2.x + q1.w * q2.y;
    v.z =  q1.x * q2.y - q1.y * q2.x + q1.z * q2.w + q1.w * q2.z;
    v.w = -q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w;
    return v;
}

vec4 mulQ2(vec4 a,vec4 b) 
{
    return vec4( a.x * b.w + a.y * b.z - a.z * b.y + a.w * b.x,
                -a.x * b.z + a.y * b.w + a.z * b.x + a.w * b.y,
                 a.x * b.y - a.y * b.x + a.z * b.w + a.w * b.z,
                -a.x * b.x - a.y * b.y - a.z * b.z + a.w * b.w);
}

vec4 mulQ3(vec4 a,vec4 b) 
{
    return vec4(dot(a.xyzw, b.wzyx * vec4( 1, 1,-1, 1)),
                dot(a.xyzw, b.zwxy * vec4(-1, 1, 1, 1)),
                dot(a.xyzw, b.yxwz * vec4( 1,-1, 1, 1)),
                dot(a.xyzw, b.xyzw * vec4(-1,-1,-1, 1)));
}

#define CONJQ(a) vec4(-(a).x,-(a).y,-(a).z,(a).w)
#define NX(a) vec4(-(a).x,(a).yzw)
//#define NY(a) vec4((a).x,-(a).y,(a).zw)
#define NY(a) NX((a).yxzw).yxzw
//#define NZ(a) vec4((a).xy,-(a).z,(a).w)
#define NZ(a) NX((a).zyxw).zyxw

// with bit of swizzling (would imagine amortized in load opcode)
vec4 mulQ4(vec4 a,vec4 b)
{
    return vec4(dot(a, NZ(b.wzyx)), dot(a, NX(b.zwxy)),
                dot(a, NY(b.yxwz)), dot(a, CONJQ(b)));
}

// with mat4 operation on negated swizzled components
vec4 mulQ5(vec4 a,vec4 b) 
{
    return a*mat4(NZ(b.wzyx), NX(b.zwxy), NY(b.yxwz), CONJQ(b));
}

// GLSL vec4 dot(u,v)   = u.x * v.x + u.y * v.y + u.z * v.z + u.w * v.w
//           cross(u,v) = {u.y * v.z - v.y * u.z, u.z * v.x - v.z * u.x, u.x * v.y - v.x * u.y}
//                      = u.yzx * v.zxy - v.yzx * u.zxy
//                      = (u * v.yzx - v * u.yzx).yzx
#define CROSS(a,b) ((a * b.yzx - b * a.yzx).yzx)

// quarternion multiplication with cross() and dot() believe this is prefered approach
vec4 mulQ(vec4 a, vec4 b)
{
    vec3 u = a.xyz, v = b.xyz;
	return vec4(v * a.w + u * b.w + cross(u,v), a.w * b.w - dot(u,v));
}

#define RED vec4(1,0,0,0)
#define GRN vec4(0,1,0,0)
#define BLU vec4(0,0,1,0)

#define SC(a) vec2(sin(a),cos(a))
#define CS(a) vec2(cos(a),sin(a))

#define CONJQ(a) vec4(-(a).x,-(a).y,-(a).z,(a).w)
#define NX(a) vec4(-(a).x,(a).yzw)
//#define NY(a) vec4((a).x,-(a).y,(a).zw)
#define NY(a) NX((a).yxzw).yxzw
//#define NZ(a) vec4((a).xy,-(a).z,(a).w)
#define NZ(a) NX((a).zyxw).zyxw

#define MULQ(a,b) ((a)*mat4(NZ((b).wzyx), NX((b).zwxy), NY((b).yxwz), CONJQ(b)))
#define QROT2D(v,a) (MULQ(vec4(v,0,0),vec4(SC(a).xxxy)).xy)

// obtuse but offers slightly better fps(?!)
#define _QROTV vec2 _sc
//per mla
//#define QROTV2D(v,a) (MULQ(vec4(v,(_sc=sin(vec2(a,a+PI2)))-_sc),_sc.xxxy).xy)
#define QROTV2D(v,a) (_sc=sin(vec2(a,a+PI2)),MULQ(vec4(v,0,0),_sc.xxxy).xy)


void mainImage( out vec4 k, in vec2 p )
{
    float t = iTime;
    vec2 ir = iResolution.xy, 
          c = p-ir.xy/2.; c /= ir.y;

    vec2 v = c;                  // use v for dynamic coords
    v *= 9.+t;                   // zoom out 

    float rt = length(c)*t;      // increasing rotation radially produces spiral

// mat2 based rotation
//    v *= rot2D(rt);
//    v *= rot(rt);

// quarternion based rotations.
//    v = mulQ(vec4(CS(rt).yxxx),vec4(0,0,v)).xy;
//    v = mulQ(vec4(SC(rt).xyxx),vec4(0,0,v)).xy;
//    v = mulQ(vec4(v,0,0),vec4(CS(rt).xxyx)).xy;
//    v = mulQ(vec4(v,0,0),vec4(SC(rt).xxxy)).xy;
//    v = MULQ(vec4(v,0,0),vec4(SC(rt).xxxy)).xy;
//    v = QROT2D(v,rt);
    _QROTV; v = QROTV2D(v,rt);

//   v *= rot(-rt); //opposite rotation i.e should result in original coords

    vec2 d = v*v;
    float e = d.x-d.y-1.;        // unit hyperbola  x^2 - y^2 = 1

    k  = RED *  vec4(e < t-3.);  // including t animates the black boundary stripe
    k += GRN * float(e > t+3.);

    k += mulQ(v.xyyy,k);         // makes things a little more colourful
}

// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Slice(p,a) dot(p,vec2(cos(a),sin(a)))
#define S(p,d,b) smoothstep(fwidth(p.y),0.0,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define MATERIAL 0

#define ZERO (min(iFrame,0))

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
    const vec3 s = vec3(27, 111, 57);
    vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdSimpleCylinder(vec3 p, float r, float h){
    float d = length(p.xz)-r;
    d = max(abs(p.y)-h,d);
    return d;
}

float baseOutsole(vec3 p, float h){
    vec3 prevP = p;

    p.xz*=Rot(radians(17.0));
    p.z*=0.4;
    p.x*=0.57;
    p.x+=0.03;
    p.z-=0.07;

    float d =sdSimpleCylinder(p,0.1,h);

    p = prevP;
    p.xy*=Rot(radians(90.0));
    p.y*=1.5;
    p.z*=1.2;
    float d2 = sdCappedCylinder(p-vec3(0.0,0.0,-0.25),h,0.2);
    d = opSmoothUnion(d, d2,0.13);

    p = prevP;
    d = max(abs(p.y)-h,d);

    return (d)*0.6;
}

float outsole(vec3 p){
    p.z*=0.9;
    vec3 prevP = p;

    float d = baseOutsole(p-vec3(0.0,0.02,0.),0.06);

    // wave traction pattern
    p.z+=sin(p.x*100.0)*0.01;
    p.z = mod(p.z,0.08)-0.04;
    float d2 = sdBox(p,vec3(0.2,0.03,0.03))*0.6;


    d = max(d,d2);
    p = prevP;
    d2 = baseOutsole(p-vec3(0.0,0.035,0.0),0.035);
    d = min(d,d2);

    p = prevP;
    float a  = radians(4.0);
    p.y-=0.06;
    float mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(mask,d);

    p = prevP;
    a  = radians(-8.0);
    p.y+=0.07;
    mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(-mask,d);

    p = prevP;
    a  = radians(10.0);
    p.y+=0.08;
    mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(-mask,d);

    return d;
}


float upperBase(vec3 p){
    p.z*=0.9;
    vec3 prevP2 = p;
    p.y+=sin(p.z*300.0)*0.005;
    p.x+=cos(p.y*70.0)*0.005;
    vec3 prevP = p;

    //p.x*=1.;
    p.z*=0.8;

    float d =sdCappedCone(p-vec3(0.0,0.18,-0.15),0.13,0.15,0.06);

    p = prevP;
    p.xz*=Rot(radians(15.0));
    p.yz*=Rot(radians(-6.0));
    p.x*=1.2;
    p.z*=0.8;

    float d2 = sdEllipsoid(p-vec3(-0.05,0.1,0.14),vec3(0.2,0.08,0.2));
    d = opSmoothUnion(d,d2,0.1);//0.1

    p = prevP;
    p.xz*=Rot(radians(0.0));
    p.x*=1.1;
    p.z*=0.8;
    p.yz*=Rot(radians(-35.0));

    d2 = sdEllipsoid(p-vec3(0.0,0.21,-0.1),vec3(0.1,0.04,0.15));

    d = opSmoothUnion(d,d2,0.17);//0.15

    p = prevP2;
    p.xy*=Rot(radians(90.0));
    p.xz*=Rot(radians(18.0));
    p.y*=1.5;
    p.z*=1.0;
    d2 = sdCappedCylinder(p-vec3(-0.1,0.0,-0.2),0.001,0.2);
    d = opSmoothUnion(d, d2,0.07);

    p = prevP2;
    p.xy*=Rot(radians(90.0));
    p.xz*=Rot(radians(15.0));
    p.y*=1.3;
    p.z*=0.99;
    d2 = sdCappedCylinder(p-vec3(-0.04,0.0,-0.22),0.001,0.2);
    d = opSmoothUnion(d, d2,0.07);

    return d;
}

float upper(vec3 p){
    vec3 prevP = p;
    float d = upperBase(p);
    p.x*=1.5;
    p.y*=1.1;
    p.z*=1.12;

    float d2 = upperBase(p-vec3(0.0,0.02,0.0));
    d = max(-d2,d);

    p = prevP;

    p.x*=1.6;
    p.z*=0.7;

    d2 =sdCappedCone(p-vec3(-0.01,0.25,-0.1),0.14,0.14,0.09)*0.6;
    d = max(-d2,d);

    p = prevP;
    float a  = radians(8.0);
    p.y-=0.31;
    float mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(mask,d);

    p = prevP;
    p.yz*=Rot(radians(-12.0));
    p.x*=1.2;
    p.y*=1.2;
    p.z*=0.65;
    d2 = sdTorus(p-vec3(-0.005,0.35,-0.145),vec2(0.1,0.02));

    d = opSmoothUnion(d,d2,0.04);

    p = prevP;
    p.x*=1.4;
    p.z*=0.75;
    d2 =sdSimpleCylinder(p-vec3(-0.005,0.35,-0.115),0.1,0.2);
    d = max(-d2,d);

    return d*0.8;
}

vec2 GetDist(vec3 p) {
    p+=noise3d(p*600.0)*0.0002;
    vec3 prevP = p;

    p.y+=0.075;
    //p.z+=0.1;
    float d = outsole(p);
    float d2 = upper(p-vec3(0.0,0.0,0.0));
    d =  opSmoothUnion(d,d2,0.03);

    vec2 model = vec2(d,MATERIAL);

    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<3; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p.x+p.y);
}

vec3 evaTex(vec2 uv, vec3 col){
    uv.y+=iTime*0.1;
    vec2 prevUV = uv;
    uv*=30.0;

    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

    float n = Hash21(id);
    gv.y*=n;

   // gv.x+=cos(uv.y*5.0)*0.2+sin(uv.y*2.0)*0.05;
    float d = length(gv)-0.5*abs(n);
    if(n<0.5){
            col = mix(col,vec3(1.3),S(prevUV,d,0.0));
    }

    return col;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    if(mat == MATERIAL){
        col = diffuseMaterial(n,rd,p,vec3(0.7)*evaTex(p.xz+p.yy,col));
        //col = diffuseMaterial(n,rd,p,vec3(0.7,0.8,0.7));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy -.3;

    vec3 ro = vec3(0, 0, 1.0);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    if(iMouse.z>0.){
        ro.yz *= Rot(-m.y*3.14+1.);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        ro.xz *= Rot(radians(-5.0+iTime*20.0));

        //ro.yz *= Rot(radians(120.0));
        //ro.xz *= Rot(radians(45.0));
    }
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        //col *= exp( -0.038*d.x*d.x*d.x );//fog
    } else {
        //col = upperTex(uv,col);
    }


    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0);
}

// Fork of "Patarty Remix" by Lanza. https://www.shadertoy.com/view/3lXGD4
