// 2019-04-26 12:45:38

float random (in vec2 st){
    // Wrapping the random value to work within
    // the specified range.
    st = mod(st, 24.);
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(mix(a, b, u.x), mix(c, d, u.x), u.y);
}

float plasma (in vec2 st) {
    // Mapping the angle to a similar integer range.
    float value = 4.*noise(st/6.2831*6.);
    // Triangle function based smoothstep with derivative 
    // smoothing factor.
    value = abs(fract(value) - .5)*2. - .5;
    return smoothstep(0., fwidth(value), value);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float bounce = fract(iTime*1.2)-0.5; //
    bounce *= bounce;
	float boomr = pow(cos(bounce), 15.);
	float boomg = pow(cos(bounce), 25.);
	float boomb = pow(cos(bounce), 35.);

    vec2 st0 = vec2(.9, .5) - fragCoord.xy/iResolution.y;
    vec2 st = vec2(length(st0), atan(st0.x, st0.y));

	vec3 color = vec3((1.- boomg));
    color.x += plasma(vec2(log(st.x) * boomr, st.y + iTime*0.1)*4.0);
    color.y += plasma(vec2(log(st.x) * boomg, st.y + iTime*0.1)*4.0);
    color.z += plasma(vec2(log(st.x) * boomb, st.y + iTime*0.1)*4.0);

	fragColor = vec4(color*0.5,1.0);
    return;
}

// a any number , b any number , 0 <= t <= 1.0
float lerp (float a, float b, float t){

    //interpolation of two numbers
    return (1.0-t)*a + t*b;
}



vec3 lerpColor (vec3 c1 , vec3 c2 , float t){

    vec3 color_interpolation = vec3 ( lerp (c1.x,c2.x,t) , lerp (c1.y,c2.y,t) , lerp (c1.z,c2.z,t) ); 

    return  color_interpolation;
}




