    //Linear gradient parting line in x-axis
    //vec3 col = vec3 (lerpColor ( c1 , c2 , uv.y) ); 


    vec3 col = vec3 (lerpColor ( c1 , c2 , abs(sin(t-x)*cos(x+t-sin(t)) ) )); 



    //Play non-linear gradient with any function from 0.0 to 1.0
    //vec3 col = vec3 (lerpColor ( c1 , c2 , func ) );

    //vec3 col = vec3 (lerpColor ( c1 , c2 , sin(2.0*tanh(cos(t)-y)*y )*sin(4.0*x-t) ) ); 


