void march_o15436(inout float d,inout vec3 p,float dS, vec3 ro, vec3 rd, float _seed_variation_) {
    for (int i=0; i < 64; i++) {
    	p = ro + rd*d;
        dS = o15436_input_mfsdf(vec4(p,0.0), _seed_variation_).w;
        d += dS;
        if (d > 14.0 || abs(dS) < 0.001) break;
    }
}

vec3 raymarch_o15436(vec2 uv, float _seed_variation_) {
    uv-=0.5;
	vec3 cam=vec3(p_o15436_CamX,(sin(iTime*0.5)*0.8+1.3),(sin(iTime*0.9)*2.0))*p_o15436_CamZoom;
	vec3 lookat=vec3(p_o15436_LookAtX,p_o15436_LookAtY,p_o15436_LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*p_o15436_CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	float dS=0.0;
	march_o15436(d,p,dS,ro,rd, _seed_variation_);

    vec3 color=vec3(0.0);
	vec3 objColor=o15436_input_mfsdf(vec4(p,1.0), _seed_variation_).xyz;   // 1 - BaseColor (r,g,b,sdf) linear (0-1) 
	float objSpecular=o15436_input_mfsdf(vec4(p,4.0), _seed_variation_).x*0.2;  // 4 - Specular  (v,0,0,sdf)
	float objRoughness=o15436_input_mfsdf(vec4(p,5.0), _seed_variation_).x; // 5 - Roughness (v,0,0,sdf)
	vec3 light=normalize(vec3(p_o15436_SunX,p_o15436_SunY,p_o15436_SunZ));
	if (d<14.0) {
	    vec3 n=normal_o15436(p, _seed_variation_);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		color=max(vec3(p_o15436_AmbLight),max(l,p_o15436_AmbLight))*objColor+4.0*pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular;
		//reflection
		d=0.01;
		march_o15436(d,p,dS,p,ref, _seed_variation_);
		vec3 objColorRef=vec3(0);
		if (d<14.0) {
			objColorRef=o15436_input_mfsdf(vec4(p,1.0), _seed_variation_).xyz;
			n=normal_o15436(p, _seed_variation_);
			l=clamp(dot(-light,-n),0.0,1.0);
			objColorRef=max(l,p_o15436_AmbLight)*objColorRef;
		} else {
			objColorRef=o15436_input_hdri(equirectangularMap(ref.xzy), _seed_variation_).xyz;
		}
		color=mix(color,objColorRef,objSpecular);
	} else {
		color=o15436_input_hdri(equirectangularMap(rd.xzy), _seed_variation_).xyz;
	}
	return pow(color,vec3(1.0/p_o15436_Gamma));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
float minSize = min(iResolution.x, iResolution.y);
float _seed_variation_ = SEED_VARIATION;
vec2 UV = vec2(0.0, 1.0) + vec2(1.0, -1.0) * (fragCoord-0.5*(iResolution.xy-vec2(minSize)))/minSize;
vec3 o15436_0_1_rgb = raymarch_o15436((UV), _seed_variation_);
fragColor = vec4(o15436_0_1_rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    col.r += sin(uv.x*iTime*5.0);
    col.b += sin(uv.y*iTime*5.1);

    col.g += sin(uv.x*uv.y*iTime*5.0);

    col *= vec3(fract(abs(sin(uv.x*iTime*2.0*uv.y))));

    col *= vec3(abs(cos(uv.y*uv.x)));

    fragColor = vec4(col,1.0);
}

//some further compression is possible
//chess figures are stylized and abstract so do not judge too strict :)
#define A abs
void mainImage(out vec4 o, vec2 F)
{
    float c,s,d,i,e=.5,h=e,y,t=iTime;
    for(vec3 p,R=iResolution,f;
      ++i<5e2&&e>1e-3;
      p=d*normalize(vec3(F/h-R.xy,R))+vec3(sin(t)/h,4,cos(t)-15.),f=floor(p*h),
      c=mod(f.x+f.z,2.),
      c=(y=p.y)>1e-2?1.-c:c,
      s=y>3.1||A(p.x)>8.||A(p.z)>8.?c=-h:--p.z>-1.3?
          max(
              max(
                  1.-A(h*mod(s=y+12.-4.*f.x,4.)-h*h),
                  h-abs(14.-A(s-16.))),
              max(
                  h-A(3.-A(s*h-8.)),
                  max(1.-A(s-17.5),h-A(2.-A(s-16.5)))
                  )
              ):max(.7-y*h,h-A(y-1.))+0.*(p.z+=2.),
      p.x=mod(p.x,2.)-1.,
      d+=e=h*min(y,length(p.xz)-s*.9)
    );
    o+=8./i;o*=c+h;
}

const float iterations = 40.;
const float zoom = 1.2;
const float quality = 2.;

vec2 cmul(vec2 a, vec2 b) { // complex multiplication
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}
vec2 cdiv(vec2 a, vec2 b) { // complex division
    return vec2(a.x*b.x+a.y*b.y, -a.x*b.y+a.y*b.x) / (b.x*b.x+b.y*b.y);
}

vec3 hsl2rgb(float h, float s, float l) {
  float hp = 6. * mod(h,1.);
  float c = s - s * abs(2.*l - 1.);
  float x = c - c * abs(mod(hp,2.) - 1.);
  float m = l - c/2.;
  if      (hp <= 1.) return vec3(c,x,0) + m;
  else if (hp <= 2.) return vec3(x,c,0) + m;
  else if (hp <= 3.) return vec3(0,c,x) + m;
  else if (hp <= 4.) return vec3(0,x,c) + m;
  else if (hp <= 5.) return vec3(x,0,c) + m;
  else               return vec3(c,0,x) + m;
}

vec3 draw(vec2 c) {
    vec2 z = vec2(1, 0);
    vec2 shift = vec2(cos(iTime/5.)/2., 0);

    float i = 0.;    
    while (i < iterations) {
        vec2 z2 = cmul(z, z);
        z =  cmul(c, (z2 + cdiv(vec2(1, 0), shift + z2)));

        if (length(z) > 16.) break;
        i++;
    }

    if (i >= iterations) {
        return vec3(0,0,0);
    } else {
        float hue = (i - log2(log(length(z))))/iterations;
        return hsl2rgb(hue, 0.5, 0.5);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {     
    vec3 color = vec3(0,0,0);
    float samples = 0.;

    float subpixel = 1./quality;
    for (float x=0.; x<1.; x+=subpixel) {
        for (float y=0.; y<1.; y+=subpixel) {
            vec2 subCoord = fragCoord + vec2(x,y);
            vec2 coord = (2.*subCoord - iResolution.xy)/iResolution.y;
            color += draw(coord/zoom);
            samples++;
        }
    }
    fragColor = vec4(color/samples, 1);
}

#define time (iTime + 100.)

//color
