#define CYAN vec3(0., 1., 1.)
#define MAGENTA vec3(1., 0., 1.)
#define YELLOW vec3(1., 1., 0.)
#define RED vec3(1., 0., 0.)
#define GREEN vec3(0., 1., 0.)
#define BLUE vec3(0., 0., 1.)
#define ORANGE vec3(1., .75, .25)
