// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 getRandomVector(float seed) {
    if (seed < .25) {
        return vec2(1., 1.);
    } else if (seed < .5) {
        return vec2(-1., 1.);
    } else if (seed < .75) {
        return vec2(1., -1.);
    } else {
        return vec2(-1., -1.);
    }
}

float getNonPeriodic(float seed) {
    return (sin(2. * seed) + sin(PI * seed)+ 2.) / 4.;
}

vec3 getRandomColor(float seed) {
    seed = clamp(seed, 0., 1.);
    float colorCount = 6.;
    float colorIncrement = 1.f / colorCount;
    float seedFloor = floor(seed / colorIncrement) * colorIncrement;
    float lerpVal = smoothstep(seedFloor, seedFloor + colorIncrement, seed);

    if (seed <= colorIncrement * 1.) {
        return mix(RED, YELLOW, lerpVal);
    } else if (seed <= colorIncrement * 2.) {
        return mix(YELLOW, GREEN, lerpVal);
    } else if (seed <= colorIncrement * 3.) {
        return mix(GREEN, CYAN, lerpVal);
    } else if (seed <= colorIncrement * 4.) {
        return mix(CYAN, BLUE, lerpVal);
    } else if (seed <= colorIncrement * 5.) {
        return mix(BLUE, MAGENTA, lerpVal);
    } else {
        return mix(MAGENTA, vec3(1.), lerpVal);
    }
}

vec3 getColor(float seed) {
    seed = clamp(seed, 0., 1.);
    float colorIncrement =.25;
    float seedFloor = floor(seed / colorIncrement) * colorIncrement;
    float lerpVal = smoothstep(seedFloor, seedFloor + colorIncrement, seed);

    if (seed <= .25) {
        return mix(PINK * .5, ORANGE, lerpVal);
    } else if (seed <= .5) {
        return mix(ORANGE, PINK * .8, lerpVal);
    } else if (seed <= .75) {
        return mix(PINK * .8, MAGENTA, lerpVal);
    } else {
        return mix(MAGENTA, WHITE, lerpVal);
    } 
}

vec3 getMixedColor(vec3 color0, vec3 color1, vec3 color2, vec3 color3, float seed) {
    seed = clamp(seed, 0., 1.);
    float colorIncrement = .2;
    float seedFloor = floor(seed / colorIncrement) * colorIncrement;
    float lerpVal = smoothstep(seedFloor, seedFloor + colorIncrement, seed);

    if (seed <= .2) {
        return color0;
    } else if (seed <= .4) {
        return mix(color0, color1, lerpVal);
    } else if (seed <= .6) {
        return mix(color1, color2, lerpVal);
    } else if (seed <= .8) {
        return mix(color2, color3, lerpVal);
    } else {
        return color3;
    }
}

float biLerp(float f0, float f1, float f2, float f3, float lerpX, float lerpY) {
    float upper = mix(f1, f2, lerpX);
    float lower = mix(f0, f3, lerpX);

    return mix(lower, upper, lerpY);
}

float getPerlinValue(vec2 uv, float gridDimension) {
    float xCoord = floor(uv.x / gridDimension) * gridDimension;
    float yCoord = floor(uv.y / gridDimension) * gridDimension;

    float xIndex = floor(uv.x / gridDimension);
    float yIndex = floor(uv.y / gridDimension);

    float p0Hash = hash(vec2(xIndex, yIndex));
    float p1Hash = hash(vec2(xIndex, yIndex + 1.));
    float p2Hash = hash(vec2(xIndex + 1., yIndex + 1.));
    float p3Hash = hash(vec2(xIndex + 1., yIndex));

    vec2 p0 = vec2(xCoord, yCoord);
    vec2 p1 = vec2(xCoord, yCoord + gridDimension);
    vec2 p2 = vec2(xCoord + gridDimension, yCoord + gridDimension);
    vec2 p3 = vec2(xCoord + gridDimension, yCoord);

    float dot0 = dot((uv - p0) / gridDimension, getRandomVector(p0Hash));
    float dot1 = dot((uv - p1) / gridDimension, getRandomVector(p1Hash));
    float dot2 = dot((uv - p2) / gridDimension, getRandomVector(p2Hash));
    float dot3 = dot((uv - p3) / gridDimension, getRandomVector(p3Hash));

    float xInterp = smoothstep(p0.x, p2.x, uv.x);
    float yInterp = smoothstep(p0.y, p2.y, uv.y);

    float val = biLerp(dot0, dot1, dot2, dot3, xInterp, yInterp);

    // normalizes the perlin value, but inhibits additive perlin functionality upstream
    //val = (val + 1.) / 2.;

    if (DEBUG) {
        float distp0 = distance(p0, uv);

        float debugDotRadius = .005;
        float debugAA = debugDotRadius * .5;

        val += smoothstep(debugDotRadius + debugAA, debugDotRadius - debugAA, distance(p0, uv));
        val += smoothstep(debugDotRadius + debugAA, debugDotRadius - debugAA, distance(p1, uv));
        val += smoothstep(debugDotRadius + debugAA, debugDotRadius - debugAA, distance(p2, uv));
        val += smoothstep(debugDotRadius + debugAA, debugDotRadius - debugAA, distance(p3, uv));
    }

    return val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    float timeScale = .2f;

    uv.x += iTime * timeScale;

    uv.y += getNonPeriodic(uv.x) * .8;

    float gridDimension = .1f;

    float perlinValue = getPerlinValue(uv, gridDimension);

    perlinValue += getPerlinValue(uv + vec2(sin(iTime * .1f)), gridDimension * 10.f);
    perlinValue += getPerlinValue(uv + vec2(sin(iTime * .1f)), gridDimension * .8f);
    perlinValue += getPerlinValue(uv + vec2(cos(iTime * .3f)), gridDimension * 2.f);
    perlinValue += getPerlinValue(uv + vec2(cos(iTime * .2)), gridDimension * .33f);
    perlinValue += getPerlinValue(uv - vec2(iTime * .02f, -iTime * .02f), gridDimension * .5f);

    float clampedPerlin = clamp(perlinValue, 0., 1.);

    fragColor = vec4(getColor(clampedPerlin) * mix(PINK, WHITE, .5), 1.0);
    fragColor = vec4(getMixedColor(GREEN * .8, BLUE, CYAN, WHITE, clampedPerlin) * mix(YELLOW, WHITE, .5), 1.0);
}

const int AMOUNT = 10;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 20.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y))*sin(3.0) / cos(3.14159265358);

        coord.x = coord.x - cos(coord.y + sin(len)) + cos(iTime / 12.0);
        coord.y = coord.y + sin(coord.x + cos(len)) + sin(iTime / 9.0);
    }

    fragColor = vec4(cos(len * 1.1), cos(len * 1.05), cos(len * 1.01), 1.0);
}

