// the eyes from Art of Code Lesson 4

#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0., 1.)

// eye function
vec4 Eye(vec2 uv, vec2 pos, float scale){
    uv = uv-pos;

    float t = iTime*.4;
    uv.x += sin(t+uv.y*15.)*.02;
    uv.y += sin(t+uv.x*25.)*.04;

    vec2 offs = vec2(sin(t+uv.y*15.),sin(t+uv.x*15.));
    offs += 1.1;  
    uv *= offs;

    float d = length(uv)*scale;

    vec4 irisCol = vec4(.3,.5,1.,1.);
    vec4 col = mix(vec4(1.),irisCol,S(.1,.7,d)*.5);  // outer eye circle

    col.rgb *= 1.-S(.45,.5,d)*.5*sat(-uv.y-uv.x);  // "shadow" effect
    col.rgb = mix(col.rgb,vec3(0.),S(.3,.28,d));  // iris outline
    irisCol.rgb *= 1.+S(.3,.05,d);  // fade on iris
    col.rgb = mix(col.rgb,irisCol.rgb,S(.28,.25,d)); // iris
    col.rgb = mix(col.rgb,vec3(.75,0.,1.),S(.16,.14,d));  // pupil

    col.a = S(.5,.48,d);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set up uv
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(1.);

    vec4 eye = Eye(uv,vec2(.1,.1),7.);    
    col.rgb = mix(col.rgb,eye.rgb,eye.a);

    eye = Eye(uv,vec2(-.7,.3),2.);    
    col.rgb = mix(col.rgb,eye.rgb,eye.a);

    eye = Eye(uv,vec2(.3,-.1),2.);    
    col.rgb = mix(col.rgb,eye.rgb,eye.a);

    eye = Eye(uv,vec2(-.3,-.2),4.);    
    col.rgb = mix(col.rgb,eye.rgb,eye.a);

    fragColor = vec4(col,1.);
}

