// https://stackoverflow.com/questions/30545052/calculate-signed-distance-between-point-and-rectangle/30545544
// https://www.shadertoy.com/view/MtBGWc
float sdfRect(vec2 c, vec2 h, vec2 p){
    vec2 v = abs(p-c);
    vec2 u = v - h;
    return length(max(u,0.0)) + min(0.0,max(u.x,u.y));
}

float sdfCircle(vec2 c, float r, vec2 p){
     return length(p-c) - r;
}

vec2 sdfToColor(float d){
    float m = 1.0 - abs(d)/0.2;
    float s = sin(d*400.0) * 0.5 + 0.5;
    return vec2(s*m*(-sign(d)*0.5+0.5),s*m*(sign(d)*0.5+0.5));
}

float sdfBlend(float d1, float d2){
    return mix(d1,d2,sin(iTime)*0.5+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 circleCenter = vec2(0.75,0.75);
    float radius = 0.24;
    if(iMouse.z > 0.0){
        circleCenter = iMouse.xy / iResolution.xy;
    }

    vec2 rectCenter = vec2(0.25,0.25);
    vec2 rectSize = vec2(0.2,0.2);

    float d1 = sdfRect(rectCenter, rectSize, uv);
    float d2 = sdfCircle(circleCenter, radius, uv);

    float t1 = sdfBlend(d1,d2);
    float d = t1;

    fragColor = vec4(sdfToColor(d),0,1);
}

