// Intersect the scene by visiting each object

its
intersect(Ray ray)
{
	float t_min = INFINITY;
    its ret_its;

    ret_its.t = 0.0;
    ret_its.material = MAT_UNDEF;
    ret_its.albedo = vec4(0.0);

	{
		float t = intersect_light(ray);
		if(t < t_min) {
			//albedo = vec3(100);
			ret_its.albedo = g_light_albedo;
			//albedo = vec3(dot(ray.dir, g_light_normal) < 0.0 ? 1.0 : 0.0);
			ret_its.normal = g_light_normal;
			t_min  = t;
			ret_its.position = ray_at(ray, t);
            ret_its.material = MAT_EMISSION;
		}
	}

    // small box
	{
		vec3 normal_tmp;
		Ray ray_tmp = ray;
		ray_tmp.origin -= vec3(0.5, -0.75, 0.35);
		float t = intersect_box(ray_tmp, normal_tmp, vec3(0.25, 0.25, 0.25));
		if(t < t_min) {
			t_min = t;
			ret_its.position = ray_at(ray, t);
			ret_its.albedo = vec4(0.7, 0.7, 0.7, 0);
			ret_its.normal = normal_tmp;
            ret_its.material = MAT_DIFFUSE;
		}
	}

    // sphere
    {
        vec3 c = vec3(0, 0, 0);
        float r = 0.4;
        vec3 normal_tmp;

		Ray ray_tmp = ray;
        float t_max;

		float t = intersect_sphere(ray_tmp, t_max, normal_tmp, c, r);
        if(t < t_min) {
                t_min = t;
                ret_its.position = ray_at(ray, t);
                ret_its.albedo = vec4(.9, .9, .9, 0);
                ret_its.normal = normal_tmp;
                ret_its.material = MAT_DIFFUSE;
        }
    }

	// left
	{
		vec3 n = vec3(1, 0, 0);
		float t = intersect_plane(ray, vec3(-1, 0, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThanEqual(p_tmp.yz, vec2(1))) && all(greaterThanEqual(p_tmp.yz,
							vec2(-1))))
			{
				ret_its.normal = n;
				ret_its.position = p_tmp;

				t_min = t;

				ret_its.albedo = vec4(0.9, 0.1, 0.1, 0);
                ret_its.material = MAT_DIFFUSE;
			}
		}
	}
	// right
	{
		vec3 n = vec3(-1, 0, 0);
		float t = intersect_plane(ray, vec3(1, 0, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThanEqual(p_tmp.yz, vec2(1))) && all(greaterThanEqual(p_tmp.yz,
							vec2(-1))))
			{
				ret_its.normal = n;
				ret_its.position = p_tmp;

				t_min = t;

				ret_its.albedo = vec4(0.1, 0.9, 0.1, 0);
                ret_its.material = MAT_DIFFUSE;
			}
		}
	}
	// floor
	{
		vec3 n = vec3(0, 1, 0);
		float t = intersect_plane(ray, vec3(0, -1, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				ret_its.normal = n;
				ret_its.position = p_tmp;
				ret_its.albedo = vec4(0.7, 0.7, 0.7, 0);

				t_min = t;
                ret_its.material = MAT_DIFFUSE;
			}
		}
	}
	// ceiling
	{
		vec3 n = vec3(0, -1, 0);
		float t = intersect_plane(ray, vec3(0, 1, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				ret_its.normal = n;
				ret_its.position = p_tmp;
				ret_its.albedo = vec4(0.7, 0.7, 0.7, 0);

				t_min = t;
                ret_its.material = MAT_DIFFUSE;
			}
		}
	}
	// back wall
	{
		vec3 n = vec3(0, 0, 1);
		float t = intersect_plane(ray, vec3(0, 0, -1), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xy, vec2(1))) && all(greaterThan(p_tmp.xy,
							vec2(-1))))
			{
				ret_its.normal = n;
				ret_its.position = p_tmp;
				ret_its.albedo = vec4(0.7, 0.7, 0.7, 0);
                ret_its.material = MAT_DIFFUSE;
				t_min = t;
			}
		}
	}


	ret_its.normal = normalize(ret_its.normal);

    ret_its.t = t_min;

	return ret_its;
}


bool
test_visibility(vec3 p1, vec3 p2)
{
	const float eps = 1e-5;

	Ray r = Ray(p1, normalize(p2 - p1));
	r.origin += eps * r.dir;


	its t_shadow = intersect(r);

	return t_shadow.t > distance(p1, p2) - 2.0 * eps;
}

