    if( inter.material == MAT_DIFFUSE ) {
        vec3 brdf = inter.albedo.rgb / PI;
        brdf_pdf = 1.0 / PI;
        return brdf;
    }
    else if( inter.material == MAT_GLOSSY ) {
        vec3 local_idir =  normalize( WorldToLocal(wcs_indir, s, t, inter.normal  ));
        vec3 local_odir =  normalize( WorldToLocal(wcs_outdir, s, t, inter.normal  )); 

        vec3 brdf = g_rho_d * 1./PI * inter.albedo.rgb + g_rho_s * WardBRDF( local_idir, local_odir, g_alpha, g_rho_s);                    
        brdf_pdf = g_rho_s * WardPDF(-local_odir, local_idir, th, ph);      

        return brdf;
    }
}

// path tracing with MIS

vec3
pt_mis(Ray ray)
{
	vec3 contrib = vec3(0);
	vec3 tp = vec3(1.0);

	vec3 position, normal;
	vec4 albedo;
	its inters = intersect(ray);

    position = inters.position;
    normal = inters.normal;
    albedo = inters.albedo;
    float t = inters.t;

	if(t == INFINITY)
		return vec3(0.0);

	if(albedo.a > 0.0) { /* hit light source */
		return albedo.rgb * albedo.a;
	}

    Ray currRay = ray;

	for(int i = 0; i < NUM_BOUNCES; i++) {


		{ /* NEE */
			vec3 pos_ls = sample_light(get_random());
			vec3 l_nee = pos_ls - position;
			float rr_nee = dot(l_nee, l_nee);
			l_nee /= sqrt(rr_nee);
			float G = max(0.0, dot(normal, l_nee)) * max(0.0, -dot(l_nee, g_light_normal)) / rr_nee;
            vec3 sv, tv;
            float theta =0.0, phi=0.0;

            defineTangents(currRay.dir, inters.normal, sv, tv);

			if(G > 0.0) {
				float light_pdf = 1.0 / (g_light_area * G);
				float brdf_pdf;

				float w = light_pdf / (light_pdf + brdf_pdf);

                vec3 h_wcs = normalize(l_nee+currRay.dir);
                vec3 h_local = WorldToLocal(h_wcs, sv, tv, inters.normal);

                float theta = acos(h_local.z);
                float phi = h_local.y/(sin(theta));           

				vec3 brdf = evalBRDF(inters, brdf_pdf, l_nee, -currRay.dir, sv, tv, theta, phi);

				if(test_visibility(position, pos_ls)) {
					vec3 Le = g_light_albedo.rgb * g_light_albedo.a;
					contrib += tp * (Le * w * brdf) / light_pdf;
				}
			}

		}


    { /* brdf */            
            vec3 sv, tv;

            defineTangents(-currRay.dir, inters.normal, sv, tv);
            float theta, phi;

			vec3 dir = getOutDir(inters, sv, tv, -currRay.dir, theta, phi);


			Ray ray_next = Ray(position, dir);
			ray_next.origin += ray_next.dir * 1e-5;


			vec3 position_next, normal_next;
			vec4 albedo_next;
			its inters_next = intersect(ray_next);
            position_next = inters_next.position, normal_next = inters_next.normal, albedo_next = inters_next.albedo;
            t = inters_next.t;


			if(t == INFINITY)
				break;

            float brdf_pdf;

            vec3 brdf = evalBRDF(inters, brdf_pdf, -dir, currRay.dir, sv, tv, theta, phi);

			if(albedo_next.a > 0.0) { /* hit light_source */
				float G = max(0.0, dot(ray_next.dir, normal)) * max(0.0, -dot(ray_next.dir, normal_next)) / (t * t);
				if(G <= 0.0) /* hit back side of light source */
					break;

				float light_pdf = 1.0 / (g_light_area * G);

				float w = brdf_pdf / (light_pdf + brdf_pdf);

				vec3 Le = g_light_albedo.rgb * g_light_albedo.a;
				contrib += tp * (Le * w * brdf) / brdf_pdf;

				break;
			}

			tp *= brdf / brdf_pdf;

            currRay = ray_next;

            inters = inters_next;
			position = position_next;
			normal = normal_next;
			albedo = albedo_next;
		} 

	}


	return contrib;
}


void
mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    g_light_size = 0.6;
	g_light_area = g_light_size * g_light_size;

	// Uncomment to make light move (comment following line)
    g_light_position = vec3(0.5 * sin(iTime), 0.90, 0.5 * cos(iTime));

	g_light_normal = vec3(0, -1, 0);
	g_seed = 0;
	g_light_albedo = vec4(1, 1, 1, 2.0 / (g_light_size * g_light_size));
	g_flat_idx = int(dot(gl_FragCoord.xy, vec2(1, 4096)));

    vec4 m = iMouse / iResolution.x;

	if( m.z>0.0 ) { // button is down
        sphere2_center = sphere2_center +  vec3(-0.1, 0., 0.3);
        // move light down
        g_light_position += vec3(0, -0.4, 0);
    }

    // Make image square
	vec2 p = fragCoord.xy  / vec2(iResolution) - vec2(0.5);
	float a = float(iResolution.x) / float(iResolution.y);
	if(a < 1.0)
		p.y /= a;
	else
		p.x *= a;

	vec3 cam_center = vec3(0, 0, 3.125);

	vec3 s = vec3(0);

    // trace NUM_SAMPLES per pixel
	for(int i = 0; i < NUM_SAMPLES; i++) {
		Ray ray;
		ray.origin = cam_center;

        // get random 2-D vector
		vec2 r = get_random();

		vec3 ray_dir = normalize(vec3(p + r.x * dFdx(p) + r.y * dFdy(p), -1));
		ray.dir = ray_dir;

        // do path tracing and MIS 
		vec3 c = pt_mis(ray);

        // accumulate color in variable s
		s += c;
	}

    // gamma correct
	fragColor = vec4(pow(s / float(NUM_SAMPLES), vec3(1.0 / 2.2)), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 10.5 + 10.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159
#define R (iResolution.xy)
#define s(v,l) smoothstep(l/R.y,0.,v) // AA
#define ro(a) (mat2(cos(a),sin(a),-sin(a),cos(a))) // 2d rotation matrix

float hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

float cir (vec2 p, vec2 c, float r, float w) {return abs(length(p-c)-r)- w;}

void mainImage(out vec4 O, in vec2 E) {

    vec2 o = (E - R*.5) / R.x;
    o *= 5.;
    o += vec2(iTime * .1);

    vec3 col = vec3(1.);
    vec3 oc = col;
    vec2 u;

    float id, aa;

    vec2 gr, ce;
    for (float g = 0.; g < 3.; g++){
        for (float i = 0.; i < 1. ; i+=.5) {
            for (float j = 0.; j < 1.; j+=.5) {
                u = o + vec2(i, j);

                gr = floor(u);
                id = hash21(gr);
                if (id * g > .25) continue;

                ce = (u - gr - .5) * 2.;
                ce *= ro((PI / 2.) * floor(id * 10.));

                vec3 c1 = mod(g, 2.) == 0. ? oc : vec3(.6,.9,.6);
                vec3 c2 = mod(g, 2.) == 0. ? vec3(.7,.95,.5) : oc;
                vec3 c3 = mod(g, 2.) == 0. ? vec3(.6,.95,.8) : oc;
                vec3 c4 = mod(g, 2.) == 0. ? vec3(.7,.9,.7) : vec3(1.,1.,.8);

                aa = 2. * (g * 2. + 2.);

                col = mix(col, c1, s(cir(ce, vec2(0.), .0, .5), aa));
                col = mix(col, c2, s(cir(ce, vec2(0.), .0, .11), aa));
                col = mix(col, c1, s(cir(ce, vec2(0.), .0, .05), aa));
                col = mix(col, c4, s(cir(ce, vec2(0.), .0, .02), aa));

                col = mix(col, c1, s(cir(ce, vec2( .5, .5), .0, 1. / 3.), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.5, .5), .0, 1. / 3.), aa));
                col = mix(col, c1, s(cir(ce, vec2( .5,-.5), .0, 1. / 3.), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.5,-.5), .0, 1. / 3.), aa));

                col = mix(col, c2, s(cir(ce, vec2( .5, .5), .0, 1. / 4.), aa));
                col = mix(col, c2, s(cir(ce, vec2(-.5, .5), .0, 1. / 4.), aa));
                col = mix(col, c2, s(cir(ce, vec2( .5,-.5), .0, 1. / 4.), aa));
                col = mix(col, c2, s(cir(ce, vec2(-.5,-.5), .0, 1. / 4.), aa));

                col = mix(col, c3, s(cir(ce, vec2( .5, .5), .0, 1. / 5.), aa));
                col = mix(col, c3, s(cir(ce, vec2(-.5, .5), .0, 1. / 5.), aa));
                col = mix(col, c3, s(cir(ce, vec2( .5,-.5), .0, 1. / 5.), aa));
                col = mix(col, c3, s(cir(ce, vec2(-.5,-.5), .0, 1. / 5.), aa));

                col = mix(col, c4, s(cir(ce, vec2( .5, .5), .0, 1. / 6.1), aa));
                col = mix(col, c4, s(cir(ce, vec2(-.5, .5), .0, 1. / 6.1), aa));
                col = mix(col, c4, s(cir(ce, vec2( .5,-.5), .0, 1. / 6.1), aa));
                col = mix(col, c4, s(cir(ce, vec2(-.5,-.5), .0, 1. / 6.1), aa));

                col = mix(col, c1, s(cir(ce, vec2( .5, .5), .0, 1. / 12.1), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.5, .5), .0, 1. / 12.1), aa));
                col = mix(col, c1, s(cir(ce, vec2( .5,-.5), .0, 1. / 12.1), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.5,-.5), .0, 1. / 12.1), aa));

                col = mix(col, c3, s(cir(ce, vec2(-.18, .18), .0, 1. / 9.), aa));
                col = mix(col, c3, s(cir(ce, vec2( .18,-.18), .0, 1. / 9.), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.18, .18), .0, 1. / 18.), aa));
                col = mix(col, c1, s(cir(ce, vec2( .18,-.18), .0, 1. / 18.), aa));

                col = mix(col, c3, s(cir(ce, vec2(-.5, .0), .0, 1. / 6.1), aa));
                col = mix(col, c3, s(cir(ce, vec2( .5, .0), .0, 1. / 6.1), aa));
                col = mix(col, c3, s(cir(ce, vec2( .0,-.5), .0, 1. / 6.1), aa));
                col = mix(col, c3, s(cir(ce, vec2( .0, .5), .0, 1. / 6.1), aa));

                col = mix(col, c2, s(cir(ce, vec2(-.5, .0), .0, 1. / 9.), aa));
                col = mix(col, c2, s(cir(ce, vec2( .5, .0), .0, 1. / 9.), aa));
                col = mix(col, c2, s(cir(ce, vec2( .0,-.5), .0, 1. / 9.), aa));
                col = mix(col, c2, s(cir(ce, vec2( .0, .5), .0, 1. / 9.), aa)); 

                if (ce.y > -(1. / .6) && ce.y < .5 && ce.x < .5)
                    col = mix(col, c2, s(cir(ce, vec2(.5), .5, 1. /  20.), aa));
                if (ce.y <  (1. / .6) && ce.y >-.5 && ce.x >-.5) 
                    col = mix(col, c2, s(cir(ce, vec2(-.5), .5, 1. / 20.), aa)); 

                col = mix(col, c1, s(cir(ce, vec2(-.147,-.147), .0, 1. / 40.), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.25,-.065), .0, 1. / 40.), aa));
                col = mix(col, c1, s(cir(ce, vec2(-.065,-.25), .0, 1. / 40.), aa));

                col = mix(col, c1, s(cir(ce, vec2( .147, .147), .0, 1. / 40.), aa));
                col = mix(col, c1, s(cir(ce, vec2( .25, .065), .0, 1. / 40.), aa));
                col = mix(col, c1, s(cir(ce, vec2( .065, .25), .0, 1. / 40.), aa));
            }
        }
        // magic formula (works only for g < 3.), i gonna fix this in the near future
        o += pow(1./8., g + 1.) * (g * 7. + 1.);
        o *= 2.;
    }

    O = vec4(col,1.0);
}

//original shader
//https://www.shadertoy.com/view/flt3zH

vec2 N22(vec2 p)
{
    vec3 a = fract(p.xyx*vec3(363.42, 524.87, 239.66));
    a += dot(a, a+34.51);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x / iResolution.y;
    float asp=iResolution.x / iResolution.y;

    float m = 0.;
    float t = iTime*.05+10.0;

    float minD = 100.;

    for(float i=0.0; i<65.; i++)
    {
        vec2 n = N22(vec2(i+0.5,i-0.5));
        vec2 p = (sin(n*t));

        float d = length(uv-p);

        m+= 0.15*d;

        if(d < minD) minD =d;       
    }

    minD = pow(m,minD);
    vec3 col = mix(vec3(.35, 0.25, .05),vec3(0.12,0.32,0.5),minD*sin(iTime*0.5));

    col+= texture(iChannel0, uv+vec2(minD)).xyz*(1.25/minD); 

    fragColor = vec4(col,1.0);
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define MAX_STEPS 64
#define MAX_DIST 64.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

vec3 path(float z)
{
    vec3 p = vec3(sin(z) * .6, cos(z * .5), z);
    return p;
}

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float B3D(vec3 p, vec3 s) {
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z);
}
vec4 GetDist(vec3 p) {
    vec3 prevP = p;
    p.z+=iTime*1.0;
    p.xy -= path(p.z).xy;
    p.z+=iTime*0.5;
    p.z=mod(p.z,1.0)-0.5;
    float d = B3D(p,vec3(0.01));
    for (float i = 0.; i < 5.; i++) {
            p.xz*=Rot(radians(90.0));
            p.yz*=Rot(radians(38.0));
            p=abs(p)-(3.3*pow(.54, i));
            d=B3D(p,vec3(0.18));
	}

    vec3 col = vec3(1.0);

    return vec4(col,d*0.6);
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 r = vec4(0.0,0.0,0.0,1.0);

    float dist;
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*r.w;
        vec4 dS = GetDist(p);
        dist =  dS.w;
        r.w += dS.w;
        r.rgb = dS.xyz;

        if(r.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return r;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(3,5,0);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float lambert = max(.0, dot( n, l))*0.2;

    return vec2((lambert+dif),0.9) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = iTime*2.0;
    vec3 col = vec3(1.0);
    vec3 ro = vec3(0.0,0.0,1.0);
    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    rd*=matRotateZ(radians(iTime*15.0));
	vec4 r = RayMarch(ro, rd);

    if(r.w<MAX_DIST) {
        vec3 p = ro + rd * r.w;
        vec3 n = GetNormal(p);
        vec2 dif = GetLight(p);
        col = vec3(dif.x)*r.rgb;
        col *= dif.y;
        col *= exp( -0.0001*r.w*r.w*r.w );
    }

    fragColor = vec4(col,1.0);
}

// Body Design and Base Animation by jorge2017a2: https://www.shadertoy.com/view/ftGGzy

const float     PI = radians(180.);
const float TWO_PI = 2. * PI;

mat2 R(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    // https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
    vec2 pa = p-a, ba = b-a;
    float h = clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0);
    return length(pa - ba*h);
}


