//
// Drawing
//

vec3 drawOver(vec3 c, vec3 color, float r)
{
    float a = smoothstep(0., fwidth(r), r);
    return mix(color, c, a);
}

vec3 drawGraph(vec3 c, vec2 uv, vec3 color, float y)
{
    y -= uv.y;
    float a = smoothstep(0., fwidth(y), abs(y));
    return mix(color, c, a);
}

vec3 drawDashed(vec3 c, vec2 uv, vec3 color, float y)
{
    y -= uv.y;
    float a = smoothstep(0., fwidth(y), abs(y));

    float dash = sin(uv.x/fwidth(uv.x)*TWO_PI/16.);
    float b = smoothstep(0., fwidth(dash), dash);

    return mix(color, c, max(a, b));
}

vec3 drawSegment(vec3 c, vec2 p, vec2 a, vec2 b, vec3 color, float radius)
{
    return drawOver(c, color, sdSegment(p, a, b) - radius);
}

const float figureThickness = 0.04;

vec3 drawArm(vec3 c, vec2 p, XArm xarm, ColArm colArm)
{
    c = drawSegment(c, p, xarm.shoulder, xarm.elbow, colArm.upper, figureThickness);
    c = drawSegment(c, p, xarm.elbow   , xarm.hand , colArm.lower, figureThickness);
    return c;
}

vec3 drawLeg(vec3 c, vec2 p, XLeg xleg, ColLeg colLeg)
{
    c = drawSegment(c, p, xleg.hip  , xleg.knee , colLeg.upper, figureThickness);
    c = drawSegment(c, p, xleg.knee , xleg.ankle, colLeg.lower, figureThickness);
    c = drawSegment(c, p, xleg.ankle, xleg.toe  , colLeg.foot , figureThickness);
    return c;
}

vec3 drawCircle(vec3 c, vec2 p, vec2 center, float radius, vec3 color)
{
    return drawOver(c, color, distance(p, center) - radius);
}

vec3 drawBody(vec3 c, vec2 p, XBody xbody, ColBody colBody)
{
    c = drawArm(c, p, xbody.armLeft, colBody.armLeft);
    c = drawLeg(c, p, xbody.legLeft, colBody.legLeft);
    c = drawCircle(c, p, xbody.head, 0.5, colBody.head);
    c = drawSegment(c, p, xbody.armLeft.shoulder, xbody.legLeft.hip, colBody.spine, figureThickness);
    c = drawLeg(c, p, xbody.legRight, colBody.legRight);
    c = drawArm(c, p, xbody.armRight, colBody.armRight);
    return c;
}


