void base_pose(out float ang1, out float ang2, out float ang3, float t, float r1, float r2, float r3)
{
    float t1, t2, t3;

    t1=t;
    t2=(1.0 + sin(t+PI));
    t3=(1.0 + sin(t+PI));

    ang1 = r1 * sin(t1);
    ang2 = r2 * sin(t2);
    ang3 = r3 * sin(t3);
}

void dont_trip_over(inout float f, inout float ang2, inout float ang3, float t)
{
    float lift = 1.2;
    float drop = 4.7;
    float b2 =  0.7;
    float b3 = -0.9;

    float phase = mod(t, TWO_PI);

    // use this, to see where the action is active
    // also helps with adjusting lift, drop, b2 and b3.
    //if (lift < phase && phase < drop) ang2 += b2;
    //if (lift < phase && phase < drop) ang3 += b3;

    f *=
      + smoothstep(lift, lift + 1.8, phase) // lift the leg smoothly
      - smoothstep(drop - 1.5, drop, phase) // drop the leg smoothly
      ;

    // use this, to get the final smooth motion.
    ang2 += b2 * f;
    ang3 += b3 * f;
}

void horizontal_while_foot_on_ground(inout float f, inout float ang3, float t, float ang1, float ang2)
{
    float start = 4.7;
    float duration = 2.8;

    float phase = mod(t - start, TWO_PI);

    float angFlat = -ang1-ang2;

    // use this, to see where the action is active
    // also helps with adjusting start and duration.
    //if (phase < duration) ang3 = angFlat-1.;

    f *=
      + smoothstep(0., 0.5, phase) // lift the leg smoothly
      - smoothstep(duration - 1.2, duration, phase) // drop the leg smoothly
      ;

    // use this, to get the final smooth motion.
    ang3 = mix(ang3, angFlat, f);
}


//
// Action + Angle structs
//

struct ActLeg
{
    float trip, ground;
};

struct ActBody
{
    ActLeg legLeft, legRight;
};

struct AngArm
{
    float shoulder, elbow;
};

struct AngLeg
{
    float hip, knee, foot;
    float fTrip, fGround;
};

struct AngBody
{
    AngArm armLeft, armRight;
    AngLeg legLeft, legRight;
};

AngLeg poseLeg(float t, ActLeg actLeg)
{
    float ang1, ang2, ang3;

    base_pose(ang1, ang2, ang3, t, 0.6, 0.3, 0.2);
    dont_trip_over(actLeg.trip, ang2, ang3, t);
    horizontal_while_foot_on_ground(actLeg.ground, ang3, t, ang1, ang2);

    return AngLeg(ang1, ang2, ang3, actLeg.trip, actLeg.ground);
}

AngArm poseArm(float t)
{
    float ang1, ang2, ang3;

    base_pose(ang1, ang2, ang3, t, 0.6, 0.2, 0.2);

    return AngArm(ang1, ang2);
}

AngBody poseBody(float t, ActBody actBody)
{
    return AngBody(
        poseArm(t + .3                 ), poseArm(t + PI + .3                  ),
        poseLeg(t     , actBody.legLeft), poseLeg(t + PI     , actBody.legRight)
    );
}


//
// Transforms
//

XArm bendArm(XArm xarm, AngArm angArm)
{
    vec2 v0 =                           xarm.shoulder;
    vec2 v1 =              xarm.elbow - xarm.shoulder;
    vec2 v2 = xarm.hand  - xarm.elbow                ;

    vec2 q0 =                                          v0;
    vec2 q1 = q0 + R(angArm.shoulder               ) * v1;
    vec2 q2 = q1 + R(angArm.shoulder + angArm.elbow) * v2;

    return XArm(q0, q1, q2);
}

XLeg bendLeg(XLeg xleg, AngLeg angLeg)
{
    vec2 v0 =                                      xleg.hip;
    vec2 v1 =                          xleg.knee - xleg.hip;
    vec2 v2 =             xleg.ankle - xleg.knee           ;
    vec2 v3 = xleg.toe  - xleg.ankle                       ;

    vec2 q0 =                                                  v0;
    vec2 q1 = q0 + R(angLeg.hip                            ) * v1;
    vec2 q2 = q1 + R(angLeg.hip + angLeg.knee              ) * v2;
    vec2 q3 = q2 + R(angLeg.hip + angLeg.knee + angLeg.foot) * v3;

    return XLeg(q0, q1, q2, q3);
}

XBody bendBody(XBody xbody, AngBody angBody)
{
    return XBody(
        xbody.head,
        bendArm(xbody.armLeft, angBody.armLeft),
        bendArm(xbody.armRight, angBody.armRight),
        bendLeg(xbody.legLeft, angBody.legLeft),
        bendLeg(xbody.legRight, angBody.legRight)
    );
}

float lowest_point_on_leg(XLeg xleg)
{
    return min(xleg.ankle, xleg.toe).y;
}

float lowest_point_on_body(XBody xbody)
{
    return min(lowest_point_on_leg(xbody.legLeft), lowest_point_on_leg(xbody.legRight));
}

