void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    vec3 col = gray;

    vec2 mouse = vec2(1);
    if (iMouse.z > .5)
        mouse = iMouse.xy / iResolution.xy;

    ActLeg actLeg = ActLeg(mouse.x, mouse.y);
    ActBody actBody = ActBody(actLeg, actLeg);

    float t = iTime;
    AngBody angBody = poseBody(t, actBody);
    XBody worldBody = bendBody(xbody, angBody);

    // animation
    {
        float ground = lowest_point_on_body(worldBody);

        vec2 uv = fragCoord / iResolution.y * 2.;
        uv *= 7.;
        uv -= vec2(2, 1. - ground);

        col = drawBody(col, uv, worldBody, colBody);
        col = drawSegment(col, uv - vec2(-0.8, ground), vec2(0, 0), vec2(6.5, 0), water, 0.08);
    }

    // graphs
    {
        vec2 uv = (fragCoord + .5 - iResolution.xy / 2.) / iResolution.y * 2.;
        uv -= vec2(.65, 0.);
        uv /= 2.;

        float t1 = uv.x * TWO_PI;
        AngBody angBody1 = poseBody(t1, actBody);
        XBody worldBody1 = bendBody(xbody, angBody1);
        float ground1 = lowest_point_on_body(worldBody1);

        if (uv.x >= -.5 && uv.x <= +.5)
        {
            float phase = mod(t + PI, TWO_PI);
            vec2 offset = vec2(.5, 0.);
            vec2 scale = vec2(1. / TWO_PI, .1);

            uv.y -= .35;
            col = drawDashed(col, uv, colBody.legLeft.lower*.5, angBody1.legLeft.fTrip * .1);
            col = drawDashed(col, uv, colBody.legLeft.foot*.5, angBody1.legLeft.fGround * .1);
            col = drawGraph(col, uv, black, 0.);
            col = drawGraph(col, uv, colBody.legLeft.upper, angBody1.legLeft.hip  * scale.y);
            col = drawGraph(col, uv, colBody.legLeft.lower, angBody1.legLeft.knee * scale.y);
            col = drawGraph(col, uv, colBody.legLeft.foot , angBody1.legLeft.foot * scale.y);

            col = drawCircle(col, uv, vec2(phase, angBody.legLeft.hip )*scale-offset, .01, colBody.legLeft.upper);
            col = drawCircle(col, uv, vec2(phase, angBody.legLeft.knee)*scale-offset, .01, colBody.legLeft.lower);
            col = drawCircle(col, uv, vec2(phase, angBody.legLeft.foot)*scale-offset, .01, colBody.legLeft.foot );

            uv.y += .25;
            col = drawDashed(col, uv, colBody.legRight.lower*.5, angBody1.legRight.fTrip * .1);
            col = drawDashed(col, uv, colBody.legRight.foot*.5, angBody1.legRight.fGround * .1);
            col = drawGraph(col, uv, black, 0.);
            col = drawGraph(col, uv, colBody.legRight.upper, angBody1.legRight.hip  * scale.y);
            col = drawGraph(col, uv, colBody.legRight.lower, angBody1.legRight.knee * scale.y);
            col = drawGraph(col, uv, colBody.legRight.foot , angBody1.legRight.foot * scale.y);

            col = drawCircle(col, uv, vec2(phase, angBody.legRight.hip )*scale-offset, .01, colBody.legRight.upper);
            col = drawCircle(col, uv, vec2(phase, angBody.legRight.knee)*scale-offset, .01, colBody.legRight.lower);
            col = drawCircle(col, uv, vec2(phase, angBody.legRight.foot)*scale-offset, .01, colBody.legRight.foot );

            uv.y += .25;
            scale.y = .05;
            col = drawDashed(col, uv, colBody.legLeft.lower*.5, angBody1.legLeft.fGround * .1);
            col = drawDashed(col, uv, colBody.legRight.lower*.5, angBody1.legRight.fGround * .1);
            col = drawGraph(col, uv, black, 0.);
            col = drawGraph(col, uv, colBody.legLeft .lower, (worldBody1.legLeft .ankle.y-ground1) * scale.y);
            col = drawGraph(col, uv, colBody.legRight.lower, (worldBody1.legRight.ankle.y-ground1) * scale.y);

            col = drawCircle(col, uv, vec2(phase, worldBody1.legLeft .ankle.y-ground1)*scale-offset, .01, colBody.legLeft .lower);
            col = drawCircle(col, uv, vec2(phase, worldBody1.legRight.ankle.y-ground1)*scale-offset, .01, colBody.legRight.lower);

            uv.y += .25;
            scale.y = .02;
            col = drawGraph(col, uv, black, 0.);
            col = drawGraph(col, uv, colBody.legLeft .lower, (worldBody1.legLeft .ankle.x) * scale.y);
            col = drawGraph(col, uv, colBody.legRight.lower, (worldBody1.legRight.ankle.x) * scale.y);

            col = drawCircle(col, uv, vec2(phase, worldBody1.legLeft .ankle.x)*scale-offset, .01, colBody.legLeft .lower);
            col = drawCircle(col, uv, vec2(phase, worldBody1.legRight.ankle.x)*scale-offset, .01, colBody.legRight.lower);
        }
    }

    fragColor = vec4(col, 1.0);
}

const float MAX_ITERATIONS = 50.0;
const vec2 CENTER = vec2(-0.5, 0);
const float INFINITY = 10000000000.; // 1./0.; dividing by zero seems to give infinity in WebGL2 outside shadertoy

vec3 hsl2rgb(float h, float s, float l) {
  float hp = 6. * mod(h,1.);
  float c = s - s * abs(2.*l - 1.);
  float x = c - c * abs(mod(hp,2.) - 1.);
  float m = l - c/2.;
  if      (hp <= 1.) return vec3(c,x,0) + m;
  else if (hp <= 2.) return vec3(x,c,0) + m;
  else if (hp <= 3.) return vec3(0,c,x) + m;
  else if (hp <= 4.) return vec3(0,x,c) + m;
  else if (hp <= 5.) return vec3(x,0,c) + m;
  else               return vec3(c,0,x) + m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    vec2 coord = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 z = vec2(0, 0);
    vec2 c = coord + CENTER;

    vec2 trap = vec2(
      sin(iTime/1.3),
      sin(iTime/1.7)
    );    
    float trapDistance = INFINITY;

    float i = 0.0;
    while (i < MAX_ITERATIONS) { 
      z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;

      trapDistance = min(trapDistance, length(z - trap));

      if (length(z) > 4.) break;
      i++;
    }

    if (i >= MAX_ITERATIONS) {
      fragColor = vec4(0,0,0,1);      
    } 
    else {  
      float escapeSpeed = (i - log2(log(length(z)))) / MAX_ITERATIONS;
      float trapDistance = min(trapDistance, 1.);
      fragColor = vec4(
        hsl2rgb(
          escapeSpeed + iTime/10.,          
          .7,
          1. - trapDistance
        ),
        1
      );     
    }
}

float sigmoid(float x, float k){
    return (x - k*x)/(k - 2.*k*abs(x) + 1.);
}

vec4 twinkleStar(vec2 uv, vec2 offset, float scale, float timeOffset){
    uv += offset;

    uv -= 0.5;
    uv *= 2.;
    uv *= scale;

    vec2 normalUV  = uv;

    uv.x = abs(uv.x);
    uv.y = abs(uv.y);

    float limit = step(1.0, step(1.0, uv.x) + step(1.0, uv.y));

    limit = 1. - limit;

    uv.x = 1.-uv.x;

    float currentTime = iTime + timeOffset;
    float animTime = sin(currentTime * 3.14 * 2.)*0.5 + 0.5;
    float value = 1. - step(sigmoid(uv.x - 0.005, 0.9 + animTime*0.1) - 0.005, uv.y);

    float animTime2 = sin(currentTime * 5.)*0.5 + 0.5;
    float ring = sigmoid(1.-length(normalUV), 0.8);
    value *= sigmoid(1.-length(normalUV), 0.1) * 1.5;
    value = min(1., value);

    return vec4(1.,1.,1.,1.0)*value*limit;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.y/2.);

    fragColor = twinkleStar(uv, vec2(-0.5,-0.5), 3.0, 1.0) + 
    twinkleStar(uv, vec2(-1.2,-0.8), 2.0, 0.25) + 
    twinkleStar(uv, vec2(-1.5,0.3), 1.5, 0.4) +
    twinkleStar(uv, vec2(-2.1,-0.5), 1.75, 0.15);
}

// BASIC RAY-MARCHING CLASS DEMO!
//
// Numerous methods borrowed from iq articles:
//   https://iquilezles.org/www/index.htm
// 
// 3D signed distance functions:
//   https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// 
// Shadows:
//   https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
// 
// Ray-marching SDFs:
//   https://www.iquilezles.org/www/articles/raymarchingdf/raymarchingdf.htm
// 
// Normals for SDFs:
//   https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
// subtracts d1 from d2

//lighting from spsherk_ https://www.shadertoy.com/view/Wlc3WN
//based on https://www.shadertoy.com/view/XtGfWG
#define MAX_STEPS 200
#define MAX_DIST 100.
#define MIN_DIST .0001


float sdfSphere(vec3 p, vec3 c, float r) {
        return distance(p, c) - r + texture(iChannel2, p.xy).r / ((sin(iTime) + 1.) * 100.);

}
//gets distance from point p to edge of a sphere
float getDist(vec3 p) {
    return sdfSphere(p, vec3(0.), 1.);
}

//normalizing the vector by getting an epsilon and subtracting it from our 
//2d vector and normalizing the values into a 3d vector
vec3 getNormal(vec3 p) {
    vec2 e = vec2(.0025, -.0025); 
    return normalize(vec3(getDist(p - e.xyy), getDist(p + e.yxy), getDist(p + e.yyx)));
}
float rayMarch(vec3 rayOrigin, vec3 rayDist) {
    float dist = 0.;

    //using iteration for our ray marching loop 
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 itPos = rayOrigin + rayDist * dist;
        float itDist = getDist(itPos);

        dist += itDist;

        //once we've reached our desired "MAX_DIST", then we break the loop
        if (dist > MAX_DIST || dist < MIN_DIST) {
            break;
        }
    }
    return dist;
}

//using dot product to create lighting 
float getLight(vec3 p)
{
    vec3 lightPos = vec3(sin(iTime * 4.), 3., -3.);
    vec3 lightDirection = normalize(p - lightPos);

    return -dot(getNormal(p), lightDirection);    
    //float d = -dot(getNormal(p), lightDirection);
	//float col =   1. / (5.5* d + 1.5*d);
	//float col = .1*exp(1./(5.5*d + 3.*d));
    //return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y; // w/h are equal
    // Time varying pixel color
    vec3 col = vec3(0.);

    float focalDist = .7;
    vec3 rayOrigin = vec3(-1,0.,-2);
    vec3 rayOrigin2 = vec3(1,0.,-2);
    vec3 rayDist = vec3(uv.x, uv.y, focalDist);

    float dist = rayMarch(rayOrigin, rayDist);
    float dist2 = rayMarch(rayOrigin2, rayDist);

    if (dist < MAX_DIST) {
        //point to edge of a sphere (SDF)
        vec3 pEdge = rayOrigin + rayDist * dist;
        col = vec3(2., 0.4, 0.3);
        col *= vec3(getLight(pEdge));

    }

    if (dist2 < MAX_DIST) {
        vec3 pEdge = rayOrigin2 + rayDist * dist2;
        col = vec3(2., 0.4, 0.3);
        col *= vec3(getLight(pEdge));

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

