// personal implementation
float pattern(vec2 pos){
    vec4 p0 = floor(mod(vec4(pos, pos * 0.5), vec4(2)));
    vec4 p1 = floor(mod(vec4(pos * 0.25, pos * 0.125), vec4(2)));

    //  |0 2|
    //  |3 1| pattern is used in 2x2, 4x4, 8x8, 16x16 scales (p.xy, p.zw, p2.xy, p2.zw)

    float res = 0.;

    // ver 1 is slower!
    //res += dot(mod(p0 * vec4(2, 3, 2, 3) * mat2x4(1,1,0,0,0,0,1,1), 4.),  vec2(64, 16)); // 2x2 & 4x4
    //res += dot(mod(p1 * vec4(2, 3, 2, 3) * mat2x4(1,1,0,0,0,0,1,1), 4.),  vec2(4, 1));   // 8x8 & 16x16

    // ver 0
    res += mod(dot(p0.xy * vec2(2, 3), vec2(1)), 4.) * 64.;   // 2x2
    res += mod(dot(p0.zw * vec2(2, 3), vec2(1)), 4.) * 16.;   // 4x4
    res += mod(dot(p1.xy * vec2(2, 3), vec2(1)), 4.) * 4.;   // 8x8
    res += mod(dot(p1.zw * vec2(2, 3), vec2(1)), 4.);        // 16x16

    return float(res + 0.5) / 256.;
}

// Bayer implementation matrix https://www.shadertoy.com/view/XtV3RGx
#define iterBayerMat 6 //size of bayer
#define bayer2x2(a)(4-(a).x-((a).y<<1))%4
//return bayer matrix(bitwise operands for speed over compatibility)
float GetBayerFromCoordLevel(vec2 pixelpos) //https://www.shadertoy.com/view/XtV3RG
{
    ivec2 p = ivec2(pixelpos); int a=0; 
    for(int i=0;i<iterBayerMat;i++)
        a += bayer2x2(p>>(iterBayerMat - 1 - i) & 1) << ( 2 * i);
    return float(a) / float( 2 << (iterBayerMat * 2-1) );
}


float dither(vec2 fragCoord, float value){
    //return float(value > GetBayerFromCoordLevel(fragCoord));
    return float(value > pattern(fragCoord));
}

vec3 dither3(vec2 fragCoord, vec3 val){
