float brick(vec2 p)
{
    // offset for each row
    float offset = sqrt(floor(mod(p.y,2.)+16.)*16.);
    p.x += offset;

    vec2 m = floor(p);
    vec2 n = fract(p)*2.-1.;

    vec2 nn = pow(n, vec2(10.));
    float d = pow(nn.x+nn.y, .1)-0.07; // controls size of the brick

    // tune softness
    //return pow(smoothstep(1., .8,d),5.);
    return pow(smoothstep(1., .8,d),1.);
}

vec3 shading(vec3 n)
{
    vec3 L = normalize(vec3(-.7,.8,1.));
    float dif = max(0., dot(L, n));
    return vec3(dif);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= vec2(4.*iResolution.x/iResolution.y,6.);
    uv.xy += iTime;

    vec2 eps=vec2(.012,0);
    vec3 nor = normalize(vec3(brick(uv-eps.xy) - brick(uv+eps.xy),
                              brick(uv-eps.yx) - brick(uv+eps.yx),1.));

    vec3 col = shading(nor);
    if(fragCoord.x > iResolution.x*.5) col = vec3(nor*.5+.5);

    fragColor = vec4(col,1.0);
}

// Note: using clamp a lot to keep sure values stay between 0. and 1. 
// and act in a consistent way with colors
// Most of the knowledge acquired in this shader comes from Flopine's tutorials
// https://www.twitch.tv/flopine/

mat2 rot(float a) {
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float circle(vec2 uv) {
    return length(uv);
}

float square(vec2 uv) {
    uv = abs(uv);
    return max(uv.x, uv.y);;
}

// smoothstep on pixel width
float ss(float s, float size) {
    float w = fwidth(s);
    return smoothstep(size+w, size-w, s);
}

float shape(vec2 uv) {
    return clamp(ss(square(uv),.2) - ss(circle(uv),.15),0.,1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    uv *= 6.;
    uv += .5; // adjustment for centering

    vec2 guv = fract(uv)-.5; // grid + center coords
    vec2 id = floor(uv); // gives id of each cell
    guv *= rot(iTime - (length(id)+0.1)*0.3); // rotate uv starting from center
    guv *= cos(.3*(length(id)-iTime)); // scale uv

    float s = shape(guv); // add shape

    vec3 col = clamp(vec3(s),0.,1.) + 0.1;
    col.r += clamp(cos(.2*(length(id)-iTime)),0.,1.); // cycle color

    fragColor = vec4(col,1.0);
}

#define rad(x) (radians(360.*(x)))
#define time (iTime*.1)

float box(vec3 p, vec3 a) {
  vec3 q = abs(p)-a;
  return length(max(q,0.))+min(0.,max(q.x,max(q.y,q.z)));
}

float sphere(vec3 p, float r) { return length(p) - r; }

mat2 rot(float a) { float c=cos(a),s=sin(a); return mat2(c,s,-s,c); }
float glow;

#define tri(x) (abs(fract((x)*.5)-.5)*2.)

float scene(vec3 p) {
  float bg = box(p+vec3(0,5+1,0),vec3(5));//p.y + 2.;
  vec3 orig_p = p.xyz;
  p.xz *= rot(rad(3.*time));
  p.xy *= rot(rad(3.*time*sqrt(2.)));
  p.yz *= rot(rad(2./6.));

  // define the glow as a regular sdf float
  float B = box(p, vec3(1))-.3;
  float S = sphere(p, 2.*(tri(time*2.)));
  float glowbox = max(B,-S);

  p = orig_p;
  p -= sin(time*6.);
  p.xz *= rot(time*10.);
  p.yz *= rot(time*20.);
  p.xy *= rot(time*30.);
  float squeeze = 4.8;
  float solid = mix(sphere(p,.1+2.*tri(time*2.)),box(p,vec3(.5)-.2),.5+.5*sin(time*17.));

  // a buffer between the solid and the fog to prevent
  // super-bright areas on the solid as the raymarcher
  // slows down. try setting this to 0 and see what happens
  // should be at least DENSITY
  // raising this is the best way to deal with edge glow around
  // the solid
  float BUFFER_ZONE = 0.05;


  // lower = more dense fog. should be at least 1/stepcount
  // (100 here)
  float FOGSTEP = .001;

  // ==== and here's the 2-line fog effect ====
  // * if we're inside the glowbox, increase the glow by how far
  // we have penetrated into the box.
  // * however, if we're passing close to the solid object, we have to
  // not increase the glow because it will overcount as we step closer
  // and closer to the solid. so we zero it out below the buffer zone
  glow += max(0., -glowbox)*smoothstep(0.,BUFFER_ZONE*2.,solid);
  // step towards the glowbox by the distance we are inside the cube,
  // plus a little buffer to get us inside the box. if we're *in* the
  // box, we want to keep moving, and abs(glowbox) will make sure we're
  // approximately inside the box still. note that we will *never* detect
  // a surface at any point of the glowbox, because the minimum distance is
  // always positive.
  glowbox = abs(glowbox) + FOGSTEP;
  return min(bg,min(solid,glowbox));
}


float ray(vec3 start, vec3 dir) {
  float dist=0.;
  glow=0.;

  for (int i=0;i<700;i++) {
    float c = scene(start+dir*dist);
    dist += c;
    if (c < .0001) return dist;
    if (dist > 1000.) return 10001.;
  }

  return min(dist,10001.);
}

vec3 grad(vec3 p) {
  mat3 K = mat3(p,p,p) - mat3(.01);
  return scene(p)-vec3(scene(K[0]),scene(K[1]),scene(K[2]));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float focus=2.;
    vec3 cam = vec3(0,0,-5.*focus);
    vec3 dir = normalize(vec3(uv,focus));

    float dist = ray(cam, dir);
    float G = glow; 
    vec3 hit = cam+dir*dist;
    vec3 norm = normalize(grad(hit));

    vec3 refl = reflect(dir,norm);
    float refldist = ray(hit+refl*.1, refl);
    float reflglow = glow;

    // thanks blackle for the awesome fake image lighting :3
    float diff = 0.;

    if (dist < 1000.) diff = length(sin(norm)*.5+.5)/sqrt(3.);


    vec3 col = diff*diff*vec3(.5,.9,1);

    // lower numbers are a denser, more uniform fog
    // high numbers are very glowy and a little glitchy
    float UNIFORMITY = 0.8;
    col += pow(G, UNIFORMITY) *.2 * vec3(.9, .1,.5);;
    col += reflglow * vec3(.9,.1,.5)*.2;
    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

#define NUM_OCTAVES 5

vec2 random2(vec2 st) {
	st = vec2(dot(st, vec2(127.1, 311.7)),
		dot(st, vec2(269.5, 183.3)));
	return -1.0 + 2.0 * fract(sin(st) * 43758.5453123 * 0.7897);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);

	vec2 u = f * f * (3.0 - 2.0 * f);

	return mix(mix(dot(random2(i + vec2(0.0, 0.0)), f - vec2(0.0, 0.0)),
		dot(random2(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0)), u.x),
		mix(dot(random2(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0)),
			dot(random2(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
	mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.7;
	}
	return v;
}

mat2 rotate(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x * 4.0;
    vec3 sph = vec3(uv.x, uv.y, cos(length(uv)));

    float r = length(uv);
	float f = (1.0 - sqrt(1.0 - r)) / r;
    vec3 sunPos = vec3(cos(iTime / 4.0), 0.0, sin(iTime / 4.0));
    float light = dot(sph, sunPos);
    float reflectivity = 1.0;

    float noiseVal = fbm((uv * f * 5.0) + 500.0) + 0.5;
    float cloudNoise = fbm((uv * f * 2.0 + vec2(-iTime / 8.0, 0.0)) + 1000.0) + 0.5;
    //cloudNoise *= rotate(iTime);
    vec3 col = vec3(0.0);

    if (r < 1.0) {
        if (noiseVal < 0.35) {
            col = vec3(1.0, 0.0, 0.0);
            light = 0.5;
        }
        else if (noiseVal > 0.65) {
            col = vec3(1.0, 1.0, 1.0) * noiseVal;
        }
        else {
            col = vec3(noiseVal / 4.0, noiseVal / 4.0, noiseVal / 4.0);
        }
        reflectivity = noiseVal * 2.0;
        //col += smoothstep(0.55, 0.6, cloudNoise);
    }
    else {
        col = vec3(0.8, 0.3, 0.3) * smoothstep(1.1, 1.0, r);
        reflectivity = 0.5;
    }

    col *= light;
    col += smoothstep(1.1, 1.0, r) * light * reflectivity * vec3(1.0, 0.5, 0.5);

    vec2 suv = uv - sunPos.xy * 3.0;
    col += 0.1 / length(suv) * smoothstep(0.99, 1.0, r) * smoothstep(0.5, 0.8, -sunPos.z);

    float flareCol = 0.0;
    vec2 fuv = suv;
    float angle = atan(fuv.y, fuv.x) + 3.14159 / 8.0;
    flareCol += 0.1/abs(sin(angle*4.0)*cos(angle*4.0)) * smoothstep(0.6, 0.0, length(fuv));
    flareCol *= 0.1/abs(sin(angle*2.0)*cos(angle*2.0)) * smoothstep(0.6, 0.0, length(fuv));
    //flareCol += 0.01 / abs(fuv.x*fuv.y) * smoothstep(1.0, 0.5, length(fuv)) / 4.0;
    //fuv *= rotate(3.14159 / 4.0);
    //flareCol += 0.01 / abs(fuv.x*fuv.y) * smoothstep(1.0, 0.5, length(fuv)) / 16.0;

    flareCol *= smoothstep(0.7, 0.8, -sunPos.z);
    flareCol *= smoothstep(0.99, 1.0, length(uv-suv));
    //flareCol *= smoothstep(0.99, 1.0, r);
    if (light < 0.01 && r < 1.0) col = vec3(0.0);
    col += flareCol;

    col += smoothstep(0.75, 0.0, noiseVal) * vec3(1.0, 0.0, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 fluid(vec2 uv1){
 vec2 uv = uv1;
 float t = iTime;
 for (float i = 1.; i < 15.; i++)
  {
    uv.x -= (t+sin(t+uv.y*i/1.5))/i;
    uv.y -= cos(uv.x*i/1.5)/i;
  }
  return uv;
}

